-- Đang đổ dữ liệu cho bảng `news`
--

INSERT INTO `news` (`id`, `news_title`, `author_id`, `news_summary`, `news_content`, `news_image`, `news_featured`, `news_view`, `tag_id`, `category_id`, `news_slug`, `deleted_at`, `created_at`, `updated_at`) VALUES
(1, 'Chủ nghĩa thực dân mới ở châu Phi nhìn từ Phong trào gilê vàng tại Pháp', 5, 'VietTimes -- Câu thành ngữ “cháy nhà ra mặt chuột” để nói về những hiện tượng tiêu cực trong xã hội bị ẩn dấu trong thời gian dài bỗng nhiên để lộ nguyên hình dưới tác động của những biến cố lớn làm đảo lộn thực tại, đang được giới phân tích chính trị quốc tế sử dụng để mô tả biến động chính trị-xã hội mang tên “Phong trào gilê vàng” ở Pháp. Trong đó, bộc lộ nguyên hình chủ nghĩa thực dân mới ở châu Phi.', '<p>VietTimes -- C&acirc;u th&agrave;nh ngữ &ldquo;ch&aacute;y nh&agrave; ra mặt chuột&rdquo; để n&oacute;i về những hiện tượng ti&ecirc;u cực trong x&atilde; hội bị ẩn dấu trong thời gian d&agrave;i bỗng nhi&ecirc;n để lộ nguy&ecirc;n h&igrave;nh dưới t&aacute;c động của những biến cố lớn l&agrave;m đảo lộn thực tại, đang được giới ph&acirc;n t&iacute;ch ch&iacute;nh trị quốc tế sử dụng để m&ocirc; tả biến động ch&iacute;nh trị-x&atilde; hội mang t&ecirc;n &ldquo;Phong tr&agrave;o gil&ecirc; v&agrave;ng&rdquo; ở Ph&aacute;p. Trong đ&oacute;, bộc lộ nguy&ecirc;n h&igrave;nh chủ nghĩa thực d&acirc;n mới ở ch&acirc;u Phi.</p>\r\n\r\n<p><a href=\"https://image.viettimes.vn/666x374/Uploaded/2020/uqvpbqmx/2019_02_18/chu_nghia_thuc_dan_chau_phi4743150_1822019.jpg\"><img alt=\"Ảnh minh họa Chủ nghĩa thực dân mới tại châu Phi.\" src=\"https://image.viettimes.vn/666x374/Uploaded/2020/uqvpbqmx/2019_02_18/chu_nghia_thuc_dan_chau_phi4743150_1822019.jpg\" /></a></p>\r\n\r\n<p>Ảnh minh họa Chủ nghĩa thực d&acirc;n mới tại ch&acirc;u Phi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một trong những t&aacute;c động rất đ&aacute;ng ch&uacute; &yacute; từ Phong tr&agrave;o gil&ecirc;&nbsp;v&agrave;ng l&agrave; đ&atilde; đẩy quan hệ giữa Ph&aacute;p v&agrave; Italia leo thang căng thẳng chưa từng thấy kể từ sau Thế chiến II, li&ecirc;n quan tới biến động ch&iacute;nh trị-x&atilde; hội được thể hiện trong c&aacute;c cuộc biểu t&igrave;nh chống ch&iacute;nh quyền của Tổng thống Ph&aacute;p Emmanuel Macron b&ugrave;ng ph&aacute;t từ cuối năm 2018 tới nay vẫn chưa c&oacute; dấu hiệu lắng dịu.&nbsp;</p>\r\n\r\n<p>Ng&agrave;y 5.2.2019, Ph&oacute; Thủ tướng Italia Luigi di Maio v&agrave; cũng l&agrave; người l&atilde;nh đạo Phong tr&agrave;o Năm sao của nước n&agrave;y cho biết &ocirc;ng đ&atilde; gặp hai nh&agrave; l&atilde;nh đạo của Phong tr&agrave;o gil&ecirc;&nbsp;v&agrave;ng ở Ph&aacute;p đồng thời l&agrave; c&aacute;c nh&agrave; hoạt động x&atilde; hội Cristophe Chalencon v&agrave; Ingris Levasei. Trong cuộc gặp n&agrave;y, &ocirc;ng Luigi di Maio ca ngợi phong tr&agrave;o ch&iacute;nh trị-x&atilde; hội n&agrave;y l&agrave; &ldquo;bước nhảy vọt đối với nước Ph&aacute;p&rdquo;, hoặc l&agrave; &ldquo;l&agrave;n gi&oacute; l&agrave;m thay đổi khu vực ch&iacute;nh trị dọc theo d&atilde;y Alps&rdquo;.</p>\r\n\r\n<p>C&ograve;n tr&ecirc;n trang Facebook c&aacute; nh&acirc;n, đồng Ph&oacute; Thủ tướng Italia, &ocirc;ng Matteo Salvini, cũng k&ecirc;u gọi người d&acirc;n Ph&aacute;p &quot;tự giải ph&oacute;ng khỏi một tổng thống tồi tệ sau bầu cử nghị viện ch&acirc;u &Acirc;u sẽ diễn ra v&agrave;o th&aacute;ng 5.2019&quot;. &Ocirc;ng Matteo Salvini kh&ocirc;ng ngần ngại tuy&ecirc;n bố rằng Tổng thống Ph&aacute;p Emmanuel Macron trong khi l&ecirc;n lớp dạy dỗ về sự đo&agrave;n kết ch&acirc;u &Acirc;u nhưng lại đẩy h&agrave;ng ng&agrave;n người nhập cư về ph&iacute;a th&agrave;nh phố Vintimille v&agrave; v&ugrave;ng Piemont&nbsp;của Italia [1,2].</p>\r\n\r\n<p>Theo nhận định của giới nghi&ecirc;n cứu về Italia, hiện tượng nhiều quan chức cao cấp trong Ch&iacute;nh quyền Roma ủng hộ Phong tr&agrave;o gil&ecirc;&nbsp;v&agrave;ng ở Ph&aacute;p chỉ l&agrave; &ldquo;giọt nước l&agrave;m tr&agrave;n li&rdquo; từ m&acirc;u thuẫn v&agrave; bất đồng&nbsp;vốn &acirc;m ỉ giữa Ph&aacute;p v&agrave; Italia xuất ph&aacute;t từ nhiều nguy&ecirc;n nh&acirc;n. Trước đ&acirc;y 8 năm, v&agrave;o năm 2011, Ch&iacute;nh quyền Paris dưới thời của Tổng thống Ph&aacute;p Nicolas Sarkozy c&ocirc;ng khai tiến h&agrave;nh chiến dịch qu&acirc;n sự để t&agrave;n ph&aacute; đất nước Libya-một quốc gia được Ch&iacute;nh quyền Roma coi l&agrave; &ldquo;s&acirc;n sau của Italia&rdquo;.</p>\r\n\r\n<p>Tiếp đến, Ph&aacute;p ph&aacute;t động chiến tranh ở Mali. Những h&agrave;nh động n&agrave;y của nước Ph&aacute;p đ&atilde; tạo ra trạng th&aacute;i hỗn loạn k&eacute;o d&agrave;i v&agrave; dẫn tới hậu quả nghi&ecirc;m trọng l&agrave; g&acirc;y n&ecirc;n l&agrave;n s&oacute;ng di cư lớn nhất tới ch&acirc;u &Acirc;u kể từ sau Thế Chiến II. Thế nhưng, thay v&igrave; phải ra tay giải quyết hậu quả n&agrave;y, Ch&iacute;nh quyền Paris lại đơn phương đ&oacute;ng cửa bi&ecirc;n giới kiểm so&aacute;t d&ograve;ng người người tị nạn ồ ạt &ldquo;đổ bộ&rdquo; v&agrave;o ch&acirc;u &Acirc;u v&agrave; bỏ mặc Ch&iacute;nh quyền Roma phải lao t&acirc;m khổ tứ đối ph&oacute; với l&agrave;n s&oacute;ng n&agrave;y. Bởi Italia l&agrave; điểm đến đầu ti&ecirc;n của người di cư từ điểm xuất ph&aacute;t Libya đi qua Địa Trung Hải.&nbsp;</p>\r\n\r\n<p>Sau khi Phong tr&agrave;o Năm sao gi&agrave;nh thắng lợi v&agrave; nắm quyền l&atilde;nh đạo ở Italia, m&acirc;u thuẫn &acirc;m ỉ giữa Paris v&agrave; Roma b&ugrave;ng ph&aacute;t mạnh mẽ. Trong một b&agrave;i ph&aacute;t biểu tr&ecirc;n truyền h&igrave;nh, Tổng thống Ph&aacute;p Emmanuel Macron đ&atilde; gay gắt l&ecirc;n &aacute;n hiện tượng nổi l&ecirc;n của Phong tr&agrave;o Năm sao ở Italia v&agrave; &ocirc;ng gọi đ&oacute; l&agrave; &ldquo;bệnh hủi d&acirc;n t&uacute;y&rdquo; đang l&acirc;y lan khắp ch&acirc;u &Acirc;u. Trong đ&oacute;, &ocirc;ng đ&atilde; nhiều lần nhắc đến t&ecirc;n Matteo Salvini, Bộ trưởng Bộ nội vụ Italia l&agrave; người đưa ra h&agrave;ng loạt ph&aacute;t ng&ocirc;n mang t&iacute;nh k&iacute;ch động Phong tr&agrave;o gil&ecirc;&nbsp;v&agrave;ng chống ch&iacute;nh phủ ở Ph&aacute;p. Th&iacute; dụ, &ocirc;ng Matteo Salvini k&ecirc;u gọi những người biểu t&igrave;nh &aacute;o v&agrave;ng ở Ph&aacute;p lật đổ Tổng thống Ph&aacute;p Emmanuel Macron. Người ph&aacute;t ng&ocirc;n Bộ ngoại giao Ph&aacute;p gọi đ&oacute; l&agrave; &ldquo;h&agrave;nh động can thiệp mang t&iacute;nh khi&ecirc;u kh&iacute;ch kh&ocirc;ng thể chấp nhận được&rdquo;.</p>\r\n\r\n<p>Căng thẳng trong quan hệ ngoại giao giữa Paris v&agrave; Roma li&ecirc;n quan tới Phong tr&agrave;o gil&ecirc;&nbsp;v&agrave;ng ở Ph&aacute;p được đẩy l&ecirc;n một nấc thang mới cao hơn li&ecirc;n quan tới một y&ecirc;u s&aacute;ch trong cương lĩnh ch&iacute;nh trị của phong tr&agrave;o n&agrave;y. Đ&oacute; l&agrave; y&ecirc;u cầu Ch&iacute;nh phủ Ph&aacute;p chấm dứt ch&iacute;nh s&aacute;ch cướp b&oacute;c cũng như sự can thiệp ch&iacute;nh trị v&agrave; qu&acirc;n sự v&agrave;o c&aacute;c nước ch&acirc;u Phi; đưa tiền v&agrave; t&agrave;i sản của những nh&agrave; cầm quyền độc t&agrave;i do b&oacute;c lột m&agrave; c&oacute; trả lại cho người d&acirc;n ch&acirc;u Phi; x&oacute;a bỏ hệ thống t&agrave;i ch&iacute;nh thuộc địa của Ph&aacute;p, theo đ&oacute; Paris đ&atilde; từng buộc c&aacute;c nước ch&acirc;u Phi&nbsp;phải duy tr&igrave; dự trữ ngoại hối bằng đồng Franc tại c&aacute;c ng&acirc;n h&agrave;ng của Ph&aacute;p, khiến ch&acirc;u Phi&nbsp;lu&ocirc;n l&acirc;m v&agrave;o t&igrave;nh cảnh ngh&egrave;o đ&oacute;i; duy tr&igrave; quan hệ b&igrave;nh đẳng v&agrave; c&ocirc;ng bằng với c&aacute;c quốc gia ch&acirc;u Phi&nbsp;[3].</p>\r\n\r\n<p>Ủng hộ nội dung n&agrave;y trong Cương lĩnh ch&iacute;nh trị của Phong tr&agrave;o gil&ecirc;&nbsp;v&agrave;ng, Ph&oacute; thủ tướng Italia, &ocirc;ng Luigi di Maio, c&aacute;o buộc Ph&aacute;p &ldquo;bần c&ugrave;ng h&oacute;a ch&acirc;u Phi&rdquo; th&ocirc;ng qua đồng tiền Franc d&agrave;nh cho ch&acirc;u Phi, gọi tắt l&agrave; CFA (Colonies Fran&ccedil;aises d&rsquo;Afrique - Những thuộc địa của Ph&aacute;p tại ch&acirc;u Phi), m&agrave; một trong những hậu quả nghi&ecirc;m trọng l&agrave; dẫn đến l&agrave;n s&oacute;ng di d&acirc;n chưa từng c&oacute; kể từ Thế Chiến II tới ch&acirc;u &Acirc;u. &Ocirc;ng Luigi di Maio c&ograve;n c&aacute;o buộc Ph&aacute;p sử dụng đồng CFA-di sản từ thời thuộc địa Ph&aacute;p ở ch&acirc;u Phi&nbsp;trong thế kỷ 20. &Ocirc;ng n&oacute;i:&ldquo;T&ocirc;i cần n&oacute;i r&otilde; một sự thật l&agrave;, khi in đồng tiền cho cả 14 quốc gia ch&acirc;u Phi, nước Ph&aacute;p đ&atilde; ngăn cản sự ph&aacute;t triển của những quốc gia tr&ecirc;n ch&acirc;u lục n&agrave;y v&agrave; do đ&oacute; đ&atilde; bần c&ugrave;ng h&oacute;a người d&acirc;n ch&acirc;u Phi, khiến họ phải l&igrave;a bỏ qu&ecirc; hương xứ sở v&agrave; cam chịu cảnh chết ch&oacute;c tr&ecirc;n khi nỗ lực vượt qua Địa Trung Hải với hy vọng được đặt ch&acirc;n l&ecirc;n bờ biển ch&acirc;u &Acirc;u&rdquo;.&nbsp;</p>\r\n\r\n<p>&Ocirc;ng Luigi di Maio y&ecirc;u cầu đưa ra c&aacute;c biện ph&aacute;p trừng phạt khẩn cấp đối với Ph&aacute;p v&igrave; h&agrave;nh động của quốc gia n&agrave;y đang thực d&acirc;n h&oacute;a ch&acirc;u Phi. Bởi theo &ocirc;ng Luigi di Maio, một trong những c&ocirc;ng cụ quan trọng của chủ nghĩa thực d&acirc;n Ph&aacute;p ở ch&acirc;u Phi&nbsp;l&agrave; đồng Franc. Theo &ocirc;ng, Ph&aacute;p l&agrave; một trong những quốc gia in tiền cho 14 quốc gia ch&acirc;u Phi. Đồng tiền n&agrave;y đ&atilde; tồn tại ở Ph&aacute;p trong nhiều thế kỷ cho đến thời điểm n&oacute; được thay thế bằng đồng tiền chung ch&acirc;u &Acirc;u v&agrave;o năm 2002. Tuy đồng Franc đ&atilde; kh&ocirc;ng c&ograve;n lưu h&agrave;nh ở Ph&aacute;p nhưng khu vực đồng tiền chung Franc vẫn c&ograve;n tồn tại d&agrave;nh cho c&aacute;c nước thuộc địa.</p>\r\n\r\n<p>B&ecirc;n ngo&agrave;i khu vực ch&acirc;u Phi&nbsp;c&ograve;n c&oacute; đồng Franc d&ugrave;ng cho v&ugrave;ng Th&aacute;i B&igrave;nh Dương, gọi tắt l&agrave; XPF, được lưu h&agrave;nh ở c&aacute;c nước New Caledonia, Polinesia thuộc Ph&aacute;p v&agrave; ở Wallis v&agrave; Futuna. Quyền ph&aacute;t h&agrave;nh đơn vị tiền tệ n&agrave;y thuộc về Viện ph&aacute;t h&agrave;nh tiền tệ cho c&aacute;c v&ugrave;ng l&atilde;nh thổ hải ngoại của Ph&aacute;p c&oacute; Văn ph&ograve;ng tại Paris. Ph&aacute;p c&ograve;n c&oacute; l&atilde;nh thổ hải ngoại ở v&ugrave;ng biển Caribe v&agrave; Ấn Độ Dương như Guadeloupe, Guiana, Martinique, Reunion, St. Pierre &amp; Miquelon v&agrave; Mayotte. Ở những quốc gia n&agrave;y lưu h&agrave;nh v&agrave; sử dụng đồng Euro do Bộ hải ngoại của Ph&aacute;p ph&aacute;t h&agrave;nh. Hai trung t&acirc;m ph&aacute;t h&agrave;nh tiền tệ n&agrave;y đều chịu sự kiểm so&aacute;t của Ng&acirc;n h&agrave;ng Trung ương Ph&aacute;p.</p>\r\n\r\n<p>Hiện nay, đồng CFA l&agrave; t&ecirc;n của một trong hai loại tiền tệ được sử dụng ở c&aacute;c nước ch&acirc;u Phi. Loại thứ nhất l&agrave; đồng Franc của 8 quốc gia ở T&acirc;y Phi thuộc Li&ecirc;n minh kinh tế v&agrave; tiền tệ T&acirc;y Phi (gồm c&aacute;c nước Benin, Burkina Faso, C&ocirc;te d&#39;Ivoire, Guinea-Bissau, Mali, Niger, Senegal, Togo). M&atilde; số của đồng Franc T&acirc;y Phi l&agrave; XOF, do Ng&acirc;n h&agrave;ng trung ương của c&aacute;c quốc gia T&acirc;y Phi ph&aacute;t h&agrave;nh BCEAO (Banque Centrale des &Eacute;tats de l&#39;Afrique de l&#39;Ouest) c&oacute; trụ sở tại Senegal. Loại thứ hai của đồng Franc phục vụ 6 quốc gia trong Cộng đồng kinh tế của c&aacute;c quốc gia Trung Phi (gồm Cameroon, Congo, Gabon, Equatorial Guinea, Cộng h&ograve;a Trung Phi v&agrave; Sat). M&atilde; số của đồng Franc Trung Phi l&agrave; XAF do Ng&acirc;n h&agrave;ng của c&aacute;c quốc gia Trung Phi ph&aacute;t h&agrave;nh, c&oacute; trụ sở tại Cameroon.</p>\r\n\r\n<p>Ng&agrave;y nay, đồng Franc CFA l&agrave; loại tiền phổ biến nhất tr&ecirc;n lục địa ch&acirc;u Phi. Hai trung t&acirc;m ph&aacute;t h&agrave;nh tiền Franc cho ch&acirc;u Phi&nbsp;l&agrave; Ng&acirc;n h&agrave;ng trung ương của c&aacute;c quốc gia T&acirc;y Phi v&agrave; Ng&acirc;n h&agrave;ng c&aacute;c quốc gia Trung Phi hợp t&aacute;c chặt chẽ với Ng&acirc;n h&agrave;ng Trung ương Ph&aacute;p. Thực chất, hai trung t&acirc;m ph&aacute;t h&agrave;nh tiền n&agrave;y l&agrave; c&aacute;c chi nh&aacute;nh khu vực của Ng&acirc;n h&agrave;ng Trung ương Ph&aacute;p.</p>\r\n\r\n<p>Theo c&aacute;c ch&iacute;nh trị gia Italia, Ph&aacute;p vẫn tiếp tiếp tục khai th&aacute;c c&aacute;c thuộc địa cũ của họ ở ch&acirc;u Phi&nbsp;v&agrave; đảm bảo cho c&aacute;c nước ch&acirc;u lục n&agrave;y chuyển đổi c&aacute;c đồng tiền Franc của họ sang đồng Euro theo tỷ lệ cố định. Tuy nhi&ecirc;n, c&aacute;c nước ch&acirc;u Phi&nbsp;thuộc địa của Ph&aacute;p phải dự trữ tiền tệ của họ bằng Euro trong một t&agrave;i khoản đặc biệt tại Kho bạc Ph&aacute;p với gi&aacute; trị &iacute;t nhất bằng 2/3 tổng gi&aacute; trị dự trữ ngoại tệ của họ. Đồng thời, Ng&acirc;n h&agrave;ng Trung ương Ph&aacute;p&nbsp;thiết lập quyền kiểm so&aacute;t chặt chẽ đối với hai trung t&acirc;m ph&aacute;t h&agrave;nh tiền cho c&aacute;c nước ch&acirc;u Phi. Do đ&oacute;, c&aacute;c nước ch&acirc;u Phi&nbsp;thuộc địa của Ph&aacute;p ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; quyền sử dụng số ngoại tệ của họ m&agrave; chỉ c&oacute; thể nhận được một phần th&ocirc;ng qua c&aacute;c khoản t&iacute;n dụng c&oacute; gi&aacute; trị kh&ocirc;ng vượt qu&aacute; 20% tổng gi&aacute; trị ngoại tệ dự trữ trong Ng&acirc;n h&agrave;ng Trung ương Ph&aacute;p.</p>\r\n\r\n<p>Sau b&agrave;i ph&aacute;t biểu của Luigi di Maio về chủ nghĩa thực d&acirc;n Ph&aacute;p thời hiện đại, c&aacute;c phương tiện truyền th&ocirc;ng mới &ldquo;mượn gi&oacute; bẻ măng&rdquo; v&agrave; c&ocirc;ng cố nhiều b&agrave;i viết tiết lộ chi tiết về t&igrave;nh cảnh c&ugrave;ng cực 7 quốc gia thuộc khu vực đồng tiền CFA được coi l&agrave; những quốc gia ngh&egrave;o nhất thế giới, ở đ&oacute; c&oacute; 2/3 d&acirc;n buộc phải sống với mức dưới 2 USD một ng&agrave;y. V&igrave; thế, nh&agrave; b&aacute;o người Đức Ernst Wolff nhận định, Phong tr&agrave;o gil&ecirc;&nbsp;v&agrave;ng ở Ph&aacute;p c&oacute; thể lan tỏa sang c&aacute;c nước thuộc địa cũ của Ph&aacute;p v&agrave; đ&acirc;y thực sự l&agrave; mối đe dọa kh&ocirc;ng nhỏ đối với Tổng thống Ph&aacute;p Emmanuel Macron [4,5].</p>\r\n\r\n<p>Cũng cần nhận thấy rằng, việc c&aacute;c ch&iacute;nh trị gia Italia l&ecirc;n &aacute;n chủ nghĩa thực d&acirc;n mới của Ph&aacute;p ở ch&acirc;u Phi&nbsp;c&ograve;n nhằm một mục ti&ecirc;u ch&iacute;nh trị quan trọng. Đ&oacute; l&agrave; buộc đảng của Tổng thống Ph&aacute;p Emmanuel Macron chịu thất bại trong cuộc bầu cử v&agrave;o Nghị viện ch&acirc;u &Acirc;u v&agrave;o th&aacute;ng 5.2019. V&agrave;o cuối năm 2018, nh&agrave; l&atilde;nh đạo của phong tr&agrave;o ch&iacute;nh trị Li&ecirc;n minh Phương Bắc, &ocirc;ng Matteo Salvini, v&agrave; l&atilde;nh đạo Phong tr&agrave;o quốc gia Ph&aacute;p Marine Le Pen đ&atilde; tuy&ecirc;n bố hợp nhất nỗ lực của họ trong cuộc bầu cử sắp tới v&agrave;o Nghị viện ch&acirc;u &Acirc;u nhằm cứu ch&acirc;u &Acirc;u chống lại c&aacute;i gọi l&agrave; &ldquo;chủ nghĩa to&agrave;n trị&rdquo; m&agrave; Tổng thống Ph&aacute;p Emmanuel Macron l&agrave; một đại diện điển h&igrave;nh-người đ&atilde; từng đề xuất &ldquo;S&aacute;ng kiến ch&acirc;u &Acirc;u&rdquo; nhằm thực hiện chủ trương tiếp tục qu&aacute; tr&igrave;nh hội nhập ng&agrave;y c&agrave;ng s&acirc;u rộng ở ch&acirc;u lục n&agrave;y. Trong khi đ&oacute;, c&aacute;c ch&iacute;nh trị gia Italia c&ocirc;ng khai ủng hộ b&agrave; Marine Le Pen - đối thủ ch&iacute;nh trị h&agrave;ng đầu của &ocirc;ng Emmanuel Macron.</p>\r\n\r\n<p>V&igrave; thế, theo nhận định của Sebastien Maillar, Gi&aacute;m đốc Viện ch&iacute;nh trị Jacques Delors của Ph&aacute;p, những va chạm hiện nay giữa ch&iacute;nh giới ở Paris v&agrave; Roma l&agrave; vết nứt kh&oacute; h&agrave;n gắn c&oacute; thể dẫn tới sự tan r&atilde; quan hệ giữa Ph&aacute;p v&agrave; Italia v&agrave; r&uacute;t cuộc sẽ khiến EU thiếu đi một yếu tố rất cần thiết cho c&ocirc;ng cuộc gắn kết ch&acirc;u &Acirc;u vốn đang l&acirc;m v&agrave;o khủng hoảng./.&nbsp;&nbsp;&nbsp;</p>', 'upload/news\\1588907716.jpeg', 1, 0, 1, 1, 'chu-nghia-thuc-dan-moi-o-chau-phi-nhin-tu-phong-trao-gile-vang-tai-phap', NULL, '2020-09-06 10:09:18', '2020-09-07 20:15:16'),
(2, 'Năm 2018: EU đứng trước kỷ nguyên bất định và bất ổn', 5, 'VietTimes -- Năm 2018 sắp qua đi chứng kiến cuộc khủng hoảng chính trị-xã hội chưa có dấu hiệu lắng dịu ở Pháp cùng với nhiều sự kiện lớn phản ánh sự chuyển dịch chưa từng có trong cục diện chính trị Châu Âu, cũng như thế giới đang đưa Liên minh Châu Âu (EU) tới trước tương lai bất định và bất ổn, thậm chí có thể không tránh được sự tan rã của “siêu quốc gia này” trong thập kỷ tới.', '<p><a href=\"https://image.viettimes.vn/666x374/Uploaded/2020/znksga/2018_12_14/eu_oafj.jpg\"><img alt=\"EU đang nằm trong kỷ nguyên bất ổn và bất định.\" src=\"https://image.viettimes.vn/666x374/Uploaded/2020/znksga/2018_12_14/eu_oafj.jpg\" /></a></p>\r\n\r\n<p>EU đang nằm trong kỷ nguy&ecirc;n bất ổn v&agrave; bất định.</p>\r\n\r\n<p><strong>Dự b&aacute;o bi quan về tương lai của EU</strong></p>\r\n\r\n<p>Nếu v&agrave;o trang t&igrave;m kiếm của Google.com g&otilde; từ kh&oacute;a &ldquo;tương lai của EU&rdquo; bằng tiếng Anh (Future&nbsp;of&nbsp;EU) hay tiếng Nga (будущее ЕС) người đọc c&oacute; thể t&igrave;m thấy h&agrave;ng ngh&igrave;n b&agrave;i viết v&agrave; c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu dự b&aacute;o, trong đ&oacute; đưa ra nhận định kh&aacute; bi quan về tương lai bất ổn v&agrave; bất định, thậm ch&iacute; l&agrave; nguy cơ tan r&atilde; của EU trong thập kỷ tới. Đơn cử l&agrave; nhận định của Cựu Bộ trưởng Bộ ngoại giao Đức, &ocirc;ng Frank-Walter Steinmeier, cho rằng EU đang tr&ecirc;n bờ vực sụp đổ m&agrave; nguy&ecirc;n nh&acirc;n c&oacute; thể l&agrave; Brexit, cuộc khủng hoảng di cư, khủng hoảng t&agrave;i ch&iacute;nh, chủ nghĩa d&acirc;n tộc v&agrave; d&acirc;n t&uacute;y, đưa ch&acirc;u lục n&agrave;y v&agrave;o v&ograve;ng xo&aacute;y bất ổn l&acirc;u d&agrave;i [1]. &nbsp;</p>\r\n\r\n<p>C&ograve;n Tiến sỹ kinh tế Joseph Stiglitz, người từng đoạt Giải Nobel kinh tế năm 2001, trong b&agrave;i trả lời phỏng vấn b&aacute;o Đức Die Welt, đưa ra dự b&aacute;o rằng EU c&oacute; thể tan r&atilde; trong những năm tới xuất ph&aacute;t từ sự sụp đổ của Li&ecirc;n minh tiền tệ Ch&acirc;u &Acirc;u. Theo &ocirc;ng, c&aacute;c nh&agrave; ch&iacute;nh trị EU c&oacute; thể đạt được thỏa thuận nhằm n&iacute;u k&eacute;o li&ecirc;n minh n&agrave;y tr&aacute;nh khỏi sụp đổ nhưng thực tế nghiệt ng&atilde; lại c&oacute; xu hướng tr&aacute;i ngược với nỗ lực đ&oacute; [2]. Để tr&aacute;nh tương lai sụp đổ, đ&atilde; đến l&uacute;c EU phải cải tổ một c&aacute;ch căn bản [3,4]</p>\r\n\r\n<p><strong>EU bước v&agrave;o kỷ nguy&ecirc;n bất ổn v&agrave; bất định</strong></p>\r\n\r\n<p>Ng&agrave;y 29/10/2018 đ&aacute;nh dấu quyết định lịch sử của Thủ tướng Đức Angela Merken r&uacute;t khỏi ch&iacute;nh trường, đồng nghĩa với việc chấm dứt &ldquo;kỷ nguy&ecirc;n Merkel&rdquo; v&agrave; c&oacute; thể sẽ l&agrave; &ldquo;cơn &aacute;c mộng&rdquo; thật sự đối với Ch&acirc;u &Acirc;u. Trước hết, cần nhận thấy, ch&iacute;nh s&aacute;ch mở cửa của Thủ tướng Đức Angela Merken để xử l&yacute; cuộc khủng hoảng nhập cư lớn nhất ở Ch&acirc;u &Acirc;u kể từ sau Thế Chiến II mặc d&ugrave; đưa b&agrave; trở th&agrave;nh nh&acirc;n vật của năm 2015 tr&ecirc;n Tạp ch&iacute;&nbsp;&ldquo;Time&rdquo; danh tiếng nhất thế giới,&nbsp;nhưng lại l&agrave; một nh&acirc;n tố quan trọng dẫn đến những thất bại ch&iacute;nh trị của b&agrave; tại Đức v&agrave;o thời điểm n&agrave;y. Hơn 1,5 triệu người nhập cư đến nước Đức kể từ năm 2014 đ&atilde; v&agrave; đang g&acirc;y ra v&ocirc; v&agrave;n chuyện ti&ecirc;u cực, lộn xộn, kh&ocirc;ng chỉ trong x&atilde; hội Đức m&agrave; c&ograve;n ở nhiều nước th&agrave;nh vi&ecirc;n EU, khiến đảng cầm quyền Li&ecirc;n minh D&acirc;n chủ - Cơ đốc gi&aacute;o mất đi sự ủng hộ của c&aacute;c cử tri, trước hết l&agrave; c&aacute;c cứ tri ủng hộ đường lối bảo thủ v&agrave; chủ nghĩa d&acirc;n tộc.</p>\r\n\r\n<p>Tiếp sau quyết định của Thủ tướng Đức Angela Merke r&uacute;t khỏi ch&iacute;nh trường l&agrave; cuộc khủng hoảng ch&iacute;nh trị - x&atilde; hội b&ugrave;ng nổ ở Ph&aacute;p trong th&aacute;ng 11.2018 đ&atilde; khiến cho bộ đ&ocirc;i Đức - Ph&aacute;p đ&oacute;ng vai tr&ograve; l&agrave; trụ cột của EU bị lung lay từ nền tảng. Do đ&oacute;, trong &ldquo;kỷ nguy&ecirc;n hậu Merkel&rdquo; v&agrave; khủng hoảng ở Ph&aacute;p, EU sẽ rơi v&agrave;o trạng th&aacute;i bất ổn v&agrave; bất định do t&aacute;c động của rất nhiều yếu tố.</p>\r\n\r\n<p><em>(1) Bất ổn v&agrave; bất định từ sự kiện Brexit</em>. Theo giới ph&acirc;n t&iacute;ch, Brexit phản &aacute;nh sự chuyển dịch địa ch&iacute;nh trị chưa từng c&oacute; kh&ocirc;ng chỉ ở Ch&acirc;u &Acirc;u m&agrave; cả tr&ecirc;n thế giới trong hơn nửa thế kỷ qua, kể từ sau Thế Chiến II. Trong cuộc khủng hoảng hiện nay ở Ph&aacute;p, trong số c&aacute;c y&ecirc;u s&aacute;ch của người biểu t&igrave;nh gửi tới ch&iacute;nh phủ c&oacute; Frexit nhằm &ldquo;noi gương&rdquo; nước Anh chia tay với EU. Brexit hay l&agrave; Frexit phản &aacute;nh xu thế trỗi dậy chủ nghĩa d&acirc;n tộc hướng tới chủ quyền quốc gia nhằm n&eacute; tr&aacute;nh cơn b&atilde;o to&agrave;n cầu h&oacute;a đang x&oacute;a nh&agrave; bi&ecirc;n giới c&aacute;c quốc gia [5].</p>\r\n\r\n<p><em>(2) Bất ổn v&agrave; bất định từ sự bế tắc trong việc h&oacute;a giải cuộc khủng hoảng nhập cư</em>. C&aacute;ch thức giải quyết cuộc khủng hoảng di cư hiện nay của c&aacute;c nước Ch&acirc;u &Acirc;u chỉ h&oacute;a giải &ldquo;phần nổi của tảng băng ch&igrave;m&rdquo;, c&ograve;n căn nguy&ecirc;n s&acirc;u xa l&agrave; t&igrave;nh trạng bất ổn k&eacute;o d&agrave;i gần như bất tận ở c&aacute;c nước Bắc Phi - Trung Đ&ocirc;ng (MENA) m&agrave; ch&iacute;nh c&aacute;c quốc gia th&agrave;nh vi&ecirc;n EU cũng &ldquo;c&oacute; tội&rdquo; g&acirc;y ra khi họ tham gia cuộc chiến tranh x&acirc;m lược Libya v&agrave; tiếp tục can thiệp v&agrave;o chủ quyền của nhiều nước kh&aacute;c.</p>\r\n\r\n<p>Kh&ocirc;ng phải ngẫu nhi&ecirc;n m&agrave; những người biểu t&igrave;nh ở Ph&aacute;p đề ra y&ecirc;u s&aacute;ch đ&ograve;i ch&iacute;nh phủ Ph&aacute;p chấm dứt h&agrave;nh động đưa qu&acirc;n x&acirc;m lược c&aacute;c nước kh&aacute;c. Ch&iacute;nh Ph&aacute;p l&agrave; quốc gia ph&aacute;t động chiến dịch can thiệp qu&acirc;n sự ở Mali trong năm 2013 sau khi kết th&uacute;c chiến tranh x&acirc;m lược Libya trong năm 2011. &nbsp;Đến n&agrave;y, c&aacute;c nước Ch&acirc;u &Acirc;u vẫn chỉ t&agrave;i trợ nhỏ giọt để t&aacute;i thiết c&aacute;c nước Bắc Phi - Trung Đ&ocirc;ng sau khi bị chiến tranh v&agrave; xung đột t&agrave;n ph&aacute;. Cựu Tổng thống Libya, &ocirc;ng Muammar al-Gaddafi, từng cảnh b&aacute;o nếu c&aacute;c nước Ch&acirc;u&nbsp; &Acirc;u x&acirc;m lược v&agrave; x&oacute;a sổ nh&agrave; nước Libya thỉ họ sẽ phải hứng chịu &ldquo;cơn đại hồng hủy&rdquo; thời hiện đại mang t&ecirc;n &ldquo;l&agrave;n s&oacute;ng di cư&rdquo; từ Bắc Phi - Trung Đ&ocirc;ng [6,7].</p>\r\n\r\n<p><em>(3)</em>&nbsp;<em>Bất ổn v&agrave; bất định từ quyết định của Tổng thống Mỹ Donald Trump r&uacute;t khỏi Hiệp ước về t&ecirc;n lửa tầm trung (INF) ở Ch&acirc;u &Acirc;u.&nbsp;</em>Bằng quyết định r&uacute;t khỏi INF, c&aacute;c tập đo&agrave;n t&agrave;i phiệt Mỹ theo đuổi tham vọng biến Ch&acirc;u &Acirc;u th&agrave;nh chiến trường b&agrave;y sẵn cho một cuộc chiến tranh thế giới mới như họ đ&atilde; từng l&agrave;m trong Thế Chiến I v&agrave; Thế Chiến II. Trước đ&acirc;y, hai cuộc chiến tranh thế giới trong thế kỷ XX đ&atilde; từng đưa Mỹ trở th&agrave;nh si&ecirc;u cường số 1 thế giới, th&igrave; cuộc chiến tranh thế giới mới ở Ch&acirc;u &Acirc;u sẽ l&agrave;m cho &ldquo;nước Mỹ vĩ đại trở lại&rdquo;.&nbsp;</p>\r\n\r\n<p><em>(4) Bất ổn v&agrave; bất định do sự chia rẽ trong việc đối ph&oacute; với chiến lược &ldquo;v&agrave;nh đai - con đường&rdquo; của Trung Quốc</em>. Chiến lược &ldquo;v&agrave;nh đai - con đường&rdquo; thực chất l&agrave; biểu hiện của trật tự thế giới mới theo kiểu Trung Quốc, thay thế trật tự thế giới do Mỹ kiểm so&aacute;t. Chiến lược n&agrave;y kh&ocirc;ng chỉ g&acirc;y chia rẽ giữa nhiều nước tr&ecirc;n thế giới m&agrave; ngay cả trong nội bộ EU. V&igrave; thế, EU đ&atilde; đề ra chiến lược kết nối Ch&acirc;u &Aacute; nhằm đối trọng với chiến lược &ldquo;v&agrave;nh đai - con đường&rdquo; của Trung Quốc.</p>\r\n\r\n<p><em>(5) Bất ổn v&agrave; bất định trong chủ trương cấm vận Nga.&nbsp;</em>Nhiều nước th&agrave;nh vi&ecirc;n EU đ&atilde; nhận thấy cấm vận Nga l&agrave; &ldquo;con dao hai lưỡi&rdquo; kh&iacute;a v&agrave;o ch&iacute;nh t&uacute;i tiền của c&aacute;c doanh nghiệp ch&acirc;u &Acirc;u. Hiện nay nhiều ch&iacute;nh kh&aacute;ch ở ch&acirc;u &Acirc;u k&ecirc;u gọi EU dỡ bỏ lệnh cấm vận n&agrave;y.</p>\r\n\r\n<p><em>(6) Sự nổi l&ecirc;n của chủ nghĩa d&acirc;n tộc.&nbsp;</em>Chia rẽ ch&iacute;nh trị ở ch&acirc;u &Acirc;u sẽ tiếp tục diễn ra do sự nỗi l&ecirc;n của chủ nghĩa d&acirc;n tộc ở Đ&ocirc;ng &Acirc;u v&agrave; c&aacute;c nước quanh khu vực Địa Trung Hải. Thất bại của Đảng D&acirc;n chủ - X&atilde; hội ở Thụy Điển trong năm 2018 trong cuộc bầu cử lập ph&aacute;p trước đảng cực hữu chống người nhập cư c&ugrave;ng với xu hướng nổi l&ecirc;n c&aacute;c đảng<a href=\"http://congan.com.vn/quoc-te/thoi-su-quoc-te/thuy-dien-co-nguy-co-treo-quoc-hoi-vi-dang-cuc-huu-thang-the_61664.html\">&nbsp;cực hữu</a>&nbsp;với tư tưởng b&agrave;i ngoại, chống người nhập cư, đ&ograve;i thắt chặt bi&ecirc;n giới kh&ocirc;ng ngừng gi&agrave;nh được tỷ lệ ủng hộ lớn ở nhiều cuộc<a href=\"http://congan.com.vn/quoc-te/thoi-su-quoc-te/thuy-dien-co-nguy-co-treo-quoc-hoi-vi-dang-cuc-huu-thang-the_61664.html\">&nbsp;bầu cử</a>&nbsp;tr&ecirc;n khắp Ch&acirc;u &Acirc;u, sẽ l&agrave; c&uacute; h&iacute;ch dẫn tới sự thay đổi ho&agrave;n to&agrave;n bộ mặt của ch&acirc;u lục n&agrave;y.</p>\r\n\r\n<p><em>(7) Tương lai bất định của bộ đ&ocirc;i Đức - Ph&aacute;p trụ cột của EU.&nbsp;</em>&nbsp;Trong bối cảnh Tổng thống Ph&aacute;p Emmanuel Macron chịu &aacute;p lực ng&agrave;y c&agrave;ng tăng sau 18 th&aacute;ng cầm quyền do những cải c&aacute;ch kh&ocirc;ng mang lại kết quả như mong muốn dẫn tới cuộc khủng hoảng ch&iacute;nh trị - x&atilde; hội đ&atilde; l&agrave;m suy giảm đ&aacute;ng kể vị thế của nước Ph&aacute;p. Trong khi đ&oacute;, nước Đức cũng đang đứng trước tương lai bất định sau khi Thủ tướng Angela Merken r&uacute;t khỏi ch&iacute;nh trường. Động th&aacute;i n&agrave;y khiến vai tr&ograve; trụ cột EU của Ph&aacute;p v&agrave; Đức đang bị lung lay.</p>\r\n\r\n<p><em>(8) Chủ trương x&acirc;y dựng Qu&acirc;n đội Ch&acirc;u &Acirc;u đang đứng trước dấu hỏi nghi vấn.&nbsp;</em>Ngay trước thềm lễ kỷ niệm 100 năm kết th&uacute;c Thế Chiến I v&agrave;o ng&agrave;y 11.11.2018, Tổng thống Ph&aacute;p Emmanuel Macron ch&iacute;nh thức tuy&ecirc;n bố chủ trương x&acirc;y dựng Qu&acirc;n đội Ch&acirc;u &Acirc;u, độc lập với NATO, để bảo vệ ch&acirc;u lục n&agrave;y chống lại bất kỳ mối đe dọa chiến tranh n&agrave;o từ Trung Quốc, Nga v&agrave; cả Mỹ nữa. Tuy&ecirc;n bố n&agrave;y nhận &nbsp;được sự ủng hộ nhiệt t&igrave;nh của Thủ tướng Đức Angela Merken nhưng lại bị Tổng thống Mỹ Donald Trump phản đối. &Ocirc;ng Donald Trump coi chủ trương của Tổng thống Ph&aacute;p Emmanuel Macron x&acirc;y dựng Qu&acirc;n đội Ch&acirc;u &Acirc;u l&agrave; &ldquo;lời x&uacute;c phạm nước Mỹ&rdquo; v&agrave; đưa ra lời cảnh b&aacute;o rằng trước khi Ch&acirc;u &Acirc;u x&acirc;y dựng qu&acirc;n đội ri&ecirc;ng th&igrave; n&ecirc;n nộp khoản đ&oacute;ng g&oacute;p b&igrave;nh đẳng của họ cho NATO.</p>\r\n\r\n<p><em>(9) Sự rạn nứt ng&agrave;y một nghi&ecirc;m trọng trong quan hệ xuy&ecirc;n Đại T&acirc;y Dương.&nbsp;</em>Trong hai năm 2000 - 2001, Ch&iacute;nh phủ Mỹ cho ph&eacute;p giải mật c&aacute;c t&agrave;i liệu của Cục lưu trữ quốc gia chứng tỏ sau Thế Chiến II, Cục t&igrave;nh b&aacute;o trung ương Mỹ (CIA) x&acirc;y dựng hai đề &aacute;n để kiểm so&aacute;t Ch&acirc;u &Acirc;u. Đ&oacute;&nbsp; l&agrave; Đề &aacute;n th&agrave;nh lập Li&ecirc;n minh qu&acirc;n sự Bắc Đại T&acirc;y Dương (NATO) v&agrave;o năm 1949 để kiểm so&aacute;t ch&acirc;u &Acirc;u về an ninh v&agrave; Đề &aacute;n th&agrave;nh lập Li&ecirc;n minh ch&acirc;u &Acirc;u để kiểm so&aacute;t Ch&acirc;u &Acirc;u về kinh tế v&agrave; ch&iacute;nh trị [8,9]. Điều n&agrave;y giải th&iacute;ch c&acirc;u hỏi v&igrave; sao c&aacute;c nước th&agrave;nh vi&ecirc;n EU cũng như th&agrave;nh vi&ecirc;n NATO nhất cử nhất động l&agrave;m theo chỉ thị của Mỹ trong quan hệ với Li&ecirc;n X&ocirc; trước đ&acirc;y v&agrave; Nga hiện nay.</p>\r\n\r\n<p>Hiện nay, với khẩu hiệu &ldquo;Nước Mỹ trước ti&ecirc;n&rdquo;, Tổng thống Mỹ Donald Trump muốn c&aacute;c nước th&agrave;nh vi&ecirc;n EU trở th&agrave;nh c&aacute;c quốc gia độc lập, c&oacute; chủ quyền, v&agrave; sẽ k&yacute; hết c&aacute;c hiệp định kinh tế trực tiếp với Mỹ nhưng vẫn l&agrave; th&agrave;nh vi&ecirc;n NATO. Ch&iacute;nh v&igrave; thế, &ocirc;ng Donald Trump cổ s&uacute;y cho Brexit v&agrave; khuy&ecirc;n c&aacute;c nước th&agrave;nh vi&ecirc;n EU kh&aacute;c n&ecirc;n &ldquo;noi gương&rdquo; nước Anh. Trong bối cảnh đ&oacute;, Thủ tướng Đức Angela Merken tuy&ecirc;n bố rằng đ&atilde; đến l&uacute;c EU cần phải &ldquo;l&agrave;m chủ vận mệnh của m&igrave;nh&rdquo; v&agrave; xem x&eacute;t lại quan hệ xuy&ecirc;n Đại T&acirc;y Dương [10]. Liệu EU c&oacute; thể tho&aacute;t khỏi &ldquo;v&ograve;ng kim c&ocirc;&rdquo; của Mỹ hay kh&ocirc;ng, vẫn l&agrave; c&acirc;u hỏi c&ograve;n bỏ ngỏ bởi./.&nbsp;&nbsp;</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://viettimes.vn/nam-2018-eu-dung-truoc-ky-nguyen-bat-dinh-va-bat-on-310888.html\">https://viettimes.vn/nam-2018-eu-dung-truoc-ky-nguyen-bat-dinh-va-bat-on-310888.html</a></p>', 'upload/news\\1588907700.jpeg', 1, 0, 4, 1, 'nam-2018-eu-dung-truoc-ky-nguyen-bat-dinh-va-bat-on', NULL, '2020-09-06 10:10:26', '2020-09-07 20:15:00'),
(3, 'Làn sóng biểu tình Gilê vàng:“Mùa Xuân Arab” lan tỏa tới Paris hay là cuộc mạng mới của nước Pháp?', 6, 'VietTimes -- Làn sóng biểu tình của những người Gilê vàng bùng phát trên toàn lãnh thổ nước Pháp từ cuối tháng 11.2018 tới nay chưa có dấu hiệu lắng dịu được gọi là “Mùa xuân Paris” do sự tương đồng với các biến động chính trị mang tên “Mùa Xuân Arab” ở các nước Bắc Phi-Trung Đông nhưng trên thực tế đã có dấu hiệu của một cuộc cách mạng mới ở nước Pháp thời hiện đại.', '<p>VietTimes -- L&agrave;n s&oacute;ng biểu t&igrave;nh của những người Gil&ecirc; v&agrave;ng b&ugrave;ng ph&aacute;t tr&ecirc;n to&agrave;n l&atilde;nh thổ nước Ph&aacute;p từ cuối th&aacute;ng 11.2018 tới nay chưa c&oacute; dấu hiệu lắng dịu được gọi l&agrave; &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo; do sự tương đồng với c&aacute;c biến động ch&iacute;nh trị mang t&ecirc;n &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo; ở c&aacute;c nước Bắc Phi-Trung Đ&ocirc;ng nhưng tr&ecirc;n thực tế đ&atilde; c&oacute; dấu hiệu của một cuộc c&aacute;ch mạng mới ở nước Ph&aacute;p thời hiện đại.</p>\r\n\r\n<p><a href=\"https://image.viettimes.vn/666x374/Uploaded/2020/znksga/2018_12_10/offoe_wyvg.png\"><img alt=\"Phong trào phản kháng của những người theo phong trào Gilê vàng!\" src=\"https://image.viettimes.vn/666x374/Uploaded/2020/znksga/2018_12_10/offoe_wyvg.png\" /></a></p>\r\n\r\n<p>Phong tr&agrave;o phản kh&aacute;ng của những người theo phong tr&agrave;o Gil&ecirc; v&agrave;ng!</p>\r\n\r\n<p><strong>L&agrave;n s&oacute;ng biểu t&igrave;nh Gil&ecirc; v&agrave;ng: &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo; lan tỏa tới Paris hay l&agrave; cuộc mạng mới của nước Ph&aacute;p?</strong></p>\r\n\r\n<p>Kể từ cuối th&aacute;ng 11/2018 tới nay, l&agrave;n s&oacute;ng bạo loạn b&ugrave;ng ph&aacute;t tr&ecirc;n to&agrave;n l&atilde;nh thổ nước Ph&aacute;p khi những người biểu t&igrave;nh đầu ti&ecirc;n kho&aacute;c l&ecirc;n người chiếc &aacute;o gil&ecirc; m&agrave;u v&agrave;ng v&agrave; đổ xuống đường biểu t&igrave;nh phản đối quyết định của ch&iacute;nh phủ Ph&aacute;p &aacute;p thuế m&ocirc;i trường l&ecirc;n xăng dầu, đến nay đ&atilde; chuyển sang y&ecirc;u s&aacute;ch ch&iacute;nh trị v&agrave; chưa c&oacute; dấu hiệu dừng lại, c&oacute; thể dẫn tới sự kết th&uacute;c chưa thể lường trước được đối với nước Ph&aacute;p. Hiện tượng được giới ph&acirc;n t&iacute;ch gọi l&agrave; &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo; do sự tương đồng với c&aacute;c biến động ch&iacute;nh trị mang t&ecirc;n &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo; ở c&aacute;c nước Bắc Phi - Trung Đ&ocirc;ng đ&atilde; từng dẫn tới sự lật đổ ch&iacute;nh thể nhiều nước ở khu vực n&agrave;y.</p>\r\n\r\n<p><strong>Sự tương đồng giữa &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo;v&agrave; &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo;.</strong></p>\r\n\r\n<p>Sự tương đồng của &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo; với &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo; b&ugrave;ng ph&aacute;t đầu ti&ecirc;n v&agrave;o cuối năm 2010 ở Tunisia mang t&ecirc;n &ldquo;c&aacute;ch mạng hoa nh&agrave;i&rdquo; được thể hiện trong c&aacute;c động th&aacute;i sau.</p>\r\n\r\n<p><em>Một l&agrave;</em>,&nbsp;<em>yếu tố ch&acirc;m ng&ograve;i cho &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo; v&agrave; &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo;.</em></p>\r\n\r\n<p>Ở Tunisia, ch&acirc;m ng&ograve;i cho l&agrave;n s&oacute;ng bạo loạn dẫn tới c&aacute;c biến động ch&iacute;nh trị mang t&ecirc;n &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo; chỉ l&agrave; sự phản đối h&agrave;nh động của một nh&acirc;n vi&ecirc;n cảnh s&aacute;t đường phố tịch thu h&agrave;ng sản xuất của người b&aacute;n h&agrave;ng Mohamed Bouazizi, khiến người n&agrave;y phẫn uất v&agrave; quyết định phản kh&aacute;ng bằng h&agrave;nh động tự thi&ecirc;u. H&igrave;nh ảnh tự thi&ecirc;u n&agrave;y lan tỏa tr&ecirc;n mạng x&atilde; hội đ&atilde; ch&acirc;m ng&ograve;i cho l&agrave;n s&oacute;ng bạo loạn v&agrave; sau đ&oacute; chuyển th&agrave;nh biến động ch&iacute;nh trị với c&aacute;c y&ecirc;u s&aacute;ch đ&ograve;i ch&iacute;nh phủ giải quyết vấn đề&nbsp;thất nghiệp,&nbsp;gi&aacute; cả thực phẩm tăng,&nbsp;nạn ch&iacute;nh quyền tham nhũng,&nbsp;tự do ng&ocirc;n luận&nbsp;bị b&oacute;p nghẹt v&agrave;&nbsp;mức sống của người d&acirc;n thấp v&agrave; cuối c&ugrave;ng l&agrave; lật đổ&nbsp;ch&iacute;nh thể của tổng thống Tunisia, Ben Ali.</p>\r\n\r\n<p>Tương tự, l&agrave;n s&oacute;ng bạo loạn ở Ph&aacute;p cũng được ch&acirc;m ng&ograve;i chỉ l&agrave; h&agrave;nh động phản đối quyết định của ch&iacute;nh phủ &aacute;p thuế l&ecirc;n gi&aacute; xăng dầu. Đ&acirc;y l&agrave; việc m&agrave; nhiều quốc gia tr&ecirc;n thế giới vẫn l&agrave;m. Thế nhưng ngay sau đ&oacute;, khẩu hiệu của những người biểu t&igrave;nh đ&atilde; vượt ra khỏi khu&ocirc;n khổ phản đối ch&iacute;nh s&aacute;ch &aacute;p thuế gi&aacute; nhi&ecirc;n liệu v&agrave; mở rộng ra tới y&ecirc;u s&aacute;ch kinh tế - x&atilde; hội, ch&iacute;nh trị v&agrave; đối ngoại, thậm ch&iacute; buộc Tổng thống Ph&aacute;p Emmanuel Macron phải từ chức, giải t&aacute;n Quốc hội v&agrave; tổ chức bầu cử trước thời hạn.</p>\r\n\r\n<p><em>Hai l&agrave;, xuất ph&aacute;t điểm c&oacute; vẻ tự ph&aacute;t, kh&ocirc;ng c&oacute; c&aacute; nh&acirc;n hay tổ chức n&agrave;o đứng ra đảm nhận vai tr&ograve; l&atilde;nh đạo phong tr&agrave;o.</em></p>\r\n\r\n<p>Nh&igrave;n bề ngo&agrave;i, c&aacute;c cuộc biểu t&igrave;nh khởi đầu &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo; như &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo; nổ ra ho&agrave;n to&agrave;n mang t&iacute;nh tự ph&aacute;t. Cũng như ở Tunisia v&agrave; nhiều nước Bắc Phi-Trung Đ&ocirc;ng, trong l&agrave;n s&oacute;ng bạo loạn ở Ph&aacute;p kh&ocirc;ng c&oacute; một tổ chức hay một nh&acirc;n vật ch&iacute;nh trị n&agrave;o đứng ra đảm nhận tr&aacute;ch nhiệm l&atilde;nh đạo những người xuống đường biểu t&igrave;nh. Chỉ trong giai đoạn sau, dư luận mới được thấy một số tổ chức ch&iacute;nh trị xuất hiện v&agrave; đề ra y&ecirc;u s&aacute;ch đ&ograve;i lật đổ ch&iacute;nh thể hiện h&agrave;nh. Nếu ở Tunisia, Egypt, Libya hay Syria xuất hiện &ldquo;c&aacute;c lực lượng đối lập&rdquo; m&agrave; điển h&igrave;nh l&agrave; tổ chức &ldquo;Anh em Hồi gi&aacute;o&rdquo;, th&igrave; ở Ph&aacute;p cũng xuất hiện c&aacute;c đảng ch&iacute;nh trị đối lập với Tổng thống Ph&aacute;p Emmanuel Macron như Đảng X&atilde; hộ v&agrave; Mặt trận quốc gia từng thất bại trong cuộc bầu cử năm 2017.</p>\r\n\r\n<p><em>Ba l&agrave;, c&oacute; vai tr&ograve; rất quan trọng của mạng x&atilde; hội trong &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo; v&agrave; &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo;.</em></p>\r\n\r\n<p>Mạng x&atilde; hội đ&oacute;ng vai tr&ograve; rất quan trọng như l&agrave; nh&agrave; cổ vũ v&agrave; tổ chức l&agrave;n s&oacute;ng bạo loạn trong phong tr&agrave;o &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo; cũng như &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo;. Ch&iacute;nh v&igrave; thế, Bộ trưởng Bộ ngoại giao Mỹ v&agrave;o thời điểm 2010 l&agrave; b&agrave; Hillary Clinton đ&atilde; gọi &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo; l&agrave; &ldquo;cuộc c&aacute;ch mạng từ Internet&rdquo;. Mang Internet l&agrave; nơi ph&aacute;t lệnh, tổ chức, kết nối, khuyến k&iacute;ch những người biểu t&igrave;nh v&agrave; đưa ra y&ecirc;u s&aacute;ch đối với ch&iacute;nh phủ m&agrave; kh&ocirc;ng cần tới người ph&aacute;t ng&ocirc;n hay người đại diện ch&iacute;nh thức cho c&aacute;c &ldquo;lực lượng c&aacute;ch mạng&rdquo; [1].</p>\r\n\r\n<p><em>Bốn l&agrave;, sự lan tỏa của &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo; v&agrave; &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo;</em></p>\r\n\r\n<p>Khởi ph&aacute;t từ Tunisia, &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo; ngay sau đ&oacute; đ&atilde; lan tỏa tới nhiều quốc gia Bắc Phi-Trung Đ&ocirc;ng như Egyt, Libya, Yemen, Mali hay Syria, tạo ra trạng th&aacute;i &ldquo;bất ổn c&oacute; kiểm so&aacute;t&rdquo; v&agrave; l&agrave;m rung chuyển to&agrave;n bộ khu vực n&agrave;y. Hiện nay, &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo; đ&atilde; lan tỏa sang c&aacute;c nước Bỉ v&agrave; H&agrave; Lan. Cảnh s&aacute;t Bỉ đ&atilde; bắt giữ khoảng 100 người v&igrave; tổ chức một cuộc biểu t&igrave;nh kh&ocirc;ng xin ph&eacute;p ở thủ đ&ocirc; Brussels. Hiện tại, cảnh s&aacute;t địa phương đang được tăng cường để duy tr&igrave; trật tự v&agrave; đề ph&ograve;ng khả năng xảy ra bạo động.</p>\r\n\r\n<p>Tại H&agrave; Lan, những người &aacute;o v&agrave;ng cũng đ&atilde; xuống đường biểu t&igrave;nh phản đối t&igrave;nh trạng gi&aacute; cả leo thang, tăng tuổi nghỉ hưu, vấn đề người nhập cư v&agrave; y&ecirc;u cầu Thủ tướng Mark Rutte từ chức. Kh&ocirc;ng loại trừ khả năng, trong thời gian tới, &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo; sẽ lan tỏa sang c&aacute;c nước kh&aacute;c trong Li&ecirc;n minh ch&acirc;u &Acirc;u (EU) bởi ở những quốc gia đ&oacute; cũng tiềm ẩn c&aacute;c yếu tố c&oacute; thể ch&acirc;m ng&ograve;i cho l&agrave;n s&oacute;ng bạo loạn như m&acirc;u thuẫn giữa c&aacute;c đảng ph&aacute;i ch&iacute;nh trị v&agrave; c&aacute;c tổ chức x&atilde; hội, khoảng c&aacute;ch giữa người gi&agrave;u v&agrave; người ngh&egrave;o ng&agrave;y c&agrave;ng rộng, ch&iacute;nh s&aacute;ch lương bổng, hưu tr&iacute;, nạn thất nghiệp, gi&aacute;o dục, nh&agrave; ở, y tế, nhập cư kh&ocirc;ng thỏa đ&aacute;ng.</p>\r\n\r\n<p><strong>Phải chăng &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo;</strong><strong>&nbsp;l&agrave; cuộc c&aacute;ch mạng mới của nước Ph&aacute;p?</strong></p>\r\n\r\n<p>Nếu nh&igrave;n v&agrave;o những y&ecirc;u s&aacute;ch của những người biểu t&igrave;nh Gil&ecirc; v&agrave;ng ghi trong &aacute;p-ph&iacute;ch in tr&ecirc;n &aacute;o gil&ecirc; của họ th&igrave; c&oacute; thể thấy bản chất của &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo; ho&agrave;n to&agrave;n kh&aacute;c với &ldquo;M&ugrave;a Xu&acirc;n Arab&rdquo;. Nội dung 25 y&ecirc;u s&aacute;ch cơ bản của phong tr&agrave;o n&agrave;y ghi trong &aacute;p-ph&iacute;ch như l&agrave; bản th&ocirc;ng điệp gửi tới ch&iacute;nh phủ Ph&aacute;p chứng tỏ những người Gil&ecirc; v&agrave;ng đang hướng tới một cuộc c&aacute;ch mạng mới.</p>\r\n\r\n<p><strong>Y&ecirc;u s&aacute;ch về kinh tế v&agrave; lao động:&nbsp;</strong>(1) cải c&aacute;ch hệ thống thuế, theo đ&oacute; nh&agrave; nước phải ban h&agrave;nh đạo luật cấm đ&aacute;nh thuế tới mức vượt qu&aacute; 25% thu nhập của c&ocirc;ng d&acirc;n; (2) ngay lập tức tăng 40% mức lương tối thiểu của người lao động, mức lương hưu v&agrave; mức sinh hoạt tối thiểu của người d&acirc;n; (3) tạo ra h&agrave;ng loạt c&aacute;c c&ocirc;ng việc mới trong lĩnh vực chăm s&oacute;c sức khỏe, gi&aacute;o dục, giao th&ocirc;ng c&ocirc;ng cộng, bảo vệ luật ph&aacute;p v&agrave; trật tự, v.v., để đảm bảo hoạt động cần thiết của tất cả c&aacute;c cơ quan dịch vụ c&ocirc;ng cộng;</p>\r\n\r\n<p>(4) ch&iacute;nh phủ cần c&oacute; kế hoạch x&acirc;y dựng 5 triệu đơn vị nh&agrave; ở gi&aacute; rẻ, giảm gi&aacute; tiền thu&ecirc; nh&agrave;, tạo điều kiện thế chấp mua nh&agrave; v&agrave; tạo việc l&agrave;m trong ng&agrave;nh x&acirc;y dựng, xử phạt nghi&ecirc;m khắc c&aacute;c thị trưởng, c&aacute;c hội đồng th&agrave;nh phố v&agrave; ch&iacute;nh quyền khu vực để xẩy ra t&igrave;nh trạng người d&acirc;n phải chịu cảnh m&agrave;n trời chiếu đất; (5) giảm quy m&ocirc; c&aacute;c ng&acirc;n h&agrave;ng v&agrave; xỏa bỏ c&aacute;c ng&acirc;n h&agrave;ng độc quyền để kh&ocirc;ng để ng&agrave;nh t&agrave;i ch&iacute;nh rơi v&agrave;o t&igrave;nh cảnh khủng hoảng, cấm c&aacute;c ng&acirc;n h&agrave;ng hoạt động đầu cơ chứng kho&aacute;n, cấm mọi hoạt động d&ugrave;ng tiền của người đ&oacute;ng thuế để t&aacute;i cấp vốn cho c&aacute;c ng&acirc;n h&agrave;ng mất khả năng thanh khoản; (6) hủy tất cả c&aacute;c khoản nợ kh&ocirc;ng tồn tại trong thực tế đ&atilde; từng được thanh to&aacute;n nhiều lần.</p>\r\n\r\n<p><strong>Y&ecirc;u s&aacute;ch trong lĩnh vực ch&iacute;nh trị:&nbsp;</strong>(7) với sự ủng hộ của to&agrave;n d&acirc;n, tiến h&agrave;nh viết lại Hiến ph&aacute;p để trao quyền lực cho nh&acirc;n d&acirc;n, th&ocirc;ng qua đạo luật cho ph&eacute;p tổ chức c&aacute;c cuộc trưng cầu &yacute; d&acirc;n theo s&aacute;ng kiến của nh&acirc;n d&acirc;n; (8) cấm mọi hoạt động vận động h&agrave;nh lang v&agrave; c&aacute;c cơ chế g&acirc;y ảnh hưởng, cấm vĩnh viễn những người c&oacute; tiền &aacute;n h&igrave;nh sự giữ c&aacute;c chức vụ được bầu, cấm quan chức ki&ecirc;m nhiệm một số chức vụ được bầu; (9) Về Frexit: đưa nước Ph&aacute;p rời khỏi EU, trả lại chủ quyền ch&iacute;nh trị, t&agrave;i ch&iacute;nh v&agrave; kinh tế cho nước Ph&aacute;p (đ&acirc;y l&agrave; &yacute; nguyện của nh&acirc;n d&acirc;n Ph&aacute;p trong cuộc trưng cầu d&acirc;n &yacute; năm 2005), kh&ocirc;i phục lại gi&aacute; trị của đồng tiền quốc gia bằng c&aacute;ch đưa nước Ph&aacute;p rời khỏi Hiệp ước Lisbon;</p>\r\n\r\n<p>(10) chấm dứt ngay t&igrave;nh trạng trốn thuế; y&ecirc;u cầu 40 c&ocirc;ng ty lớn nhất ho&agrave;n trả lại cho nh&agrave; nước khoản nợ 80 tỷ euro; (11) dừng ngay lập tức qu&aacute; tr&igrave;nh tư nh&acirc;n h&oacute;a v&agrave; chuyển th&agrave;nh t&agrave;i sản do nh&agrave; nước quản l&yacute; như đường cao tốc, s&acirc;n bay, b&atilde;i đậu xe v&agrave; đường sắt; (12) ngay lập tức hủy bỏ ho&agrave;n to&agrave;n c&aacute;c trạm rađa v&agrave; trạm cảnh b&aacute;o bằng lời n&oacute;i tr&ecirc;n c&aacute;c tuyến đường giao th&ocirc;ng bởi c&aacute;c phương tiện đ&oacute; kh&ocirc;ng c&oacute; t&aacute;c dụng trong việc ngăn ngừa tai nạn giao th&ocirc;ng m&agrave; chỉ tạo ra một loại thu thuế tr&aacute; h&igrave;nh;</p>\r\n\r\n<p>(13) loại bỏ &yacute; thức hệ ra khỏi Bộ gi&aacute;o dục v&agrave; nghi&ecirc;m t&uacute;c xem x&eacute;t&nbsp; c&aacute;c phương ph&aacute;p giảng dạy phổ biến to&agrave;n cầu mang t&iacute;nh chất ph&aacute; hoại; (14) trong lĩnh vực tư ph&aacute;p: tăng ng&acirc;n s&aacute;ch l&ecirc;n bốn lần v&agrave; giới hạn thời gian cho ph&eacute;p tối đa để tiến h&agrave;nh c&aacute;c thủ tục ph&aacute;p l&yacute;; ban h&agrave;nh quy định c&aacute;c hoạt động bảo vệ c&ocirc;ng l&yacute; l&agrave; miễn ph&iacute; v&agrave; phổ cập cho to&agrave;n d&acirc;n;</p>\r\n\r\n<p>(15) ph&aacute; bỏ sự độc quyền của truyền th&ocirc;ng, x&oacute;a bỏ mọi quan hệ dinh l&iacute;u giữa giới truyền th&ocirc;ng với c&aacute;c ch&iacute;nh trị gia, coi c&aacute;c phương tiện truyền th&ocirc;ng thuộc về to&agrave;n d&acirc;n v&agrave; đảm bảo đa nguy&ecirc;n &yacute; kiến, cấm c&aacute;c nh&agrave; xuất bản hoạt động tuy&ecirc;n truyền, dừng trợ cấp 2 tỷ mỗi năm cho c&aacute;c phương tiện truyền th&ocirc;ng v&agrave; loại bỏ c&aacute;c khoản ưu ti&ecirc;n giảm thuế cho c&aacute;c nh&agrave; b&aacute;o; (16) đảm bảo quyền tự do của c&ocirc;ng d&acirc;n; đưa v&agrave;o trong Hiến ph&aacute;p điều khoản cấm nh&agrave; nước can thiệp v&agrave;o c&aacute;c vấn đề gi&aacute;o dục, chăm s&oacute;c sức khỏe v&agrave; thế chế gia đ&igrave;nh.</p>\r\n\r\n<p><strong>Y&ecirc;u s&aacute;ch về m&ocirc;i trường v&agrave; sức khỏe:&nbsp;</strong>(17) ban h&agrave;nh đạo luật cấm c&aacute;c nh&agrave; sản xuất k&eacute;o d&agrave;i thời hạn sử dụng thiết bị qu&aacute; 10 năm v&agrave; phải c&oacute; sẵn phụ t&ugrave;ng thay thế; (18) cấm sản xuất v&agrave; sử dụng c&aacute;c loại hộp bao g&oacute;i bằng nhựa v&agrave; c&aacute;c loại bao b&igrave; kh&aacute;c g&acirc;y &ocirc; nhiễm m&ocirc;i trường; (19) hạn chế ảnh hưởng v&agrave; t&aacute;c động của c&aacute;c c&ocirc;ng ty dược phẩm đối với hệ thống chăm s&oacute;c sức khỏe v&agrave; bệnh viện; (20) n&ocirc;ng nghiệp: cấm sản xuất c&aacute;c n&ocirc;ng phẩm biến đổi gen, cấm sử dụng c&aacute;c loại thuốc trừ s&acirc;u c&oacute; t&aacute;c dụng g&acirc;y ung thư v&agrave; c&aacute;c bệnh thuộc hệ thống nội tiết; (21) thực hiện ch&iacute;nh s&aacute;ch t&aacute;i c&ocirc;ng nghiệp h&oacute;a nước Ph&aacute;p nhằm giảm nhập khẩu v&agrave; bảo vệ m&ocirc;i trường.</p>\r\n\r\n<p><strong>Y&ecirc;u s&aacute;ch về địa ch&iacute;nh trị:&nbsp;</strong>(22) ngay lập tức đưa Ph&aacute;p r&uacute;t khỏi NATO v&agrave; cấm sử dụng qu&acirc;n đội Ph&aacute;p trong c&aacute;c cuộc chiến tranh x&acirc;m lược; (23) quan hệ giữa Ph&aacute;p v&agrave; Ch&acirc;u Phi: ngăn chặn ch&iacute;nh s&aacute;ch cướp b&oacute;c cũng như sự can thiệp ch&iacute;nh trị v&agrave; qu&acirc;n sự; đưa tiền v&agrave; t&agrave;i sản của những nh&agrave; cầm quyền độc t&agrave;i do b&oacute;c lột m&agrave; c&oacute; trả lại cho người d&acirc;n Ch&acirc;u Phi;</p>\r\n\r\n<p>Ngay lập tức đưa qu&acirc;n Ph&aacute;p (đang chinh chiến ở nước ngo&agrave;i) trở về nh&agrave;; x&oacute;a bỏ hệ thống t&agrave;i ch&iacute;nh thuộc địa của Ph&aacute;p, theo đ&oacute; đ&atilde; từng buộc c&aacute;c nước Ch&acirc;u Phi phải duy tr&igrave; dự trữ ngoại hối bằng đồng Frank tại c&aacute;c ng&acirc;n h&agrave;ng của Ph&aacute;p, khiến Ch&acirc;u Phi lu&ocirc;n l&acirc;m v&agrave;o t&igrave;nh cảnh ngh&egrave;o đ&oacute;i; duy tr&igrave; quan hệ b&igrave;nh đẳng v&agrave; c&ocirc;ng bằng với c&aacute;c quốc gia Ch&acirc;u Phi; (24) ngăn chặn d&ograve;ng người nhập cư kh&ocirc;ng thể tiếp nhận được v&agrave; kh&ocirc;ng thể h&ograve;a nhập được v&agrave;o x&atilde; hội Ph&aacute;p đ&atilde; từng đưa đất nước ch&uacute;ng ta rơi v&agrave;o t&igrave;nh cảnh một cuộc khủng hoảng văn minh; (25) trong ch&iacute;nh s&aacute;ch đối ngoại: t&ocirc;n trọng luật ph&aacute;p quốc tế v&agrave; c&aacute;c thỏa thuận đ&atilde; k&yacute; kết [2].</p>\r\n\r\n<p>Bản y&ecirc;u s&aacute;ch của những người biểu t&igrave;nh &aacute;o v&agrave;ng kết luận: &ldquo;Ph&aacute;p l&agrave; một đất nước của những người dũng cảm v&agrave; y&ecirc;u lao động. Ch&uacute;ng ta xứng đ&aacute;ng kh&ocirc;ng phải chịu sống trong ngh&egrave;o khổ&rdquo;. R&otilde; r&agrave;ng, đ&acirc;y l&agrave; chương tr&igrave;nh kh&aacute; triệt, tương tự như một Bản tuy&ecirc;n ng&ocirc;n v&agrave; chỉ c&oacute; thể thực hiện được một kh&iacute; nước Ph&aacute;p tiến h&agrave;nh th&agrave;nh c&ocirc;ng cuộc c&aacute;ch mạng mới.</p>\r\n\r\n<p>Cả thế giới đang chờ xem diễn biến của &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo; trong những ng&agrave;y tới sẽ đi tới đ&acirc;u Tuy nhi&ecirc;n, d&ugrave; kết cục thế n&agrave;o th&igrave; nước Ph&aacute;p sắp tới đ&acirc;y v&agrave; cả Ch&acirc;u &Acirc;u nữa, cũng sẽ kh&aacute;c trước trong một thế giới đang thay đổi.</p>\r\n\r\n<p>Đ&uacute;ng v&agrave;o thời điểm &ldquo;M&ugrave;a xu&acirc;n Paris&rdquo; đang l&ecirc;n tới cao tr&agrave;o, th&igrave; Bộ trưởng Bộ ngoại giao Mỹ Mike Pompeo đưa ra tuy&ecirc;n bố g&acirc;y sốc rằng, Tổng thống Donald Trump chủ trương th&agrave;nh lập một trật tự thế giới mới v&agrave; &ocirc;ng muốn chứng tỏ cho c&aacute;c đồng minh Ch&acirc;u &Acirc;u thấy rằng họ cần trở n&ecirc;n độc lập, th&ocirc;ng minh, can đảm hơn v&agrave; kh&ocirc;ng n&ecirc;n h&agrave;nh động theo hệ thống trật tự thế giới cũ đ&atilde; từng được h&igrave;nh th&agrave;nh sau Chiến tranh thế giới lần thứ II.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, Mike Pompeo cũng cho biết, Hoa Kỳ vẫn l&agrave; si&ecirc;u cường duy nhất tr&ecirc;n thế giới v&agrave; sẽ tiếp tục dẫn dắt trật tự thế giới mới m&agrave; Tổng thống Mỹ Donald Trump đang chủ trương x&acirc;y dựng.&nbsp; Theo &ocirc;ng Pompeo, nước Anh c&oacute; thể l&agrave; một &ldquo;tấm gương&rdquo; noi theo cho tất cả c&aacute;c quốc gia kh&aacute;c ở Ch&acirc;u &Acirc;u bởi người Anh rời EU ch&iacute;nh l&agrave; do Li&ecirc;n minh Ch&acirc;u &Acirc;u trong suốt qu&aacute; tr&igrave;nh tồn tại đ&atilde; kh&ocirc;ng thể x&acirc;y dựng được một chiến lược hợp t&aacute;c hiệu quả [3].</p>', 'upload/news\\1588907674.jpeg', 1, 0, 4, 1, 'lan-song-bieu-tinh-gile-vangmua-xuan-arab-lan-toa-toi-paris-hay-la-cuoc-mang-moi-cua-nuoc-phap', NULL, '2020-09-06 10:51:51', '2020-09-07 20:14:34');
INSERT INTO `news` (`id`, `news_title`, `author_id`, `news_summary`, `news_content`, `news_image`, `news_featured`, `news_view`, `tag_id`, `category_id`, `news_slug`, `deleted_at`, `created_at`, `updated_at`) VALUES
(4, 'hahah', 9, 'dcsdc', 'dscds', 'upload/news\\1588788303.jpeg', 1, 0, 1, 12, 'hahah', '2020-09-06 12:13:26', '2020-09-06 10:55:22', '2020-09-06 12:13:26'),
(5, 'Tình trạng Gilê vàng bạo loạn tại Pháp mang ý nghĩa gì?', 1, 'VietTimes -- Nhà kinh tế Scott B. MacDonald cho rằng những hành động đang diễn ra của nhóm mang áo Gilê vàng rất đáng báo động và có thể gây ảnh hưởng lâu dài tới nước Pháp và phần còn lại của châu Âu. Đây là cuộc chiến không chỉ của những người bất mãn trong nước Pháp mà còn của những người dân túy, dân tộc chủ nghĩa với phần còn lại của Liên minh châu Âu.', '<p>VietTimes -- Nh&agrave; kinh tế Scott B. MacDonald cho rằng những h&agrave;nh động đang diễn ra của nh&oacute;m mang &aacute;o Gil&ecirc; v&agrave;ng rất đ&aacute;ng b&aacute;o động v&agrave; c&oacute; thể g&acirc;y ảnh hưởng l&acirc;u d&agrave;i tới nước Ph&aacute;p v&agrave; phần c&ograve;n lại của ch&acirc;u &Acirc;u. Đ&acirc;y l&agrave; cuộc chiến kh&ocirc;ng chỉ của những người bất m&atilde;n trong nước Ph&aacute;p m&agrave; c&ograve;n của những người d&acirc;n t&uacute;y, d&acirc;n tộc chủ nghĩa với phần c&ograve;n lại của Li&ecirc;n minh ch&acirc;u &Acirc;u.</p>\r\n\r\n<p><a href=\"https://image.viettimes.vn/666x374/Uploaded/2020/uqvpbqmx/2018_12_09/crsgiletsjaunesm_mxer.jpg\"><img alt=\"Lực lượng an ninh Pháp bắt giữ một người Gilê vàng.\" src=\"https://image.viettimes.vn/666x374/Uploaded/2020/uqvpbqmx/2018_12_09/crsgiletsjaunesm_mxer.jpg\" /></a></p>\r\n\r\n<p>Lực lượng an ninh Ph&aacute;p bắt giữ một người Gil&ecirc; v&agrave;ng.</p>\r\n\r\n<p>Ph&aacute;p đang chịu đựng một l&agrave;n s&oacute;ng bất m&atilde;n của c&ocirc;ng ch&uacute;ng dưới h&igrave;nh thức tổ chức &quot;&aacute;o Gil&ecirc; v&agrave;ng&quot;, một l&agrave;n s&oacute;ng tự ph&aacute;t v&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; ai l&atilde;nh đạo biểu t&igrave;nh v&agrave; g&acirc;y rối loạn bắt nguồn từ những khu vực n&ocirc;ng th&ocirc;n v&agrave; thị trấn nhỏ của đất nước n&agrave;y. Bắt đầu từ th&aacute;ng 11, một số lượng đ&ocirc;ng đảo người d&acirc;n đ&atilde; biểu t&igrave;nh chống lại thuế m&ocirc;i trường với xăng v&agrave; dầu diesel để hạn chế việc sử dụng &ocirc; t&ocirc; v&agrave; xe tải.</p>\r\n\r\n<p>Theo ước t&iacute;nh của Bộ Nội vụ Ph&aacute;p v&agrave;o ng&agrave;y 2.12, gần 136.000 người đ&atilde; ra đường biểu t&igrave;nh, con số cao hơn tuần trước đ&oacute; l&agrave; 105.000. Mặc d&ugrave; ch&iacute;nh phủ của &ocirc;ng Macron đ&atilde; tuy&ecirc;n bố v&agrave;o ng&agrave;y 3.12 rằng sẽ ho&atilde;n việc tăng thuế nhi&ecirc;n liệu trong 6 th&aacute;ng. Những người &quot;Gil&ecirc; v&agrave;ng&quot; vẫn chưa dừng lại. Thực tế, c&oacute; những quan ngại đang tăng l&ecirc;n rằng hoạt động của họ c&oacute; thể khiến nền kinh tế chững lại, th&uacute;c đẩy phải thay đổi ch&iacute;nh trị v&agrave; c&aacute;c nh&acirc;n tố trong cuộc bầu cử nghị viện Li&ecirc;n minh ch&acirc;u &Acirc;u v&agrave;o th&aacute;ng 5.2019.</p>\r\n\r\n<p>Nguy&ecirc;n nh&acirc;n ch&iacute;nh cho h&agrave;nh động của nh&oacute;m Gil&ecirc; v&agrave;ng l&agrave; việc tăng thuế đối với dầu diesel loại nhi&ecirc;n liệu được sử dụng rộng r&atilde;i tại Ph&aacute;p. Trong v&ograve;ng 12 th&aacute;ng qua, gi&aacute; dầu diesel đ&atilde; tăng 23% so với mức gi&aacute; trung b&igrave;nh l&agrave; 1,71 USD/l&iacute;t, mức gi&aacute; cao nhất kể từ đầu những năm 2000. Mặc d&ugrave; đ&ocirc;i khi việc tăng gi&aacute; l&agrave; do gi&aacute; dầu thế giới tăng (nhưng cũng vừa mới hạ), ch&iacute;nh phủ của &ocirc;ng Macron đ&atilde; tăng thuế hydrocarbon năm 2018 với mức 7,6 cent/l&iacute;t với dầu diesel v&agrave; 3,9 cent/l&iacute;t với xăng. Gần đ&acirc;y nhất, ch&iacute;nh phủ Ph&aacute;p lại tuy&ecirc;n bố kế hoạch sẽ tăng những loại thuế n&agrave;y bắt đầu v&agrave;o th&aacute;ng 1.1.2019. Tổng thống Emmanuel Macron n&oacute;i qu&aacute; tr&igrave;nh n&agrave;y l&agrave; một phần để th&uacute;c đẩy qu&aacute; tr&igrave;nh chuyển đổi sinh th&aacute;i quốc gia.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/uqvpbqmx/2018_12_09/00_zjdg.jpg\"><img alt=\"Tình trạng Gilê vàng bạo loạn tại Pháp mang ý nghĩa gì? - ảnh 1\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/uqvpbqmx/2018_12_09/00_zjdg.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;Khởi ph&aacute;t l&agrave; những cuộc biểu t&igrave;nh &ocirc;n h&ograve;a, giờ những cuộc &quot;ra đường&quot; của người Gil&ecirc; v&agrave;ng đ&atilde; biến th&agrave;nh bạo động.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Vấn đề của tổng thống Macron l&agrave; dư luận Ph&aacute;p đ&atilde; rất tức giận về những g&igrave; họ coi l&agrave; sự yếu k&eacute;m trong quản l&yacute; của ch&iacute;nh phủ; khoảng c&aacute;ch giữa tầng lớp tinh hoa ch&iacute;nh trị v&agrave; d&acirc;n thường; sự thiếu minh bạch trong chi ti&ecirc;u ch&iacute;nh phủ; v&agrave; mức thuế cao kh&ocirc;ng khiến dịch vụ c&ocirc;ng tốt hơn. Ngo&agrave;i ra cũng c&oacute; sự bất m&atilde;n với việc giảm thuế cho người gi&agrave;u v&agrave; khấu trừ thuế cho kinh doanh. T&oacute;m lại, một phần của nước Ph&aacute;p, thường n&oacute;i về những người lao động tầng lớp dưới trung b&igrave;nh, cảm thấy họ bị bỏ rơi v&agrave; nay đang đứng l&ecirc;n v&igrave; quyền lợi của m&igrave;nh.</p>\r\n\r\n<p>Trạng th&aacute;i dồn n&eacute;n của rất nhiều người l&agrave; thu nhập của họ qu&aacute; thấp để trả c&aacute;c chi ph&iacute;, trong khi phải trả qu&aacute; cao cho ph&uacute;c lợi x&atilde; hội. Ng&agrave;y 26.11, một người biểu t&igrave;nh đ&atilde; trả lời phỏng vấn Financial Times: &quot;C&oacute; qu&aacute; nhiều chi ph&iacute; tăng l&ecirc;n: Thuế bảo hiểm x&atilde; hội cho người nghỉ hưu, thuế nhi&ecirc;n liệu. Tổng thống kh&ocirc;ng quan t&acirc;m đến d&acirc;n. &Ocirc;ng ta lấy tiền của những người lao động nhỏ b&eacute; v&agrave; người về hưu để cung cấp cho những người gi&agrave;u c&oacute;&quot;.</p>\r\n\r\n<p>Ch&iacute;nh phủ của &ocirc;ng Macron đang t&igrave;m c&aacute;ch t&aacute;i cấu tr&uacute;c t&agrave;i ch&iacute;nh nh&agrave; nước vốn trong t&igrave;nh trạng yếu k&eacute;m từ thời ch&iacute;nh phủ của &ocirc;ng Hollande (2012-2017). Nhiều người Ph&aacute;p đang phụ thuộc v&agrave;o c&aacute;c chương tr&igrave;nh x&atilde; hội của nh&agrave; nước, khiến cho Ph&aacute;p l&agrave; một trong những ch&iacute;nh phủ c&oacute; mức chi ti&ecirc;u theo GDP cao nhất trong c&aacute;c nền kinh tế ph&aacute;t triển. Theo Tổ chức Hợp t&aacute;c Kinh tế v&agrave; Ph&aacute;t triển (OECD) chi ti&ecirc;u của Ch&iacute;nh phủ Ph&aacute;p l&agrave; 56,6% GDP.</p>\r\n\r\n<p>Chỉ c&oacute; Phần Lan c&oacute; chi ti&ecirc;u nh&agrave; nước cao hơn ở mức 57,1%. Tại Đức, chi ti&ecirc;u của ch&iacute;nh phủ so với GDP l&agrave; 43,8%, tại Italia l&agrave; 50,2%, tại Nhật l&agrave; 39,2% v&agrave; tại Mỹ l&agrave; 37,4%. Trong khi ch&iacute;nh phủ Ph&aacute;p kh&oacute; l&ograve;ng chống đỡ việc chi ti&ecirc;u như vậy v&agrave; g&aacute;nh nặng nợ c&ocirc;ng đang ng&agrave;y một gia tăng (hiện bằng khoảng 125% GDP theo OECD), việc cắt giảm chi ti&ecirc;u kh&ocirc;ng dễ v&agrave; g&acirc;y bất b&igrave;nh đối với người d&acirc;n.</p>\r\n\r\n<p>Khi người d&acirc;n bắt đầu nắm lấy chiếc &aacute;o Gil&ecirc; v&agrave;ng -- thứ m&agrave; họ phải mang theo trong xe do luật quy định ph&ograve;ng khi họ cần trợ gi&uacute;p l&uacute;c xe hỏng, th&igrave; h&agrave;nh động n&agrave;y nhanh ch&oacute;ng trở th&agrave;nh một h&agrave;nh động phản kh&aacute;ng rộng r&atilde;i. Gil&ecirc; v&agrave;ng đ&atilde; phong tỏa đường tới kho nhi&ecirc;n liệu lớn tại cảng Fos-sur-Mer gần Marseille (một trong những cảng biển lớn nhất nước Ph&aacute;p) v&agrave; c&aacute;c trạm xăng tr&ecirc;n đất nước n&agrave;y nhanh ch&oacute;ng bị thiếu nhi&ecirc;n liệu. Sự hạn chế cũng được &aacute;p dụng tại Bretagne về lượng nhi&ecirc;n liệu m&agrave; mỗi người l&aacute;i xe được ph&eacute;p mua.</p>\r\n\r\n<p>Tại c&aacute;c v&ugrave;ng của Ph&aacute;p tại nước ngo&agrave;i như Reunion (nằm tr&ecirc;n Ấn Độ Dương) c&aacute;c cuộc biểu t&igrave;nh phản đối tăng thuế nhi&ecirc;n liệu trở th&agrave;nh những cuộc cướp b&oacute;c v&agrave; bạo động v&agrave;o th&aacute;ng 11, khiến ch&iacute;nh phủ phải triển khai qu&acirc;n đội để kh&ocirc;i phục trật tự. Bất cứ ai l&aacute;i xe tr&ecirc;n đường cao tốc ch&iacute;nh của Ph&aacute;p đều bị chững lại bởi những chiếc xe v&agrave; xe tải đi với một tốc độ chậm chạp hay phải đi qua những nh&oacute;m người biểu t&igrave;nh tại c&aacute;c trạm thu ph&iacute;. Như một nh&agrave; quan s&aacute;t ghi nhận: &quot;Họ l&agrave; những người d&acirc;n b&igrave;nh thường, giống như cha hoặc mẹ bạn, đang đứng trong h&agrave;ng&quot;.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/uqvpbqmx/2018_12_09/5854x482_iylr.jpg\"><img alt=\"Tình trạng Gilê vàng bạo loạn tại Pháp mang ý nghĩa gì? - ảnh 2\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/uqvpbqmx/2018_12_09/5854x482_iylr.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;Gil&ecirc; v&agrave;ng tập trung tại Khải Ho&agrave;n M&ocirc;n, Paris.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Phản ứng của ch&iacute;nh phủ ban đầu l&agrave; cứng rắn trước những cuộc biểu t&igrave;nh tr&ecirc;n khắp cả nước, trong khi &ocirc;ng Macron giải th&iacute;ch sự cần thiết với nước Ph&aacute;p chuyển từ nhi&ecirc;n liệu h&oacute;a thạch sang nhi&ecirc;n liệu th&acirc;n thiện hơn với m&ocirc;i trường. &Ocirc;ng Macro cũng t&igrave;m c&aacute;ch đổ lỗi những cuộc biểu t&igrave;nh xảy ra l&agrave; do những người cực hữu v&agrave; Tập hợp Quốc gia của b&agrave; Marine Le Pen, trước đ&acirc;y l&agrave; Mặt trận Quốc gia. Tuy nhi&ecirc;n, b&agrave; Le Pen đ&atilde; rất r&otilde; r&agrave;ng khi n&oacute;i động th&aacute;i n&agrave;y, đặc biệt l&agrave; những cuộc biểu t&igrave;nh ng&agrave;y c&agrave;ng bạo lực kh&ocirc;ng được k&ecirc;u gọi bởi tổ chức của b&agrave;.</p>\r\n\r\n<p>Điều g&igrave; sẽ đến tiếp theo? &Ocirc;ng Macron đ&atilde; tạm lui trong vấn đề thuế v&agrave; c&oacute; thể đang cố để điều chỉnh chi ti&ecirc;u t&agrave;i ch&iacute;nh nhưng đ&acirc;y kh&ocirc;ng phải l&agrave; một tiến tr&igrave;nh dễ thực hiện v&igrave; tổng thống Ph&aacute;p đ&atilde; t&igrave;m c&aacute;ch để giữ th&acirc;m hụt ng&acirc;n s&aacute;ch ở mức dưới 3% GDP (một mục ti&ecirc;u được to&agrave;n bộ c&aacute;c th&agrave;nh vi&ecirc;n của Li&ecirc;n minh ch&acirc;u &Acirc;u t&aacute;n th&agrave;nh). C&ugrave;ng l&uacute;c, &ocirc;ng Macron đang mạnh mẽ chống lại c&aacute;ch l&agrave;m của Italy nhằm k&iacute;ch th&iacute;ch nền kinh tế th&ocirc;ng qua chi ti&ecirc;u nh&agrave; nước lớn hơn (khiến th&acirc;m hụt của nước n&agrave;y tăng từ 1,8% l&ecirc;n th&agrave;nh 2,4% v&agrave;o năm 2018).</p>\r\n\r\n<p>Nếu tổng thống Macron từ bỏ việc giữ th&acirc;m hụt ng&acirc;n s&aacute;ch của Ph&aacute;p ở mức thấp th&igrave; việc &ocirc;ng chống lại kế hoạch k&iacute;ch th&iacute;ch kinh tế của ch&iacute;nh phủ d&acirc;n t&uacute;y Italia sẽ khiến &ocirc;ng c&oacute; vẻ đạo đức giả, điều n&agrave;y sẽ t&aacute;c động mạnh mẽ đến cuộc bầu cử th&aacute;ng 5.2019, định h&igrave;nh cuộc chiến giữa những phe ủng hộ Li&ecirc;n minh ch&acirc;u &Acirc;u, l&atilde;nh đạo bởi Macron v&agrave; những người chống lại chủ d&acirc;n tộc, d&acirc;n t&uacute;y -- l&atilde;nh đạo bởi Thủ tướng Hungary Viktor Orban, Bộ trưởng Nội vụ Italia v&agrave; l&agrave; l&atilde;nh đạo đảng Lega, Matteo Salvini.</p>\r\n\r\n<p>Vấn đề đối với ch&iacute;nh phủ Ph&aacute;p l&agrave; việc ho&atilde;n &aacute;p thuế nhi&ecirc;n liệu kh&ocirc;ng đủ để ngăn chặn phong tr&agrave;o đ&atilde; lan rộng do vấn đề thuế nhi&ecirc;n liệu ban đầu trong qu&aacute; khứ, bao h&agrave;m sự phức tạp hơn về những rối ren trong c&aacute;c vấn đề kinh tế - x&atilde; hội.&nbsp; Như một th&agrave;nh vi&ecirc;n Gil&ecirc; sau khi từ chối đối thoại với ch&iacute;nh phủ của Macron tuy&ecirc;n bố: &quot;Người Ph&aacute;p kh&ocirc;ng phải l&agrave; những con sẻ. Ch&uacute;ng t&ocirc;i kh&ocirc;ng th&iacute;ch vụn b&aacute;nh mỳ. Ch&uacute;ng t&ocirc;i muốn một chiếc ba-gh&eacute;t [baguette - loại b&aacute;nh mỳ d&agrave;i của Ph&aacute;p]&quot;.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/uqvpbqmx/2018_12_09/macrong20m_pcbw.jpg\"><img alt=\"Tình trạng Gilê vàng bạo loạn tại Pháp mang ý nghĩa gì? - ảnh 3\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/uqvpbqmx/2018_12_09/macrong20m_pcbw.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;&Ocirc;ng Macron tại Hội nghị Thượng đỉnh G20.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Vậy &ocirc;ng Macron c&oacute; những lựa chọn n&agrave;o? Nếu bị th&uacute;c &eacute;p &ocirc;ng c&oacute; thể thay đổi ch&iacute;nh phủ theo hiến ph&aacute;p, sa thải thủ tướng &Eacute;douard Philippe v&agrave; thay thế &ocirc;ng bằng một gương mặt biết phục t&ugrave;ng c&ocirc;ng ch&uacute;ng. Nếu c&aacute;c t&igrave;nh huống trở n&ecirc;n tồi tệ hơn, tổng thống c&oacute; thể tuy&ecirc;n bố t&igrave;nh trạng khẩn cấp. Tuy nhi&ecirc;n, &ocirc;ng Macron nhận thức r&otilde; việc thổi b&ugrave;ng l&ecirc;n ngọn lửa giận dữ tr&ecirc;n khắp cả nước đ&atilde; l&agrave;m rung chuyển nước Ph&aacute;p năm 1968 m&agrave; khởi đầu l&agrave; những cuộc biểu t&igrave;nh của sinh vi&ecirc;n, sau đ&oacute; đ&atilde; lan rộng ra do ch&iacute;nh phủ đ&aacute;p trả bằng những h&agrave;nh động cứng rắn, thay v&igrave; những biện ph&aacute;p mềm mỏng để gi&agrave;nh sự cảm th&ocirc;ng rộng r&atilde;i của d&acirc;n ch&uacute;ng. Việc tuy&ecirc;n bố đất nước trong t&igrave;nh trạng khẩn cấp sẽ l&agrave; bước đi c&oacute; t&aacute;c động gh&ecirc; gớm v&agrave; c&oacute; phần chắc sẽ k&iacute;ch th&iacute;ch những cuộc biểu t&igrave;nh lớn hơn của d&acirc;n ch&uacute;ng.</p>\r\n\r\n<p>Theo một cuộc thăm d&ograve; dư luận thực hiện bởi Harris Interactive, 72% d&acirc;n số Ph&aacute;p ủng hộ Gil&ecirc; v&agrave;ng, tuy 85% chỉ r&otilde; họ kh&ocirc;ng ủng hộ việc sử dụng bạo lực. Với họ, cảnh s&aacute;t đ&atilde; rất thận trọng trong việc giải quyết vấn đề v&agrave; trong số những người bị thương tại những cuộc loạn đả c&oacute; rất nhiều người thuộc lực lượng an ninh. Mặc d&ugrave; chỉ c&oacute; 3 người chết v&agrave; h&agrave;ng trăm người bị thương, nếu ch&iacute;nh phủ quyết định &aacute;p dụng thật sự một biện ph&aacute;p trừng trị thẳng tay hơn con số tr&ecirc;n chắc chắn sẽ tăng cao.</p>\r\n\r\n<p>&Ocirc;ng Macron đ&atilde; từng đại diện cho một l&agrave;n s&oacute;ng mới trong nền ch&iacute;nh trị nước Ph&aacute;p, một sự khởi đầu từ c&aacute;c đảng ph&aacute;i truyền thống của cả c&aacute;nh tả v&agrave; c&aacute;nh hữu. Năm 2017, c&aacute;c cử tri đ&atilde; bầu &ocirc;ng l&agrave;m tổng thống v&agrave; bầu cho đảng &ocirc;ng một số lượng ghế lớn trong quốc hội, trong khi chỉ tr&iacute;ch nặng nề những người x&atilde; hội v&agrave; c&aacute;nh hữu &ocirc;n h&ograve;a.</p>\r\n\r\n<p>Nhưng kh&ocirc;ng may, &ocirc;ng Macron đ&atilde; kh&ocirc;ng tận dụng được vị thế ưu việt trong ch&iacute;nh trị, tr&aacute;i lại, &ocirc;ng đ&atilde; l&agrave;m cho h&igrave;nh ảnh của m&igrave;nh trở n&ecirc;n xa c&aacute;ch, một gương mặt mang t&iacute;nh chất thống trị, c&oacute; xu hướng nghĩ lại tương lai của ch&acirc;u &Acirc;u v&agrave; trật tự tự do thế giới trong khi t&igrave;m một c&aacute;ch thức để t&aacute;i định h&igrave;nh lại những kh&oacute; khăn của nước Ph&aacute;p. Rất nhiều người Ph&aacute;p kh&ocirc;ng qu&ecirc;n trong khi nhiều nơi tại Paris đang bị thi&ecirc;u rụi th&igrave; l&atilde;nh đạo của họ đang ở Buenos Aires dự Hội nghị Thượng đỉnh G20, s&aacute;t c&aacute;nh c&ugrave;ng tổng thống Mỹ Donald Trump, người đồng cấp Trung Quốc Tập Cận B&igrave;nh v&agrave; thủ tướng Đức Angela Merkel.</p>\r\n\r\n<p>Phản đối gia tăng nhắm v&agrave;o &ocirc;ng Macron l&yacute; giải kết quả những cuộc thăm d&ograve; dư luận gần đ&acirc;y: tỷ lệ người ủng hộ tổng thống Ph&aacute;p đ&atilde; rơi xuống mức 21%. Hơn nữa, cuộc thăm d&ograve; dư luận v&agrave;o th&aacute;ng 11 của Viện Nghi&ecirc;n cứu Dư luận C&ocirc;ng ch&uacute;ng Ph&aacute;p IFOP, chỉ ra rằng Đảng Cộng h&ograve;a Tiến bước (LREM) của &ocirc;ng Macron đang đứng sau Đảng Tập hợp Quốc gia của b&agrave; Le Pen -- chỉ c&oacute; 19% số cử tri tiềm năng so với 21% của đảng Cực hữu. Cũng c&ugrave;ng cuộc thăm d&ograve; n&agrave;y cho thấy một đảng cực hữu kh&aacute;c theo chủ nghĩa ho&agrave;i nghi ch&acirc;u &Acirc;u l&agrave; Nước Ph&aacute;p Đứng l&ecirc;n với 7% số cử tri tiềm năng v&agrave; 2% cho 2 đảng Frexit (muốn nước Ph&aacute;p ra khỏi Li&ecirc;n minh ch&acirc;u &Acirc;u). C&ugrave;ng nhau, c&aacute;c đảng cực hữu chiếm tới 30% số cử tri tiềm năng, tăng 5 điểm so với hồi th&aacute;ng 8.</p>\r\n\r\n<p>Một con mắt phải d&otilde;i theo Gil&ecirc; v&agrave;ng trong khi con mắt c&ograve;n lại phải tr&ocirc;ng chừng sự trỗi dậy của ph&aacute;i cực hữu, tổng thống Macron đang phải giải quyết cuộc bầu cử nghị viện ch&acirc;u &Acirc;u v&agrave;o th&aacute;ng 5.2019 như l&agrave; một &quot;cuộc cạnh tranh giữa những người tiến bộ v&agrave; những người d&acirc;n tộc chủ nghĩa&quot;. Mục ti&ecirc;u của &ocirc;ng trong cuộc chiến l&agrave; b&agrave; Le Pen tại nước Ph&aacute;p nhưng cũng l&agrave; những ai trong c&aacute;c nước ch&acirc;u &Acirc;u đang cổ x&uacute;y, dẫn dắt &quot;căn bệnh d&acirc;n tộc chủ nghĩa&quot;.</p>\r\n\r\n<p>Nhưng đ&acirc;y l&agrave; vấn đề lớn với tổng thống Ph&aacute;p v&igrave; khi &ocirc;ng muốn cứu ch&acirc;u &Acirc;u khỏi mối đe dọa của chủ nghĩa d&acirc;n tộc, &ocirc;ng phải đối mặt với sự bất m&atilde;n nặng nề trong nội địa bắt nguồn từ những kh&oacute; khăn của nền kinh tế. C&aacute;ch &ocirc;ng xử l&yacute; những người Gil&ecirc; v&agrave;ng sẽ x&aacute;c định kết quả của cuộc bầu cử nghị viện ch&acirc;u &Acirc;u th&aacute;ng 5.2019 tại Ph&aacute;p. Kh&ocirc;ng ai c&oacute; thể n&oacute;i quản trị nước Ph&aacute;p l&agrave; việc dễ, v&agrave; phong tr&agrave;o Gil&ecirc; v&agrave;ng đang chứng minh điều đ&oacute;.</p>', 'upload/news\\1588907624.jpeg', 1, 0, 3, 1, 'tinh-trang-gile-vang-bao-loan-tai-phap-mang-y-nghia-gi', NULL, '2020-09-06 11:20:51', '2020-09-07 20:13:44'),
(6, 'Biển Đông: Anh, Pháp “giương oai” theo Mỹ khiến Trung Quốc phát khùng', 9, 'VietTimes -- Ngoài Mỹ, Pháp và Anh đã tổ chức cuộc tuần tra tự do chung qua đá Vành Khăn, Su Bi và Chữ Thập trên quần đảo Trường Sa vào cuối tháng 6 như đã tuyên bố trong Hội nghị thượng đỉnh An ninh châu Á lần thứ 17 hay Đối thoại Shangri-La năm 2018..., theo The Diplomat.', '<p>T&agrave;u đổ bộ c&oacute; lượng cho&aacute;n nước 22.000 tấn của Hải qu&acirc;n Ho&agrave;ng gia Anh mang t&ecirc;n HMS Albion đ&atilde; tổ chức một cuộc tuần tra tự do h&agrave;ng hải FON tại khu vực quần đảo Ho&agrave;ng Sa (của Việt Nam) tr&ecirc;n Biển Đ&ocirc;ng v&agrave;o cuối th&aacute;ng 8 vừa qua.</p>\r\n\r\n<p>Cuộc tuần tra của HMS Albion l&agrave; c&aacute;ch khẳng định truyền thống về sự tự đo đi lại tr&ecirc;n những v&ugrave;ng biển khơi - kh&ocirc;ng giống như nhiệm vụ tuần tra v&igrave; tự do h&agrave;ng hải FONOP của Hải qu&acirc;n Mỹ được thiết kế nhằm th&aacute;ch thức những g&igrave; Mỹ coi l&agrave; y&ecirc;u s&aacute;ch th&aacute;i qu&aacute; về hải phận. Điểm kh&aacute;c biệt n&agrave;y những thay đổi kh&aacute;c nhau trong c&aacute;ch tiếp cận của c&aacute;c đồng m&igrave;nh để tham gia thực hiện FON tr&ecirc;n Biển Đ&ocirc;ng.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/uqvpbqmx/2018_09_18/0000hmsalbion_vetw.jpg\"><img alt=\"Biển Đông: Anh, Pháp “giương oai” theo Mỹ khiến Trung Quốc phát khùng - ảnh 1\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/uqvpbqmx/2018_09_18/0000hmsalbion_vetw.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;T&agrave;u đổ bộ của Hải qu&acirc;n Ho&agrave;ng gia Anh quốc HMS Albion cập cảng th&agrave;nh phố Hồ Ch&iacute; Minh ng&agrave;y 3.9.2018.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>V&agrave;o th&aacute;ng 1.1974, Trung Quốc đ&atilde; d&ugrave;ng vũ lực chiếm đ&oacute;ng quần đảo Ho&agrave;ng Sa của Việt Nam một c&aacute;ch phi ph&aacute;p. Hiện tại, Bắc Kinh giận dữ tuy&ecirc;n bố rằng HMS Albion đ&atilde; thực hiện cuộc hải h&agrave;nh trong hải phận của quần đảo Ho&agrave;ng Sa m&agrave; kh&ocirc;ng xin ph&eacute;p trước. Một nguồn tin nặc danh của Anh lưu &yacute; rằng t&agrave;u chiến Ho&agrave;ng gia Anh kh&ocirc;ng đi v&agrave;o v&ugrave;ng giới hạn 12 hải l&yacute; với bất cứ thực thể n&agrave;o tr&ecirc;n quần đảo Ho&agrave;ng Sa. Nhưng hoạt động tuần tra n&agrave;y được tổ chức theo c&aacute;ch thức nhằm v&ocirc; hiệu h&oacute;a những y&ecirc;u s&aacute;ch qu&aacute; mức của Trung Quốc với hải phận trong khu vực.</p>\r\n\r\n<p>Cuộc tuần tra của t&agrave;u chiến Anh chứng minh &yacute; định nghi&ecirc;m t&uacute;c của Anh quốc tham gia c&aacute;c sự kiện trong khu vực Đ&ocirc;ng Nam &Aacute;. N&oacute; gửi đi t&iacute;n hiệu rằng Hải qu&acirc;n Ho&agrave;ng gia Anh muốn tham gia tuần tra thường xuy&ecirc;n tr&ecirc;n Biển Đ&ocirc;ng. Như c&aacute;c t&aacute;c giả Ian Storey v&agrave; Euan Graham nhấn mạnh, cuộc tuần tra sẽ khiến Mỹ h&agrave;i l&ograve;ng v&igrave; n&oacute; cộng hưởng với lời k&ecirc;u gọi của Washington duy tr&igrave; tự do h&agrave;ng hải tr&ecirc;n Biển Đ&ocirc;ng. Gần đ&acirc;y, Anh quốc v&agrave; &Uacute;c đ&atilde; c&ocirc;ng khai thỏa thuận đẩy mạnh hợp t&aacute;c qu&acirc;n sự. T&agrave;u s&acirc;n bay Anh HMS Queen Elizabeth sẽ được triển khai tại Th&aacute;i B&igrave;nh Dương v&agrave;o đầu năm 2020 v&agrave; s&aacute;t c&aacute;nh với những t&agrave;u hải qu&acirc;n của &Uacute;c.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/uqvpbqmx/2018_09_18/0000french_fugc.jpg\"><img alt=\"Biển Đông: Anh, Pháp “giương oai” theo Mỹ khiến Trung Quốc phát khùng - ảnh 2\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/uqvpbqmx/2018_09_18/0000french_fugc.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;T&agrave;u khu trục của Ph&aacute;p thực hiện tuần tra v&igrave; tự do h&agrave;ng hải tr&ecirc;n Biển Đ&ocirc;ng.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Hải qu&acirc;n Ho&agrave;ng gia Anh kh&ocirc;ng giải th&iacute;ch r&otilde; mục ti&ecirc;u hoạt động tuần tra. Ph&aacute;t ng&ocirc;n vi&ecirc;n của hải qu&acirc;n Anh chỉ tuy&ecirc;n bố vắn tắt rằng &quot;HMS Albion thực hiện quyền tự do h&agrave;ng hải ho&agrave;n to&agrave;n theo quy tắc v&agrave; luật lệ quốc tế&quot;. Tuy nhi&ecirc;n, c&aacute;c quan s&aacute;t vi&ecirc;n trong khu vực giải th&iacute;ch hoạt động n&agrave;y v&agrave; những cuộc tuần tra trước đ&oacute; l&agrave; một sự th&aacute;ch thức với những y&ecirc;u s&aacute;ch phi l&yacute; của Trung Quốc tr&ecirc;n quần đảo Ho&agrave;ng Sa cũng như tr&ecirc;n Biển Đ&ocirc;ng.</p>\r\n\r\n<p>Với quần đảo Ho&agrave;ng Sa, Trung Quốc đ&atilde; tuy&ecirc;n bố đơn phương một c&aacute;ch phi ph&aacute;p khi tự vẽ những đường cơ sở thẳng kết nối 28 điểm l&acirc;n cận tr&ecirc;n quần đảo v&agrave;o năm 1996. Những đường cơ sở thẳng m&agrave; Trung Quốc tự &yacute; đặt ra bao quanh l&atilde;nh hải của quần đảo Ho&agrave;ng Sa với những điểm cơ sở. C&ugrave;ng l&uacute;c, Trung Quốc tuy&ecirc;n bố y&ecirc;u s&aacute;ch phi ph&aacute;p xung quanh phạm vi 12 hải l&yacute; tr&ecirc;n l&atilde;nh hải của quần đảo Ho&agrave;ng Sa. Như gi&aacute;o sư Carl Thayer nhấn mạnh, việc Trung Quốc tuy&ecirc;n bố c&aacute;c đường cơ sở thẳng tr&ecirc;n quần đảo Ho&agrave;ng Sa tr&aacute;i với C&ocirc;ng ước Li&ecirc;n Hợp Quốc về Luật Biển năm 1982 (UNCLOS). Theo c&ocirc;ng ước n&agrave;y, việc vẽ những đường cơ sở thẳng chỉ c&oacute; thể &aacute;p dụng với những nước đảo quốc. Đ&aacute;ng lưu &yacute; hơn, t&ograve;a trọng t&agrave;i The Hague đưa ra ph&aacute;n quyết v&agrave;o 12.7.2016 đ&atilde; v&ocirc; hiệu h&oacute;a việc Trung Quốc tuy&ecirc;n bố chủ quyền lịch sử với những v&ugrave;ng biển trong c&aacute;i gọi l&agrave;&nbsp; &quot;đường 9 đoạn&quot; phi ph&aacute;p bao qu&aacute;t cả quần đảo Ho&agrave;ng Sa.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/uqvpbqmx/2018_09_18/0000vnhakhan_dgzg.jpg\"><img alt=\"Biển Đông: Anh, Pháp “giương oai” theo Mỹ khiến Trung Quốc phát khùng - ảnh 3\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/uqvpbqmx/2018_09_18/0000vnhakhan_dgzg.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;Nh&agrave; chứa m&aacute;y bay Trung Quốc x&acirc;y dựng phi ph&aacute;p tr&ecirc;n đ&aacute; V&agrave;nh Khăn thuộc quần đảo Trường Sa của Việt Nam.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Bắc Kinh chỉ đưa ra những lời b&igrave;nh luận ch&iacute;nh thức sau khi truyền th&ocirc;ng phương T&acirc;y tr&iacute;ch dẫn nguồn tin quốc ph&ograve;ng giấu t&ecirc;n về hoạt động tuần tra hải qu&acirc;n của HMS Albion. Cũng giống như đối với c&aacute;c hoạt động FONOP của Mỹ, Trung Quốc đưa ra những lời cảnh b&aacute;o rằng t&agrave;u chiến v&agrave; m&aacute;y bay chiến đấu sẽ theo d&otilde;i hoạt động của HMS Albion. Chỉ trong 1 tuần sau đ&oacute;, Bộ trưởng Ngoại giao Trung Quốc buộc tội hoạt động n&agrave;y l&agrave; sự khi&ecirc;u kh&iacute;ch, vi phạm luật nội địa Trung Quốc v&agrave; luật quốc tế, vi phạm chủ quyền Trung Quốc. Bắc Kinh cũng cảnh b&aacute;o rằng những hoạt động tiếp theo sẽ g&acirc;y bất lợi cho mối quan hệ song phương, h&ograve;a b&igrave;nh v&agrave; ổn định trong khu vực.</p>\r\n\r\n<p>Đ&aacute;ng lưu &yacute;, những phản ứng của Trung Quốc d&egrave; dặt v&agrave; thận trọng ở cả lời n&oacute;i v&agrave; những h&agrave;nh động tr&ecirc;n biển để tr&aacute;nh xung đột trực tiếp với t&agrave;u chiến Anh v&agrave; ngăn chặn căng thẳng leo thang. Nhưng Trung Quốc đ&atilde; biểu thị quyết t&acirc;m của m&igrave;nh khi hủy bỏ những thỏa thuận thương mại hậu Brexit m&agrave; London theo đuổi với Bắc Kinh v&igrave; th&aacute;ch thức của HMS Albion.</p>\r\n\r\n<p>Thực tế, HMS Albion cho thấy nỗ lực của những cường quốc b&ecirc;n ngo&agrave;i để duy tr&igrave; quyền tự do tiếp cận những con đường h&agrave;ng hải v&agrave; h&agrave;ng kh&ocirc;ng tr&ecirc;n Biển Đ&ocirc;ng. Trong một bước đi c&oacute; li&ecirc;n quan, Ph&aacute;p v&agrave; Anh đ&atilde; tổ chức cuộc tuần tra tự do chung qua đ&aacute; V&agrave;nh Khăn, Su Bi v&agrave; Chữ Thập tr&ecirc;n quần đảo Trường Sa v&agrave;o cuối th&aacute;ng 6 như đ&atilde; tuy&ecirc;n bố trong Hội nghị thượng đỉnh An ninh ch&acirc;u &Aacute; lần thứ 17 hay Đối thoại Shangri-La năm 2018.</p>\r\n\r\n<p>Cả London v&agrave; Paris coi họ l&agrave; những cường quốc tr&ecirc;n v&ugrave;ng Ấn Độ - Th&aacute;i B&igrave;nh Dương v&agrave; cam kết sẽ bảo vệ h&agrave;nh lang tự do th&ocirc;ng qua c&aacute;c đường biển chiến lược trong những cuộc đối thoại tại Đ&ocirc;ng Nam &Aacute; tu&acirc;n theo luật h&agrave;ng hải quốc tế. B&ecirc;n cạnh hai cuộc tuần tra c&ocirc;ng khải, cả hai nước (c&ugrave;ng với &Uacute;c) duy tr&igrave; c&aacute;c hoạt động hải qu&acirc;n trong khu vực. V&iacute; dụ như Ph&aacute;p đ&atilde; đưa &iacute;t nhất 5 t&agrave;u v&agrave;o Biển Đ&ocirc;ng năm 2017.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/uqvpbqmx/2018_09_18/ussmustinddg89_nbvi.jpg\"><img alt=\"Biển Đông: Anh, Pháp “giương oai” theo Mỹ khiến Trung Quốc phát khùng - ảnh 4\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/uqvpbqmx/2018_09_18/ussmustinddg89_nbvi.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;T&agrave;u khu trục USS Mustin của Mỹ đ&atilde; thực thi tuần tra FONOP tr&ecirc;n Biển Đ&ocirc;ng v&agrave;o th&aacute;ng 3.2018.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Mỹ đ&atilde; tổ chức 11 cuộc tuần tra FONOP một c&aacute;ch c&ocirc;ng khai để th&aacute;ch thức những y&ecirc;u s&aacute;ch v&ocirc; l&yacute; của Trung Quốc tr&ecirc;n Biển Đ&ocirc;ng kể từ th&aacute;ng 10.2015. 5 cuộc tuần tra FONOP thực hiện tr&ecirc;n quần đảo Ho&agrave;ng Sa do c&aacute;c t&agrave;u USS Curtis Wilbur (thực hiện th&aacute;ng 1.2016), USS Decatur (th&aacute;ng 10.2016), USS Sethem (7.2017), USS Chafee (10.2017) v&agrave; hoạt động tuần tra chung đầu ti&ecirc;n giữa USS Higgins v&agrave; USS Antietam (5.2018). USS Lassen (th&aacute;ng 10.2015), USS William P. Lawrence (5.2016), USS Dewey (5.2017), USS John S. McCain (8.2017) v&agrave; USS Mustin (3.2018) đ&atilde; thực thi 5 cuộc tuần tra FONOP tại khu vực quần đảo Trường Sa. Cuộc tuần tra FONOP của t&agrave;u USS Hopper thực hiện v&agrave;o th&aacute;ng 1.2018 ở b&atilde;i cạn Scarborough. &nbsp;</p>\r\n\r\n<p>Nhưng c&aacute;c hoạt động qu&acirc;n sự trong khu vực của Mỹ, trong v&agrave; sau thời ch&iacute;nh quyền của tổng thống Barack Obama đưa ra chiến lược xoay trục v&agrave; t&aacute;i c&acirc;n bằng với ch&acirc;u &Aacute; - Th&aacute;i B&igrave;nh Dương đ&atilde; kh&ocirc;ng ngăn cản được Trung Quốc x&acirc;y dựng tr&aacute;i ph&eacute;p c&aacute;c đảo nh&acirc;n tạo v&agrave; những căn cứ qu&acirc;n sự một c&aacute;ch phi ph&aacute;p tr&ecirc;n Biển Đ&ocirc;ng. Trung Quốc đ&atilde; ho&agrave;n th&agrave;nh những c&ocirc;ng tr&igrave;nh tr&aacute;i ph&eacute;p tr&ecirc;n 7 đảo nh&acirc;n tạo bồi đắp phi ph&aacute;p v&agrave; trang bị cho ch&uacute;ng c&aacute;c vũ kh&iacute; tấn c&ocirc;ng v&agrave; ph&ograve;ng thủ hiện đại.</p>\r\n\r\n<p>Trung Quốc đ&atilde; qu&acirc;n sự h&oacute;a Biển Đ&ocirc;ng bằng c&aacute;ch triển khai c&aacute;c hệ thống g&acirc;y nhiễu, hệ thống radar tr&ecirc;n đ&aacute; Chữ Thập v&agrave; đ&aacute; V&agrave;nh Khăn, t&ecirc;n lửa h&agrave;nh tr&igrave;nh chống hạm YJ-12B v&agrave; t&ecirc;n lửa đất đối kh&ocirc;ng HQ-9B tr&ecirc;n đ&aacute; Chữ Thập, đ&aacute; Su Bi v&agrave; đ&aacute; V&agrave;nh Khăn. Th&ecirc;m nữa, Trung Quốc c&ograve;n ngang nhi&ecirc;n triển khai những m&aacute;y bay n&eacute;m bom chiến lược c&oacute; khả năng tấn c&ocirc;ng hạt nh&acirc;n H-6K tr&ecirc;n đảo Ph&uacute; L&acirc;m (thuộc quần đảo Ho&agrave;ng Sa của Việt Nam). C&acirc;u hỏi đặt ra hiện tại l&agrave; Trung Quốc sẽ thực hiện qu&acirc;n sự h&oacute;a tr&aacute;i ph&eacute;p tr&ecirc;n c&aacute;c đảo nh&acirc;n tạo tới phạm vi n&agrave;o?</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/uqvpbqmx/2018_09_18/0000hq9b_entg.jpg\"><img alt=\"Biển Đông: Anh, Pháp “giương oai” theo Mỹ khiến Trung Quốc phát khùng - ảnh 5\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/uqvpbqmx/2018_09_18/0000hq9b_entg.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;T&ecirc;n lửa đất đối kh&ocirc;ng HQ-9B của Trung Quốc.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Những đất nước c&oacute; những quyền lợi bất di bất dịch tr&ecirc;n Biển Đ&ocirc;ng đang quan ngại hơn v&igrave; việc Trung Quốc gia tăng hoạt động qu&acirc;n sự h&oacute;a c&aacute;c đảo nh&acirc;n tạo trong khu vực. Những mối lo ngại tăng cao do sự mập mờ về mặt ch&iacute;nh s&aacute;ch của Mỹ tại ch&acirc;u &Aacute;. Sự giải th&iacute;ch của Ngoại trưởng Mỹ Mike Pompeo về chiến lược Ấn Độ - Th&aacute;i B&igrave;nh Dương tự do v&agrave; rộng mở kh&ocirc;ng đủ để gi&uacute;p trật tự quốc tế dựa tr&ecirc;n nền tảng luật ph&aacute;p c&oacute; thể trụ vững. Ch&iacute;nh s&aacute;ch mới của ch&iacute;nh quyền tổng thống Trump tập trung chủ yếu về kinh tế v&agrave; thu hẹp về mặt an ninh.</p>\r\n\r\n<p>Theo Diplomat, c&aacute;c nước trong khu vực kh&oacute; c&oacute; thể t&igrave;m thấy sự t&aacute;i đảm bảo trong kế hoạch của ch&iacute;nh quyền tổng thống Trump để đối ph&oacute; với chiến lược &quot;sự đ&atilde; rồi&quot; của Trung Quốc tr&ecirc;n Biển Đ&ocirc;ng. Tiếp theo, những cam kết t&agrave;i ch&iacute;nh của Mỹ với tầm nh&igrave;n mới về Ấn Độ - Th&aacute;i B&igrave;nh Dương qu&aacute; nhỏ b&eacute; so với tham vọng &quot;v&agrave;nh đai - con đường&quot; của Trung Quốc. Sẽ thật ng&acirc;y thơ khi nghĩ rằng cam kết của Ngoại trưởng Pompeo với những nước Đ&ocirc;ng Nam &Aacute; trung t&acirc;m của khu vực Ấn Độ - Th&aacute;i B&igrave;nh Dương sẽ giải quyết được những ho&agrave;i nghi về quyết t&acirc;m của Mỹ đẩy lui tham vọng của Trung Quốc.</p>\r\n\r\n<p>Diplomat nhận định cuộc tuần tra chung của Anh - Ph&aacute;p v&agrave; hoạt động của HMS Albion minh họa cho sự gia tăng tham gia v&agrave;o những tranh chấp tr&ecirc;n Biển Đ&ocirc;ng của những cường quốc b&ecirc;n ngo&agrave;i. Những nước Đ&ocirc;ng Nam &Aacute; sẽ h&agrave;nh động một c&aacute;ch thận trọng với những bước tiến triển gần đ&acirc;y.&nbsp; Sự tham gia của những cường quốc b&ecirc;n ngo&agrave;i đi sau c&aacute;i nh&igrave;n mới về chủ nghĩa khu vực được th&uacute;c đẩy bởi những nước ASEAN trong nhiều thập kỷ qua. Tuy nhi&ecirc;n, những nước ASEAN cần thận trọng v&igrave; khu vực của họ c&oacute; thể một lần nữa trở th&agrave;nh một vũ đ&agrave;i cạnh tranh giữa c&aacute;c cường quốc lớn. Đ&acirc;y kh&ocirc;ng phải l&agrave; kết quả m&agrave; c&aacute;c nước trong khu vực mong muốn sau hơn 5 thập kỷ x&acirc;y dựng.</p>', 'upload/news\\1588907578.jpeg', 1, 0, 1, 1, 'bien-dong-anh-phap-giuong-oai-theo-my-khien-trung-quoc-phat-khung', NULL, '2020-09-07 20:12:08', '2020-09-07 20:12:58'),
(7, 'NATO tan nát, phương Tây suy tàn vì Donald Trump, Tập Cận Bình?', 1, 'Trước cuộc ganh đua của Nga trên chính trường quốc tế và sức mạnh đe dọa của Trung Quốc, nhiều quốc gia phương Tây kẹt trong thế gọng kìm. Trong khi, tổng thống Mỹ Donald Trump dường như liên tục có những đòn đánh phá từ bên trong liên minh truyền thống hai bờ Đại Tây Dương, báo chí Pháp  và Mỹ nhận xét.', '<p>Trước cuộc ganh đua của Nga tr&ecirc;n ch&iacute;nh trường quốc tế v&agrave; sức mạnh đe dọa của Trung Quốc, nhiều quốc gia phương T&acirc;y kẹt trong thế gọng k&igrave;m. Trong khi, tổng thống Mỹ Donald Trump dường như li&ecirc;n tục c&oacute; những đ&ograve;n đ&aacute;nh ph&aacute; từ b&ecirc;n trong li&ecirc;n minh truyền thống hai bờ Đại T&acirc;y Dương, b&aacute;o ch&iacute; Ph&aacute;p&nbsp; v&agrave; Mỹ nhận x&eacute;t.</p>\r\n\r\n<p><a href=\"https://image.viettimes.vn/666x374/Uploaded/2020/gehlragubatfb/2018_07_15/trumpxijinpingsliptgettyfull169_hksq_fgzy.jpg\"><img alt=\"Donald Trump và ông Tập Cận Bình\" src=\"https://image.viettimes.vn/666x374/Uploaded/2020/gehlragubatfb/2018_07_15/trumpxijinpingsliptgettyfull169_hksq_fgzy.jpg\" /></a></p>\r\n\r\n<p>Donald Trump v&agrave; &ocirc;ng Tập Cận B&igrave;nh</p>\r\n\r\n<p>Trong b&agrave;i<em>&nbsp;&quot;</em>Tr&uacute;ng đạn nhưng kh&ocirc;ng ch&igrave;m&quot;, tờ Courrier International nhận x&eacute;t: &quot;Giữa những lời dao to b&uacute;a lớn của Donald Trump, vị thế mờ nhạt của ch&acirc;u &Acirc;u, l&agrave;n s&oacute;ng b&agrave;i ngoại d&acirc;ng cao, th&aacute;i độ của tổng thống Nga Vladimir Putin v&agrave; tham vọng to&agrave;n cầu của chủ tịch Trung Quốc Tập Cận B&igrave;nh, thế giới đ&atilde; mất phương hướng. Phương T&acirc;y tr&uacute;ng thương nhưng kh&ocirc;ng ng&atilde; quỵ&quot;.</p>\r\n\r\n<p>Trung Quốc sẽ tiếp tục đuổi theo Mỹ nhưng nước Mỹ, cho d&ugrave; gặp giai đoạn suy tho&aacute;i, vẫn l&agrave; si&ecirc;u cường kh&ocirc;ng so s&aacute;nh được. Mỹ tiếp tục ph&aacute;t triển kh&ocirc;ng ngừng với th&agrave;nh phần d&acirc;n số trẻ trung. Tr&aacute;i lại, ch&acirc;u &Acirc;u mới đ&aacute;ng lo v&igrave; từ 2015 đi v&agrave;o thời kỳ giảm d&acirc;n số v&agrave; kh&ocirc;ng c&ograve;n hợp &yacute; với Mỹ. Ch&iacute;nh t&igrave;nh đo&agrave;n kết trong khối phương T&acirc;y bị suy t&agrave;n, Courrier International nhận định.</p>\r\n\r\n<p>Nhận định tr&ecirc;n đ&acirc;y được một số c&acirc;y viết Mỹ, Nhật, Nga đ&agrave;o s&acirc;u.&nbsp;Nh&igrave;n từ Washington, trong b&agrave;i &quot;Trump th&aacute;o gỡ trật tự thế giới&quot;,&nbsp;The New York Times đưa giả thuyết: Donald Trump c&oacute; &yacute; đồ đ&aacute;nh ph&aacute; li&ecirc;n minh phương T&acirc;y.</p>\r\n\r\n<p>Từ c&aacute;c cuộc thượng đỉnh G7 ở Canada, NATO tại Brussel v&agrave; sắp tới đ&acirc;y l&agrave; hội nghị Mỹ-Nga tại Phần Lan&hellip;. Chủ nh&acirc;n Nh&agrave; Trắng kh&ocirc;ng bỏ lỡ một cơ hội n&agrave;o để l&agrave;m lung lay nền m&oacute;ng của khối phương T&acirc;y. Vấn đề l&agrave; kh&ocirc;ng ai biết &ocirc;ng Trump t&iacute;nh g&igrave;, c&oacute; chiến lược cao si&ecirc;u hay chỉ v&igrave; v&ocirc; &yacute; thức? Nhưng h&agrave;nh động từ hơn một năm nay của Donald Trump kh&ocirc;ng kh&aacute;c g&igrave; một kế hoạch mật để đập tan n&aacute;t NATO.</p>\r\n\r\n<p>Bằng chứng l&agrave; tại Qu&eacute;bec (Canada), Donald Trump k&ecirc;u gọi G7 mời Nga trở lại cũng như biện minh cho việc Nga s&aacute;p nhập b&aacute;n đảo Crimea của Ukraine. C&acirc;y b&uacute;t David Leonhardt n&ecirc;u một giả thuyết thứ hai: C&oacute; thể &ocirc;ng Trump th&iacute;ch l&agrave;m ngược lại những tổng thống tiền nhiệm.</p>\r\n\r\n<p>Nhiều tờ b&aacute;o Ph&aacute;p khuy&ecirc;n trong mọi trường hợp, c&aacute;c nh&agrave; l&atilde;nh đạo phương T&acirc;y nhất l&agrave; Justin Trudeau của Canada, tổng thống Ph&aacute;p Emmanuel Macron phải noi gương Angela Merkel kh&ocirc;ng n&ecirc;n dễ d&atilde;i với Donald Trump. Ngay từ đầu thủ tướng Đức đ&atilde; tỏ ra cứng rắn với Donald Trump m&agrave; kh&ocirc;ng cần đổ dầu v&agrave;o lửa.</p>\r\n\r\n<p>Nh&igrave;n từ Ch&acirc;u &Aacute;, tạp ch&iacute; Nhật Nikkei Asian Review cảnh b&aacute;o: Nếu T&acirc;y phương suy yếu, Bắc Kinh sẽ &aacute;p đặt luật chơi quốc tế v&agrave; xuất khẩu m&ocirc; h&igrave;nh ch&iacute;nh trị, kinh tế Trung Quốc ra khắp thế giới. &Ocirc;ng Tập Cận B&igrave;nh đ&atilde; n&oacute;i r&otilde; như thế tại đại hội đảng cộng sản Trung Quốc hồi th&aacute;ng 3.</p>\r\n\r\n<p>Nh&agrave; ch&iacute;nh trị học Mỹ Ian Bremmer, t&aacute;c giả b&agrave;i ph&acirc;n t&iacute;ch&nbsp;&quot;Trung Quốc l&ecirc;n h&agrave;ng l&atilde;nh đạo h&agrave;nh tinh&quot;&nbsp;nhấn mạnh v&agrave;o ch&iacute;nh s&aacute;ch&nbsp;<em>&quot;</em>nước Mỹ trước ti&ecirc;n&quot;&nbsp;của Donald Trump tạo ra một khoảng trống v&agrave; Trung Quốc đ&atilde; sẵn s&agrave;ng thay thế.</p>\r\n\r\n<p>Hệ quả n&agrave;y một phần l&agrave; do sai lầm của giới l&atilde;nh đạo phương T&acirc;y. Trong nhiều thập ni&ecirc;n, họ nghĩ rằng h&atilde;y gi&uacute;p cho Trung Quốc ph&aacute;t triển, một tầng lớp trung lưu sẽ vươn l&ecirc;n sẽ buộc chế độ cởi mở hơn.</p>\r\n\r\n<p>Nhưng ng&agrave;y nay, người d&acirc;n ở c&aacute;c nước phương T&acirc;y l&ecirc;n &aacute;n ch&iacute;nh s&aacute;ch to&agrave;n cầu h&oacute;a t&aacute;c hại đến mức sống v&agrave; đ&ograve;i hỏi ch&iacute;nh quyền phải đổi mới, điều m&agrave; giới ch&iacute;nh trị kh&ocirc;ng c&oacute; giải ph&aacute;p khả thi. C&aacute;c chế độ phương T&acirc;y bị đe dọa v&igrave; d&acirc;n ch&uacute;ng c&agrave;ng ng&agrave;y c&agrave;ng mất niềm tin v&agrave;o c&aacute;c đảng truyền thống v&agrave; độ ch&iacute;nh x&aacute;c của th&ocirc;ng tin.</p>\r\n\r\n<p>Ngược lại, ch&iacute;nh quyền Trung Quốc c&oacute; thể tự khen đ&atilde; g&oacute;p phần l&agrave;m đất nước gi&agrave;u l&ecirc;n v&agrave; ph&aacute;t huy h&igrave;nh ảnh Trung Quốc ra khắp thế giới. T&igrave;nh trạng tham nhũng, &ocirc; nhiễm... vẫn tồn tại nhưng cuộc sống hằng ng&agrave;y đ&atilde; được cải thiện, đ&oacute; l&agrave; l&yacute; do c&aacute;c c&ocirc;ng d&acirc;n Trung Quốc tin tưởng v&agrave;o l&atilde;nh đạo của họ.</p>\r\n\r\n<p>Hệ quả l&agrave; Bắc Kinh &aacute;p đặt c&aacute;c nguy&ecirc;n tắc quốc tế v&agrave; ng&agrave;y c&agrave;ng &iacute;t bị chống đối. Trước hết, Trung Quốc l&agrave; nước duy nhất tiến h&agrave;nh một chiến lược to&agrave;n cầu về thương mại v&agrave; đầu tư .</p>\r\n\r\n<p>Với chiến lược&nbsp;<em>&quot;</em>Con đường tơ lụa mới&quot;, với quyết t&acirc;m cho vay kh&ocirc;ng đặt điều kiện ch&iacute;nh trị ti&ecirc;n quyết, Bắc Kinh gi&agrave;nh được h&agrave;ng loạt bạn h&agrave;ng ở c&aacute;c nước đang ph&aacute;t triển. Ch&iacute;nh quyền c&aacute;c quốc gia ch&acirc;u &Aacute;, ch&acirc;u Mỹ La tinh, ch&acirc;u Phi v&agrave; Trung Đ&ocirc;ng ng&agrave;y c&agrave;ng c&oacute; xu hướng đi theo Bắc Kinh.</p>\r\n\r\n<p>Trong cuộc đấu c&ocirc;ng nghệ cao, th&ocirc;ng minh nh&acirc;n tạo, Trung Quốc cũng ở thế mạnh hơn. Trong khi Mỹ để cho s&aacute;ng kiến tư nh&acirc;n tự do chủ động th&igrave; ch&iacute;nh quyền Trung Quốc chỉ đạo c&aacute;c tập đo&agrave;n c&ocirc;ng nghiệp. Ảnh hưởng &aacute;p đảo cho ph&eacute;p Trung Quốc buộc c&aacute;c nước đối t&aacute;c nhỏ tu&acirc;n thủ chuẩn mực c&aacute;c c&ocirc;ng ty Trung Quốc.</p>\r\n\r\n<p>Giới chuy&ecirc;n gia nhận định, sức thu h&uacute;t của Trung Quốc c&oacute; giới hạn v&agrave; phải cần nhiều chục năm mới đủ sức cạnh tranh với Mỹ nhưng ở cấp v&ugrave;ng, Bắc Kinh l&agrave; cường quốc qu&acirc;n sự th&aacute;ch thức Mỹ với những y&ecirc;u s&aacute;ch chủ quyền v&ocirc; lối, bất chấp luật ph&aacute;p quốc tế. Nhưng với th&aacute;i độ&nbsp;tho&aacute;i lui của Donald Trump, Trung Quốc c&oacute; thể lắp v&agrave;o khoảng trống ảnh hưởng. Đ&oacute; ch&iacute;nh l&agrave; bất trắc lớn cho địa ch&iacute;nh trị hiện nay.</p>\r\n\r\n<p>Như để minh họa cho t&igrave;nh trạng mất phương hướng của thế giới, nh&igrave;n từ Nam Phi, nh&agrave; b&aacute;o Kalim Rajad cho rằng thế nổi trội của nền tư tưởng phương T&acirc;y Thi&ecirc;n Ch&uacute;a gi&aacute;o đang &quot;g&acirc;y bất b&igrave;nh cho giới tr&iacute; thức ở c&aacute;c nước k&eacute;m ph&aacute;t triển&quot;. Theo &ocirc;ng, tư tưởng của chủ tịch Trung Quốc ảnh hưởng truyền thống Khổng gi&aacute;o hy sinh quyền lợi c&aacute; nh&acirc;n cho h&agrave;i h&ograve;a x&atilde; hội cũng như tinh thần d&acirc;n tộc chủ nghĩa của thủ tướng Ấn Độ Narendra Modi c&oacute; những điểm tương đồng.</p>\r\n\r\n<p>Nh&igrave;n từ Nga, Timofei Bordatchev, gi&aacute;m đốc viện nghi&ecirc;n cứu ch&acirc;u &Acirc;u ở Matxcơva, tr&ecirc;n b&aacute;o mạng Profil, dự b&aacute;o một cuộc chiến tranh gi&agrave;nh ảnh hưởng bắt đầu: Chiến tranh lạnh thứ hai đ&atilde; khai m&agrave;o từ trước năm 2017 khi Nga can thiệp v&agrave;o Syria. Nhưng theo t&aacute;c giả, diễn biến mới từ sau năm 2014 l&agrave; kh&ocirc;ng c&ograve;n những vụ tấn c&ocirc;ng đơn phương, trừ hai vụ ngoại lệ phương T&acirc;y kh&ocirc;ng k&iacute;ch Syria năm 2017 v&agrave; 2018 nhưng cũng thận trọng kh&ocirc;ng để Nga trả đũa. Cuộc chiến tranh lạnh mới n&agrave;y nằm trong khu&ocirc;n khổ thay đổi trật tự thế giới theo một thế tương quan lực lượng qu&acirc;n b&igrave;nh.</p>\r\n\r\n<p>Theo b&aacute;o Ph&aacute;p, ng&agrave;y nay chiến tranh n&oacute;ng rất kh&oacute; xảy ra v&igrave; b&ecirc;n n&agrave;o cũng c&oacute; vũ kh&iacute; mạnh. Tuy nhi&ecirc;n, nếu dựa tr&ecirc;n tuy&ecirc;n bố của tổng thống Donald Trump th&igrave; chiến lược của Mỹ đặt tr&ecirc;n cơ sở bảo vệ nguồn lợi duy nhất của Mỹ. Thế giới ng&agrave;y nay quay trở lại kịch bản lịch sử cũ:<em>&nbsp;</em>Tranh đấu kh&ocirc;ng v&igrave; l&yacute; tưởng m&agrave; chỉ v&igrave; ph&acirc;n chia t&agrave;i nguy&ecirc;n. Cuộc chiến n&agrave;y sẽ k&eacute;o d&agrave;i.</p>\r\n\r\n<p>Về đối ngoại, sức mạnh của Trung Quốc l&agrave;m thế giới lo ngại nhưng về đối nội, h&agrave;ng loạt dự &aacute;n chết non. Courrier International d&agrave;nh cho b&aacute;o mạng Hồng K&ocirc;ng The Initium đăng tải nhiều trang v&agrave; h&igrave;nh ảnh một th&agrave;nh phố mới trong dự &aacute;n khu đ&ocirc;i thị 2.000 km2 ở H&ugrave;ng An, H&agrave; Bắc bị bỏ hoang v&igrave; kh&ocirc;ng người tới ở.</p>\r\n\r\n<p>Mọi c&ocirc;ng tr&igrave;nh x&acirc;y cất&nbsp;&quot;thủ đ&ocirc; của giấc mơ Trung Hoa&quot;&nbsp;bị ngừng lại v&igrave; phải chờ quyết định đại hội đảng. Đại hội qua rồi nhưng lệnh t&aacute;i khởi động kh&ocirc;ng thấy đ&acirc;u, kể cả trong diễn văn của&nbsp;&ocirc;ng Tập. Một trong những nh&agrave; đầu tư bị ph&aacute; sản, t&acirc;m sự: L&uacute;c n&agrave;y t&ocirc;i mới hiểu &yacute; nghĩa của cụm từ &quot;dự &aacute;n của thế kỷ&quot;.</p>\r\n\r\n<p>&nbsp;</p>', 'upload/news\\1588907842.jpeg', 0, 0, 2, 1, 'nato-tan-nat-phuong-tay-suy-tan-vi-donald-trump-tap-can-binh', NULL, '2020-09-07 20:17:22', '2020-09-07 20:17:22'),
(8, 'Báo Pháp công bố báo cáo điều tra những vấn đề chí mạng của Phòng thí nghiệm Vũ Hán', 6, 'VietTimes – Kể từ khi bị nghi ngờ virus Corona mới thoát ra từ phòng thí nghiệm Vũ Hán, cuộc tranh luận về việc Pháp cung cấp công nghệ P4 cho Trung Quốc lại bùng lên và dư luận rất lo ngại về nguy cơ Pháp bán công nghệ lưỡng dụng quân sự - dân sự cho Trung Quốc.', '<p>VietTimes &ndash; Kể từ khi bị nghi ngờ virus Corona mới tho&aacute;t ra từ ph&ograve;ng th&iacute; nghiệm Vũ H&aacute;n, cuộc tranh luận về việc Ph&aacute;p cung cấp c&ocirc;ng nghệ P4 cho Trung Quốc lại b&ugrave;ng l&ecirc;n v&agrave; dư luận rất lo ngại về nguy cơ Ph&aacute;p b&aacute;n c&ocirc;ng nghệ lưỡng dụng qu&acirc;n sự - d&acirc;n sự cho Trung Quốc.</p>', 'upload/news\\1588908451.jpeg', 1, 0, 1, 1, 'bao-phap-cong-bo-bao-cao-dieu-tra-nhung-van-de-chi-mang-cua-phong-thi-nghiem-vu-han', NULL, '2020-09-07 20:27:31', '2020-09-07 20:27:31');
INSERT INTO `news` (`id`, `news_title`, `author_id`, `news_summary`, `news_content`, `news_image`, `news_featured`, `news_view`, `tag_id`, `category_id`, `news_slug`, `deleted_at`, `created_at`, `updated_at`) VALUES
(9, 'Phe đối lập Venezuela ký hợp đồng 213 triệu USD để ám sát Tổng thống', 1, 'VietTimes – Phe đối lập ở Venezuela trong tháng 10/2019 đã ký một hợp đồng trị giá 213 triệu USD với công ty quân sự tư nhân Silvercorp USA của Mỹ hòng lật đổi Tổng thống Nicolas Maduro; theo một tài liệu mà tờ Washington Post công bố.', '<p>VietTimes &ndash; Phe đối lập ở Venezuela trong th&aacute;ng 10/2019 đ&atilde; k&yacute; một hợp đồng trị gi&aacute; 213 triệu USD với c&ocirc;ng ty qu&acirc;n sự tư nh&acirc;n Silvercorp USA của Mỹ h&ograve;ng lật đổi Tổng thống Nicolas Maduro; theo một t&agrave;i liệu m&agrave; tờ Washington Post c&ocirc;ng bố.</p>\r\n\r\n<p><a href=\"https://image.viettimes.vn/666x374/Uploaded/2020/spivplcg/2020_05_08/tong-thong-venezuela-nicolas-maduro---sputnik-3454024_852020.jpg\"><img alt=\"Tổng thống Venezuela Nicolas Maduro cho rằng đây là một âm mưu hòng ám sát ông (Ảnh: Sputnik)\" src=\"https://image.viettimes.vn/666x374/Uploaded/2020/spivplcg/2020_05_08/tong-thong-venezuela-nicolas-maduro---sputnik-3454024_852020.jpg\" /></a></p>\r\n\r\n<p>Tổng thống Venezuela Nicolas Maduro cho rằng đ&acirc;y l&agrave; một &acirc;m mưu h&ograve;ng &aacute;m s&aacute;t &ocirc;ng (Ảnh: Sputnik)</p>\r\n\r\n<p>Tờ Washington Post n&oacute;i rằng t&agrave;i liệu tr&ecirc;n được cung cấp bởi ch&iacute;nh phe đối lập Venezuela.</p>\r\n\r\n<p>&quot;C&aacute;c cố vấn cung cấp dịch vụ sẽ tham vấn v&agrave; hỗ trợ nh&oacute;m đối t&aacute;c trong việc l&ecirc;n kế hoạch v&agrave; thực hiện một chiến dịch nhằm bắt/giam giữ/loại bỏ Nicolas Maduro (Mục ti&ecirc;u ch&iacute;nh), lật đổ chế độ hiện tại v&agrave; đưa Tổng thống Juan Guaido (tự phong) l&ecirc;n nắm quyền&quot; - t&agrave;i liệu d&agrave;i 42 trang c&oacute; ghi.</p>\r\n\r\n<p>Theo Washington Post, phe đối lập Venezuela phải chi khoảng 213 triệu USD cho c&ocirc;ng ty Silvercorp để thực hiện phi vụ tr&ecirc;n.</p>\r\n\r\n<p>&quot;Nguồn tiền n&agrave;y sẽ được sử dụng để mua trang thiết bị li&ecirc;n lạc, l&ecirc;n kế hoạch v&agrave; di trang trải chi ph&iacute; di chuyển. Dự kiến tổng chi ph&iacute; to&agrave;n dự &aacute;n sẽ l&agrave; 212.900.000 USD, thực hiện trong v&ograve;ng 495 ng&agrave;y&quot; - t&agrave;i liệu c&oacute; đoạn.</p>\r\n\r\n<p>T&agrave;i liệu tr&ecirc;n được k&yacute; bởi Sergio Vergara, một th&agrave;nh vi&ecirc;n của Quốc hội đang thuộc tầm kiểm so&aacute;t của phe đối lập Venezuela, v&agrave; Jordan Goudreau, CEO của Silvercorp.</p>\r\n\r\n<p>C&aacute;c đại diện của &ocirc;ng Guaido trước đ&oacute; đ&atilde; b&aacute;c bỏ mọi sự li&ecirc;n quan tới phi vụ thu&ecirc; l&iacute;nh đ&aacute;nh thu&ecirc; bất th&agrave;nh n&agrave;y.</p>\r\n\r\n<p>H&ocirc;m Chủ nhật tuần trước, Bộ trưởng Nội vụ Venezuela Nestor Reverol cho hay một số kẻ c&oacute; vũ trang định th&acirc;m nhập bang miền Bắc La Guaira bằng xuồng cao tốc. &Ocirc;ng th&ecirc;m rằng 8 kẻ trong số n&agrave;y bị ti&ecirc;u diệt trong khi 2 kẻ kh&aacute;c bị bắt giữ. &Ocirc;ng Reverol tuy&ecirc;n bố rằng một trong số n&agrave;y l&agrave; đặc vụ của Cục chống ma t&uacute;y Mỹ (DEA) - mặc d&ugrave; DEA đ&atilde; b&aacute;c bỏ c&aacute;o buộc n&agrave;y.</p>\r\n\r\n<p>Một trong số những kẻ bị bắt giữ, c&ocirc;ng d&acirc;n Mỹ t&ecirc;n Luke Denman l&agrave;m việc cho Silvercorp, n&oacute;i trong một cuộc thẩm vấn rằng mục đ&iacute;ch của nh&oacute;m n&agrave;y l&agrave; chiếm giữ s&acirc;n bay ở thủ đ&ocirc; Caracas v&agrave; kiểm so&aacute;t n&oacute; cho tới khi Tổng thống Maduro c&oacute; chuyến bay tới Mỹ. Ch&iacute;nh phủ Mỹ đ&atilde; b&aacute;c bỏ mọi sự d&iacute;nh l&iacute;u tới những tay l&iacute;nh đ&aacute;nh thu&ecirc; n&agrave;y.</p>\r\n\r\n<p>Jordan Goudreau - một cựu binh sĩ Mũ nồi Xanh v&agrave; đang l&agrave; CEO của Silvercorp USA - tuy&ecirc;n bố rằng &ocirc;ng ch&iacute;nh l&agrave; người đứng đằng sau &acirc;m mưu n&agrave;y, trong một video đăng tải tr&ecirc;n YouTube h&ocirc;m Chủ nhật tuần trước. Theo Goudreau, Tổng thống &quot;tự phong&quot; Juan Guiado đ&atilde; k&yacute; hợp đồng d&agrave;i 8 trang với Silversorp v&agrave;o th&aacute;ng 10/2019, tuy nhi&ecirc;n th&ocirc;ng tin n&agrave;y bị nh&oacute;m phụ tr&aacute;ch truyền th&ocirc;ng của &ocirc;ng Guaido phủ nhận trong một tuy&ecirc;n bố đưa ra h&ocirc;m đầu tuần n&agrave;y.</p>\r\n\r\n<p>Tổng thống Venezuela Nicolas Maduro h&ocirc;m đầu tuần n&agrave;y n&oacute;i rằng vụ x&acirc;m lược h&agrave;ng hải tr&ecirc;n l&agrave; &quot;h&agrave;nh động khủng bố trong bối cảnh đại dịch&quot; COVID-19, v&agrave; rằng ch&iacute;nh quyền của &ocirc;ng c&oacute; đủ bằng chứng cho thấy những tay l&iacute;nh đ&aacute;nh thu&ecirc; bị bắt giữ đ&atilde; được huấn luyện tại nước l&aacute;ng giềng Colombia trong một chiến dịch được r&oacute;t vốn bởi Washington v&agrave; Bogota nhằm &aacute;m s&aacute;t &ocirc;ng. Tuy nhi&ecirc;n, ch&iacute;nh phủ Colombia đ&atilde; b&aacute;c bỏ mọi c&aacute;c buộc tr&ecirc;n.</p>', 'upload/news\\1588908595.jpeg', 1, 0, 4, 6, 'phe-doi-lap-venezuela-ky-hop-dong-213-trieu-usd-de-am-sat-tong-thong', NULL, '2020-09-07 20:29:55', '2020-09-07 20:29:55'),
(10, 'Chuyên gia WHO: hiện tượng tái dương tính SARS-CoV-2 là một phần của quá trình khỏi bệnh!', 1, 'VietTimes -- Trên thế giới đã có nhiều trường hợp bệnh nhân COVID-19 đã  được chữa khỏi, nhưng khi xét nghiệm axit nucleic lại cho kết quả dương tính trở lại với SARS-CoV-2,  (còn gọi là “phục dương), gây ra nghi ngờ về việc mắc bệnh trở lại hoặc tiếp tục lây nhiễm cho người khác. WHO đã chính thức lên tiếng về vấn đề này.', '<p>VietTimes -- Tr&ecirc;n thế giới đ&atilde; c&oacute; nhiều trường hợp bệnh nh&acirc;n COVID-19 đ&atilde; &nbsp;được chữa khỏi, nhưng khi x&eacute;t nghiệm axit nucleic lại cho kết quả dương t&iacute;nh trở lại với SARS-CoV-2, &nbsp;(c&ograve;n gọi l&agrave; &ldquo;phục dương), g&acirc;y ra nghi ngờ về việc mắc bệnh trở lại hoặc tiếp tục l&acirc;y nhiễm cho người kh&aacute;c. WHO đ&atilde; ch&iacute;nh thức l&ecirc;n tiếng về vấn đề n&agrave;y.</p>', 'upload/news\\1588908692.jpeg', 1, 0, 5, 3, 'chuyen-gia-who-hien-tuong-tai-duong-tinh-sars-cov-2-la-mot-phan-cua-qua-trinh-khoi-benh', NULL, '2020-09-07 20:31:32', '2020-09-07 20:31:32'),
(11, 'Nga trở thành vùng dịch lớn thứ 5 trên thế giới', 8, 'VietTimes -- Tổng số ca nhiễm virus corona chủng mới ở nước Nga đã lên tới 177.160 trong ngày 7/5 sau khi báo cáo số ca nhiễm cao kỷ lục trong ngày, biến nước này trở thành vùng dịch lớn thứ 5 trên thế giới với tổng số ca nhiễm vượt qua Đức và Pháp.', '<p>VietTimes --&nbsp;Tổng số ca nhiễm virus corona chủng mới ở nước Nga đ&atilde; l&ecirc;n tới 177.160 trong ng&agrave;y 7/5 sau khi b&aacute;o c&aacute;o số ca nhiễm cao kỷ lục trong ng&agrave;y, biến nước n&agrave;y trở th&agrave;nh v&ugrave;ng dịch lớn thứ 5 tr&ecirc;n thế giới với tổng số ca nhiễm vượt qua Đức v&agrave; Ph&aacute;p.</p>\r\n\r\n<p><a href=\"https://image.viettimes.vn/666x374/Uploaded/2020/spivplcg/2020_05_07/covid-19-dien-bien-kho-luong-o-nga---vox-1772546_752020.jpg\"><img alt=\"Nga trở thành vùng dịch lớn thứ 5 thế giới sau khi ghi nhận số ca nhiễm mới COVID-19 trong ngày cao kỷ lục (Ảnh: VOX)\" src=\"https://image.viettimes.vn/666x374/Uploaded/2020/spivplcg/2020_05_07/covid-19-dien-bien-kho-luong-o-nga---vox-1772546_752020.jpg\" /></a></p>\r\n\r\n<p>Nga trở th&agrave;nh v&ugrave;ng dịch lớn thứ 5 thế giới sau khi ghi nhận số ca nhiễm mới COVID-19 trong ng&agrave;y cao kỷ lục (Ảnh: VOX)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong h&ocirc;m 7/5 ch&iacute;nh quyền Nga ghi nhận th&ecirc;m mức kỷ lục 11.231 ca nhiễm chỉ trong v&ograve;ng 24 giờ, đ&acirc;y l&agrave; con số người tử vong ghi nhận trong ng&agrave;y cao nhất từ trước đến nay ở Nga. Hơn một nửa trong tổng số ca nhiễm mới v&agrave; ca tử vong mới l&agrave; ở thủ đ&ocirc; Moscow, th&agrave;nh phố được coi l&agrave; t&acirc;m dịch ở Nga với hơn 6.703 ca nhiễm mới được ghi nhận trong h&ocirc;m thứ Năm.</p>\r\n\r\n<p>Số ca nhiễm mới trong 24 giờ tại Nga đ&atilde; ph&aacute; vỡ kỷ lục 10.633 ca h&ocirc;m 3/5, duy tr&igrave; đ&agrave; tăng&nbsp;tr&ecirc;n 10.000 ca mỗi ng&agrave;y trong 5 ng&agrave;y&nbsp;li&ecirc;n tiếp. Với tổng cộng 177.160 ca nhiễm, Nga vượt Ph&aacute;p, quốc gia ghi nhận hơn 174.000 ca, trở th&agrave;nh v&ugrave;ng dịch lớn thứ 5 thế giới sau Mỹ, T&acirc;y Ban Nha, Italy v&agrave; Anh.</p>\r\n\r\n<p>Trong số ca nhiễm mới h&ocirc;m 7/5, c&oacute; 5.471 người kh&ocirc;ng triệu chứng, tương đương 48,7%, Trung t&acirc;m Xử l&yacute; Khủng hoảng do COVID-19 thuộc Cơ quan Gi&aacute;m s&aacute;t Bảo vệ Quyền lợi Người ti&ecirc;u d&ugrave;ng v&agrave; Nh&acirc;n quyền Li&ecirc;n bang Nga (Rospotrebnadzor) cho biết.</p>\r\n\r\n<p>Con số ca tử vong do COVID-19 ở Nga, hiện ở mức thấp hơn nhiều so với nhiều quốc gia kh&aacute;c tr&ecirc;n thế giới, cũng tăng l&ecirc;n 1.625 người sau khi c&oacute; th&ecirc;m 66 ca tử vong được b&aacute;o c&aacute;o chỉ trong v&ograve;ng 24 giờ; theo đội đặc nhiệm ứng ph&oacute; đại dịch COVID-19 của Ch&iacute;nh phủ Nga.</p>\r\n\r\n<p>Thị trưởng Moscow Sergei Sobyanin, trong b&agrave;i ph&aacute;t biểu dưa ra h&ocirc;m trước đ&oacute;, n&oacute;i rằng con số ca nhiễm COVID-19 được x&aacute;c nhận đang tăng dần ở th&agrave;nh phố thủ đ&ocirc; l&agrave; bởi ch&iacute;nh quyền c&aacute;c cấp đang tăng cường chương tr&igrave;nh x&eacute;t nghiệm diện rộng v&agrave; rằng t&igrave;nh h&igrave;nh về dịch bệnh tr&ecirc;n thực tế đang ổn định dần.</p>\r\n\r\n<p>Ch&iacute;nh phủ Nga trong khi đ&oacute; cho hay họ đ&atilde; thực hiện được hơn 4,8 triệu x&eacute;t nghiệm virus corona chủng mới.</p>\r\n\r\n<p>Hiện nay tỷ lệ tử vong do COVID-19 ở Nga được duy tr&igrave; ở mức thấp. Tuy nhi&ecirc;n, điều n&agrave;y lại khiến nhiều người quan ngại rằng con số người chết thực tế do bệnh dịch c&oacute; thể chưa được thống k&ecirc; đầy đủ. Ch&iacute;nh quyền Nga đ&atilde; l&ecirc;n tiếng b&aacute;c bỏ điều n&agrave;y, chỉ ra rằng dịch COVID-19 bắt đầu xuất hiện ở Nga muộn hơn so với nhiều quốc gia kh&aacute;c, gi&uacute;p cho ch&iacute;nh quyền c&aacute;c cấp c&oacute; sự chuẩn bị tốt hơn.</p>\r\n\r\n<p>Tổng thống Nga Vladimir Putin trong h&ocirc;m thứ Tư vừa qua đ&atilde; ủng hộ một kế hoạch m&agrave; Thị trưởng Sobyanin đề xuất nhằm dần dần gỡ bỏ c&aacute;c lệnh phong tỏa sau ng&agrave;y 12/5, cho ph&eacute;p một số cơ sở c&ocirc;ng nghiệp nhất định hoạt động trở lại. Tuy nhi&ecirc;n, &ocirc;ng Putin cũng cảnh b&aacute;o c&aacute;c l&atilde;nh đạo địa phương kh&ocirc;ng vội v&agrave;ng nới c&aacute;c biện ph&aacute;p hạn chế để ngăn dịch, khuyến c&aacute;o họ c&acirc;n nhắc tất cả yếu tố v&agrave; rủi ro c&oacute; thể xảy ra.</p>\r\n\r\n<p>Được biết, Moscow v&agrave; nhiều khu vực kh&aacute;c của nước Nga đ&atilde; được &aacute;p dụng lệnh phong tỏa trong v&ograve;ng 6 tuần lễ.</p>\r\n\r\n<p>Người d&acirc;n ở th&agrave;nh phố thủ đ&ocirc; nhận được chỉ thị ở y&ecirc;n trong nh&agrave; của họ trừ c&aacute;c trường hợp nhất định như đi ra ngo&agrave;i để mua thực phẩm v&agrave; thuốc men. Họ cũng cần phải nhận được giấy ph&eacute;p điện tử để c&oacute; thể di chuyển tới nơi c&ocirc;ng cộng hay tham gia phương tiện giao th&ocirc;ng c&ocirc;ng cộng.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://viettimes.vn/nga-tro-thanh-vung-dich-lon-thu-5-tren-the-gioi-389070.html\">https://viettimes.vn/nga-tro-thanh-vung-dich-lon-thu-5-tren-the-gioi-389070.html</a></p>', 'upload/news\\1588908776.jpeg', 1, 0, 5, 3, 'nga-tro-thanh-vung-dich-lon-thu-5-tren-the-gioi', NULL, '2020-09-07 20:32:56', '2020-09-07 20:33:30'),
(12, 'Quan hệ Châu Âu – Trung Quốc đổi chiều, virus Corona mới đã làm thay đổi mọi thứ', 4, 'VietTimes -- Trước khi đại dịch COVID-19 hoành hành trên khắp thế giới, năm 2020 ban đầu là một năm cực kỳ quan trọng đối với EU và Trung Quốc để tăng cường quan hệ kinh tế và chiến lược. Tuy nhiên, theo truyền thông Mỹ, sự xuất hiện của đại dịch đã thay đổi tất cả', '<p><a href=\"https://image.viettimes.vn/666x374/Uploaded/2020/bpcivpwi/2020_05_07/quan-he-trung-quoc---eu-8136367_752020.png\"><img alt=\"Giới truyền thông phương Tây cho rằng dịch bệnh COVID-19 đã khiến quan hệ châu Âu - Trung Quốc thay đổi (Ảnh: RFI).\" src=\"https://image.viettimes.vn/666x374/Uploaded/2020/bpcivpwi/2020_05_07/quan-he-trung-quoc---eu-8136367_752020.png\" /></a></p>\r\n\r\n<p>Giới truyền th&ocirc;ng phương T&acirc;y cho rằng dịch bệnh COVID-19 đ&atilde; khiến quan hệ ch&acirc;u &Acirc;u - Trung Quốc thay đổi (Ảnh: RFI).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nh&agrave; b&aacute;o kỳ cựu Luke McGee của&nbsp;<em>CNN</em>&nbsp;h&ocirc;m 5/5 đ&atilde; đưa ra một ph&acirc;n t&iacute;ch, n&oacute;i rằng trong hội nghị thượng đỉnh dự kiến được tổ chức v&agrave;o th&aacute;ng 9 năm nay, hai b&ecirc;n EU v&agrave; Trung Quốc sẽ c&oacute; một bước tiến quan trọng trong quan hệ kinh tế v&agrave; chiến lược. &Iacute;t nhất đ&oacute; l&agrave; kế hoạch ban đầu. Nhưng phản ứng của Trung Quốc đối với dịch bệnh COVID-19 đ&atilde; để lại vị đắng trong miệng c&aacute;c quan chức ch&acirc;u &Acirc;u. Từ sự &aacute;p chế gay gắt c&aacute;c c&ocirc;ng d&acirc;n trong nước của Trung Quốc đến việc gieo rắc th&ocirc;ng tin sai lệch ở ch&acirc;u &Acirc;u, cuộc khủng hoảng n&agrave;y nhắc nhở mọi người rằng tiếp x&uacute;c gần hơn với Trung Quốc sẽ mang lại rủi ro.</p>\r\n\r\n<p>Theo kế hoạch cũ, Thủ tướng Đức Angela Merkel sẽ chủ tr&igrave; hội nghị thượng đỉnh tại th&agrave;nh phố Leipzig của Đức v&agrave;o ng&agrave;y 14/9 với sự tham dự của c&aacute;c nh&agrave; l&atilde;nh đạo EU v&agrave; Chủ tịch Trung Quốc Tập Cận B&igrave;nh. C&aacute;c nh&agrave; ngoại giao Đức cho rằng địa điểm n&agrave;y được chọn v&igrave; mối quan hệ lịch sử giữa CHDC Đức cũ v&agrave; Trung Quốc. Họ n&oacute;i rằng với tư c&aacute;ch l&agrave; chủ tịch lu&acirc;n phi&ecirc;n của EU, b&agrave; Merkel sẽ tổ chức hội nghị thượng đỉnh lần cuối c&ugrave;ng trong sự nghiệp của b&agrave; v&agrave; dốc sức cho sự th&agrave;nh c&ocirc;ng của hội nghị thượng đỉnh n&agrave;y.</p>\r\n\r\n<p><strong>Đại dịch khiến người ch&acirc;u &Acirc;u suy nghĩ lại về Trung Quốc</strong></p>\r\n\r\n<p>&Ocirc;ng Luke McGee n&oacute;i rằng thực sự, c&aacute;c nh&agrave; l&atilde;nh đạo EU kỳ vọng đưa c&aacute;c quan niệm về nh&acirc;n quyền, biến đổi kh&iacute; hậu v&agrave; chủ nghĩa đa phương của Trung Quốc v&agrave; ch&acirc;u &Acirc;u đến gần nhau hơn; thế nhưng, cảm gi&aacute;c thực tế tại Brussels l&agrave; đại dịch bệnh n&agrave;y đ&atilde; khiến mọi người phải xem x&eacute;t lại quan điểm của họ về Trung Quốc.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/bpcivpwi/2020_05_07/steven-blockman-csf-5154192_752020.jpg\"><img alt=\"Quan hệ Châu Âu – Trung Quốc đổi chiều, virus Corona mới đã làm thay đổi mọi thứ - ảnh 1\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/bpcivpwi/2020_05_07/steven-blockman-csf-5154192_752020.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&Ocirc;ng Steven Blockmans: cho d&ugrave; tiền vốn của Trung Quốc hấp dẫn đến mấy, họ vẫn l&agrave; đối thủ c&oacute; t&iacute;nh hệ thống (Ảnh: CSF).</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Steven Blockmans, người đứng đầu ch&iacute;nh s&aacute;ch đối ngoại của EU tại Trung t&acirc;m nghi&ecirc;n cứu ch&iacute;nh s&aacute;ch ch&acirc;u &Acirc;u, n&oacute;i: &ldquo;T&ocirc;i nghĩ rằng coronavirus lu&ocirc;n l&agrave; lời nhắc nhở cần thiết cho nhiều nước EU: cho d&ugrave; tiền vốn của Trung Quốc hấp dẫn đến mấy, nhưng họ cũng l&agrave; một đối thủ c&oacute; t&iacute;nh hệ thống&rdquo;.</p>\r\n\r\n<p>Blokmans đề cập đến một th&ocirc;ng c&aacute;o do Ủy ban ch&acirc;u &Acirc;u ban h&agrave;nh v&agrave;o th&aacute;ng 3/2019, trong đ&oacute; m&ocirc; tả Trung Quốc l&agrave; &ldquo;một đối thủ c&oacute; hệ thống th&uacute;c đẩy c&aacute;c m&ocirc; h&igrave;nh quản trị c&oacute; t&iacute;nh thay thế&rdquo;.</p>\r\n\r\n<p>McGee n&oacute;i rằng hiện tượng thiếu tin tưởng v&agrave;o Trung Quốc dường như đang lan rộng khắp Brussels (nơi đ&oacute;ng trụ sở EU). Cuối tuần trước, Chuy&ecirc;n vi&ecirc;n phụ tr&aacute;ch &nbsp;ngoại giao của EU Josep Borrell đ&atilde; n&oacute;i trong một cuộc phỏng vấn với &nbsp;tạp ch&iacute; Ph&aacute;p&nbsp;<em>Le Journal du Dimanche</em>&nbsp;rằng, EU đ&atilde; từng &ldquo;rất ng&acirc;y thơ&rdquo; khi giao dịch với Trung Quốc. &Ocirc;ng thừa nhận Trung Quốc c&oacute; c&aacute;ch hiểu kh&aacute;c về trật tự quốc tế.</p>\r\n\r\n<p>Những bất đồng n&agrave;y rất kh&oacute; khiến người ta tin rằng EU v&agrave; Trung Quốc sẽ đạt được sự đồng thuận trong năm nay. Rất &iacute;t người tin rằng hội nghị thượng đỉnh ở Leipzig, Đức v&agrave;o th&aacute;ng 9 sẽ diễn ra như b&agrave; Merkel hoặc những người kh&aacute;c l&ecirc;n kế hoạch ban đầu.</p>\r\n\r\n<p>B&agrave; Velina Tchakarova, gi&aacute;m đốc Viện nghi&ecirc;n cứu Ch&iacute;nh s&aacute;ch an ninh v&agrave; ch&acirc;u &Acirc;u của &Aacute;o, dự đo&aacute;n: &ldquo;H&agrave;nh động của Trung Quốc trong v&agrave; sau COVID-19 sẽ dẫn đến việc xuất hiện sự bất đồng v&agrave; chia rẽ hơn nữa trong nội bộ c&aacute;c nước ch&acirc;u &Acirc;u về c&aacute;ch giải quyết quan hệ với Bắc Kinh&rdquo;.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/bpcivpwi/2020_05_08/velina-3779558_852020.jpg\"><img alt=\"Quan hệ Châu Âu – Trung Quốc đổi chiều, virus Corona mới đã làm thay đổi mọi thứ - ảnh 2\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/bpcivpwi/2020_05_08/velina-3779558_852020.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>B&agrave; Velina Tchakarova: &quot;H&agrave;nh động của Trung Quốc trong v&agrave; sau COVID-19 sẽ dẫn đến việc xuất hiện sự bất đồng v&agrave; chia rẽ hơn nữa trong nội bộ c&aacute;c nước ch&acirc;u &Acirc;u về c&aacute;ch giải quyết quan hệ với Bắc Kinh&quot; (Ảnh: ORF).</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>B&agrave; n&oacute;i rằng c&oacute; sự kh&aacute;c biệt trong EU về việc c&oacute; cho ph&eacute;p c&aacute;c c&ocirc;ng ty Trung Quốc x&acirc;y dựng mạng 5G hay kh&ocirc;ng v&agrave; ai c&oacute; thể v&agrave; kh&ocirc;ng thể đầu tư v&agrave;o c&aacute;c nước th&agrave;nh vi&ecirc;n.</p>\r\n\r\n<p>&Ocirc;ng McGee n&oacute;i v&igrave; những nguy&ecirc;n nh&acirc;n n&agrave;y v&agrave; c&aacute;c nguy&ecirc;n nh&acirc;n kh&aacute;c, đa số mọi người nghĩ rằng hội nghị thượng đỉnh ở Leipzig sẽ ho&agrave;n to&agrave;n bị lu mờ bởi virus corona.</p>\r\n\r\n<p>Mặc d&ugrave; chuỗi cung ứng của EU phụ thuộc v&agrave;o Trung Quốc ở một mức độ nhất định, nhưng điều đ&oacute; kh&ocirc;ng c&oacute; nghĩa l&agrave; EU kh&ocirc;ng thể g&acirc;y &aacute;p lực l&ecirc;n Trung Quốc. Một nh&agrave; ngoại giao Đức n&oacute;i: &ldquo;Ảnh hưởng của Trung Quốc đang gia tăng, nhưng đ&acirc;y kh&ocirc;ng phải l&agrave; đơn phương. R&otilde; r&agrave;ng, Trung Quốc cũng cần ch&acirc;u &Acirc;u&rdquo;. C&aacute;c quan chức Brussels hy vọng rằng điều n&agrave;y c&oacute; nghĩa l&agrave; c&oacute; thể d&ugrave;ng nh&acirc;n quyền để g&acirc;y sức &eacute;p với Trung Quốc.</p>\r\n\r\n<p>Đối với EU, tiếp x&uacute;c với Trung Quốc kh&ocirc;ng chỉ l&agrave; tiền bạc. &Ocirc;ng Steven Blockmans n&oacute;i: &ldquo;Tăng cường tiếp x&uacute;c với Trung Quốc mang lại cho EU cơ hội nỗ lực tăng gấp bội c&aacute;c ưu ti&ecirc;n chiến lược của họ&rdquo;.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, những lo ngại của EU về sự minh bạch của Trung Quốc trong đại dịch COVID-19 đ&atilde; nhắc nhở mọi người về &yacute; nghĩa thực tế&nbsp; của việc tiếp x&uacute;c với Trung Quốc.</p>\r\n\r\n<p><strong>Quan hệ Trung Quốc &ndash; ch&acirc;u &Acirc;u đổi hướng</strong></p>\r\n\r\n<p>Luke McGee n&oacute;i rằng trong bối cảnh virus Corona mới, m&ocirc; h&igrave;nh quản trị của Trung Quốc đ&atilde; khiến c&aacute;c quan chức EU b&agrave;y tỏ mối quan ngại.</p>\r\n\r\n<p>Th&aacute;ng trước, một b&aacute;o c&aacute;o nội bộ của Li&ecirc;n minh ch&acirc;u &Acirc;u đ&atilde; c&aacute;o buộc Trung Quốc gieo rắc &ldquo;th&ocirc;ng tin sai lệch&rdquo; về dịch bệnh để thu được lợi &iacute;ch chiến lược. Tờ&nbsp;<em>Politico</em>&nbsp;của Mỹ đ&atilde; tiết lộ tr&iacute;ch đoạn b&aacute;o c&aacute;o nội bộ của EU chỉ ra rằng c&oacute; bằng chứng cho thấy Bắc Kinh sử dụng &ldquo;thủ đoạn c&ocirc;ng khai v&agrave; b&iacute; mật&rdquo; để thực hiện &ldquo;chiến dịch tuy&ecirc;n truyền th&ocirc;ng tin sai lệch to&agrave;n cầu&rdquo; nhằm trốn tr&aacute;nh việc thế giới quy tr&aacute;ch nhiệm về đại dịch virus cho Bắc Kinh v&agrave; cố gắng cải thiện h&igrave;nh ảnh quốc tế của họ.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/bpcivpwi/2020_05_08/eu-lo-ngai-trung-quoc-3547255_852020.jpg\"><img alt=\"Quan hệ Châu Âu – Trung Quốc đổi chiều, virus Corona mới đã làm thay đổi mọi thứ - ảnh 3\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/bpcivpwi/2020_05_08/eu-lo-ngai-trung-quoc-3547255_852020.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Một số nước EU đang t&igrave;m kiếm một số ch&iacute;nh s&aacute;ch nhằm giảm bớt sự phụ thuộc v&agrave;o Trung Quốc v&agrave; kiềm chế &quot;đầu tư kiểu cướp đoạt&quot;&nbsp; (Ảnh Getty).</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Luke McGee n&oacute;i rằng r&otilde; r&agrave;ng l&agrave; một số người trong Li&ecirc;n minh Ch&acirc;u &Acirc;u rất lo lắng về h&agrave;nh vi của Trung Quốc. &Ocirc;ng cho rằng một mặt, EU kh&ocirc;ng muốn bị kẹt trong cuộc đọ sức giữa Trung Quốc v&agrave; Mỹ; mặt kh&aacute;c, lịch sử gần đ&acirc;y cho thấy Trung Quốc l&agrave; một đối t&aacute;c kh&ocirc;ng đ&aacute;ng tin cậy v&agrave; Bắc Kinh đang g&acirc;y chia rẽ giữa c&aacute;c quốc gia th&agrave;nh vi&ecirc;n EU .</p>\r\n\r\n<p>Một b&agrave;i b&aacute;o của&nbsp;<em>RFI</em>&nbsp;ng&agrave;y 19/4 đ&atilde; viết, con virus nhỏ b&eacute; kh&ocirc;ng thể nh&igrave;n thấy n&agrave;y đang thay đổi s&acirc;u sắc quan hệ ch&acirc;u &Acirc;u - Trung Quốc. Tổng thống Ph&aacute;p Macron c&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u n&oacute;i với&nbsp;<em>Financial Times</em>&nbsp;rằng &ldquo;đừng ng&acirc;y thơ nghĩ rằng Trung Quốc đ&atilde; kiểm so&aacute;t dịch tốt hơn. Trung Quốc r&otilde; r&agrave;ng l&agrave;m nhiều điều người ta chưa biết&rdquo;.</p>\r\n\r\n<p>Nước Đức k&ecirc;u gọi tiến h&agrave;nh một cuộc điều tra về nguồn gốc của virus Corona mới. Trong cuộc họp b&aacute;o v&agrave;o ng&agrave;y 20/4, Thủ tướng Angela Merkel k&ecirc;u gọi Trung Quốc h&atilde;y đối mặt với nguồn gốc của virus với th&aacute;i độ minh bạch. B&agrave; n&oacute;i: &ldquo;Trung Quốc c&agrave;ng minh bạch về nguồn gốc của virus, th&igrave; c&agrave;ng tốt cho mọi người tr&ecirc;n h&agrave;nh tinh n&agrave;y&rdquo;.</p>\r\n\r\n<p>H&atilde;ng tin&nbsp;<em>Bloomberg</em>&nbsp;ng&agrave;y 21/4 đ&atilde; đưa một b&agrave;i b&aacute;o n&oacute;i, năm nay đ&aacute;ng lẽ l&agrave; &ldquo;Năm ngoại giao Trung Quốc - Ch&acirc;u &Acirc;u&rdquo;; nhưng ngược lại, Ch&acirc;u &Acirc;u cảnh b&aacute;o e rằng c&oacute; thể xuất hiện một vết nứt c&oacute; t&iacute;nh hủy hoại trong mối quan hệ giữa hai b&ecirc;n.</p>\r\n\r\n<p>&ldquo;Trong v&agrave;i th&aacute;ng qua, Trung Quốc đ&atilde; mất ch&acirc;u &Acirc;u&rdquo;. Reinhard Butikofer, nghị sĩ của Đảng Xanh Đức v&agrave; Trưởng đo&agrave;n đại diện Nghị viện ch&acirc;u &Acirc;u đối với Trung Quốc, n&oacute;i từ việc &ldquo;quản l&yacute; sự thật&rdquo; của Trung Quốc trong giai đoạn đầu dịch bệnh b&ugrave;ng ph&aacute;t đến lập trường cực đoan của Bộ Ngoại giao Trung Quốc đều rất đ&aacute;ng lo ngại.</p>\r\n\r\n<p>Mặc d&ugrave; ch&iacute;nh phủ Donald Trump của Mỹ đ&atilde; bắt đầu chỉ tr&iacute;ch Trung Quốc một lần nữa, nhưng c&aacute;c quan chức EU về truyền thống kh&ocirc;ng muốn chỉ tr&iacute;ch c&ocirc;ng khai, một phần v&igrave; sợ bị Trung Quốc trả th&ugrave;; nhưng b&acirc;y giờ c&aacute;c ch&iacute;nh trị gia ở Berlin, Paris, London v&agrave; Brussels đều bất b&igrave;nh với tuy&ecirc;n bố của Bắc Kinh về sự kiện virus, điều đ&oacute; c&oacute; nghĩa l&agrave; mức độ kh&ocirc;ng h&agrave;i l&ograve;ng s&acirc;u sắc hơn v&agrave; hậu quả sẽ rất lớn.</p>\r\n\r\n<p>Một số quốc gia th&agrave;nh vi&ecirc;n EU đ&atilde; t&igrave;m kiếm một số ch&iacute;nh s&aacute;ch nhằm giảm bớt sự phụ thuộc v&agrave;o Trung Quốc v&agrave; kiềm chế kiểu đầu tư cướp đoạt. Những biện ph&aacute;p c&oacute; t&iacute;nh ph&ograve;ng thủ n&agrave;y c&oacute; thể l&agrave;m tổn hại quan hệ thương mại Trung Quốc - EU trị gi&aacute; gần 750 tỷ USD v&agrave;o năm ngo&aacute;i.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/bpcivpwi/2020_05_08/tong-thong-phap-4480629_852020.jpg\"><img alt=\"Quan hệ Châu Âu – Trung Quốc đổi chiều, virus Corona mới đã làm thay đổi mọi thứ - ảnh 4\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/bpcivpwi/2020_05_08/tong-thong-phap-4480629_852020.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Tổng thống Ph&aacute;p Macron: &ldquo;Đừng ng&acirc;y thơ nghĩ rằng Trung Quốc đ&atilde; kiểm so&aacute;t dịch tốt hơn. Trung Quốc r&otilde; r&agrave;ng l&agrave;m nhiều điều người ta chưa biết&rdquo; (Ảnh: AP).</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Đ&agrave;i&nbsp;<em>RFI&nbsp;</em>chỉ ra rằng những thay đổi như vậy đ&atilde; xảy ra trong mối quan hệ giữa EU v&agrave; Bắc Kinh. Sự l&acirc;y lan của virus Corona mới đ&atilde; phơi b&agrave;y nhiều điều, v&agrave; n&oacute; c&oacute; t&aacute;c dụng như một m&aacute;y gia tốc. Thực tế, trong suốt một thời gian, EU đ&atilde; chủ trương &aacute;p dụng một chiến lược thực tế hơn với Trung Quốc. Goldmont, một chuy&ecirc;n gia chiến lược Đ&ocirc;ng &Aacute; v&agrave; Trung Quốc của Ph&aacute;p, n&oacute;i: &ldquo;Ảo tưởng về c&oacute; thể c&oacute; sự đồng thuận v&agrave; tin tưởng lẫn nhau giữa ch&acirc;u &Acirc;u v&agrave; Trung Quốc khoảng 12, 13 năm trước đ&atilde; biến mất&rdquo;.</p>\r\n\r\n<p>Tin n&oacute;i rằng, điều khiến EU lo lắng hơn l&agrave; th&aacute;i độ h&agrave;nh xử của c&aacute;c nh&agrave; ngoại giao Trung Quốc, những người nu&ocirc;i dưỡng ảnh hưởng tại Li&ecirc;n hợp quốc, lấp đầy khoảng trống m&agrave; Mỹ để lại; hoặc trực tiếp hoặc gi&aacute;n tiếp gi&agrave;nh lấy vị tr&iacute; quan trọng hơn. Chuyện ở Tổ chức Lương n&ocirc;ng thế giới (FAO) l&agrave; v&iacute; dụ r&otilde; r&agrave;ng.</p>\r\n\r\n<p>Trong một b&agrave;i ph&aacute;t biểu về an ninh quốc ph&ograve;ng quốc gia hồi th&aacute;ng 2 năm nay, Tổng thống Ph&aacute;p Macron coi Trung Quốc l&agrave; một &ldquo;chủ đề chiến lược&rdquo;. &Ocirc;ng coi việc th&uacute;c đẩy t&aacute;i tổ chức ch&acirc;u &Acirc;u l&agrave; nhiệm vụ ưu ti&ecirc;n ngoại giao v&agrave; rất bất b&igrave;nh về chủ trương chia rẽ ch&acirc;u &Acirc;u của Bắc Kinh th&ocirc;ng qua s&aacute;ng kiến &ldquo;hợp t&aacute;c 17 cộng 1&rdquo;. &nbsp;</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://viettimes.vn/quan-he-chau-au-trung-quoc-doi-chieu-virus-corona-moi-da-lam-thay-doi-moi-thu-389103.html\">https://viettimes.vn/quan-he-chau-au-trung-quoc-doi-chieu-virus-corona-moi-da-lam-thay-doi-moi-thu-389103.html</a></p>', 'upload/news\\1588909037.png', 1, 0, 5, 3, 'quan-he-chau-au-trung-quoc-doi-chieu-virus-corona-moi-da-lam-thay-doi-moi-thu', NULL, '2020-09-07 20:37:17', '2020-09-07 20:37:40'),
(13, '', 9, '<p>Một gi&aacute;o sư người Mỹ, gốc Hoa đang nghi&ecirc;n cứu về Covid-19 đ&atilde; bị bắn chết tại căn hộ của m&igrave;nh v&agrave;o cuối tuần vừa rồi.</p>', '<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://image.viettimes.vn/w800/Uploaded/2020/cqjwqqrnd/2020_05_07/giao-su-bing-liu-bi-ban-tai-nha-rieng-1727947_752020.jpg\"><img alt=\"Giáo sư Bing Liu bị bắn tại nhà riêng hôm 2/5. Ảnh: CNN\" src=\"https://image.viettimes.vn/w666/Uploaded/2020/cqjwqqrnd/2020_05_07/giao-su-bing-liu-bi-ban-tai-nha-rieng-1727947_752020.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Gi&aacute;o sư Bing Liu bị bắn tại nh&agrave; ri&ecirc;ng h&ocirc;m 2/5. Ảnh: CNN</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Gi&aacute;o sư trợ l&yacute; nghi&ecirc;n cứu Bing Liu, được t&igrave;m thấy chết tại nh&agrave; ri&ecirc;ng h&ocirc;m 2/5 với những vết bắn v&agrave;o đầu, cổ, th&acirc;n tr&ecirc;n, theo Sở cảnh s&aacute;t Ross. C&aacute;c nh&agrave; điều tra tin rằng một người đ&agrave;n &ocirc;ng được t&igrave;m thấy chết trong xe hơi ch&iacute;nh l&agrave; thủ phạm đ&atilde; bắn &ocirc;ng Liu sau đ&oacute; tự s&aacute;t. Theo cảnh s&aacute;t, hai người đ&agrave;n &ocirc;ng n&agrave;y biết nhau nhưng&nbsp;<em>&quot;kh&ocirc;ng c&oacute; dấu hiệu n&agrave;o cho thấy &ocirc;ng Liu bị s&aacute;t hại bởi &ocirc;ng l&agrave; người Trung Quốc&quot;.</em></p>\r\n\r\n<p>Trường ĐH Pittsburgh, nơi nạn nh&acirc;n l&agrave;m việc đ&atilde; b&agrave;y tỏ nỗi buồn s&acirc;u sắc trước thảm kịch của gi&aacute;o sư Bing Liu, một nh&agrave; nghi&ecirc;n cứu c&oacute; nhiều c&ocirc;ng tr&igrave;nh, một đồng nghiệp đ&aacute;ng ngưỡng mộ. Nh&agrave; trường cũng gửi lời chia buồn tới gia đ&igrave;nh, bạn b&egrave; v&agrave; đồng nghiệp của &ocirc;ng Liu trong qu&atilde;ng thời gian kh&oacute; khăn n&agrave;y.</p>\r\n\r\n<p>&quot;<em>Bing sắp c&oacute; ph&aacute;t hiện &yacute; nghĩa về cơ chế tế b&agrave;o g&acirc;y nhiễm SARS-CoV-2 v&agrave; cơ sở tế b&agrave;o của những biến chứng sau đ&oacute;</em>&quot;, c&aacute;c đồng nghiệp của &ocirc;ng tuy&ecirc;n bố.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://viettimes.vn/nha-nghien-cuu-covid19-goc-hoa-tai-my-tu-vong-bat-thuong-389028.html\">https://viettimes.vn/nha-nghien-cuu-covid19-goc-hoa-tai-my-tu-vong-bat-thuong-389028.html</a></p>', 'upload/news\\1588909202.jpeg', 1, 0, 1, 3, 'nha-nghien-cuu-covid-19-goc-hoa-tai-my-tu-vong-bat-thuong', NULL, '2020-09-07 20:40:02', '2020-09-07 21:12:14'),
(14, 'Thủ tướng: 25 năm nữa là đủ để xuất hiện những đế chế kinh doanh khổng lồ mang tên Việt Nam', 1, '<p>Hiện Việt Nam đ&atilde; c&oacute; những tập đo&agrave;n lớn vươn tầm cạnh tranh quốc tế như Viettel, TH, FPT, Vingroup... Kinh tế nh&agrave; nước trong thời gian dịch cũng ph&aacute;t huy vai tr&ograve; tốt như giảm gi&aacute; điện, nước, viễn th&ocirc;ng... Tuy nhi&ecirc;n, Việt Nam vẫn chưa c&oacute; doanh nghiệp n&agrave;o lọt v&agrave;o top 500 doanh nghiệp lớn nhất thế giới&quot;, Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c n&oacute;i tại Hội nghị s&aacute;ng 9/5.</p>', '<p><img alt=\"Thủ tướng: 25 năm nữa là đủ để xuất hiện những đế chế kinh doanh khổng lồ mang tên Việt Nam\" src=\"https://cafefcdn.com/thumb_w/650/2020/5/9/nqh01939-15889909021481761226505-crop-15889909088252020492189.JPG\" /></p>\r\n\r\n<h2>&quot;Hiện Việt Nam đ&atilde; c&oacute; những tập đo&agrave;n lớn vươn tầm cạnh tranh quốc tế như Viettel, TH, FPT, Vingroup... Kinh tế nh&agrave; nước trong thời gian dịch cũng ph&aacute;t huy vai tr&ograve; tốt như giảm gi&aacute; điện, nước, viễn th&ocirc;ng... Tuy nhi&ecirc;n, Việt Nam vẫn chưa c&oacute; doanh nghiệp n&agrave;o lọt v&agrave;o top 500 doanh nghiệp lớn nhất thế giới&quot;, Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c n&oacute;i tại Hội nghị s&aacute;ng 9/5.</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>S&aacute;ng nay, Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c chủ tr&igrave; Hội nghị đối thoại với doanh nghiệp để t&igrave;m ra c&aacute;c biện ph&aacute;p vượt qua th&aacute;ch thức, đ&oacute;n thời cơ, phục hồi nền kinh tế hậu đại dịch Covid-19.</p>\r\n\r\n<p>Ph&aacute;t biểu khai mạc, Thủ tướng cho biết thế giới n&oacute;i chung v&agrave; Việt Nam n&oacute;i ri&ecirc;ng đang trải qua bước ngoặt do đại dịch Covid-19 g&acirc;y ra. Theo &ocirc;ng, hiếm c&oacute; sự kiện y tế n&agrave;o g&acirc;y ảnh hưởng to&agrave;n cầu, vượt xa khủng hoảng do dịch SARS, hay H1N1, c&oacute; thể s&aacute;nh ngang với Đại dịch hạch, dịch C&uacute;m T&acirc;y Ban Nha... trong qu&aacute; khứ.</p>\r\n\r\n<p>&quot;Covid-19 kh&ocirc;ng chỉ ảnh hưởng đến sức khoẻ một cộng đồng m&agrave; ảnh hưởng đến h&agrave;ng tỷ người tr&ecirc;n h&agrave;nh tinh n&agrave;y&quot;, &ocirc;ng nhấn mạnh.</p>\r\n\r\n<p>Khủng hoảng y tế đ&atilde; k&eacute;o th&agrave;nh khủng hoảng kinh tế, lan rộng tr&ecirc;n nhiều mặt m&agrave; kh&ocirc;ng một nền kinh tế n&agrave;o, với quy m&ocirc; n&agrave;o c&oacute; thể tr&aacute;nh khỏi ảnh hưởng.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, Thủ tướng nhấn mạnh: Như bao biến cố kh&aacute;c, lo&agrave;i người sẽ vượt qua. &Ocirc;ng nhắc đến thuyết tiến ho&aacute; khi n&oacute;i rằng kh&ocirc;ng phải lo&agrave;i mạnh, th&ocirc;ng minh nhất sẽ sống s&oacute;t, thay v&agrave;o đ&oacute;, l&agrave; lo&agrave;i th&iacute;ch nghi tốt nhất. Những doanh nghiệp, ở quy m&ocirc; n&agrave;o, d&ugrave; l&agrave; nhỏ nhất tham dự ng&agrave;y h&ocirc;m nay, l&agrave; những doanh nghiệp th&iacute;ch nghi tốt nhất. D&ugrave; vậy, &ocirc;ng cũng lấy l&agrave;m tiếc khi c&oacute; một bộ phận doanh nghiệp đ&atilde; phải rời bỏ thị trường.</p>\r\n\r\n<p>N&oacute;i&nbsp; về kinh tế Việt Nam trong thời gian qua, Thủ tướng cho biết mức 3,82% đạt được trong qu&yacute; I d&ugrave; thấp nhất trong 10 năm trở lại đ&acirc;y nhưng vẫn khả quan nếu so với t&igrave;nh trạng suy tho&aacute;i chung của c&aacute;c quốc gia trong khu vực v&agrave; tr&ecirc;n thế giới.</p>\r\n\r\n<p>C&aacute;c dự b&aacute;o của World Bank, IMF cũng c&oacute; những đ&aacute;nh gi&aacute; khả quan cho nền kinh tế 96 triệu d&acirc;n.</p>\r\n\r\n<p>&quot;Việt Nam theo đuổi chiến lược mục ti&ecirc;u k&eacute;p, một mặt ph&ograve;ng chống dịch, mặt kh&aacute;c duy tr&igrave; kinh tế để ngọn lửa tăng trưởng phải ch&aacute;y v&agrave; b&ugrave;ng l&ecirc;n trở lại khi dịch bệnh được kiểm so&aacute;t tốt&quot;, &ocirc;ng n&oacute;i.</p>\r\n\r\n<p>Th&agrave;nh c&ocirc;ng của Việt Nam tr&ecirc;n 2 mặt trận kinh tế, y tế đến thời điểm n&agrave;y theo Thủ tướng l&agrave; nhờ t&iacute;nh đo&agrave;n kết, tu&acirc;n thủ của người d&acirc;n. &quot;Nếu mỗi người chấp nhận hi sinh một phần lợi &iacute;ch th&igrave; tất cả c&oacute; thể được lợi&quot;, &ocirc;ng n&oacute;i. &Ocirc;ng cũng nhấn mạnh tuy Việt Nam kh&ocirc;ng được chủ quan nhưng cũng đừng lo lắng v&igrave; đ&atilde; kiểm so&aacute;t tốt, cơ bản đẩy l&ugrave;i dịch.</p>\r\n\r\n<p>&Ocirc;ng cũng khẳng định Việt Nam c&oacute; t&iacute;ch luỹ nguồn lực trong những năm gần đ&acirc;y. Việt Nam kh&ocirc;ng qu&aacute; phụ thuộc v&agrave;o thị trường thế giới, năng lực nội sinh của kinh tế, doanh nghiệp Việt Nam v&ocirc; c&ugrave;ng lớn.</p>\r\n\r\n<p>Thủ tướng nhấn mạnh: Năm 2020 tăng trưởng Việt Nam phải đạt tr&ecirc;n 5%, lạm ph&aacute;t kiểm so&aacute;t dưới 4%, chứ kh&ocirc;ng như dự b&aacute;o của IMF với GDP chỉ hơn 2%.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://cafef.vn/thu-tuong-25-nam-nua-la-du-de-xuat-hien-nhung-de-che-kinh-doanh-khong-lo-mang-ten-viet-nam-20200509092213122.chn\">https://cafef.vn/thu-tuong-25-nam-nua-la-du-de-xuat-hien-nhung-de-che-kinh-doanh-khong-lo-mang-ten-viet-nam-20200509092213122.chn</a></p>', 'upload/news\\1588910291.jpeg', 1, 0, 8, 2, 'thu-tuong-25-nam-nua-la-du-de-xuat-hien-nhung-de-che-kinh-doanh-khong-lo-mang-ten-viet-nam', NULL, '2020-09-07 20:58:11', '2020-09-07 20:58:11'),
(15, 'Phố Wall tiếp tục khởi sắc bất chấp tỷ lệ thất nghiệp cao nhất kể từ Thế chiến II, Dow Jones bứt phá gần 500 điểm', 4, '<p>Kết th&uacute;c phi&ecirc;n 8/5, chứng kho&aacute;n Mỹ đồng loạt tăng điểm bất chấp th&ocirc;ng tin ti&ecirc;u cực về số liệu việc l&agrave;m, khi nh&agrave; đầu tư đặt kỳ lớn rằng diễn biến v&agrave; t&aacute;c động tồi tệ nhất của dịch Covid-19 đ&atilde; đi qua.</p>', '<p><img alt=\"Phố Wall tiếp tục khởi sắc bất chấp tỷ lệ thất nghiệp cao nhất kể từ Thế chiến II, Dow Jones bứt phá gần 500 điểm\" src=\"https://cafefcdn.com/thumb_w/650/2020/5/9/screen-shot-2020-05-09-at-62438-am-15889802925531503885318-crop-1588980301866395783847.png\" /></p>\r\n\r\n<h2>Kết th&uacute;c phi&ecirc;n 8/5, chứng kho&aacute;n Mỹ đồng loạt tăng điểm bất chấp th&ocirc;ng tin ti&ecirc;u cực về số liệu việc l&agrave;m, khi nh&agrave; đầu tư đặt kỳ lớn rằng diễn biến v&agrave; t&aacute;c động tồi tệ nhất của dịch Covid-19 đ&atilde; đi qua.</h2>\r\n\r\n<p>Cụ thể, chỉ số trung b&igrave;nh c&ocirc;ng nghiệp Dow Jones đ&atilde; tăng 455,43 điểm, tương đương 1,9%, l&ecirc;n 24.3331,32 điểm, đ&oacute;ng cửa gần vớimức cao nhất trong phi&ecirc;n. S&amp;P 500 tăng 1,6%, tương đương 48,61 điểm, l&ecirc;n 2.929,80 điểm, trong khi Nasdaq Composite tăng 1,5%, tương đương 141,66 điểm, l&ecirc;n 9.121,32 điểm. Cả 3 chỉ số ch&iacute;nh đều ghi nhận mức tăng đầu ti&ecirc;n trong 3 tuần. Ở tuần n&agrave;y, Dow Jones v&agrave; S&amp;P 500 lần lượt tăng 2,5% v&agrave; 3,5%. Trong khi đ&oacute;, Nasdaq tăng 6%.&nbsp;</p>\r\n\r\n<p>Mới đ&acirc;y, Bộ Lao động Mỹ cho biết số lượng việc l&agrave;m việc bị mất trong th&aacute;ng trước đạt mức cao kỷ lục 20,5 triệu, trong khi tỷ lệ thất nghiệp tăng vọt từ 4,4% l&ecirc;n 14,7%. Đ&acirc;y l&agrave; những con số lớn kỷ lục kể từ sau Thế chiến II. Trong khi đ&oacute;, c&aacute;c nh&agrave; kinh tế tham gia cuộc thăm d&ograve; của Dow Jones dự kiến sẽ c&oacute; 21,5 triệu việc l&agrave;m bị mất v&agrave;&nbsp;tỷ lệ thất nghiệp l&agrave; 16%.</p>\r\n\r\n<p><a href=\"https://cafefcdn.com/2020/5/9/106528273-158894122631420200508joblossnonfarmpayrollsapril2020-1588980568185727208615.png\"><img alt=\"Phố Wall tiếp tục khởi sắc bất chấp tỷ lệ thất nghiệp cao nhất kể từ Thế chiến II, Dow Jones bứt phá gần 500 điểm  - Ảnh 1.\" src=\"https://cafefcdn.com/thumb_w/640/2020/5/9/106528273-158894122631420200508joblossnonfarmpayrollsapril2020-1588980568185727208615.png\" /></a></p>\r\n\r\n<p>Phố Wall ghi nhận đ&agrave; tăng mạnh từ mức thấp trong th&aacute;ng 3 khi c&aacute;c nh&agrave; đầu tư đặt kỳ vọng lớn v&agrave;o việc mở cửa lại nền kinh tế v&agrave; nhiều c&ocirc;ng ty c&ocirc;ng nghệ c&oacute; doanh thu vững chắc, ngay cả khi ngừng hoạt động. Mới đ&acirc;y, Apple cho biết họ sẽ mở lại c&aacute;c cửa h&agrave;ng bắt đầu v&agrave;o tuần tới, sẽ thực hiện kiểm tra th&acirc;n nhiệt v&agrave; giới hạn số lượng kh&aacute;ch h&agrave;ng đến c&ugrave;ng một l&uacute;c.</p>\r\n\r\n<p>S&amp;P 500 đ&atilde; hồi phục hơn 30% từ mức thấp khi thị trường biến động v&igrave; dịch bệnh v&agrave; chỉ c&ograve;n thấp hơn 13,6% so với mức cao kỷ lục. Nasdaq Composite đ&atilde; tăng hơn 35% so với mức thấp v&agrave; hiện tăng 1,6% trong năm 2020. Diễn biến t&iacute;ch cực từ cổ phiếu Facebook, Alphabet, Amazon v&agrave; Apple đ&atilde; gi&uacute;p đưa chỉ số n&agrave;y trở lại &quot;l&atilde;nh thổ&quot; t&iacute;ch cực trong năm 2020. C&oacute; thời điểm, Nasdaq đ&atilde; giảm hơn 25% từ đầu năm đến nay.</p>\r\n\r\n<p>Ngo&agrave;i ra, t&acirc;m l&yacute; nh&agrave; đầu tư cũng được th&uacute;c đẩy sau khi&nbsp;T&agrave;i ch&iacute;nh Steven Mnuchin v&agrave; Đại diện Thương mại Hoa Kỳ Robert Lighthizer c&oacute; cuộc thảo luận với&nbsp;Ph&oacute; Thủ tướng Trung Quốc Lưu Hạc v&agrave;o h&ocirc;m 7/5. Theo đ&oacute;, cả 2 b&ecirc;n đ&atilde; đồng &yacute; rằng, bất chấp t&igrave;nh trạng khẩn cấp về vấn đề dịch bệnh tr&ecirc;n to&agrave;n cầu hiện nay, Mỹ v&agrave; Trung Quốc đều dự kiến sẽ thực hiện c&aacute;c điều khoản đ&atilde; thoả thuận đ&uacute;ng thời gian.&nbsp;</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://cafef.vn/pho-wall-tiep-tuc-khoi-sac-bat-chap-ty-le-that-nghiep-cao-nhat-ke-tu-the-chien-ii-dow-jones-but-pha-gan-500-diem-20200509063123372.chn\">https://cafef.vn/pho-wall-tiep-tuc-khoi-sac-bat-chap-ty-le-that-nghiep-cao-nhat-ke-tu-the-chien-ii-dow-jones-but-pha-gan-500-diem-20200509063123372.chn</a></p>', 'upload/news\\1588910543.png', 1, 0, 8, 2, 'pho-wall-tiep-tuc-khoi-sac-bat-chap-ty-le-that-nghiep-cao-nhat-ke-tu-the-chien-ii-dow-jones-but-pha-gan-500-diem', NULL, '2020-09-07 21:02:23', '2020-09-07 21:03:08');
INSERT INTO `news` (`id`, `news_title`, `author_id`, `news_summary`, `news_content`, `news_image`, `news_featured`, `news_view`, `tag_id`, `category_id`, `news_slug`, `deleted_at`, `created_at`, `updated_at`) VALUES
(16, 'Nhà nghiên cứu Nguyễn Trần Bạt: Chúng ta sẽ thấy sự sáng tạo của nhân loại vĩ đại như thế nào khi con người tìm cách thoát ra khỏi sự chết chóc!', 4, '<h2>&ldquo;T&ocirc;i kh&ocirc;ng muốn l&agrave;m quần ch&uacute;ng đơn giản, chỉ biết hưởng ứng v&agrave; vỗ tay trước th&agrave;nh t&iacute;ch của Đảng v&agrave; Ch&iacute;nh phủ trong ph&ograve;ng chống dịch bệnh&rdquo;, nh&agrave; nghi&ecirc;n cứu, Chủ tịch InvestConsult Group Nguyễn Trần Bạt n&oacute;i với Tr&iacute; Thức Trẻ. &Ocirc;ng nhấn mạnh: Việt Nam mới chỉ giải quyết c&aacute;c vấn đề x&atilde; hội do Covid-19 g&acirc;y ra, c&ograve;n nền kinh tế vẫn chưa thực sự c&oacute; được &ldquo;đơn thuốc&rdquo; hợp l&yacute;.</h2>', '<p><a href=\"https://cafefcdn.com/2020/5/6/w08-1588762410886900763416.png\"><img alt=\"Nhà nghiên cứu Nguyễn Trần Bạt: Chúng ta sẽ thấy sự sáng tạo của nhân loại vĩ đại như thế nào khi con người tìm cách thoát ra khỏi sự chết chóc! - Ảnh 1.\" src=\"https://cafefcdn.com/thumb_w/640/2020/5/6/w08-1588762410886900763416.png\" /></a></p>\r\n\r\n<p><strong><em>-Nh&igrave;n v&agrave;o diễn biến đại dịch Covid-19 v&agrave; qu&aacute; tr&igrave;nh chống chọi với dịch bệnh của Việt Nam trong ba th&aacute;ng vừa qua, &ocirc;ng c&oacute; ấn tượng g&igrave;?</em></strong></p>\r\n\r\n<p>Ấn tượng của t&ocirc;i l&agrave; ch&uacute;ng ta rất th&agrave;nh c&ocirc;ng trong việc tổ chức hoạt động chống dịch của cả nh&agrave; nước lẫn x&atilde; hội. Sự ph&acirc;n c&ocirc;ng lớp lang trong qu&aacute; tr&igrave;nh chống dịch l&agrave; ở cả ba mặt ch&iacute;nh trị, x&atilde; hội v&agrave; y tế rất hiệu quả.</p>\r\n\r\n<p>Ch&uacute;ng ta đ&atilde; thể hiện được sự uyển chuyển của hệ thống ch&iacute;nh trị trong việc giải quyết vấn đề chống dịch.</p>\r\n\r\n<p>Về mặt văn h&oacute;a th&igrave; khẩu hiệu &quot;chống dịch như chống giặc&quot; c&oacute; t&aacute;c dụng gợi lại bản năng h&agrave;nh động của một x&atilde; hội quen với t&igrave;nh trạng thời chiến. Đ&acirc;y l&agrave; một trong những nghệ thuật để tạo ra phản xạ x&atilde; hội cần thiết cho việc chống dịch. T&ocirc;i kh&ocirc;ng nghi ngờ g&igrave; về sự th&agrave;nh c&ocirc;ng của nước ta trong giai đoạn chống dịch vừa rồi v&agrave; điều đ&oacute; tạo ra h&igrave;nh ảnh tốt của Việt Nam trong con mắt quốc tế.</p>\r\n\r\n<p><strong>-Như &ocirc;ng n&oacute;i, th&agrave;nh c&ocirc;ng trong chống dịch của Việt Nam cũng được truyền th&ocirc;ng quốc tế ghi nhận v&agrave; li&ecirc;n tục nhắc đến như một điển h&igrave;nh. Liệu c&oacute; cơ hội n&agrave;o cho đất nước từ điều n&agrave;y?</strong></p>\r\n\r\n<p>T&ocirc;i l&agrave; một trong những người rất tự h&agrave;o về c&aacute;c th&agrave;nh t&iacute;ch chống dịch của ch&uacute;ng ta, nhưng sự s&aacute;ng suốt của c&aacute; nh&acirc;n t&ocirc;i đ&atilde; &eacute;p t&ocirc;i phải chấm dứt niềm tự h&agrave;o ấy sớm để c&oacute; được sự tỉnh t&aacute;o của một nh&agrave; khoa học. T&ocirc;i kh&ocirc;ng muốn l&agrave;m quần ch&uacute;ng đơn giản, chỉ biết hưởng ứng v&agrave; vỗ tay trước th&agrave;nh t&iacute;ch của Đảng v&agrave; Ch&iacute;nh phủ trong ph&ograve;ng chống dịch bệnh.</p>\r\n\r\n<p><strong>-Ch&iacute;nh x&aacute;c điều &ocirc;ng muốn n&oacute;i đến l&agrave; g&igrave;?</strong></p>\r\n\r\n<p>Vấn đề khắc phục hậu quả kinh tế kh&ocirc;ng giống như b&aacute;o ch&iacute; đang đề cập hiện nay. Nếu x&eacute;t ri&ecirc;ng về mặt kinh tế th&igrave; đ&atilde; bắt đầu lộ r&otilde; nỗi lo của Ch&iacute;nh phủ, của c&aacute;c tổ chức nghề nghiệp về hậu quả kinh tế do dịch bệnh g&acirc;y ra. Nhưng đấy l&agrave; nỗi lo chứ chưa phải l&agrave; sự ph&acirc;n t&iacute;ch khoa học v&agrave; c&agrave;ng kh&ocirc;ng phải l&agrave; giải ph&aacute;p cứu trợ kinh tế.</p>\r\n\r\n<p><a href=\"https://cafefcdn.com/2020/5/6/w13-1588762493330474016926.png\"><img alt=\"Nhà nghiên cứu Nguyễn Trần Bạt: Chúng ta sẽ thấy sự sáng tạo của nhân loại vĩ đại như thế nào khi con người tìm cách thoát ra khỏi sự chết chóc! - Ảnh 2.\" src=\"https://cafefcdn.com/thumb_w/640/2020/5/6/w13-1588762493330474016926.png\" /></a></p>\r\n\r\n<p><em><strong>-Nhưng Ch&iacute;nh phủ đ&atilde; đưa ra g&oacute;i t&iacute;n dụng l&ecirc;n đến 300.000 tỷ v&agrave; g&oacute;i t&agrave;i kh&oacute;a 180.000 tỷ để hỗ trợ doanh nghiệp.</strong></em></p>\r\n\r\n<p>T&ocirc;i vẫn cho rằng ngay cả những g&oacute;i như vậy cũng mới chỉ l&agrave; biểu hiện của nỗi lo về hậu quả kinh tế.</p>\r\n\r\n<p>Ch&uacute;ng ta c&oacute; một nền kinh tế được cho l&agrave; c&oacute; độ mở cao, m&agrave; người Việt kh&aacute; ch&acirc;n thật trong việc tham gia to&agrave;n cầu h&oacute;a v&agrave; nhiều khi sửa m&igrave;nh một c&aacute;ch th&aacute;i qu&aacute; trước c&aacute;c đ&ograve;i hỏi của to&agrave;n cầu h&oacute;a.</p>\r\n\r\n<p>Tr&ecirc;n thực tế, hiệu ứng to&agrave;n cầu h&oacute;a của kinh tế Việt Nam lớn hơn năng lực tưởng tượng của c&aacute;c lực lượng trong x&atilde; hội, kể cả giới l&atilde;nh đạo, cho n&ecirc;n ng&agrave;y c&agrave;ng xuất hiện nhiều nỗi lo.</p>\r\n\r\n<p>Nhưng đ&oacute; l&agrave; những nỗi lo mang t&iacute;nh chất hiện tượng, c&ograve;n ph&acirc;n t&iacute;ch c&aacute;c hậu quả kinh tế của Covid-19 một c&aacute;ch nghi&ecirc;m t&uacute;c th&igrave; t&ocirc;i chưa thấy tiếng n&oacute;i của cơ sở khoa học n&agrave;o về chuyện ấy.</p>\r\n\r\n<p>Ch&uacute;ng ta c&oacute; Viện Kinh tế thuộc Viện H&agrave;n l&acirc;m, c&oacute; Viện nghi&ecirc;n cứu quản l&yacute; kinh tế thuộc Bộ KHĐT, cả Ban Kinh tế Trung ương của Ban chấp h&agrave;nh trung ương Đảng, chưa n&oacute;i đến c&aacute;c viện nghi&ecirc;n cứu kh&aacute;c&hellip; c&aacute;c cơ sở nghi&ecirc;n cứu khoa học của ch&uacute;ng ta chưa c&oacute; chương tr&igrave;nh v&agrave; chưa c&oacute; ph&acirc;n t&iacute;ch về hậu quả kinh tế sau dịch bệnh. X&atilde; hội rất cần c&aacute;c tiếng n&oacute;i chuy&ecirc;n nghiệp như vậy.</p>\r\n\r\n<p>Khi c&aacute;c cơ sở nghi&ecirc;n cứu đưa ra được c&aacute;c đ&aacute;nh gi&aacute; c&oacute; t&iacute;nh chất khoa học th&igrave; ch&uacute;ng ta mới biết thế giới sẽ dịch chuyển, sẽ t&aacute;i cấu tr&uacute;c, sẽ biến h&igrave;nh thế n&agrave;o sau dịch bệnh. Biết được những điều đ&oacute; th&igrave; ch&uacute;ng ta mới c&oacute; được đối s&aacute;ch ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p>C&ograve;n những g&oacute;i vừa nhắc th&igrave; t&ocirc;i nghĩ n&oacute; chỉ l&agrave; h&igrave;nh thức, kh&ocirc;ng phải l&agrave; những phương thuốc c&oacute; thể chữa những căn bệnh được chẩn đo&aacute;n cụ thể. Chưa c&oacute; b&aacute;c sĩ kinh tế n&agrave;o c&oacute; tiếng n&oacute;i đầy đủ về căn bệnh kinh tế Việt Nam, chưa c&oacute; b&aacute;c sĩ kinh tế n&agrave;o n&oacute;i một tiếng n&oacute;i chuy&ecirc;n nghiệp về nền kinh tế của thế giới v&agrave; tr&ecirc;n cơ sở đ&oacute; ph&aacute;n đo&aacute;n về nền kinh tế của ch&uacute;ng ta.</p>\r\n\r\n<p><a href=\"https://cafefcdn.com/2020/5/6/w17-15887625153781739236212.png\"><img alt=\"Nhà nghiên cứu Nguyễn Trần Bạt: Chúng ta sẽ thấy sự sáng tạo của nhân loại vĩ đại như thế nào khi con người tìm cách thoát ra khỏi sự chết chóc! - Ảnh 3.\" src=\"https://cafefcdn.com/thumb_w/640/2020/5/6/w17-15887625153781739236212.png\" /></a></p>\r\n\r\n<p><em><strong>-Đại dịch Covid-19 sẽ để lại cho nền kinh tế Việt Nam những vết sẹo g&igrave;?</strong></em></p>\r\n\r\n<p>Chưa c&oacute; vết sẹo. Ch&uacute;ng ta mới đang giải phẫu để xem x&eacute;t đến việc t&aacute;i cấu tr&uacute;c &quot;cơ thể&quot; kinh tế của m&igrave;nh. Vết sẹo ch&iacute;nh l&agrave; vết của t&aacute;i cấu tr&uacute;c, ch&uacute;ng ta chưa t&aacute;i cấu tr&uacute;c n&ecirc;n chưa c&oacute; sẹo.</p>\r\n\r\n<p><em><strong>-Như vậy đ&acirc;y chỉ l&agrave; những vết thương?</strong></em></p>\r\n\r\n<p>Tất cả c&aacute;c thị trường m&agrave; Việt Nam c&oacute; để thể hiện năng lực to&agrave;n cầu h&oacute;a của m&igrave;nh đang tan hoang, đang đối mặt với nguy cơ suy sụp. C&aacute;c thị trường cấu tạo ra nền kinh tế, cấu tạo ra đời sống của ch&uacute;ng ta, l&agrave;m định hướng cho sự ph&aacute;t triển lực lượng lao động, ph&aacute;t triển khoa học c&ocirc;ng nghệ của ch&uacute;ng ta đều gặp kh&oacute; khăn nghi&ecirc;m trọng. Vậy th&igrave; đương nhi&ecirc;n thực tế ấy sẽ &eacute;p ch&uacute;ng ta phải thay đổi.</p>\r\n\r\n<p>Nhưng ch&uacute;ng ta thay đổi th&igrave; phải hiểu được sự biến dạng của c&aacute;c thị trường ấy, m&agrave; ch&uacute;ng ta chưa c&oacute; nghi&ecirc;n cứu n&agrave;o. Một v&agrave;i tiếng n&oacute;i cũng chỉ mới đề cập tới sự cản trở của chủ nghĩa d&acirc;n tộc về mặt ch&iacute;nh trị v&agrave; văn h&oacute;a m&agrave; vẫn chưa ph&acirc;n biệt được n&oacute; với chủ nghĩa d&acirc;n tộc về mặt kinh tế.</p>\r\n\r\n<p><strong>-Theo &ocirc;ng, c&oacute; những động lực n&agrave;o để nền kinh tế n&agrave;y c&oacute; thể vượt qua cơn khủng hoảng?</strong></p>\r\n\r\n<p>Mỗi một con người đều phải sống. Ham sống sợ chết ch&iacute;nh l&agrave; động lực cơ bản của to&agrave;n thế giới chứ kh&ocirc;ng phải của ri&ecirc;ng Việt Nam. Mỗi người tho&aacute;t ra khỏi c&aacute;i chết một kiểu. Rồi ch&uacute;ng ta sẽ thấy sự s&aacute;ng tạo của nh&acirc;n loại vĩ đại như thế n&agrave;o khi con người t&igrave;m c&aacute;ch tho&aacute;t ra khỏi sự chết ch&oacute;c!</p>\r\n\r\n<p><a href=\"https://cafefcdn.com/2020/5/6/w21-1588762578035918055974.png\"><img alt=\"Nhà nghiên cứu Nguyễn Trần Bạt: Chúng ta sẽ thấy sự sáng tạo của nhân loại vĩ đại như thế nào khi con người tìm cách thoát ra khỏi sự chết chóc! - Ảnh 4.\" src=\"https://cafefcdn.com/thumb_w/640/2020/5/6/w21-1588762578035918055974.png\" /></a></p>\r\n\r\n<p><strong><em>-Nghĩa l&agrave; sự phục hồi sẽ phụ thuộc v&agrave;o nội lực v&agrave;o sự ham sống của từng doanh nghiệp, từng c&aacute; thể?</em></strong></p>\r\n\r\n<p>Những yếu tố đ&oacute; cộng với c&aacute;c kinh nghiệm x&atilde; hội sẽ tạo ra bước nhảy. Bước nhảy ấy ch&iacute;nh l&agrave; t&aacute;i cấu tr&uacute;c v&agrave; t&aacute;i cấu tr&uacute;c ch&iacute;nh l&agrave; con đường tạo ra c&aacute;c &quot;vết sẹo&quot; m&agrave; bạn n&oacute;i.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://cafef.vn/nha-nghien-cuu-nguyen-tran-bat-chung-ta-se-thay-su-sang-tao-cua-nhan-loai-vi-dai-nhu-the-nao-khi-con-nguoi-tim-cach-thoat-ra-khoi-su-chet-choc-20200506180344701.chn\">https://cafef.vn/nha-nghien-cuu-nguyen-tran-bat-chung-ta-se-thay-su-sang-tao-cua-nhan-loai-vi-dai-nhu-the-nao-khi-con-nguoi-tim-cach-thoat-ra-khoi-su-chet-choc-20200506180344701.chn</a></p>', 'upload/news\\1588910794.png', 1, 0, 5, 3, 'nha-nghien-cuu-nguyen-tran-bat-chung-ta-se-thay-su-sang-tao-cua-nhan-loai-vi-dai-nhu-the-nao-khi-con-nguoi-tim-cach-thoat-ra-khoi-su-chet-choc', NULL, '2020-09-07 21:06:34', '2020-09-07 21:07:01'),
(17, 'Thị trường bất động sản ở nhiều quốc gia đang phục hồi trở lại, Trung Quốc diễn biến tích cực hậu Covid-19', 5, '<h2>Theo khảo s&aacute;t của Savills Việt Nam, thị trường Trung Quốc ghi nhận mức t&acirc;m l&yacute; kh&aacute; t&iacute;ch cực khi một số hoạt động bất động sản đ&atilde; quay trở lại. Trong khi H&agrave;n Quốc v&agrave; Việt Nam, t&acirc;m l&yacute; thị trường ở mức trung lập, chứ kh&ocirc;ng ti&ecirc;u cực.</h2>', '<p>Savills đ&atilde; tiến h&agrave;nh một khảo s&aacute;t về hiện trạng v&agrave; t&acirc;m l&yacute; c&aacute;c ng&agrave;nh kinh doanh tại 31 thị trường tr&ecirc;n to&agrave;n cầu kể từ th&aacute;ng 3/2020. Kết quả cho thấy,&nbsp;khoảng 29% c&aacute;c quốc gia tham gia hiện ở mức trung lập, 52% đ&aacute;nh gi&aacute; kh&aacute; ti&ecirc;u cực, v&agrave; chỉ 16% đ&aacute;nh gi&aacute; ti&ecirc;u cực. Đ&aacute;ng ch&uacute; &yacute;, thị trường Trung Quốc ghi nhận mức t&acirc;m l&yacute; kh&aacute; t&iacute;ch cực khi một số hoạt động bất động sản đ&atilde; quay trở lại. H&agrave;n Quốc v&agrave; Việt Nam, hai quốc gia với tỷ lệ nhiễm bệnh giảm nhanh, cũng đều t&acirc;m l&yacute; thị trường ở mức trung lập.</p>\r\n\r\n<p><strong>Nguồn cầu của kh&aacute;ch thu&ecirc;&nbsp;</strong><strong>đang ổn trở lại</strong></p>\r\n\r\n<p>Theo Savills, kh&aacute;ch thu&ecirc; đ&atilde; phải điều chỉnh đ&aacute;ng kể lối sống v&agrave; l&agrave;m việc kể từ khi đại dịch xuất hiện. Đ&aacute;ng ch&uacute; &yacute;, một số ng&agrave;nh kinh doanh bắt đầu c&oacute; c&aacute;c dấu hiệu ổn định khi dịch bệnh đạt đỉnh tại một số quốc gia.</p>\r\n\r\n<p>Nguồn cầu văn ph&ograve;ng giữ ổn định tại 42% quốc gia, trong khi 55% c&ograve;n lại c&oacute; nguồn cầu ở mức giảm trung b&igrave;nh. Hưởng lợi từ sự tăng trưởng t&iacute;ch cực của ng&agrave;nh b&aacute;n lẻ trực tuyến, nguồn cầu cho lĩnh vực hậu cần kh&ocirc;ng thay đổi hoặc tăng vừa phải tại hơn 79% c&aacute;c quốc gia. Chỉ duy nhất lĩnh vực b&aacute;n lẻ v&agrave; kh&aacute;ch sạn c&oacute; nhu cầu giảm, với mức giảm mạnh tại hơn một nửa số lượng quốc gia.</p>\r\n\r\n<p><a href=\"https://cafefcdn.com/2020/5/9/24-15889278825402127816214-15889863866051413398532.png\"><img alt=\"Thị trường bất động sản ở nhiều quốc gia đang phục hồi trở lại, Trung Quốc diễn biến tích cực hậu Covid-19 - Ảnh 1.\" src=\"https://cafefcdn.com/thumb_w/640/2020/5/9/24-15889278825402127816214-15889863866051413398532.png\" /></a></p>\r\n\r\n<p>Đặc biệt, tại thị trường Trung Quốc, hoạt động cho thu&ecirc; b&aacute;n lẻ v&agrave; văn ph&ograve;ng tăng ở mức vừa phải trong nửa đầu th&aacute;ng 4/2020. Tỉ&nbsp;lệ sử dụng đ&atilde; tăng l&ecirc;n khi nhiều văn ph&ograve;ng v&agrave; cửa h&agrave;ng bắt đầu mở cửa trở lại. Ng&agrave;nh kh&aacute;ch sạn cũng đang dần phục hồi, nhưng nguồn cầu vẫn c&ograve;n thấp hơn so với mức trước khi dịch bệnh bắt đầu.</p>\r\n\r\n<p>&nbsp;<strong>G</strong><strong>i&aacute; thu&ecirc; chưa giảm mạnh</strong></p>\r\n\r\n<p>Theo khảo s&aacute;t của Savills, gi&aacute;&nbsp;thu&ecirc; được ghi nhận kh&ocirc;ng thay đổi tr&ecirc;n 60% c&aacute;c ng&agrave;nh v&agrave; quốc gia, v&agrave; tăng ở 71% c&aacute;c quốc gia đối với lĩnh vực văn ph&ograve;ng. Dấu hiệu giảm r&otilde; rệt hơn ở lĩnh vực kh&aacute;ch sạn v&agrave; b&aacute;n lẻ.&nbsp;</p>\r\n\r\n<p>Một l&yacute; do khiến gi&aacute; thu&ecirc; chưa giảm mạnh l&agrave; do việc sử dụng rộng r&atilde;i c&aacute;c khoản hỗ trợ kh&aacute;ch thu&ecirc; trong khoảng thời gian n&agrave;y. Kh&aacute;ch thu&ecirc; b&aacute;n lẻ được hưởng lợi nhiều nhất, với 80% c&aacute;c quốc gia cho biết c&oacute; tồn tại h&igrave;nh thức hỗ trợ gi&aacute; thu&ecirc;. Việc ho&atilde;n trả ph&iacute; dịch vụ, thay đổi cơ cấu thanh to&aacute;n cũng kh&aacute; phổ biến v&agrave; đang được &aacute;p dụng tại 40% quốc gia.</p>\r\n\r\n<p>Ngay cả với thị trường văn ph&ograve;ng, nơi gi&aacute; thu&ecirc; phần lớn kh&ocirc;ng bị ảnh hưởng, vẫn c&oacute; đến 43% quốc gia ghi nhận việc sử dụng một số g&oacute;i hỗ trợ thu&ecirc; cho kh&aacute;ch thu&ecirc;. B&ecirc;n cạnh đ&oacute;, lĩnh vực hậu cần chịu &iacute;t ảnh hưởng nhất v&agrave; do đ&oacute;, c&oacute; rất &iacute;t khoản hỗ trợ được &aacute;p dụng.</p>\r\n\r\n<p><a href=\"https://cafefcdn.com/2020/5/8/25-1588928032246436152712.png\"><img alt=\"Thị trường bất động sản ở nhiều quốc gia đang phục hồi trở lại, Trung Quốc diễn biến tích cực hậu Covid-19 - Ảnh 2.\" src=\"https://cafefcdn.com/2020/5/8/25-1588928032246436152712.png\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ở hầu hết c&aacute;c nước, ch&iacute;nh s&aacute;ch phong tỏa mới được &aacute;p dụng hơn một th&aacute;ng, song song với c&aacute;c ch&iacute;nh s&aacute;ch v&agrave; biện ph&aacute;p hỗ trợ từ Ch&iacute;nh phủ v&agrave; chủ nh&agrave;, hiện tượng ph&aacute; vỡ c&aacute;c hợp đồng cho thu&ecirc; vẫn chưa được ghi nhận rộng r&atilde;i. Hiện, c&aacute;c quốc gia ghi nhận một số kh&aacute;ch thu&ecirc; v&agrave; c&aacute;c c&ocirc;ng ty nhỏ đ&atilde; chấm dứt sớm hợp đồng l&agrave; Trung Quốc, &Yacute;, Bồ Đ&agrave;o Nha, v&agrave; Thụy Sĩ. Sự can thiệp của ch&iacute;nh phủ l&agrave; một dấu ấn mạnh mẽ của cuộc khủng hoảng n&agrave;y. C&aacute;c g&oacute;i hỗ trợ hoặc can thiệp từ Ch&iacute;nh phủ như giảm thuế t&agrave;i sản hoặc tạm thời cấm hoạt động xuất ngoại, được ghi nhận tại 59% c&aacute;c quốc gia tham gia khảo s&aacute;t.</p>\r\n\r\n<p>Lĩnh vực b&aacute;n lẻ được hưởng lợi nhiều nhất, với một số g&oacute;i hỗ trợ được ghi nhận tại 75% c&aacute;c quốc gia. V&iacute; dụ, tỷ lệ kinh doanh tại Anh Quốc tạm ngưng ở mọi doanh nghiệp trong năm t&agrave;i ch&iacute;nh 2020-2021, v&agrave; tại Singapore, c&aacute;c nh&agrave; h&agrave;ng, cửa h&agrave;ng, kh&aacute;ch sạn, địa điểm du lịch sẽ kh&ocirc;ng phải trả thuế bất động sản năm 2020.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Giao dịch c&oacute; giảm nhưng kh&ocirc;ng c&ograve;n giảm mạnh</strong></p>\r\n\r\n<p>Khảo s&aacute;t đ&atilde; chỉ ra rằng khối lượng giao dịch hiện đang giảm nhưng kh&ocirc;ng c&ograve;n giảm mạnh. Trong nửa đầu th&aacute;ng 4/2020, 44% quốc gia ghi nhận kh&ocirc;ng thay đổi về khối lượng giao dịch. Đối với thị trường văn ph&ograve;ng, gần một nửa số quốc gia cho biết kh&ocirc;ng c&oacute; thay &nbsp;đổi về khối lượng giao dịch kể từ cuối th&aacute;ng 3/2020, khi 73% quốc gia c&oacute; khối lượng giảm trung b&igrave;nh hoặc giảm mạnh.</p>\r\n\r\n<p>Lĩnh vực b&aacute;n lẻ v&agrave; kh&aacute;ch sạn tiếp tục chứng kiến sự sụt giảm mạnh trong c&aacute;c loại h&igrave;nh giao dịch, với tương ứng 73% v&agrave; 68% quốc gia ghi nhận giảm, ảnh hưởng từ việc đ&oacute;ng cửa quốc gia v&agrave; hạn chế đi lại. Khảo s&aacute;t cũng chỉ ra rằng dịch bệnh kh&ocirc;ng l&agrave;m ảnh hưởng đến gi&aacute; trị vốn với 63% quốc gia cho biết gi&aacute; trị vốn của họ kh&ocirc;ng thay đổi mặc d&ugrave; với khối lượng giao dịch nhỏ.</p>\r\n\r\n<p>Lĩnh vực hậu cần v&agrave; y tế vẫn giữ gi&aacute; tốt, với tương ứng 87% v&agrave; 95% quốc gia c&oacute; gi&aacute; trị vốn kh&ocirc;ng thay đổi v&agrave; tăng. Cả hai lĩnh vực n&agrave;y sẽ tiếp tục c&oacute; lượng cầu tăng cao trong tương lai gần. Hơn 2/3 c&aacute;c quốc gia tham gia khảo s&aacute;t kh&ocirc;ng ghi nhận sự thay đổi của gi&aacute; trị vốn trong lĩnh vực văn ph&ograve;ng v&agrave; nh&agrave; ở.</p>\r\n\r\n<p>Trong lĩnh vực nh&agrave; ở, khi ch&iacute;nh s&aacute;ch đ&oacute;ng cửa để kiềm tỏa dịch bệnh ảnh hưởng nghi&ecirc;m trọng đến c&aacute;c giao dịch với kh&aacute;ch mua, 73% quốc gia ghi nhận gi&aacute; trị vốn kh&ocirc;ng thay đổi, so với mức 53% tại một cuộc khảo s&aacute;t trước đ&oacute;.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://cafef.vn/thi-truong-bat-dong-san-o-nhieu-quoc-gia-dang-phuc-hoi-tro-lai-trung-quoc-dien-bien-tich-cuc-hau-covid-19-2020050815570456.chn\">https://cafef.vn/thi-truong-bat-dong-san-o-nhieu-quoc-gia-dang-phuc-hoi-tro-lai-trung-quoc-dien-bien-tich-cuc-hau-covid-19-2020050815570456.chn</a></p>', 'upload/news\\1588910955.png', 1, 0, 8, 2, 'thi-truong-bat-dong-san-o-nhieu-quoc-gia-dang-phuc-hoi-tro-lai-trung-quoc-dien-bien-tich-cuc-hau-covid-19', NULL, '2020-09-07 21:09:15', '2020-09-07 21:09:59'),
(18, 'Con trai Warren Buffett: Từng sống rất đạm bạc, bán cổ phiếu được thừa kế để theo đuổi đam mê', 5, 'Nếu giữ 90.000 USD cổ phiếu Berkshire Hathaway được nhận năm 19 tuổi, Peter Buffett sẽ có 200 triệu USD ở thời điểm hiện tại. Peter không hề hối tiếc về lựa chọn của mình.', '<p><img alt=\"Con trai Warren Buffett: Từng sống rất đạm bạc, b&amp;#225;n cổ phiếu được thừa kế để theo đuổi đam m&amp;#234;\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/600/162123310254002176/2020/5/9/photo1588989874986-1588989875118221608155.jpg\" /></p>\r\n\r\n<p>Năm 1977, khi tr&ograve;n 19 tuổi, con trai của Warren Buffett, Peter, nhận được một khoản t&agrave;i sản thừa kế. Đ&oacute; l&agrave; 90.000 USD thu được từ việc b&aacute;n trang trại của &ocirc;ng nội v&agrave; được Warren Buffett chuyển th&agrave;nh cổ phiếu của Berkshire Hathaway.</p>\r\n\r\n<p>&ldquo;T&ocirc;i hiểu rằng m&igrave;nh kh&ocirc;ng n&ecirc;n mong đợi g&igrave; th&ecirc;m&rdquo;, Peter Buffett &ndash; nh&agrave; từ thiện v&agrave; nhạc sĩ từng đoạt giải Emmy viết trong cuốn hồi k&yacute; ph&aacute;t h&agrave;nh năm 2010 của &ocirc;ng.</p>\r\n\r\n<p>Trong một cuộc phỏng vấn, Peter từng tiết lộ rằng d&ugrave; &ocirc;ng v&agrave; anh chị em của m&igrave;nh được cha cho một khoản tiền khổng lồ để l&agrave;m từ thiện, nhưng 90.000 USD đ&oacute; lại l&agrave; t&agrave;i sản thừa kế duy nhất &ocirc;ng được sử dụng c&aacute; nh&acirc;n.</p>\r\n\r\n<p>Vậy một sinh vi&ecirc;n đại học như Peter khi đ&oacute; sẽ l&agrave;m g&igrave; với số tiền n&agrave;y? Mua một chiếc xe hơi y&ecirc;u th&iacute;ch, một căn hộ b&ecirc;n bờ biển hay đi du lịch với những chuyến bay hạng sang?</p>\r\n\r\n<p>&ldquo;T&ocirc;i từng chứng kiến 2 anh chị lớn của m&igrave;nh ti&ecirc;u sạch số tiền họ c&oacute; một c&aacute;ch nhanh ch&oacute;ng. V&agrave; t&ocirc;i kh&ocirc;ng muốn đi theo con đường đ&oacute;&rdquo;, con trai &uacute;t của Warren Buffett viết.</p>\r\n\r\n<p><strong>Theo đuổi ước mơ</strong></p>\r\n\r\n<p>V&agrave;o thời điểm Peter nhận được quyền thừa kế, &ocirc;ng quyết định sẽ theo đuổi ước mơ trở th&agrave;nh nhạc sĩ. V&igrave; vậy, &ocirc;ng b&aacute;n hết số cổ phiếu m&igrave;nh c&oacute; v&agrave; sử dụng số tiền n&agrave;y &ldquo;mua khoảng thời gian cần thiết gi&uacute;p t&ocirc;i hiểu được m&igrave;nh c&oacute; thể th&agrave;nh c&ocirc;ng trong lĩnh vực &acirc;m nhạc hay kh&ocirc;ng&quot;, Peter viết.</p>\r\n\r\n<p>Động th&aacute;i đầu ti&ecirc;n của Peter l&agrave; bỏ học tại Đại học Stanford. D&ugrave; thừa nhận kh&ocirc;ng biết l&agrave;m c&aacute;ch n&agrave;o để trở th&agrave;nh nhạc sĩ chuy&ecirc;n nghiệp, nhưng &ocirc;ng hiểu rằng sẽ chẳng thể th&agrave;nh c&ocirc;ng nếu chỉ tham gia c&aacute;c lớp học.</p>\r\n\r\n<p>Peter lập ra một ng&acirc;n s&aacute;ch v&agrave; chuyển đến San Francisco, sống rất đạm bạc trong một căn hộ nhỏ. Điều xa xỉ nhất &ocirc;ng l&agrave;m l&agrave; &ldquo;n&acirc;ng cấp v&agrave; mua th&ecirc;m thiết bị thu &acirc;m&quot;.</p>\r\n\r\n<p>&Ocirc;ng kh&ocirc;ng ngừng nỗ lực để n&acirc;ng cao khả năng của m&igrave;nh, cả với tư c&aacute;ch l&agrave; một nghệ sĩ piano v&agrave; nh&agrave; sản xuất &acirc;m nhạc. &Ocirc;ng s&aacute;ng t&aacute;c, thử nghiệm với thanh &acirc;m v&agrave; kỹ thuật thu &acirc;m. Peter cũng đặt quảng c&aacute;o trong mục rao vặt của tờ San Francisco Chronicle v&agrave; chấp nhận l&agrave;m việc kh&ocirc;ng lương.</p>\r\n\r\n<p>Cuối c&ugrave;ng, Peter đ&atilde; đạt được th&agrave;nh c&ocirc;ng trong lĩnh vực &acirc;m nhạc m&agrave; &ocirc;ng lu&ocirc;n mong ước. Bước ngoặt lớn nhất của Peter kh&ocirc;ng đến từ những mối quan hệ của người cha tỷ ph&uacute;. Thậm ch&iacute;, c&oacute; lần đang rửa chiếc xe cũ n&aacute;t của m&igrave;nh, một người h&agrave;ng x&oacute;m hỏi Peter rằng &ocirc;ng l&agrave;m g&igrave; để kiếm sống.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&ocirc;i n&oacute;i với &ocirc;ng ấy rằng t&ocirc;i l&agrave; một nh&agrave; soạn nhạc đang gặp kh&oacute; khăn, Peter kể lại trong cuốn s&aacute;ch của &ocirc;ng. Người h&agrave;ng x&oacute;m đ&atilde; giới thiệu Peter với con rể của m&igrave;nh - một họa sĩ hoạt h&igrave;nh cần những giai điệu quảng c&aacute;o cho một k&ecirc;nh truyền h&igrave;nh c&aacute;p mới ra đời. K&ecirc;nh truyền h&igrave;nh đ&oacute; trở th&agrave;nh một hiện tượng văn h&oacute;a những năm 1980 v&agrave; n&oacute; ch&iacute;nh l&agrave; MTV.</p>\r\n\r\n<p><strong>Thời gian c&oacute; thể l&agrave; khoản đầu tư gi&aacute; trị nhất</strong></p>\r\n\r\n<p>Nh&igrave;n lại những g&igrave; đ&atilde; trải qua, Peter n&oacute;i rằng c&oacute; một con đường kh&aacute;c m&agrave; c&oacute; thể &ocirc;ng đ&atilde; đi theo. Đ&oacute; l&agrave; tốt nghiệp đại học, t&igrave;m một c&ocirc;ng việc ổn định, lương cao &ndash; c&oacute; thể l&agrave; tại ch&iacute;nh c&ocirc;ng ty của cha m&igrave;nh &ndash; c&ugrave;ng với đ&oacute; tiếp tục nắm giữ khoản cổ phiếu được thừa kế năm xưa.</p>\r\n\r\n<p>(Theo t&iacute;nh to&aacute;n của&nbsp;<em>CNBC</em>, 90.000 USD cổ phiếu Berkshire Hathaway năm 1977 sẽ c&oacute; gi&aacute; trị hơn 200 triệu USD v&agrave;o ng&agrave;y 6/5/2020, với lợi nhuận hơn 250.000%).</p>\r\n\r\n<p>&ldquo;T&ocirc;i kh&ocirc;ng hề hối tiếc về lựa chọn đ&oacute;. T&ocirc;i đ&atilde; d&ugrave;ng n&oacute; để mua một thứ c&ograve;n qu&yacute; hơn tiền, đ&oacute; l&agrave; thời gian&rdquo;, Peter viết. V&agrave; ch&iacute;nh khoảng thời gian đ&oacute; đ&atilde; gi&uacute;p &ocirc;ng th&agrave;nh c&ocirc;ng với c&ocirc;ng việc m&agrave; m&igrave;nh y&ecirc;u th&iacute;ch.</p>\r\n\r\n<p>Ngay từ nhỏ, Peter đ&atilde; học được b&agrave;i học rất quan trọng về đạo đức l&agrave;m việc. Điều Warren Buffett dạy cho con trai kh&ocirc;ng phải l&agrave; l&agrave;m mọi c&aacute;ch để kiếm được nhiều tiền nhất c&oacute; thể (được Peter gọi l&agrave; đạo đức gi&agrave;u c&oacute;). Thay v&agrave;o đ&oacute;, h&atilde;y l&agrave;m điều bạn y&ecirc;u th&iacute;ch v&agrave; khiến bạn muốn rời khỏi giường mỗi buổi s&aacute;ng.</p>\r\n\r\n<p>Sự kh&aacute;c biệt đ&oacute; rất dễ bị bỏ qua trong trường hợp của Warren Buffett, bởi c&ocirc;ng việc y&ecirc;u th&iacute;ch nhất của &ocirc;ng - đầu tư &ndash; li&ecirc;n quan rất lớn đến tiền bạc. Đ&oacute; l&agrave; một c&ocirc;ng việc nếu bạn l&agrave;m tốt v&agrave; bạn l&agrave;m n&oacute; với niềm đam m&ecirc;, bạn c&oacute; thể trở n&ecirc;n rất, rất gi&agrave;u c&oacute;. Nhưng chủ tịch Berkshire kh&ocirc;ng bao giờ l&agrave;m việc chỉ v&igrave; tiền. Tr&ecirc;n thực tế, &ocirc;ng khuy&ecirc;n c&aacute;c bạn trẻ ng&agrave;y nay &ldquo;H&atilde;y chọn c&ocirc;ng việc gi&uacute;p bạn độc lập về t&agrave;i ch&iacute;nh&rdquo;.</p>\r\n\r\n<p><strong>Kh&ocirc;ng chọn con đường dễ d&agrave;ng nhất</strong></p>\r\n\r\n<p>Hầu hết ch&uacute;ng ta kh&ocirc;ng c&oacute; cơ hội l&agrave;m những g&igrave; Peter l&agrave;m. &ldquo;T&ocirc;i nhận thức r&otilde; r&agrave;ng rằng số tiền thừa kế của t&ocirc;i nhiều hơn hầu hết những g&igrave; c&aacute;c bạn trẻ kh&aacute;c được nhận để bắt đầu cuộc sống&rdquo;, &ocirc;ng thừa nhận trong cuốn s&aacute;ch. &ldquo;C&oacute; được số tiền đ&oacute; l&agrave; một đặc &acirc;n, một m&oacute;n qu&agrave; t&ocirc;i chưa kiếm được&rdquo;.</p>\r\n\r\n<p>&ldquo;C&oacute; nhiều người sẽ c&oacute; lợi thế hơn người kh&aacute;c, c&oacute; thể về tiền bạc, sự động vi&ecirc;n về tinh thần, một t&agrave;i năng độc đ&aacute;o hoặc cơ hội duy nhất n&agrave;o đ&oacute;. Nhưng họ kh&ocirc;ng hiểu được gi&aacute; trị của thời gian v&agrave; cố gắng lao theo định mệnh của m&igrave;nh&rdquo;, v&agrave; kết quả l&agrave; &ldquo;họ c&oacute; thể l&agrave;m c&ocirc;ng việc ph&ugrave; hơp hoặc cũng c&oacute; thể kh&ocirc;ng, cảm thấy hạnh ph&uacute;c hoặc cũng c&oacute; thể kh&ocirc;ng h&agrave;i l&ograve;ng&rdquo;.</p>\r\n\r\n<p>Theo Peter, nếu kh&ocirc;ng c&oacute; h&agrave;ng trăm giờ nỗ lực cố gắng m&agrave; chẳng đ&ograve;i hỏi trả lương, &ocirc;ng sẽ kh&ocirc;ng c&oacute; được th&agrave;nh c&ocirc;ng như hiện nay. Điều đ&oacute; đ&ograve;i hỏi sự ki&ecirc;n nhẫn v&agrave; thời gian.</p>\r\n\r\n<p>&ldquo;T&ocirc;i đ&atilde; học được nhiều điều về bản th&acirc;n v&agrave; sự ki&ecirc;n cường của m&igrave;nh trong giai đoạn kh&oacute; khăn đ&oacute;. Điều m&agrave; t&ocirc;i sẽ kh&ocirc;ng hiểu được nếu c&oacute; một đống tiền v&agrave; sống m&ocirc;t c&aacute;ch dễ d&agrave;ng. Thật l&ograve;ng t&ocirc;i thấy rằng việc cha từ chối trao cho t&ocirc;i một giải ph&aacute;p đơn giản l&agrave; h&agrave;nh động thể hiện t&igrave;nh y&ecirc;u&rdquo; &ndash; giống như &ocirc;ng ấy muốn n&oacute;i rằng &ldquo;Cha tin con v&agrave; con kh&ocirc;ng cần sự gi&uacute;p đỡ của cha&rdquo;.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://cafebiz.vn/con-trai-warren-buffett-tung-song-rat-dam-bac-ban-co-phieu-duoc-thua-ke-de-theo-duoi-dam-me-20200509090547122.chn\">https://cafebiz.vn/con-trai-warren-buffett-tung-song-rat-dam-bac-ban-co-phieu-duoc-thua-ke-de-theo-duoi-dam-me-20200509090547122.chn</a></p>', 'upload/news\\1588911118.jpeg', 1, 0, 8, 2, 'con-trai-warren-buffett-tung-song-rat-dam-bac-ban-co-phieu-duoc-thua-ke-de-theo-duoi-dam-me', NULL, '2020-09-07 21:11:58', '2020-09-07 21:11:58'),
(19, 'Những chuyến xe chở người nghi nhiễm Covid-19 và mối lo lớn nhất của đồng sáng lập Grab Hooi Ling Tan sau đại dịch', 6, '<h2>&quot;Khi ca dương t&iacute;nh với Covid-19 đầu ti&ecirc;n được x&aacute;c lập tại Vũ H&aacute;n, một cuộc tranh c&atilde;i đ&atilde; nổ ra trong d&agrave;n l&atilde;nh đạo Grab. Th&agrave;nh thực m&agrave; n&oacute;i, một phần trong t&ocirc;i tự nhủ c&oacute; lẽ đ&acirc;y chỉ l&agrave; vấn đề địa phương của ri&ecirc;ng Trung Quốc, n&oacute; chẳng thể trở th&agrave;nh một vấn đề của khu vực Đ&ocirc;ng Nam &Aacute; hay một vấn nạn to&agrave;n cầu...&quot;, Cofounder Grab Hooi Ling Tan t&acirc;m sự.</h2>', '<h2>&quot;Khi ca dương t&iacute;nh với Covid-19 đầu ti&ecirc;n được x&aacute;c lập tại Vũ H&aacute;n, một cuộc tranh c&atilde;i đ&atilde; nổ ra trong d&agrave;n l&atilde;nh đạo Grab. Th&agrave;nh thực m&agrave; n&oacute;i, một phần trong t&ocirc;i tự nhủ c&oacute; lẽ đ&acirc;y chỉ l&agrave; vấn đề địa phương của ri&ecirc;ng Trung Quốc, n&oacute; chẳng thể trở th&agrave;nh một vấn đề của khu vực Đ&ocirc;ng Nam &Aacute; hay một vấn nạn to&agrave;n cầu...&quot;, Cofounder Grab Hooi Ling Tan t&acirc;m sự.</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p><img alt=\"Những chuyến xe chở người nghi nhiễm Covid-19 v&amp;#224; mối lo lớn nhất của đồng s&amp;#225;ng lập Grab Hooi Ling Tan sau đại dịch\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/600/162123310254002176/2020/5/8/-15889021308811134913806.jpg\" style=\"margin-top:10px; width:600px\" /></p>\r\n\r\n<p>Tại Malaysia, 20% - 25% đơn h&agrave;ng food được vận chuyển bởi c&aacute;c t&agrave;i xế&nbsp;<a href=\"https://cafebiz.vn/grabcar.html\" target=\"_blank\">GrabCar</a>&nbsp;&ndash; những người bị mất c&ocirc;ng việc ch&iacute;nh do lệnh hạn chế di chuyển.</p>\r\n\r\n<p>Trong bối cảnh Covid-19 biến c&aacute;c th&agrave;nh phố nổi tiếng với &quot;đặc sản&quot; tắc đường như Jakarta, Manila, Bangkok th&agrave;nh c&aacute;c khu phố vắng ngắt, một l&agrave;n s&oacute;ng dịch chuyển c&aacute;c t&agrave;i xế từ chở người sang chở h&agrave;ng h&oacute;a, thực phẩm được th&uacute;c đẩy nhanh tại c&aacute;c quốc gia thị trường của Grab.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/8/c11901250-15889030093091045010796.jpg\"><img alt=\"Những chuyến xe chở người nghi nhiễm Covid-19 và mối lo lớn nhất của các Cofounders Grab Anthony Tan - Hooi Ling Tan sau đại dịch - Ảnh 1.\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/640/162123310254002176/2020/5/8/c11901250-15889030093091045010796.jpg\" /></a></p>\r\n\r\n<p><em>Một phụ nữ nh&igrave;n v&agrave;o khu chợ vắng lặng tại Bangkok. Ảnh: Bangkok Post.</em></p>\r\n\r\n<p>&quot;Khi ca dương t&iacute;nh với&nbsp;<a href=\"https://cafebiz.vn/covid-19.html\" target=\"_blank\">Covid-19</a>&nbsp;đầu ti&ecirc;n được x&aacute;c lập tại Vũ H&aacute;n, một cuộc tranh c&atilde;i đ&atilde; nổ ra trong d&agrave;n l&atilde;nh đạo Grab. Th&agrave;nh thực m&agrave; n&oacute;i, một phần trong t&ocirc;i tự nhủ c&oacute; lẽ đ&acirc;y chỉ l&agrave; vấn đề địa phương của ri&ecirc;ng Trung Quốc, n&oacute; chẳng thể trở th&agrave;nh một vấn đề của khu vực Đ&ocirc;ng Nam &Aacute; hay một vấn nạn to&agrave;n cầu. Nhưng qua một đ&ecirc;m, mọi chuyện đ&atilde; thay đổi&quot;, Cofounder Grab Hooi Ling Tan chia sẻ tại sự kiện trực tuyến &quot;Sooner Than You Think: Hero Technologies&quot; do Bloomberg tổ chức mới đ&acirc;y.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i xin lược lại nội dung trao đổi giữa đồng s&aacute;ng lập Grab Hooi Ling Tan v&agrave; Haslinda Amin &ndash; MC của Truyền h&igrave;nh Bloomberg dưới đ&acirc;y.</p>\r\n\r\n<hr />\r\n<p><strong>Đến chợ cũng phải số h&oacute;a</strong></p>\r\n\r\n<p><strong>* MC Haslinda Amin: B&ecirc;n cạnh việc cung cấp c&aacute;c sản phẩm thiết yếu, Grab c&ograve;n đưa ra cả chương tr&igrave;nh &quot;lấp lỗ hổng&quot; trong hệ thống chăm s&oacute;c sức khỏe. Bạn c&oacute; thể chia sẻ cụ thể hơn c&acirc;u chuyện n&agrave;y được kh&ocirc;ng?</strong></p>\r\n\r\n<p><strong>Hooi Ling Tan</strong>: Trong đại dịch Covid-19, ch&uacute;ng t&ocirc;i muốn đảm bảo 2 việc: Về ph&iacute;a t&agrave;i xế, ch&uacute;ng t&ocirc;i mong muốn họ c&oacute; thể duy tr&igrave; cuộc sống trong bổi cảnh đại dịch ảnh hướng tới kế sinh nhai. Về ph&iacute;a cầu, ch&uacute;ng t&ocirc;i thấy c&oacute; sự thay đổi th&uacute; vị.</p>\r\n\r\n<p>Tại Singapore, ch&uacute;ng t&ocirc;i ph&aacute;t động chiến dịch tr&ecirc;n social media mang t&ecirc;n &quot;Everyday Heroes&quot;. Tại quốc đảo sư tử, những &quot;người l&iacute;nh&quot; tuyến đầu chống dịch &ndash; c&aacute;c y t&aacute;, b&aacute;c sỹ, bị kỳ thị. Tr&ecirc;n c&aacute;c phương tiện c&ocirc;ng cộng, người d&acirc;n xa l&aacute;nh họ, nh&igrave;n họ một c&aacute;ch kỳ quặc. N&agrave;y, đấy đ&acirc;u phải l&agrave; c&aacute;ch ch&uacute;ng ta n&ecirc;n ứng xử! V&agrave; ch&uacute;ng t&ocirc;i nghĩ xem m&igrave;nh c&oacute; thể gi&uacute;p đỡ bằng c&aacute;ch n&agrave;o.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/8/-1588902767626219808743.jpg\"><img alt=\"Những chuyến xe chở người nghi nhiễm Covid-19 và mối lo lớn nhất của các Cofounders Grab Anthony Tan - Hooi Ling Tan sau đại dịch - Ảnh 3.\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/640/162123310254002176/2020/5/8/-1588902767626219808743.jpg\" /></a></p>\r\n\r\n<p><em>Hooi Ling Tan (phải) tại sự kiện trực tuyến của Bloomberg.</em></p>\r\n\r\n<p>Trong 72 giờ đồng đồng hồ, ch&uacute;ng t&ocirc;i cho ra đời dịch vụ GrabCare &ndash; dịch vụ phục vụ 24/7 cho những người h&agrave;nh nghề y. B&ecirc;n cạnh đ&oacute;, ch&uacute;ng t&ocirc;i cũng k&ecirc;u gọi sự hỗ trợ t&igrave;nh nguyện từ cộng đồng t&agrave;i xế. Kết quả, 3.000 người đ&atilde; k&yacute; v&agrave;o đơn t&igrave;nh nguyện, con số ấy hiện đ&atilde; l&ecirc;n đến 10.000 tại Singapore. Dịch vụ n&agrave;y cũng được cung cấp tại Indonesia v&agrave; Th&aacute;i Lan.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, ch&uacute;ng t&ocirc;i phối hợp với Bộ Y tế. Họ nhận thức rằng c&oacute; nhiều trường hợp nghi nhiễm ngo&agrave;i cộng đồng. Nhưng l&agrave;m thế n&agrave;o để những ca nghi nhiễm c&oacute; thể đến bệnh viện hay c&aacute;c ph&ograve;ng kh&aacute;m để x&eacute;t nghiệm, khi xe cứu thương th&igrave; qu&aacute; tải c&ograve;n phương tiện c&ocirc;ng cộng th&igrave; ngừng hoạt động? Ch&uacute;ng t&ocirc;i nhận tr&aacute;ch nhiệm đ&oacute;, k&ecirc;u gọi c&aacute;c t&agrave;i xế t&igrave;nh nguyện, c&oacute; thể chấp nhận rủi ro để đưa c&aacute;c ca nghi nhiễm tới bệnh viện.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/8/-1588902708286484935388.jpg\"><img alt=\"Những chuyến xe chở người nghi nhiễm Covid-19 và mối lo lớn nhất của các Cofounders Grab Anthony Tan - Hooi Ling Tan sau đại dịch - Ảnh 4.\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/640/162123310254002176/2020/5/8/-1588902708286484935388.jpg\" /></a></p>\r\n\r\n<p><em>C&aacute;c t&agrave;i xế Grab được đ&agrave;o tạo để chở người nghi nhiễm Covid-19 đến c&aacute;c cơ sở y tế.</em></p>\r\n\r\n<p><strong>* MC: Điều g&igrave; Grab cho rằng c&oacute; thể đ&atilde; l&agrave;m tốt hơn trong việc ứng ph&oacute; với đại dịch Covid-19?</strong></p>\r\n\r\n<p><strong>Hooi Ling Tan:</strong>&nbsp;Gi&uacute;p c&aacute;c t&agrave;i xế chở người dịch chuyển nhanh hơn sang lĩnh vực chở h&agrave;ng h&oacute;a v&agrave; thực phẩm. Gi&uacute;p mở c&aacute;c dịch vụ mới v&agrave; đưa nhiều merchants (đối t&aacute;c b&aacute;n h&agrave;ng) l&ecirc;n nền tảng nhanh hơn.</p>\r\n\r\n<p>Hiện nay h&agrave;ng trăm ngh&igrave;n l&aacute;i xe GrabCar ở Malaysia v&agrave; trong khu vực đ&atilde; dịch chuyển từ lĩnh vực chở người sang chở Food v&agrave; h&agrave;ng h&oacute;a cũng như c&aacute;c dịch vụ h&agrave;ng ng&agrave;y kh&aacute;c. Tại Malaysia, khoảng 20% - 25% đơn h&agrave;ng Food được vận chuyển bởi c&aacute;c t&agrave;i xế GrabCar</p>\r\n\r\n<p>Ở ph&iacute;a merchants, khoảng 15% c&aacute;c doanh nghiệp nhỏ v&agrave; vừa tại khu vực Đ&ocirc;ng Nam &Aacute; c&oacute; sự hiện diện số (digitally present). Bạn h&atilde;y tưởng tượng khi&nbsp;<a href=\"https://cafebiz.vn/covid-19.html\" target=\"_blank\">Covid-19</a>&nbsp;ập đến, lượng traffic tới phần lớn cửa h&agrave;ng vật l&yacute; bằng 0, những doanh nghiệp với m&ocirc; h&igrave;nh kinh doanh tưởng như bền vững bỗng dưng kh&ocirc;ng c&ograve;n doanh số chỉ sau một đ&ecirc;m.</p>\r\n\r\n<p><strong>* MC: &Yacute; của bạn l&agrave; một trong những yếu tố sống c&ograve;n trong dịch l&agrave; số h&oacute;a (Digitalization) v&agrave; Grab đang hỗ trợ họ?</strong></p>\r\n\r\n<p><strong>Hooi Ling Tan:</strong>&nbsp;T&ocirc;i lấy một v&iacute; dụ về việc đưa lĩnh vực truyền thống l&ecirc;n online. Bạn thường ra &quot;wet market&quot; (chợ truyền thống) &ndash; nơi để mua thịt tươi, c&aacute; lội, rau xanh..., tất tật nhu cầu thực phẩm h&agrave;ng ng&agrave;y. V&agrave; với &quot;c&uacute; đấm&quot; từ Covid, bức tranh nhộn nhịp bạn thấy thường ng&agrave;y ngo&agrave;i chợ biến mất. Chuyện n&agrave;y chưa từng xảy ra.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://cafebiz.vn/nhung-chuyen-xe-cho-nguoi-nghi-nhiem-covid-19-va-moi-lo-lon-nhat-cua-dong-sang-lap-grab-hooi-ling-tan-sau-dai-dich-20200508091640997.chn\">https://cafebiz.vn/nhung-chuyen-xe-cho-nguoi-nghi-nhiem-covid-19-va-moi-lo-lon-nhat-cua-dong-sang-lap-grab-hooi-ling-tan-sau-dai-dich-20200508091640997.chn</a></p>', 'upload/news\\1588911303.jpeg', 1, 0, 5, 3, 'nhung-chuyen-xe-cho-nguoi-nghi-nhiem-covid-19-va-moi-lo-lon-nhat-cua-dong-sang-lap-grab-hooi-ling-tan-sau-dai-dich', NULL, '2020-09-07 21:15:03', '2020-09-07 21:15:32');
INSERT INTO `news` (`id`, `news_title`, `author_id`, `news_summary`, `news_content`, `news_image`, `news_featured`, `news_view`, `tag_id`, `category_id`, `news_slug`, `deleted_at`, `created_at`, `updated_at`) VALUES
(20, 'Từ Vlogger thành ông chủ chuỗi Xofa Café & Bistro rồi All Day Coffee, Đỗ Hoàng Minh Khôi chia sẻ: Dù mở quán cà phê 3 sao cũng nên dành thời gian trải nghiệm mô hình 5 sao', 8, '<p>&quot;Bản th&acirc;n l&agrave; &quot;fan cứng&quot; của Starbucks, một huyền thoại trong lĩnh vực kinh doanh của m&igrave;nh n&ecirc;n khi mở All Day Coffee, m&igrave;nh mong qu&aacute;n cũng c&oacute; được những gi&aacute; trị tương đương với ti&ecirc;u chuẩn quốc tế như Starbucks&quot;, ch&agrave;ng trai sinh năm 1992 Đỗ Ho&agrave;ng Minh Kh&ocirc;i, c&ograve;n được biết đến nhiều hơn với c&aacute;i t&ecirc;n &quot;He Always Smiles&quot; chia sẻ.</p>', '<h2>&quot;Bản th&acirc;n l&agrave; &quot;fan cứng&quot; của Starbucks, một huyền thoại trong lĩnh vực kinh doanh của m&igrave;nh n&ecirc;n khi mở All Day Coffee, m&igrave;nh mong qu&aacute;n cũng c&oacute; được những gi&aacute; trị tương đương với ti&ecirc;u chuẩn quốc tế như Starbucks&quot;, ch&agrave;ng trai sinh năm 1992 Đỗ Ho&agrave;ng Minh Kh&ocirc;i, c&ograve;n được biết đến nhiều hơn với c&aacute;i t&ecirc;n &quot;He Always Smiles&quot; chia sẻ.</h2>\r\n\r\n<ul>\r\n	<li>06-05-2020&nbsp;<a href=\"https://cafebiz.vn/sau-nhieu-nam-im-lang-thai-tu-samsung-vua-cui-dau-xin-loi-vi-hanh-vi-sai-trai-tiet-lo-se-khong-bao-gio-de-con-cai-thua-ke-ngai-vang-20200506163004013.chn\">Sau nhiều năm im lặng, &lsquo;th&aacute;i tử&rsquo; Samsung vừa c&uacute;i đầu xin lỗi v&igrave; &#39;h&agrave;nh vi...&nbsp;</a></li>\r\n	<li>06-05-2020&nbsp;<a href=\"https://cafebiz.vn/jll-kich-ban-phuc-hoi-thi-truong-bat-dong-san-tu-bai-hoc-cua-trung-quoc-20200506154309982.chn\">JLL: Kịch bản phục hồi thị trường bất động sản từ b&agrave;i học của Trung Quốc&nbsp;</a></li>\r\n	<li>06-05-2020&nbsp;<a href=\"https://cafebiz.vn/mat-bao-lau-thi-nganh-khach-san-phuc-hoi-20200506104425951.chn\">Mất bao l&acirc;u th&igrave; ng&agrave;nh kh&aacute;ch sạn phục hồi?&nbsp;</a></li>\r\n</ul>\r\n\r\n<p><img alt=\"Từ Vlogger th&amp;#224;nh &amp;#244;ng chủ chuỗi Xofa Caf&amp;#233; &amp;amp; Bistro rồi All Day Coffee, Đỗ Ho&amp;#224;ng Minh Kh&amp;#244;i chia sẻ: D&amp;#249; mở qu&amp;#225;n c&amp;#224; ph&amp;#234; 3 sao cũng n&amp;#234;n d&amp;#224;nh thời gian trải nghiệm m&amp;#244; h&amp;#236;nh 5 sao\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/600/162123310254002176/2020/5/6/photo1588758631670-158875863181113384937.jpg\" style=\"margin-top:10px; width:600px\" /></p>\r\n\r\n<p>C&ugrave;ng với JVevermind, PewPew hay Huyme,&nbsp;<a href=\"https://cafebiz.vn/do-hoang-minh-khoi.html\" target=\"_blank\">Đỗ Ho&agrave;ng Minh Kh&ocirc;i</a>&nbsp;(1992) hay c&ograve;n được biết đến nhiều hơn với c&aacute;i t&ecirc;n &quot;He Always Smiles&quot; l&agrave; một trong những vlogger đời đầu của l&agrave;ng youtube Việt.</p>\r\n\r\n<p>Bắt đầu l&agrave;m vlog từ 2013, anh nhanh ch&oacute;ng chiếm trọn t&igrave;nh cảm của giới trẻ nhờ h&igrave;nh ảnh độc đ&aacute;o với chiếc đầu hộp kh&aacute;c biệt c&ugrave;ng nội dung t&iacute;ch cực, truyền cảm hứng. Tuy nhi&ecirc;n, đến 2015, khi đang c&oacute; chỗ đứng nhất định trong giới vlogger, Kh&ocirc;i đột ngột rời bỏ youtube để tập trung cho việc kinh doanh.</p>\r\n\r\n<p>Từng tạo n&ecirc;n tr&agrave;o lưu kem dừa Th&aacute;i Samui Hut g&acirc;y sốt một thời, ch&agrave;ng vlogger đồng s&aacute;ng lập ra Xofa Caf&eacute; &amp; Bistro (2015), s&aacute;ng lập All Day Coffee (2018) - những thương hiệu c&agrave; ph&ecirc; rất được l&ograve;ng giới trẻ.</p>\r\n\r\n<p>Năm 2020, trở lại l&agrave;m vlog sau thời gian d&agrave;i &quot;mất t&iacute;ch&quot;, Minh Kh&ocirc;i chia sẻ nhiều hơn về c&acirc;u chuyện v&agrave; những kinh nghiệm kinh doanh của m&igrave;nh, mong muốn truyền cảm hứng tới c&aacute;c bạn trẻ. Trong một video gần đ&acirc;y, &quot;ch&agrave;ng trai hay cười&quot; đ&atilde; chỉ ra 5 bước cơ bản để bắt đầu kinh doanh.</p>\r\n\r\n<p><strong>1. H&atilde;y l&agrave;m một kh&aacute;ch h&agrave;ng kh&oacute; t&iacute;nh</strong></p>\r\n\r\n<p><em>&quot;Trong suốt hơn 10 năm qua, từ khi học cấp 3, m&igrave;nh đ&atilde; d&agrave;nh thời gian để đi rất nhiều c&aacute;c qu&aacute;n c&agrave; ph&ecirc;. Ở bất kỳ nơi n&agrave;o khi đi du lịch, m&igrave;nh cũng c&oacute; một danh s&aacute;ch những qu&aacute;n nổi tiếng, được ưa th&iacute;ch nhất ở đ&oacute; để cố gắng gh&eacute; thăm nhiều nhất c&oacute; thể.</em></p>\r\n\r\n<p><em>M&igrave;nh tin rằng c&oacute; trải nghiệm thực tế v&agrave; s&acirc;u sắc dưới g&oacute;c nh&igrave;n của kh&aacute;ch h&agrave;ng, thậm ch&iacute; như một kh&aacute;ch h&agrave;ng kh&oacute; t&iacute;nh, l&agrave; điều rất quan trọng. Bởi l&agrave;m sao ta c&oacute; thể cung cấp cho kh&aacute;ch h&agrave;ng những gi&aacute; trị tốt nếu như bản th&acirc;n c&ograve;n chưa biết thế n&agrave;o l&agrave; tốt?&quot;</em></p>\r\n\r\n<p>Kh&ocirc;i cho rằng, khi gh&eacute; đến bất kỳ qu&aacute;n c&agrave; ph&ecirc; n&agrave;o, cần quan s&aacute;t xem đồ ăn, thức uống ở đ&oacute; ra sao, kh&ocirc;ng gian được b&agrave;y tr&iacute; như thế n&agrave;o. Kh&ocirc;ng chỉ vậy, những chi tiết như m&ugrave;i hương, &acirc;m nhạc, &aacute;nh s&aacute;ng, nh&acirc;n vi&ecirc;n phục vụ, thậm ch&iacute; l&agrave; wifi cũng l&agrave; điều v&ocirc; c&ugrave;ng quan trọng.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/6/-15887555611061755969485.jpg\"><img alt=\"\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/660/162123310254002176/2020/5/6/-15887555611061755969485.jpg\" style=\"height:193px; width:289.274px\" /></a></p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/6/9568381730203115013239185885029468187131904n-1588750459182229634875.jpg\"><img alt=\"\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/660/162123310254002176/2020/5/6/9568381730203115013239185885029468187131904n-1588750459182229634875.jpg\" style=\"height:193px; width:144.75px\" /></a></p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/6/8513246429905379776433806069171273265577984o-15887502591841834748863.jpg\"><img alt=\"\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/660/162123310254002176/2020/5/6/8513246429905379776433806069171273265577984o-15887502591841834748863.jpg\" style=\"height:193px; width:193px\" /></a></p>\r\n\r\n<p>H&igrave;nh ảnh tại Xofa Caf&eacute; &amp; Bistro</p>\r\n\r\n<p>Nhưng quan s&aacute;t th&ocirc;i chưa đủ, bạn h&atilde;y đ&oacute;ng trọn vai kh&aacute;ch h&agrave;ng kh&oacute; t&iacute;nh bằng 2 c&aacute;ch:</p>\r\n\r\n<p>Đầu ti&ecirc;n, liệt k&ecirc; v&agrave; trải nghiệm 5 c&aacute;i t&ecirc;n tốt nhất tr&ecirc;n thị trường, cũng ch&iacute;nh l&agrave; đối thủ tương lai của bạn. Sau đ&oacute;, lập bảng so s&aacute;nh xem mỗi b&ecirc;n mạnh, yếu thế n&agrave;o.</p>\r\n\r\n<p>Phương ph&aacute;p thứ hai, cho d&ugrave; mục ti&ecirc;u của bạn chỉ l&agrave; mở một qu&aacute;n c&agrave; ph&ecirc; chất lượng phổ th&ocirc;ng (3 sao) th&igrave; cũng n&ecirc;n d&agrave;nh thời gian trải nghiệm những m&ocirc; h&igrave;nh 5 sao. Việc n&agrave;y sẽ gi&uacute;p bạn nhận biết được ti&ecirc;u chuẩn của thị trường, đồng thời đ&aacute;nh gi&aacute; xem m&igrave;nh đang nằm ở đoạn n&agrave;o trong thang ti&ecirc;u chuẩn đ&oacute;. Mặt kh&aacute;c, cũng c&oacute; thể &aacute;p dụng một v&agrave;i điểm cho qu&aacute;n của m&igrave;nh.</p>\r\n\r\n<p>Nhờ đ&oacute;, bạn sẽ h&igrave;nh th&agrave;nh được những ti&ecirc;u chuẩn cho ri&ecirc;ng m&igrave;nh.</p>\r\n\r\n<p><strong>2. C&oacute; đội nh&oacute;m tốt</strong></p>\r\n\r\n<p><em>&quot;Nếu để chọn ra bước quan trọng nhất, th&igrave; đ&oacute; ch&iacute;nh l&agrave; việc c&oacute; một đội nh&oacute;m tốt. C&oacute; những con người tốt rồi th&igrave; sản phẩm, dịch vụ sẽ tốt.&quot;</em></p>\r\n\r\n<p>Minh Kh&ocirc;i lấy v&iacute; dụ từ ch&iacute;nh thương hiệu All Day Coffee được th&agrave;nh lập từ 2018 của m&igrave;nh. Đ&oacute; l&agrave; chị Ho&agrave;ng Dung, người đ&atilde; đồng h&agrave;nh từ dự &aacute;n Xofa Cafe từ 6 năm trước, từng phụ tr&aacute;ch marketing cho Cộng C&agrave; ph&ecirc;, đ&oacute; l&agrave; chuy&ecirc;n gia từng đạt giải nhất trong cuộc thi về c&agrave; ph&ecirc; tại Thượng Hải. B&ecirc;n cạnh đ&oacute;, anh c&ograve;n chi&ecirc;u mộ những nh&acirc;n t&agrave;i trẻ tuổi, từng c&oacute; kinh nghiệm l&agrave;m việc cho&nbsp;<a href=\"https://cafebiz.vn/starbucks.html\" target=\"_blank\">Starbucks</a>, Golden Gate.</p>\r\n\r\n<p><em>&quot;D&ugrave; l&agrave; một thương hiệu trẻ nhưng m&igrave;nh cố gắng để c&oacute; được những nh&acirc;n sự tốt nhất c&oacute; thể cho dự &aacute;n đ&oacute;. Kỳ thực bản th&acirc;n kh&ocirc;ng mạnh về c&agrave; ph&ecirc;, những đồng đội kh&aacute;c của m&igrave;nh giỏi hơn nhiều. Mọi người nắm tr&aacute;ch nhiệm ở phần đ&oacute; để Kh&ocirc;i tập trung v&agrave;o những điểm m&igrave;nh mạnh. Kh&ocirc;ng c&oacute; ai giỏi mọi mặt cả, mỗi người n&ecirc;n được tập trung v&agrave;o những thứ họ giỏi, trở th&agrave;nh phần b&ugrave; của nhau.&quot;</em></p>\r\n\r\n<p>Tuy nhi&ecirc;n, một đội nh&oacute;m d&ugrave; tốt nhưng cũng cần thời gian l&agrave;m việc c&ugrave;ng nhau để hiểu nhau hơn. Hiện Minh Kh&ocirc;i đang tập trung v&agrave;o việc tuyển dụng, t&igrave;m những người đồng h&agrave;nh ph&ugrave; hợp.</p>\r\n\r\n<p><strong>3. Biết m&igrave;nh phục vụ ai v&agrave; hiểu r&otilde; họ</strong></p>\r\n\r\n<p><em>&quot;Đ&ocirc;i khi, sản phẩm ta l&agrave;m ra v&agrave; người ta muốn b&aacute;n cho lại kh&ocirc;ng tr&ugrave;ng khớp với nhau. Cho d&ugrave; c&oacute; s&agrave;nh sỏi đến mấy, kh&oacute; t&iacute;nh đến mấy th&igrave; thực tế ta chỉ đại diện cho một nh&oacute;m kh&aacute;ch h&agrave;ng rất nhỏ. Phần đ&ocirc;ng kh&aacute;ch h&agrave;ng nghĩ g&igrave;, muốn g&igrave; ch&iacute;nh l&agrave; điều cần t&igrave;m hiểu.&quot;</em></p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/6/84777676102158883346213575890947684012916736o-15887524552351842173501.jpg\"><img alt=\"\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/660/162123310254002176/2020/5/6/84777676102158883346213575890947684012916736o-15887524552351842173501.jpg\" style=\"height:289px; width:193.069px\" /></a></p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/6/photo-1-15887513817271924776999.jpg\"><img alt=\"\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/660/162123310254002176/2020/5/6/photo-1-15887513817271924776999.jpg\" style=\"height:289px; width:216.75px\" /></a></p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/6/photo-1-1588751019261216879856.jpg\"><img alt=\"\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/660/162123310254002176/2020/5/6/photo-1-1588751019261216879856.jpg\" style=\"height:289px; width:216.75px\" /></a></p>\r\n\r\n<p>Vlogger &quot;He Always Smiles&quot; - Đỗ Ho&agrave;ng Minh Kh&ocirc;i</p>\r\n\r\n<p>Kh&ocirc;i chia sẻ 2 c&aacute;ch gi&uacute;p vẽ ra ch&acirc;n dung kh&aacute;ch h&agrave;ng mục ti&ecirc;u.</p>\r\n\r\n<p>Thứ nhất, khi đến những qu&aacute;n c&agrave; ph&ecirc; c&oacute; m&ocirc; h&igrave;nh tương tự, h&atilde;y quan s&aacute;t xem kh&aacute;ch h&agrave;ng ở đ&oacute; l&agrave; ai, họ đến với mục đ&iacute;ch l&agrave;m việc hay gặp gỡ bạn b&egrave;, ngồi một m&igrave;nh hay ngồi cả nh&oacute;m, họ thường ăn uống g&igrave;. Khi t&iacute;ch lũy được những quan s&aacute;t đủ lớn, bạn sẽ r&uacute;t ra được mẫu số chung v&agrave; dần h&igrave;nh dung được kh&aacute;ch h&agrave;ng mục ti&ecirc;u.</p>\r\n\r\n<p>Thứ hai, trực tiếp hỏi những kh&aacute;ch h&agrave;ng mục ti&ecirc;u của m&igrave;nh, gần gũi nhất l&agrave; bạn b&egrave;. Để việc khảo s&aacute;t trực quan v&agrave; sinh động hơn, bạn c&oacute; thể l&agrave;m thử những cốc c&agrave; ph&ecirc; hay đồ ăn mẫu. Với kh&ocirc;ng gian qu&aacute;n, h&atilde;y đưa cho họ h&igrave;nh ảnh thiết kế dự kiến. Việc n&agrave;y &iacute;t nhiều sẽ gi&uacute;p bạn c&oacute; c&aacute;i nh&igrave;n nhất định về quan điểm của kh&aacute;ch h&agrave;ng v&agrave; tr&aacute;nh mắc lỗi kh&ocirc;ng đ&aacute;ng c&oacute;.</p>\r\n\r\n<p><em>&quot;M&igrave;nh tin việc hiểu r&otilde; kh&aacute;ch h&agrave;ng l&agrave; việc l&agrave;m trọn đời. Những kh&aacute;ch h&agrave;ng của m&igrave;nh h&ocirc;m nay nhưng 5 năm, 10 năm nữa, suy nghĩ của họ sẽ thay đổi. Thị trường vận động, ti&ecirc;u chuẩn của kh&aacute;ch h&agrave;ng kh&ocirc;ng ngừng thay đổi v&agrave; ch&iacute;nh ch&uacute;ng ta cũng phải vậy. Ngay cả những dự &aacute;n đang chạy, m&igrave;nh vẫn lu&ocirc;n thực hiện khảo s&aacute;t kh&aacute;ch h&agrave;ng.&quot;</em></p>\r\n\r\n<p><strong>4. Lập kế hoạch kinh doanh</strong></p>\r\n\r\n<p>Điều m&igrave;nh muốn nhấn mạnh trong bản kế hoạch kinh doanh ch&iacute;nh l&agrave; liệt k&ecirc; ra những &quot;giải ph&aacute;p gi&aacute; trị&quot;, tức những gi&aacute; trị n&agrave;o bạn c&oacute; thể đem lại cho kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Bạn c&oacute; thể t&igrave;m ra lời giải đ&aacute;p th&ocirc;ng qua 3 c&acirc;u hỏi:</p>\r\n\r\n<p><strong>- Điều g&igrave; bạn c&oacute; thể l&agrave;m tốt như những thương hiệu c&oacute; sẵn tr&ecirc;n thị trường?</strong></p>\r\n\r\n<p><em>&quot;Bản th&acirc;n l&agrave; &quot;fan cứng&quot; của Starbucks, một huyền thoại trong lĩnh vực kinh doanh của m&igrave;nh n&ecirc;n khi mở All Day Coffee, m&igrave;nh mong qu&aacute;n cũng c&oacute; được những gi&aacute; trị tương đương với ti&ecirc;u chuẩn quốc tế như Starbucks. Từ kh&ocirc;ng gian, sản phẩm v&agrave; dịch vụ, m&igrave;nh nỗ lự v&igrave; điều đ&oacute;. M&igrave;nh nghĩ rằng 80% những g&igrave; thương hiệu c&ugrave;ng ph&acirc;n kh&uacute;c l&agrave;m được th&igrave; ta cũng n&ecirc;n l&agrave;m được.&quot;</em></p>\r\n\r\n<p><strong>- Điều g&igrave; bạn c&oacute; thể l&agrave;m tốt hơn họ?</strong></p>\r\n\r\n<p>Kh&ocirc;i cho biết anh muốn cung cấp hương vị c&agrave; ph&ecirc; c&oacute; chất lượng nhỉnh hơn mặt bằng chung. Với dịch vụ, nh&acirc;n vi&ecirc;n hướng đến sự &acirc;n cần, y&ecirc;u thương kh&aacute;ch h&agrave;ng đến từ tr&aacute;i tim.</p>\r\n\r\n<p><strong>- Điều g&igrave; bạn c&oacute; thể l&agrave;m kh&aacute;c biệt với họ?</strong></p>\r\n\r\n<p><em>&quot;Theo đuổi sự kh&aacute;c biệt l&agrave; lối nghĩ của m&igrave;nh, từ khi m&igrave;nh l&agrave;m youtube v&agrave; đội chiếc hộp l&ecirc;n đầu. Năm 2018, All Day Coffee l&agrave; một trong số &iacute;t những thương hiệu kinh doanh c&agrave; ph&ecirc; đặc sản, tự rang mới mỗi ng&agrave;y.&quot;</em></p>\r\n\r\n<p>Những bộ b&agrave;n ghế, ly, cốc đều được thiết kế ri&ecirc;ng, ph&ugrave; hợp từng loại đồ uống ri&ecirc;ng, ch&uacute; &yacute; từ những chi tiết nhỏ nhất. Thay v&igrave; chi nhiều tiền quảng c&aacute;o, đội ngũ của thương hiệu ch&uacute; trọng hơn v&agrave;o c&aacute;c giải ph&aacute;p mang đến gi&aacute; trị tốt cho kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/6/photo-1-15887513146491363972092.jpg\"><img alt=\"\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/660/162123310254002176/2020/5/6/photo-1-15887513146491363972092.jpg\" style=\"height:169px; width:253.5px\" /></a></p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/6/photo-1-1588751438495141608649.jpg\"><img alt=\"\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/660/162123310254002176/2020/5/6/photo-1-1588751438495141608649.jpg\" style=\"height:169px; width:253.236px\" /></a></p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/6/photo-1-1588751357258474580833.jpg\"><img alt=\"\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/660/162123310254002176/2020/5/6/photo-1-1588751357258474580833.jpg\" style=\"height:169px; width:120.589px\" /></a></p>\r\n\r\n<p>H&igrave;nh ảnh từ All Day Coffee</p>\r\n\r\n<p><strong>5. H&agrave;nh động</strong></p>\r\n\r\n<p>Bước cuối c&ugrave;ng nhưng mang t&iacute;nh quyết định, nằm ở h&agrave;nh động.</p>\r\n\r\n<p><em>&quot;12 năm trước, nh&agrave; m&igrave;nh kh&ocirc;ng c&oacute; ai kinh doanh, m&igrave;nh cũng chỉ bắt đầu bằng việc tra google, b&acirc;y giờ vẫn đang google. 12 năm trước m&igrave;nh cũng kh&ocirc;ng biết l&agrave;m thế n&agrave;o để mở một qu&aacute;n c&agrave; ph&ecirc; nhưng b&acirc;y giờ đ&atilde; c&oacute; thể trả lời những c&acirc;u hỏi chi tiết như b&aacute;n gi&aacute; bao nhi&ecirc;u, menu cần c&oacute; những g&igrave;, kh&ocirc;ng gian ra sao,&hellip;</em></p>\r\n\r\n<p><em>Bằng l&ograve;ng đam m&ecirc; v&agrave; khao kh&aacute;t, điều g&igrave; ch&uacute;ng ta cũng c&oacute; thể thực hiện được&quot;</em>,&nbsp;Minh Kh&ocirc;i nhắn nhủ.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://cafebiz.vn/16-tuoi-ban-hang-online-23-tuoi-so-huu-them-phong-game-nha-hang-mo-xofa-cafe-bistro-roi-all-day-coffee-vlogger-chia-se-du-mo-quan-ca-phe-3-sao-cung-nen-danh-thoi-gian-trai-nghiem-mo-hinh-5-sao-20200506165448591.chn\">https://cafebiz.vn/16-tuoi-ban-hang-online-23-tuoi-so-huu-them-phong-game-nha-hang-mo-xofa-cafe-bistro-roi-all-day-coffee-vlogger-chia-se-du-mo-quan-ca-phe-3-sao-cung-nen-danh-thoi-gian-trai-nghiem-mo-hinh-5-sao-20200506165448591.chn</a></p>', 'upload/news\\1588911438.jpeg', 1, 0, 8, 2, 'tu-vlogger-thanh-ong-chu-chuoi-xofa-cafe-bistro-roi-all-day-coffee-do-hoang-minh-khoi-chia-se-du-mo-quan-ca-phe-3-sao-cung-nen-danh-thoi-gian-trai-nghiem-mo-hinh-5-sao', NULL, '2020-09-07 21:17:18', '2020-09-07 21:17:18'),
(21, 'Hàng loạt tour du lịch nội địa giá rẻ giảm sâu sau dịch Covid-19', 6, '<h2>Sau dịch Covid-19, nhiều c&ocirc;ng ty tour vẫn cửa đ&oacute;ng then c&agrave;i rơi v&agrave;o t&igrave;nh trạng ế kh&aacute;ch, nhiều đơn vị đ&atilde; tung ra c&aacute;c ưu đ&atilde;i nhằm &ldquo;giải cứu&rdquo; du lịch thu h&uacute;t kh&aacute;ch h&agrave;ng sau thời gian tạm ngừng.</h2>', '<h2>Sau dịch Covid-19, nhiều c&ocirc;ng ty tour vẫn cửa đ&oacute;ng then c&agrave;i rơi v&agrave;o t&igrave;nh trạng ế kh&aacute;ch, nhiều đơn vị đ&atilde; tung ra c&aacute;c ưu đ&atilde;i nhằm &ldquo;giải cứu&rdquo; du lịch thu h&uacute;t kh&aacute;ch h&agrave;ng sau thời gian tạm ngừng.</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p><img alt=\"H&amp;#224;ng loạt tour du lịch nội địa gi&amp;#225; rẻ giảm s&amp;#226;u sau dịch Covid-19\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/600/162123310254002176/2020/5/9/photo1588989878354-1588989878486300828999.jpg\" /></p>\r\n\r\n<p>Khi dịch Covid-19 c&oacute; những diễn biến khả quan tại Việt Nam, c&aacute;c đơn vị hoạt động trong lĩnh vực du lịch, lữ h&agrave;nh đ&atilde; nhanh ch&oacute;ng tung ra thị trường những tour nội địa mới với nhiều mức gi&aacute; ưu đ&atilde;i đ&aacute;p ứng đ&uacute;ng ti&ecirc;u chuẩn an to&agrave;n chống dịch.</p>\r\n\r\n<p>Đơn cử như h&atilde;ng du lịch lữ h&agrave;nh Vietravel đ&atilde; t&aacute;i khởi động thị trường du lịch nội địa bằng c&aacute;c g&oacute;i free &amp; easy (combo dịch vụ v&eacute; m&aacute;y bay v&agrave; kh&aacute;ch sạn) cho đối tượng bạn trẻ th&iacute;ch đi du lịch tự t&uacute;c.</p>\r\n\r\n<p>Với gi&aacute; tour 3 - 3,5 triệu đồng/người (chưa bao gồm v&eacute; m&aacute;y bay), ch&ugrave;m tour đặc biệt kỷ niệm 130 ng&agrave;y sinh Chủ tịch Hồ Ch&iacute; Minh 19/5 được kỳ vọng sẽ h&uacute;t kh&aacute;ch trong dịp n&agrave;y.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/9/photo-1-158898986391727342781.png\"><img alt=\" Hàng loạt tour du lịch nội địa giá rẻ giảm sâu sau dịch Covid-19  - Ảnh 1.\" src=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/9/photo-1-158898986391727342781.png\" /></a></p>\r\n\r\n<p>Nhiều tour du lịch miền T&acirc;y được Vietravel tung ra thu h&uacute;t kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, để thu h&uacute;t du kh&aacute;ch quay trở lại, những sản phẩm tour n&agrave;y được Vietravel tung ra<strong>&nbsp;</strong>c&aacute;c mức ưu đ&atilde;i 25-50% so với trước đ&acirc;y. Đơn cử như combo xe v&agrave; dịch vụ nghỉ dưỡng 3 ng&agrave;y 2 đ&ecirc;m tại c&aacute;c kh&aacute;ch sạn, resort 5 sao ở Đ&agrave; Lạt chỉ 890.000 đồng/đ&ecirc;m, ở Ph&uacute; Quốc chưa đến 2,9 triệu đồng/đ&ecirc;m.</p>\r\n\r\n<p>Ngo&agrave;i ch&ugrave;m tour lịch sử, hướng về cội nguồn, C&ocirc;ng ty du lịch Vietravel c&ograve;n đ&aacute;nh dấu sự trở lại thị trường bằng ch&ugrave;m tour k&iacute;ch cầu đến c&aacute;c khu vực miền T&acirc;y, Đ&ocirc;ng Nam Bộ, Nam Trung Bộ hay T&acirc;y Nguy&ecirc;n - nơi c&oacute; kh&iacute; hậu m&aacute;t mẻ để du kh&aacute;ch &quot;tr&aacute;nh n&oacute;ng&quot;.</p>\r\n\r\n<p>C&ograve;n Saigontourist đưa ra mức gi&aacute; ưu đ&atilde;i với c&aacute;c tour định kỳ, đồng thời tung ra ch&ugrave;m tour đặc biệt &quot;h&agrave;nh tr&igrave;nh theo ch&acirc;n B&aacute;c&quot; d&agrave;i 4 ng&agrave;y gồm Nghệ An - Ninh B&igrave;nh - Khu di t&iacute;ch đ&aacute; ch&ocirc;ng K9 - H&agrave; Nội v&agrave; Nghệ An - Vinh - H&agrave; Tĩnh - Quảng B&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, c&ocirc;ng ty C&aacute;t B&agrave; Discovery cũng thu h&uacute;t kh&aacute;ch h&agrave;ng bằng c&aacute;ch giảm gi&aacute; c&aacute;c tour du lịch cũng như gi&aacute; t&agrave;u xe xuống c&ograve;n 800.000 đồng/combo tour chưa bao gồm tiền ph&ograve;ng.</p>\r\n\r\n<p>Gi&aacute; kh&aacute;ch lẻ book tour đ&atilde; được giảm từ 800.000 đồng xuống c&ograve;n 600.000 đồng, tiền xe chuyến H&agrave; Nội &ndash; C&aacute;t B&agrave; cũng giảm chỉ c&ograve;n hơn 200.000 đồng/người.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/9/photo-1-15889898661711552028611.png\"><img alt=\" Hàng loạt tour du lịch nội địa giá rẻ giảm sâu sau dịch Covid-19  - Ảnh 2.\" src=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/9/photo-1-15889898661711552028611.png\" /></a></p>\r\n\r\n<p>Trao đổi với b&aacute;o ch&iacute;, b&agrave; Nguyễn Nguyệt V&acirc;n Khanh - ph&oacute; gi&aacute;m đốc ban tiếp thị Vietravel cho biết việc định hướng c&ocirc;ng ty khởi động tour mới sẽ b&aacute;m theo 3 xu hướng du lịch sau dịch l&agrave; du lịch ở qu&atilde;ng đường gần dưới phạm vi 300km, du lịch theo nh&oacute;m nhỏ giữa c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh v&agrave; du lịch c&aacute; nh&acirc;n.</p>\r\n\r\n<p>&quot;Ch&uacute;ng t&ocirc;i sẽ khởi động thị trường du lịch trong nước trước th&ocirc;ng qua c&aacute;c sản phẩm tour trọn g&oacute;i cho nh&oacute;m kh&aacute;ch gia đ&igrave;nh v&agrave; g&oacute;i free &amp; easy (combo dịch vụ v&eacute; m&aacute;y bay v&agrave; kh&aacute;ch sạn) cho nh&oacute;m bạn trẻ th&iacute;ch du lịch tự t&uacute;c. Hi vọng sau thời gian gi&atilde;n c&aacute;ch x&atilde; hội v&agrave; chịu nhiều sức &eacute;p bởi th&ocirc;ng tin dịch bệnh trong v&agrave; ngo&agrave;i nước, người d&acirc;n sẽ mong muốn được thư gi&atilde;n, nghỉ ngơi, t&aacute;i tạo năng lượng t&iacute;ch cực&quot;, b&agrave; V&acirc;n Khanh n&oacute;i.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ c&aacute;c tour du lịch tung ra c&aacute;c combo v&agrave; chương tr&igrave;nh ưu đ&atilde;i kh&aacute;ch h&agrave;ng sau dịch, c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng cũng c&oacute; nhiều chương tr&igrave;nh đặc biệt khi được tăng chuyến theo nhu cầu của h&agrave;nh kh&aacute;ch, bỏ quy định hạn chế tần suất bay.</p>\r\n\r\n<p>Vietnam Airlines ưu đ&atilde;i đặc biệt với gi&aacute; v&eacute; chỉ từ 199.000 đồng/chặng (tương đương 689.000 đồng/chặng bao gồm thuế, ph&iacute; tr&ecirc;n nhiều đường bay như H&agrave; Nội &ndash; TP HCM hay H&agrave; Nội &ndash; Đ&agrave; Nẵng, &aacute;p dụng cho v&eacute; khởi h&agrave;nh trong giai đoạn từ 7/5 - 30/6/2020.</p>\r\n\r\n<p>Chặng H&agrave; Nội - Quy Nhơn; TPHCM - Hải Ph&ograve;ng/Ph&uacute; Quốc/Thanh Ho&aacute;/Vinh với gi&aacute; 399.000 đồng/chiều (tương đương 909.000 đồng gồm thuế, ph&iacute;).</p>\r\n\r\n<p>Chặng H&agrave; Nội - Nha Trang với gi&aacute; 499.000 đồng/chiều (tương đương 1.019.000 đồng gồm thuế, ph&iacute;) v&agrave; H&agrave; Nội - Ph&uacute; Quốc với gi&aacute; 699.000 đồng/chiều (tương đương 1.239.000 đồng gồm thuế, ph&iacute;).</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://cafebiz.vn/hang-loat-tour-du-lich-noi-dia-gia-re-giam-sau-sau-dich-covid-19-20200509090524154.chn\">https://cafebiz.vn/hang-loat-tour-du-lich-noi-dia-gia-re-giam-sau-sau-dich-covid-19-20200509090524154.chn</a></p>', 'upload/news\\1588911533.jpeg', 1, 0, 8, 2, 'hang-loat-tour-du-lich-noi-dia-gia-re-giam-sau-sau-dich-covid-19', NULL, '2020-09-07 21:18:53', '2020-09-07 21:18:53'),
(22, 'Cuộc dịch chuyển dòng vốn đầu tư kép FDI vào Việt Nam', 7, '<h2>Tuy vốn FDI c&oacute; dấu hiệu chững lại do Covid-19, song theo c&aacute;c chuy&ecirc;n gia, Việt Nam sẽ đ&oacute;n l&agrave;n s&oacute;ng đầu tư mới sau đại dịch.</h2>', '<h2>Tuy vốn FDI c&oacute; dấu hiệu chững lại do Covid-19, song theo c&aacute;c chuy&ecirc;n gia, Việt Nam sẽ đ&oacute;n l&agrave;n s&oacute;ng đầu tư mới sau đại dịch.</h2>\r\n\r\n<ul>\r\n	<li>09-05-2020&nbsp;<a href=\"https://cafebiz.vn/23-ngay-viet-nam-khong-co-ca-mac-covid-19-trong-cong-dong-20200509082540404.chn\">23 ng&agrave;y Việt Nam kh&ocirc;ng c&oacute; ca mắc Covid-19 trong cộng đồng&nbsp;</a></li>\r\n	<li>09-05-2020&nbsp;<a href=\"https://cafebiz.vn/nghien-cuu-moi-khong-chi-phoi-virus-sars-cov-2-con-co-the-lam-ton-thuong-nao-20200509081109122.chn\">Nghi&ecirc;n cứu mới: Kh&ocirc;ng chỉ phổi, virus SARS-CoV-2 c&ograve;n c&oacute; thể l&agrave;m tổn thương...&nbsp;</a></li>\r\n	<li>09-05-2020&nbsp;<a href=\"https://cafebiz.vn/cong-dan-anh-cam-thay-cach-ly-2-tuan-o-vn-nhu-di-nghi-duong-va-lua-chon-cua-nhieu-nguoi-nuoc-ngoai-o-vn-20200509080806013.chn\">C&ocirc;ng d&acirc;n Anh cảm thấy c&aacute;ch ly 2 tuần ở VN &quot;như đi nghỉ dưỡng&quot; v&agrave; lựa chọn...&nbsp;</a></li>\r\n</ul>\r\n\r\n<p><img alt=\"Cuộc dịch chuyển d&amp;#242;ng vốn đầu tư k&amp;#233;p FDI v&amp;#224;o Việt Nam\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/600/162123310254002176/2020/5/9/photo1588987633639-15889876337701012401328.jpg\" /></p>\r\n\r\n<p>Theo số liệu mới nhất của Cục Đầu tư nước ngo&agrave;i (Bộ Kế hoạch v&agrave; Đầu tư),&nbsp;t&iacute;nh đến hết ng&agrave;y 20/42020,&nbsp;vốn đầu tư trực tiếp nước ngo&agrave;i (FDI) đạt 12,33 tỉ USD, giảm 15,5% so với c&ugrave;ng kỳ năm ngo&aacute;i. Vốn thực hiện ước đạt 5,15 tỉ USD, giảm 9,6% so với c&ugrave;ng kỳ do hoạt động sản xuất, kinh doanh bị đ&igrave;nh trệ dưới t&aacute;c động ti&ecirc;u cực của đại dịch Covid-19.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/9/photo-1-1588987623854607675658.jpg\"><img alt=\"Cuộc dịch chuyển dòng vốn đầu tư kép FDI vào Việt Nam - Ảnh 1.\" src=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/9/photo-1-1588987623854607675658.jpg\" /></a></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n		</tr>\r\n		<tr>\r\n			<td><em>Dẫn đầu c&aacute;c nh&agrave; đầu tư FDI tại Việt Nam trong 4 th&aacute;ng đầu năm 2020 l&agrave; Singapore, Th&aacute;i Lan, Nhật Bản, H&agrave;n Quốc, Trung Quốc... (Ảnh minh họa)</em></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Cục Đầu tư nước ngo&agrave;i cho rằng, vốn FDI 4 th&aacute;ng đầu năm nay sụt giảm do việc đi lại của c&aacute;c nh&agrave; đầu tư cũng như c&aacute;c quyết định đầu tư mới v&agrave; mở rộng quy m&ocirc; dự &aacute;n gặp kh&oacute; khăn v&igrave; dịch bệnh. Nguồn cung nguy&ecirc;n liệu đầu v&agrave;o phục vụ sản xuất cũng như nhu cầu sản phẩm đầu ra của c&aacute;c doanh nghiệp gặp nhiều kh&oacute; khăn. Thị trường ti&ecirc;u thụ c&oacute; xu hướng thu hẹp, nhiều đơn h&agrave;ng bị ho&atilde;n, hủy. Nhiều doanh nghiệp FDI gặp kh&oacute; khi duy tr&igrave; v&agrave; mở rộng sản xuất kinh doanh do thiếu chuy&ecirc;n gia nước ngo&agrave;i.</p>\r\n\r\n<p>Tuy vốn FDI c&oacute; dấu hiệu chững lại do Covid-19, song theo c&aacute;c chuy&ecirc;n gia, Việt Nam sẽ đ&oacute;n l&agrave;n s&oacute;ng đầu tư mới sau đại dịch. Việt Nam được cộng đồng quốc tế đ&aacute;nh gi&aacute; cao về ph&ograve;ng chống dịch, tạo niềm tin cho c&aacute;c nh&agrave; đầu tư nước ngo&agrave;i y&ecirc;n t&acirc;m đầu tư, kinh doanh. Việt Nam cũng được đ&aacute;nh gi&aacute; l&agrave; quốc gia thuộc nh&oacute;m c&aacute;c nước bị ảnh hưởng &iacute;t nhất v&agrave; dự b&aacute;o sẽ vượt qua &quot;cơn b&atilde;o&quot; suy tho&aacute;i kinh tế to&agrave;n cầu.</p>\r\n\r\n<p>GS. Nguyễn Mại, Chủ tịch Hiệp hội Doanh nghiệp (DN) đầu tư nước ngo&agrave;i cho rằng, thu h&uacute;t FDI 4 th&aacute;ng đầu năm 2020 giảm kh&ocirc;ng phải l&agrave; xu hướng m&agrave; chỉ mang t&iacute;nh tạm thời, ảnh hưởng ch&iacute;nh từ dịch Covid-19 khiến kinh tế to&agrave;n cầu tăng trưởng chậm.</p>\r\n\r\n<p>C&oacute; một số t&iacute;n hiệu lạc quan n&ecirc;n GS. Nguyễn Mại dự b&aacute;o thu h&uacute;t FDI sẽ &quot;b&ugrave;ng nổ&quot; sau dịch. T&iacute;n hiệu lạc quan, theo GS. Nguyễn Mại, đ&oacute; l&agrave; nỗ lực của Việt Nam&nbsp;chống dịch, kiểm so&aacute;t dịch tốt v&agrave; tr&aacute;nh được những cuộc phong tỏa k&eacute;o d&agrave;i. B&ecirc;n cạnh đ&oacute;, xu hướng chuyển nh&agrave; m&aacute;y ra khỏi Trung Quốc đ&atilde; ng&agrave;y một r&otilde; rệt v&agrave; Việt Nam với yếu tố t&iacute;ch cực chống dịch hiệu quả đang l&agrave; đ&iacute;ch ngắm của c&aacute;c nh&agrave; đầu tư nước ngo&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ năm 2019, nhiều tập đo&agrave;n c&ocirc;ng nghệ đ&atilde; l&ecirc;n kế hoạch rời Trung Quốc để tr&aacute;nh bị tổn thương từ cuộc chiến tranh thương mại Mỹ - Trung. C&aacute;c c&ocirc;ng ty đa quốc gia như Samsung v&agrave; LG đ&atilde; đầu tư d&acirc;y chuyển sản xuất mới tại Việt Nam thay v&igrave; Trung Quốc theo kế hoạch cũ.</p>\r\n\r\n<p>Tờ Nikkei của Nhật Bản cũng đưa tin c&aacute;c &quot;&ocirc;ng lớn&quot; như Google, Microsoft đang chuyển d&acirc;y chuyền sản xuất điện thoại, laptop từ Trung Quốc sang Việt Nam do ảnh hưởng của dịch bệnh. H&atilde;ng tr&ograve; chơi điện tử Nintendo cũng đ&atilde; chuyển một phần hoạt động sản xuất m&aacute;y chơi game Switch Lite sang Việt Nam...</p>\r\n\r\n<p>GS. Nguyễn Mại lưu &yacute;: Việt Nam mong muốn thu h&uacute;t được nhiều dự &aacute;n lớn tr&ecirc;n thế giới. Nếu chỉ thu h&uacute;t được c&aacute;c dự &aacute;n nhỏ li ti th&igrave; tham vọng trở th&agrave;nh &quot;c&ocirc;ng xưởng&quot; thế giới của Việt Nam c&oacute; thể đạt về số lượng, nhưng chất lượng kh&ocirc;ng cao. Cần thu h&uacute;t c&aacute;c dự &aacute;n FDI c&ocirc;ng nghệ cao, c&ocirc;ng nghệ mới cho tương lai.</p>\r\n\r\n<p>Xu hướng dịch chuyển vốn FDI ra khỏi Trung Quốc sang c&aacute;c nước Đ&ocirc;ng Nam &Aacute;, trong đ&oacute; c&oacute; Việt Nam, do t&aacute;c động k&eacute;p từ cuộc chiến thương mại Mỹ - Trung v&agrave; dịch Covid-19 b&ugrave;ng ph&aacute;t ng&agrave;y c&agrave;ng được thể hiện r&otilde; v&agrave; đang được đẩy nhanh. Việt Nam được xem l&agrave; một điểm đến đầy hứa hẹn từ khi l&agrave;n s&oacute;ng dịch chuyển nh&agrave; m&aacute;y khỏi Trung Quốc bắt đầu. Việc đứt g&atilde;y chuỗi cung ứng to&agrave;n cầu trong v&agrave;i th&aacute;ng qua đ&atilde; khiến nhiều quốc gia nhận thấy sự cấp b&aacute;ch của việc đa dạng h&oacute;a danh mục sản xuất, kh&ocirc;ng chỉ tập trung tại Trung Quốc.</p>\r\n\r\n<p>Theo Cục Đầu tư nước ngo&agrave;i, thu h&uacute;t FDI của Việt Nam cho thấy những t&iacute;n hiệu kh&aacute; lạc quan khi tăng dần trở lại v&agrave;o cuối năm nay v&agrave; tạo đ&agrave; cho năm 2021 do đ&oacute;n đầu d&ograve;ng vốn t&aacute;i định vị sản xuất của c&aacute;c c&ocirc;ng ty đa quốc gia. Nhiều tập đo&agrave;n lớn tr&ecirc;n thế giới đang chuẩn bị kế hoạch đầu tư tại Việt Nam nhờ sự hấp dẫn của m&ocirc;i trường v&agrave; ch&iacute;nh s&aacute;ch đầu tư của Việt Nam./.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://cafebiz.vn/cuoc-dich-chuyen-dong-von-dau-tu-kep-fdi-vao-viet-nam-20200509082752529.chn\">https://cafebiz.vn/cuoc-dich-chuyen-dong-von-dau-tu-kep-fdi-vao-viet-nam-20200509082752529.chn</a></p>', 'upload/news\\1588911605.jpeg', 1, 0, 8, 2, 'cuoc-dich-chuyen-dong-von-dau-tu-kep-fdi-vao-viet-nam', NULL, '2020-09-07 21:20:05', '2020-09-07 21:20:05'),
(23, 'Covid-19: Thế giới vượt mốc 4 triệu ca mắc, Nga thành ổ dịch lớn thứ 5 09/05/2020 08:35 AM | TH', 8, '<h2>Dịch Covid-19 đ&atilde; khiến hơn 4 triệu người tr&ecirc;n thế giới mắc bệnh, trong đ&oacute; tại những điểm n&oacute;ng mới, dịch bệnh vẫn diễn biến phức tạp.</h2>', '<p><img alt=\"Covid-19: Thế giới vượt mốc 4 triệu ca mắc, Nga th&amp;#224;nh ổ dịch lớn thứ 5\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/600/162123310254002176/2020/5/9/photo1588987573224-15889875733591272795015.jpeg\" /></p>\r\n\r\n<p>Sau khi ghi nhận th&ecirc;m 26.164 ca mắc Covid-19 v&agrave; 1.575 ca tử vong trong 24h, tổng số ca Covid-19 tại&nbsp;<strong>Mỹ&nbsp;</strong>hiện đ&atilde; l&ecirc;n đến hơn 1,3 triệu với 78.503 ca tử vong.</p>\r\n\r\n<p>Ph&aacute;t biểu tại cuộc gặp với c&aacute;c th&agrave;nh vi&ecirc;n Quốc hội thuộc đảng Cộng h&ograve;a h&ocirc;m 8/5, Tổng thống Donald Trump dự b&aacute;o sẽ c&oacute; tới 95.000 người c&oacute; thể sẽ tử vong do Covid-19 tại Mỹ.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/9/photo-1-15889875508901175989601.jpeg\"><img alt=\"Covid-19: Thế giới vượt mốc 4 triệu ca mắc, Nga thành ổ dịch lớn thứ 5 - Ảnh 1.\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/640/162123310254002176/2020/5/9/photo-1-15889875508901175989601.jpeg\" /></a></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n		</tr>\r\n		<tr>\r\n			<td>Mọi người đeo khẩu trang như một biện ph&aacute;p ph&ograve;ng ngừa dịch Covid-19 tại Quảng trường Đỏ ở Moscow, Nga ng&agrave;y 17/3/2020. Ảnh: Getty</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trong khi đ&oacute;, tại ch&acirc;u &Acirc;u, số ca tử vong h&agrave;ng ng&agrave;y tại Ph&aacute;p đ&atilde; tăng trở lại, 3 ng&agrave;y trước khi nước n&agrave;y bắt đầu dỡ bỏ lệnh phong tỏa to&agrave;n quốc trong gần 2 th&aacute;ng.</p>\r\n\r\n<p>Bộ Y tế&nbsp;<strong>Ph&aacute;p</strong>&nbsp;cho biết đ&atilde; c&oacute; th&ecirc;m 243 người chết do Covid-19 tại quốc gia n&agrave;y trong 24h qua, tăng so với con số 178 ca h&ocirc;m 7/5, n&acirc;ng tổng số ca tử vong tại Ph&aacute;p l&ecirc;n 26.230 người. Ph&aacute;p c&oacute; số ca tử vong cao thứ 5 thế giới, sau Mỹ, Anh, Italy v&agrave; T&acirc;y Ban Nha.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, số người phải điều trị trong ph&ograve;ng chăm s&oacute;c t&iacute;ch cực đ&atilde; giảm 93 ca, tương ứng với 3%, xuống c&ograve;n 2.868 ca, giảm đ&aacute;ng kể so với ng&agrave;y đạt đỉnh l&agrave; 8/4 với 7.148 ca. Ph&aacute;p hiện c&oacute; 176.079 ca mắc Covid-19.</p>\r\n\r\n<p><strong>Italy&nbsp;</strong>ng&agrave;y 9/5 đ&atilde; trở th&agrave;nh quốc gia thứ 3 tr&ecirc;n thế giới ghi nhận tổng số ca tử vong vượt mốc 30.000 người sau khi ghi nhận th&ecirc;m 243 người chết v&igrave; Covid-19 trong ng&agrave;y. Số ca mắc mới trong ng&agrave;y giảm nhẹ xuống c&ograve;n 1.327 ca so với 1.401 ca ng&agrave;y 7/5 trước đ&oacute;, n&acirc;ng tổng số ca mắc Covid-19 tại Italy l&ecirc;n 217.185. Cơ quan Bảo vệ D&acirc;n sự Italy cho biết hơn 1,6 triệu người đ&atilde; được x&eacute;t nghiệm Covid-19 trong tổng số khoảng 60 triệu d&acirc;n tại quốc gia n&agrave;y.</p>\r\n\r\n<p>Vương quốc&nbsp;<strong>Anh</strong>&nbsp;tiếp tục ghi nhận số ca tử vong trong ng&agrave;y cao nhất ch&acirc;u &Acirc;u với 626 ca. Nước n&agrave;y hiện l&agrave; quốc gia c&oacute; số ca tử vong cao thứ 2 thế giới, chỉ sau Mỹ với 31.241 trường hợp. Tổng số ca mắc ở Anh hiện l&agrave; 211.364 sau khi ghi nhận th&ecirc;m 4.649 ca mắc mới trong 24h. Thủ tướng Boris Johnson sẽ th&ocirc;ng b&aacute;o về c&aacute;c kế hoạch của &ocirc;ng với nước Anh trong một b&agrave;i ph&aacute;t biểu tr&ecirc;n truyền h&igrave;nh tối 10/5 song một trong những Bộ trưởng của &ocirc;ng khẳng định h&ocirc;m 8/5 rằng sẽ kh&ocirc;ng c&oacute; thay đổi lớn n&agrave;o.</p>\r\n\r\n<p>&quot;Ch&uacute;ng ta phải thực tế bởi sẽ kh&ocirc;ng c&oacute; bất kỳ sự thay đổi mạnh mẽ n&agrave;o chỉ qua một đ&ecirc;m&quot;, Bộ trưởng M&ocirc;i trường George Eustice nhận định, đồng thời cho biết: &quot;Ch&uacute;ng ta sẽ sống chung với loại virus n&agrave;y một thời gian nữa v&agrave; v&igrave; vậy, một điều quan trọng l&agrave; cần phải tr&aacute;nh dịch bệnh đạt đỉnh lần thứ 2 bởi điều đ&oacute; sẽ khiến Cơ quan Y tế Quốc gia bị qu&aacute; tải&quot;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với số ca mắc gia tăng nhanh ch&oacute;ng,&nbsp;<strong>Nga&nbsp;</strong>đ&atilde; vượt Ph&aacute;p v&agrave; Đức trở th&agrave;nh ổ dịch lớn thứ 5 thế giới với 187.859 ca. Nga cũng ghi nhận th&ecirc;m 98 ca tử vong trong 24h, n&acirc;ng tổng số người chết v&igrave; Covid-19 tại quốc gia n&agrave;y l&ecirc;n 1.723.</p>\r\n\r\n<p>Gi&aacute;m đốc điều h&agrave;nh chương tr&igrave;nh khẩn cấp của Tổ chức Y tế Thế giới (WHO) Mike Ryan nhận định: &quot;Nga đang ở trong một giai đoạn kh&aacute;c của đại dịch v&agrave; c&oacute; thể học hỏi từ những b&agrave;i học đắt gi&aacute; ở ch&acirc;u &Aacute;, Bắc Mỹ v&agrave; T&acirc;y &Acirc;u&quot;. &Ocirc;ng Ryan cũng đề nghị Nga tiếp tục thực hiện &quot;c&aacute;c biện ph&aacute;p gi&atilde;n c&aacute;ch x&atilde; hội v&agrave; y tế cộng đồng tr&ecirc;n quy m&ocirc; rất lớn&quot; để ngăn chặn sự b&ugrave;ng ph&aacute;t của đại dịch.</p>\r\n\r\n<p>Người đứng đầu chương tr&igrave;nh khẩn cấp của WHO cho biết Nga x&eacute;t nghiệm được cho 430.000 người với tỷ lệ số người dương t&iacute;nh với SARS-CoV-2 l&agrave; gần 3,6%, vẫn tương đối thấp so với c&aacute;c quốc gia kh&aacute;c.</p>\r\n\r\n<p>Trong khi đ&oacute;, Tổng thống Brazil Bolsonaro khẳng định hồi đầu tuần rằng &ocirc;ng tin l&agrave; &quot;điều tồi tệ nhất đ&atilde; đi qua&quot;, mặc d&ugrave; đến nay&nbsp;<strong>Brazil&nbsp;</strong>đ&atilde; nằm trong top 10 quốc gia c&oacute; số ca mắc Covid-19 cao nhất thế giới với 145.328 ca mắc v&agrave; 9.897 ca tử vong. Người ph&aacute;t ng&ocirc;n của &ocirc;ng Bolsanaro cũng được x&aacute;c nhận đ&atilde; dương t&iacute;nh với SARS-CoV-2. Cựu Bộ trưởng Y tế Brazil Luiz Henrique Mandetta nhận định &quot;những th&aacute;ng kh&oacute; khăn nhất&quot; c&oacute; thể l&agrave; th&aacute;ng 5 v&agrave; th&aacute;ng 6 trong 1 cuộc phỏng vấn hồi th&aacute;ng trước, 4 ng&agrave;y trước khi &ocirc;ng bị Tổng thống Bolsonaro sa thải v&igrave; bất đồng quan điểm trong chiến lược chống Covid-19 tại quốc gia n&agrave;y.</p>\r\n\r\n<p>Sau khi chứng kiến số ca mắc tăng mạnh trong l&agrave;n s&oacute;ng b&ugrave;ng ph&aacute;t dịch bệnh lần thứ 2, số ca mắc mới trong ng&agrave;y ở Singapore hiện đ&atilde; giảm xuống dưới mức 1.000. Trong 24h qua, nước n&agrave;y ghi nhận th&ecirc;m 768 ca mắc Covid-19, n&acirc;ng tổng số ca l&ecirc;n 21.707 với 20 trường hợp tử vong.</p>\r\n\r\n<p>Indonesia v&agrave; Philippines vẫn ghi nhận số ca mắc mới ở mức 3 con số. Cụ thể, Indonesia c&oacute; th&ecirc;m 336 ca mắc Covid-19, n&acirc;ng tổng số ca tại nước n&agrave;y l&ecirc;n 13.112 với 943 trường hợp tử vong. Philippines cũng c&oacute; th&ecirc;m 120 ca mắc mới v&agrave; 11 ca tử vong, n&acirc;ng tổng số ca tại nước n&agrave;y l&ecirc;n 10.463 với 696 ca tử vong.</p>\r\n\r\n<p>Th&aacute;i Lan chỉ ghi nhận số ca mắc mới ở mức 1 con số l&agrave; 8 người, n&acirc;ng tổng số ca tại nước n&agrave;y c&aacute;n mốc 3.000, trong đ&oacute; c&oacute; 55 trường hợp tử vong.</p>\r\n\r\n<p>Tổng số ca mắc Covid-19 tr&ecirc;n to&agrave;n cầu hiện đ&atilde; vượt mốc 4 triệu với gần 276.000 người tử vong. &Ocirc;ng Mike Ryan nhận định cuộc sống của mọi người tr&ecirc;n khắp thế giới sẽ đối mặt với &quot;sự thay đổi đ&aacute;ng kể&quot; cho đến khi vaccine ngừa Covid-19 được ph&aacute;t triển. &Ocirc;ng Ryan cho biết WHO đ&atilde; cảm nhận được những t&aacute;c động của c&aacute;c biện ph&aacute;p gi&atilde;n c&aacute;ch tương tự như phần c&ograve;n lại của thế giới.</p>\r\n\r\n<p>&quot;Ch&uacute;ng t&ocirc;i đ&atilde; kh&ocirc;ng bắt tay v&agrave; &ocirc;m những người bạn của m&igrave;nh trong 18 th&aacute;ng&quot;, nh&agrave; chức tr&aacute;ch WHO cho hay, song cũng khẳng định rằng &quot;Cuộc sống l&agrave; cuộc sống. Kh&ocirc;ng c&oacute; chỗ cho sự rủi ro&quot;./.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://cafebiz.vn/covid-19-the-gioi-vuot-moc-4-trieu-ca-mac-nga-thanh-o-dich-lon-thu-5-20200509082650357.chn\">https://cafebiz.vn/covid-19-the-gioi-vuot-moc-4-trieu-ca-mac-nga-thanh-o-dich-lon-thu-5-20200509082650357.chn</a></p>', 'upload/news\\1588911714.jpeg', 1, 0, 8, 2, 'covid-19-the-gioi-vuot-moc-4-trieu-ca-mac-nga-thanh-o-dich-lon-thu-5-09052020-0835-am-th', NULL, '2020-09-07 21:21:54', '2020-09-07 21:21:54'),
(24, 'Những hình ảnh đầu tiên về nơi ở của nhà Sussex tại Mỹ: Không hề tầm thường, nhân vật đứng đằng sau hỗ trợ cũng rất thế lực', 9, '<h2>Th&ocirc;ng tin về nơi ở sang chảnh của nh&agrave; Sussex tại Mỹ đang tr&agrave;n ngập tr&ecirc;n khắp c&aacute;c mặt b&aacute;o, thu h&uacute;t sự ch&uacute; &yacute; của dư luận.</h2>', '<h2>Th&ocirc;ng tin về nơi ở sang chảnh của nh&agrave; Sussex tại Mỹ đang tr&agrave;n ngập tr&ecirc;n khắp c&aacute;c mặt b&aacute;o, thu h&uacute;t sự ch&uacute; &yacute; của dư luận.</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p><img alt=\"Những h&amp;#236;nh ảnh đầu ti&amp;#234;n về nơi ở của nh&amp;#224; Sussex tại Mỹ: Kh&amp;#244;ng hề tầm thường, nh&amp;#226;n vật đứng đằng sau hỗ trợ cũng rất thế lực\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/600/162123310254002176/2020/5/9/photo1588987434169-1588987434338761960540.jpg\" /></p>\r\n\r\n<p>Theo th&ocirc;ng tin độc quyền từ DailyMailTV, kể từ khi chuyển đến Mỹ hồi cuối th&aacute;ng 3, nh&agrave; Sussex đ&atilde; ẩn n&aacute;u tại biệt thự trị gi&aacute; 18,5 triệu USD (hơn 400 tỷ đồng) thuộc về &ocirc;ng tr&ugrave;m Hollywood Tyler Perry qua sự giới thiệu của người bạn chung, nữ ho&agrave;ng truyền th&ocirc;ng Oprah Winfrey.</p>\r\n\r\n<p>Biệt thự mang phong c&aacute;ch Tuscan của Perry c&oacute; 8 ph&ograve;ng ngủ, 12 ph&ograve;ng tắm, nằm tr&ecirc;n một ngọn đồi rộng gần 9 ha được bảo vệ cực kỳ nghi&ecirc;m ngặt trong khu Beverly Ridge Estates. Từ s&acirc;n sau của căn biệt thự c&oacute; thể nh&igrave;n bao qu&aacute;t cả th&agrave;nh phố Los Angeles. Hiện kh&ocirc;ng r&otilde; liệu vợ chồng Harry - Meghan Markle đang thu&ecirc; bất động sản của Perry hay ở đ&oacute; với tư c&aacute;ch kh&aacute;ch mời. Chỉ biết rằng kh&ocirc;ng c&oacute; hồ sơ n&agrave;o ghi nhận biệt thự n&agrave;y đ&atilde; được b&aacute;n.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/9/photo-1-1588987427616614283580.jpg\"><img alt=\"Những hình ảnh đầu tiên về nơi ở của nhà Sussex tại Mỹ: Không hề tầm thường, nhân vật đứng đằng sau hỗ trợ cũng rất thế lực  - Ảnh 1.\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/640/162123310254002176/2020/5/9/photo-1-1588987427616614283580.jpg\" /></a></p>\r\n\r\n<p>Biệt thự xa xỉ nơi vợ chồng Harry - Meghan c&ugrave;ng con trai đang sống kể từ khi chuyển đến Mỹ.</p>\r\n\r\n<p>Trong khi đ&oacute;, nh&agrave; Sussex chưa bao giờ xuất hiện c&ugrave;ng Perry trước c&ocirc;ng ch&uacute;ng. Tại Mỹ, cặp đ&ocirc;i n&agrave;y cũng chỉ c&oacute; v&agrave;i lần lộ mặt sau khi chuyển đến Los Angeles như đi ph&aacute;t thức ăn từ thiện hay dắt ch&oacute; cưng đi dạo. Tuy nhi&ecirc;n, cả hai b&ecirc;n đều coi b&agrave; ho&agrave;ng truyền th&ocirc;ng Winfrey l&agrave; cố vấn ở Hollywood.</p>\r\n\r\n<p>C&ocirc;ng tước v&agrave; Nữ c&ocirc;ng tước xứ Sussex hiện hợp t&aacute;c với Winfrey trong một bộ phim t&agrave;i liệu. C&ograve;n Perry từng hợp t&aacute;c với k&ecirc;nh truyền h&igrave;nh OWN của Winfrey. Oprah Winfrey c&ograve;n l&agrave; mẹ đỡ đầu cho cậu con trai 5 tuổi của Perry - Aman - v&agrave; dự tiệc rửa tội của cậu b&eacute; v&agrave;o năm 2015 tại ch&iacute;nh ng&ocirc;i nh&agrave; Harry - Meghan đang ở.</p>\r\n\r\n<p>Một nguồn tin cho biết: &quot;<em>Meghan v&agrave; Harry cực kỳ cẩn thận để giữ b&iacute; mật chỗ ở của họ tại Los Angeles. &Ecirc;kip đ&atilde; gi&uacute;p họ chọn địa điểm một c&aacute;ch kh&ocirc;n ngoan. Beverly Ridge c&oacute; cổng bảo vệ ri&ecirc;ng v&agrave; lối v&agrave;o nh&agrave; của Perry cũng được một đội an ninh theo d&otilde;i s&aacute;t sao</em>&quot;.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/9/photo-1-15889874298731653935482.jpg\"><img alt=\"Những hình ảnh đầu tiên về nơi ở của nhà Sussex tại Mỹ: Không hề tầm thường, nhân vật đứng đằng sau hỗ trợ cũng rất thế lực  - Ảnh 2.\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/640/162123310254002176/2020/5/9/photo-1-15889874298731653935482.jpg\" /></a></p>\r\n\r\n<p>Tyler Perry tại biệt thự ở Beverly (tr&aacute;i) v&agrave; từng mời cựu tổng thống Mỹ Obama đến chơi nh&agrave; v&agrave;o năm ngo&aacute;i (phải).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/9/photo-2-1588987430917271532251.jpg\"><img alt=\"Những hình ảnh đầu tiên về nơi ở của nhà Sussex tại Mỹ: Không hề tầm thường, nhân vật đứng đằng sau hỗ trợ cũng rất thế lực  - Ảnh 3.\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/640/162123310254002176/2020/5/9/photo-2-1588987430917271532251.jpg\" /></a></p>\r\n\r\n<p>H&igrave;nh ảnh hiếm hoi của vợ chồng Meghan Markle xuất hiện tr&ecirc;n đường phố Mỹ.</p>\r\n\r\n<p>Biệt thự n&agrave;y được đ&aacute;nh gi&aacute; l&agrave; nơi ở qu&aacute; ho&agrave;n hảo để tr&aacute;nh bị d&ograve;m ng&oacute;. H&agrave;ng x&oacute;m chủ yếu l&agrave; c&aacute;c đại gia kỳ cựu v&agrave; c&aacute;c nh&agrave; kinh doanh gi&agrave;u c&oacute;. N&oacute; được đ&aacute;nh gi&aacute; l&agrave; tọa lạc tại vị tr&iacute; đẹp nhất v&agrave; được săn đ&oacute;n nhất ở Los Angeles.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, khu vực n&agrave;y nằm gần s&acirc;n bay tư nh&acirc;n Van Nuys v&agrave; một số trường tư thục s&aacute;ng gi&aacute; nhất ở Westside, Los Angeles. Đại l&yacute; bất động sản h&agrave;ng đầu Hollywood, Rochelle Maize, tiết lộ: &quot;<em>Beverly Ridge chỉ c&oacute; 14 ng&ocirc;i nh&agrave; nhưng đều uy t&iacute;n v&agrave; sang trọng. Kh&ocirc;ng c&oacute; nhiều nh&agrave; cho thu&ecirc; ở đ&oacute; v&igrave; số lượng bất động sản hạn chế&quot;.</em></p>\r\n\r\n<p>Tyler Perry x&acirc;y căn biệt thự n&agrave;y sau khi mua khu đất với gi&aacute; 4,3 triệu USD v&agrave;o năm 2004. L&agrave; một người đa t&agrave;i l&agrave;m diễn vi&ecirc;n, nh&agrave; văn ki&ecirc;m đạo diễn v&agrave; nh&agrave; sản xuất, Perry hiện được cho l&agrave; c&oacute; khối t&agrave;i sản khổng lồ trị gi&aacute; 600 triệu USD. Được biết, Perry sinh ra ở New Orleans v&agrave; chủ yếu sống, l&agrave;m việc tại Atlanta, điều h&agrave;nh c&ocirc;ng ty sản xuất phim Tyler Perry Studios.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/9/photo-3-1588987430373549973266.jpg\"><img alt=\"Những hình ảnh đầu tiên về nơi ở của nhà Sussex tại Mỹ: Không hề tầm thường, nhân vật đứng đằng sau hỗ trợ cũng rất thế lực  - Ảnh 4.\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/640/162123310254002176/2020/5/9/photo-3-1588987430373549973266.jpg\" /></a></p>\r\n\r\n<p>Căn biệt thự nằm ho&agrave;n to&agrave;n biệt lập với thế giới b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<p>Nh&agrave; của Perry cũng l&agrave; nơi vợ chồng Harry - Meghan tổ chức sinh nhật 1 tuổi cho b&eacute; Archie h&ocirc;m 6/5. Cũng trong ng&agrave;y h&ocirc;m đ&oacute;, nh&agrave; Sussex đ&atilde; chia sẻ video Meghan đọc s&aacute;ch cho Archie - được quay bởi Harry từ tuần trước - l&ecirc;n t&agrave;i khoản của tổ chức từ thiện Save the Children nhằm g&acirc;y quỹ chống Covid-19.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://cafebiz.vn/nhung-hinh-anh-dau-tien-ve-noi-o-cua-nha-sussex-tai-my-khong-he-tam-thuong-nhan-vat-dung-dang-sau-ho-tro-cung-rat-the-luc-20200509082501404.chn\">https://cafebiz.vn/nhung-hinh-anh-dau-tien-ve-noi-o-cua-nha-sussex-tai-my-khong-he-tam-thuong-nhan-vat-dung-dang-sau-ho-tro-cung-rat-the-luc-20200509082501404.chn</a></p>', 'upload/news\\1588911808.jpeg', 1, 0, 7, 4, 'nhung-hinh-anh-dau-tien-ve-noi-o-cua-nha-sussex-tai-my-khong-he-tam-thuong-nhan-vat-dung-dang-sau-ho-tro-cung-rat-the-luc', NULL, '2020-09-07 21:23:28', '2020-09-07 21:23:28');
INSERT INTO `news` (`id`, `news_title`, `author_id`, `news_summary`, `news_content`, `news_image`, `news_featured`, `news_view`, `tag_id`, `category_id`, `news_slug`, `deleted_at`, `created_at`, `updated_at`) VALUES
(25, 'Cỗ máy cho vay giải ngân sau 3 phút của Jack Ma: Dự kiến bơm hàng trăm tỷ USD sau Covid-19, biến cả chục triệu doanh nghiệp vừa và nhỏ thành con nợ', 9, '<p>Cỗ m&aacute;y cho vay giải ng&acirc;n sau 3 ph&uacute;t của Jack Ma: Dự kiến bơm h&agrave;ng trăm tỷ USD sau Covid-19, biến cả chục triệu doanh nghiệp vừa v&agrave; nhỏ th&agrave;nh con nợ</p>', '<h2>Cỗ m&aacute;y cho vay giải ng&acirc;n sau 3 ph&uacute;t của Jack Ma đang &acirc;m mưu biến h&agrave;ng chục triệu doanh nghiệp Trung Quốc th&agrave;nh con nợ sau Covid-19.</h2>\r\n\r\n<ul>\r\n	<li>23-04-2020&nbsp;<a href=\"https://cafebiz.vn/bat-tay-voi-mark-zuckerberg-ty-phu-an-do-gianh-lai-ngoi-giau-nhat-chau-a-cua-jack-ma-20200423145811468.chn\">Bắt tay với Mark Zuckerberg, tỷ ph&uacute; Ấn Độ gi&agrave;nh lại ng&ocirc;i gi&agrave;u nhất ch&acirc;u &Aacute;...&nbsp;</a></li>\r\n	<li>22-04-2020&nbsp;<a href=\"https://cafebiz.vn/chu-tich-35-tuoi-cua-nen-tang-tmall-va-taobao-dinh-nghi-an-ngoai-tinh-ty-phu-jack-ma-va-alibaba-bat-ngo-bi-lien-luy-20200422151423862.chn\">Chủ tịch 35 tuổi của nền tảng Tmall v&agrave; Taobao d&iacute;nh nghi &aacute;n ngoại t&igrave;nh, tỷ...&nbsp;</a></li>\r\n	<li>15-04-2020&nbsp;<a href=\"https://cafebiz.vn/bai-kiem-tra-toan-1-diem-cua-jack-ma-va-cach-nguoi-sang-lap-tap-doan-alibaba-dap-tra-sau-ca-chuc-nam-20200415211148893.chn\">B&agrave;i kiểm tra To&aacute;n 1 điểm của Jack Ma v&agrave; c&aacute;ch người s&aacute;ng lập tập đo&agrave;n...&nbsp;</a></li>\r\n</ul>\r\n\r\n<p><img alt=\"Cỗ m&amp;#225;y cho vay giải ng&amp;#226;n sau 3 ph&amp;#250;t của Jack Ma: Dự kiến bơm h&amp;#224;ng trăm tỷ USD sau Covid-19, biến cả chục triệu doanh nghiệp vừa v&amp;#224; nhỏ th&amp;#224;nh con nợ\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/600/162123310254002176/2020/5/6/photo1588733506035-15887335061662133183347.jpg\" /></p>\r\n\r\n<p>Khi nền kinh tế Trung Quốc sụt giảm mạnh v&agrave; h&agrave;ng triệu doanh nghiệp nhỏ hết sạch tiền mặt, nền tảng cho vay trực tuyến được Jack Ma đầu tư bắt đầu bật &quot;chế độ khủng hoảng&quot;.</p>\r\n\r\n<p>Khoảng giữa th&aacute;ng 2, tức l&agrave; đỉnh dịch Covid-19 ở Trung Quốc, MYbank hiểu rằng họ cần phải đưa ra quyết định nhanh ch&oacute;ng xem liệu c&oacute; n&ecirc;n giảm hay tiếp tục tăng cho vay. Sau 2 ng&agrave;y tất bật với những email v&agrave; cuộc gọi trực tuyến, c&aacute;c l&atilde;nh đạo c&ocirc;ng ty đ&atilde; đồng &yacute; với 25 ng&acirc;n h&agrave;ng đối t&aacute;c về một chiến lược c&oacute; rủi ro rất lớn: Giảm l&atilde;i suất v&agrave; mở điều kiện tiếp cận t&iacute;n dụng ở mức chưa từng c&oacute; trước đ&acirc;y.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/6/800x-1-15887333252392070760961.jpg\"><img alt=\"Cỗ máy cho vay giải ngân sau 3 phút của Jack Ma: Dự kiến bơm hàng trăm tỷ USD sau Covid-19, biến cả chục triệu doanh nghiệp vừa và nhỏ thành con nợ - Ảnh 1.\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/640/162123310254002176/2020/5/6/800x-1-15887333252392070760961.jpg\" /></a></p>\r\n\r\n<p>Chủ tịch Jin Xiaolong.</p>\r\n\r\n<p>MYbank hiện đang chuẩn bị ph&aacute;t h&agrave;nh kỷ lục 2 ngh&igrave;n tỷ NDT (282 tỷ USD) gi&aacute; trị những khoản vay mới cho c&aacute;c doanh nghiệp vừa v&agrave; nhỏ trong năm nay, tăng gần 18% so với năm 2019. &quot;Đối mặt với dịch virus, ch&uacute;ng t&ocirc;i kh&ocirc;ng hạ mục ti&ecirc;u kinh doanh&quot;, Jin Xiaolong &ndash; Chủ tịch c&ocirc;ng ty n&oacute;i trong b&agrave;i phỏng vấn với tờ Bloomberg.</p>\r\n\r\n<p>D&ugrave; việc tăng cho vay của MYbank ph&ugrave; hợp với nỗ lực của ch&iacute;nh phủ Trung Quốc để kh&ocirc;i phục nền kinh tế lớn thứ 2 thế giới từ sự sụt giảm của đại dịch, nhưng r&otilde; r&agrave;ng việc n&agrave;y đi k&egrave;m với h&agrave;ng loạt rủi ro với ch&iacute;nh bản th&acirc;n MYbank v&agrave; cổ đ&ocirc;ng lớn nhất của họ l&agrave; chi nh&aacute;nh t&agrave;i ch&iacute;nh Ant Financial của tỷ ph&uacute; Jack Ma.</p>\r\n\r\n<p>C&aacute;c chuy&ecirc;n gia ph&acirc;n t&iacute;ch th&igrave; nhận định rằng, cuộc khủng hoảng năm nay đ&aacute;nh dấu ph&eacute;p thử lớn nhất với thuật to&aacute;n cho vay của MYbank. Ng&acirc;n h&agrave;ng n&agrave;y vốn hoạt động dựa tr&ecirc;n việc thanh to&aacute;n theo thời gian thực v&agrave; đa phần kh&aacute;ch h&agrave;ng được thẩm định đều c&oacute; t&agrave;i sản thế chấp rất &iacute;t hoặc lịch sử t&iacute;n dụng kh&ocirc;ng tốt. Người vay chỉ cần v&agrave;i thao t&aacute;c tr&ecirc;n smartphone l&agrave; sẽ nhận được tiền gần như ngay lập tức nếu được chấp thuận. To&agrave;n bộ qu&aacute; tr&igrave;nh chỉ mất 3 ph&uacute;t, kh&ocirc;ng cần nh&acirc;n vi&ecirc;n thực v&agrave; tỷ lệ vỡ nợ đến nay chỉ l&agrave; 1%.</p>\r\n\r\n<p>Nếu việc đẩy cho vay g&acirc;y ra tỷ lệ vỡ nợ tăng, mức gi&aacute; trị thị trường 150 tỷ USD của Ant Financial chắc chắn sẽ bị ảnh hưởng v&agrave; g&acirc;y tổn hại đến c&aacute;c kế hoạch cho một vụ IPO.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;M&ocirc; h&igrave;nh n&agrave;y chưa được thử nghiệm trong v&ograve;ng t&iacute;n dụng đầy đủ&quot;, theo Wang Haimei &ndash; một chuy&ecirc;n gia ph&acirc;n t&iacute;ch tại Thượng Hải.</p>\r\n\r\n<p>MYbank l&agrave; bộ phận chủ đạo của chiến lược ng&acirc;n h&agrave;ng mở của Ant &ndash; gồm một nền tảng cho vay ti&ecirc;u d&ugrave;ng v&agrave; một tập đo&agrave;n c&ocirc;ng nghệ chuy&ecirc;n b&aacute;n dịch vụ điện to&aacute;n đ&aacute;m m&acirc;y v&agrave; những cơ sở hạ tầng kh&aacute;c cho người cho vay. Ant dự kiến tạo ra 65% doanh thu từ những dịch vụ n&agrave;y trong năm 2021, tăng so với mức 35% v&agrave;o năm 2017.</p>\r\n\r\n<p>&quot;Một v&agrave;i oanh nghiệp nhỏ đang gặp kh&oacute; khăn v&agrave; tỷ lệ trả lại c&aacute;c khoản vay kh&ocirc;ng cao như trước&quot;, Jin n&oacute;i v&agrave; bổ sung th&ecirc;m rằng &quot;nhưng chất lượng t&iacute;n dụng trong th&aacute;ng 2 v&agrave; 3 phần lớn tốt&quot;.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/6/-1588733537434870643328.jpg\"><img alt=\"Cỗ máy cho vay giải ngân sau 3 phút của Jack Ma: Dự kiến bơm hàng trăm tỷ USD sau Covid-19, biến cả chục triệu doanh nghiệp vừa và nhỏ thành con nợ - Ảnh 2.\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/640/162123310254002176/2020/5/6/-1588733537434870643328.jpg\" /></a></p>\r\n\r\n<p>Mỗi năm, MYbank thu h&uacute;t th&ecirc;m h&agrave;ng chục triệu kh&aacute;ch h&agrave;ng mới.</p>\r\n\r\n<p>Tỷ lệ nợ xấu ở hệ thống ng&acirc;n h&agrave;ng Trung Quốc tăng từ mức 0,06% từ 3 th&aacute;ng trước th&agrave;nh 2,04% trong th&aacute;ng 3 d&ugrave; c&aacute;c nh&agrave; băng đ&atilde; tạm ho&atilde;n thời gian thanh to&aacute;n với nhiều khoản vay hoặc tiếp tục cung ứng ra tổng cộng 1,5 ngh&igrave;n tỷ NDT gi&aacute; trị c&aacute;c khoản vay. China Merchant &ndash; một trong những nh&agrave; băng lớn nhất Trung Quốc cho c&aacute;c doanh nghiệp nhỏ chứng kiến những khoản vay qu&aacute; hạn thanh to&aacute;n gần gấp đ&ocirc;i so với cuối năm ngo&aacute;i l&ecirc;n mức 6,2 tỷ NDT trong qu&yacute; đầu ti&ecirc;n.</p>\r\n\r\n<p>D&ugrave; vậy, để đ&aacute;nh gi&aacute; việc mất khả năng thanh to&aacute;n liệu c&oacute; trở th&agrave;nh vấn đề lớn hơn c&ograve;n phải phụ thuộc v&agrave;o việc nền kinh tế của Trung Quốc c&oacute; sớm hồi phục từ mức 6,8% trong qu&yacute; đầu ti&ecirc;n hay kh&ocirc;ng. Sự sụt giảm nhu cầu to&agrave;n cầu vẫn l&agrave; vấn đề nan giải trong nhiều th&aacute;ng tới nhưng Jin đ&atilde; thấy những t&iacute;n hiệu lạc quan khi c&aacute;c lệnh phong tỏa đ&atilde; dần được dỡ bỏ.</p>\r\n\r\n<p>&quot;C&aacute;c doanh nghiệp phục hồi v&agrave;o th&aacute;ng 3. Ch&uacute;ng t&ocirc;i tin rằng c&oacute; thể ph&aacute;t h&agrave;nh hơn 2 ngh&igrave;n tỷ NDT (hơn 280 tỷ USD) gi&aacute; trị c&aacute;c khoản vay trong năm nay&quot;.</p>', 'upload/news\\1588911927.jpeg', 1, 0, 8, 2, 'co-may-cho-vay-giai-ngan-sau-3-phut-cua-jack-ma-du-kien-bom-hang-tram-ty-usd-sau-covid-19-bien-ca-chuc-trieu-doanh-nghiep-vua-va-nho-thanh-con-no', NULL, '2020-09-07 21:25:27', '2020-09-11 18:18:31'),
(26, 'Tái khởi động kích cầu du lịch: Dân hưởng lợi', 8, '<h2>Dự kiến trở lại từ th&aacute;ng 5, chương tr&igrave;nh k&iacute;ch cầu du lịch do Hiệp hội Du lịch chủ tr&igrave; c&oacute; nhiều ưu đ&atilde;i về gi&aacute; v&agrave; chất lượng sản phẩm.</h2>', '<p><img alt=\"T&amp;#225;i khởi động k&amp;#237;ch cầu du lịch: D&amp;#226;n hưởng lợi\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/600/162123310254002176/2020/5/9/photo1588985960129-15889859604001095717927.jpg\" /></p>\r\n\r\n<p><strong>K&Iacute;CH CẦU TRỞ LẠI</strong></p>\r\n\r\n<p>Hiệp hội Du lịch (HHDL) vừa họp trực tuyến với một loạt hiệp hội địa phương, Hiệp hội Lữ h&agrave;nh, kh&aacute;ch sạn, đầu bếp, hướng dẫn vi&ecirc;n v&agrave; nhiều doanh nghiệp du lịch kh&aacute;c về chương tr&igrave;nh k&iacute;ch cầu người Việt du lịch trong nước. Dự kiến từ giữa th&aacute;ng 5, Hiệp hội triển khai k&iacute;ch cầu to&agrave;n quốc, kh&ocirc;ng g&oacute;i gọn trong li&ecirc;n minh k&iacute;ch cầu du lịch 4 tỉnh th&agrave;nh như thời đầu dịch bệnh.</p>\r\n\r\n<p>Chiến lược lần n&agrave;y hướng tới tăng t&iacute;nh li&ecirc;n kết v&ugrave;ng miền. Đối với ph&iacute;a Nam, trung t&acirc;m du lịch TPHCM tăng li&ecirc;n kết với c&aacute;c tỉnh ĐBSCL, c&aacute;c tỉnh miền Đ&ocirc;ng Nam bộ. Ở ph&iacute;a Bắc, H&agrave; Nội l&agrave; trung t&acirc;m. Hiệp hội chủ tr&igrave; v&agrave; phối hợp địa phương khảo s&aacute;t triển khai k&iacute;ch cầu tại c&aacute;c tỉnh ven biển như: H&agrave; Tĩnh, Nghệ An, Thanh H&oacute;a, Hải Ph&ograve;ng, Quảng Ninh v&agrave; c&aacute;c tỉnh Bắc bộ (Ninh B&igrave;nh, Th&aacute;i B&igrave;nh, Nam Định&hellip;).</p>\r\n\r\n<p>Du lịch T&acirc;y Bắc v&agrave; v&ograve;ng cung Đ&ocirc;ng Bắc (H&ograve;a B&igrave;nh, Sơn La, Điện Bi&ecirc;n, L&agrave;o Cai, Lạng Sơn, Cao Bằng, Bắc Kạn, Th&aacute;i Nguy&ecirc;n, Tuy&ecirc;n Quang, H&agrave; Giang) cần được quan t&acirc;m hơn nữa. Trong đ&oacute;, khu vực c&oacute; bản sắc văn h&oacute;a d&acirc;n tộc độc đ&aacute;o như H&agrave; Giang, Cao Bằng c&oacute; hai c&ocirc;ng vi&ecirc;n địa chất to&agrave;n cầu c&oacute; thể tạo ra sản phẩm đặc trưng kh&aacute;c biệt.</p>\r\n\r\n<p>Huế-Đ&agrave; Nẵng-Quảng Nam l&agrave; trung t&acirc;m du lịch ở miền Trung, kết nối với c&aacute;c địa phương c&ograve;n lại. Bốn tỉnh T&acirc;y Nguy&ecirc;n- Nam Trung bộ tiếp tục giữ chiến lược li&ecirc;n minh k&iacute;ch cầu như đ&atilde; đưa ra trong đợt dịch vừa rồi. Sản phẩm li&ecirc;n kết giữa Gia Lai, Đắk Lắk, B&igrave;nh Định, Ph&uacute; Y&ecirc;n tạo hứng th&uacute; cho kh&aacute;ch h&agrave;ng, nay c&oacute; thể mở rộng ra th&ecirc;m Kon Tum v&agrave; Đắk N&ocirc;ng.</p>\r\n\r\n<p><strong>NHIỀU ƯU &ETH;&Atilde;I</strong></p>\r\n\r\n<p>Hiệp hội Du lịch Việt Nam khuyến kh&iacute;ch c&aacute;c địa phương chủ động x&acirc;y dựng chương tr&igrave;nh ph&ugrave; hợp. &Ocirc;ng B&ugrave;i Văn Mạnh, Ph&oacute; Gi&aacute;m đốc Sở Du lịch Ninh B&igrave;nh cho biết, doanh nghiệp hưởng ứng tuy nhi&ecirc;n Sở v&agrave; doanh nghiệp đang c&acirc;n nhắc mức độ, thời gian ph&ugrave; hợp để tung g&oacute;i ưu đ&atilde;i. &ldquo;Đang m&ugrave;a thấp điểm của du lịch Ninh B&igrave;nh, cho n&ecirc;n ch&uacute;ng t&ocirc;i dự kiến l&ugrave;i chương tr&igrave;nh tới cuối th&aacute;ng 8, đầu th&aacute;ng 9&rdquo;, &ocirc;ng Mạnh n&oacute;i. Dịp lễ vừa rồi, khoảng hơn 44 ngh&igrave;n lượt kh&aacute;ch tới Ninh B&igrave;nh.</p>\r\n\r\n<p>&ldquo;Chương tr&igrave;nh lần n&agrave;y kh&ocirc;ng chỉ tập trung k&iacute;ch cầu đường h&agrave;ng kh&ocirc;ng, c&ograve;n cả đường bộ v&agrave; đường sắt. Hiệp hội v&agrave; c&aacute;c doanh nghiệp đang t&iacute;nh to&aacute;n, tuy thế kh&aacute;ch h&agrave;ng c&oacute; thể tr&ocirc;ng đợi mức giảm gi&aacute; tối thiểu bằng đợt k&iacute;ch cầu lớn mấy năm trước&rdquo;, &ocirc;ng Nguyễn C&ocirc;ng Hoan, Trưởng Ban Truyền th&ocirc;ng HHDL Việt Nam n&oacute;i. Một số địa phương t&iacute;nh to&aacute;n, c&oacute; thể giảm 50% gi&aacute; dịch vụ v&agrave; ph&iacute; tham quan, g&oacute;p phần hạ gi&aacute; tua để h&uacute;t kh&aacute;ch.</p>\r\n\r\n<p>Đại diện Vietravel cho biết đơn vị n&agrave;y tung bộ sản phẩm k&iacute;ch cầu nội địa giảm tới 50% &aacute;p dụng cho cả tua trọn g&oacute;i, dịch vụ F&amp;E như kh&aacute;ch sạn, trọn g&oacute;i ph&ograve;ng kh&aacute;ch sạn k&egrave;m v&eacute; m&aacute;y bay với tuyến đường bay hoặc xe &ocirc; t&ocirc; với tuyến đường bộ. Một số sản phẩm nổi bật: trọn g&oacute;i 3 ng&agrave;y 2 đ&ecirc;m bay Bamboo Airways, ở kh&aacute;ch sạn 4 - 5 sao tại Ph&uacute; Quốc, Đ&agrave; Nẵng, Nha Trang, Quy Nhơn c&oacute; gi&aacute; chỉ từ 2,75 triệu đồng; tua du thuyền Hạ Long Paradise 5 sao trọn g&oacute;i chỉ từ 3,5 triệu đồng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Ocirc;ng Đinh Mạnh Thắng, Chủ tịch HHDL Thừa Thi&ecirc;n - Huế th&ocirc;ng b&aacute;o: c&aacute;c đơn vị lữ h&agrave;nh, vận chuyển dự kiến giảm 20% gi&aacute; dịch vụ, kh&aacute;ch sạn c&oacute; ch&iacute;nh s&aacute;ch ngủ 3 đ&ecirc;m t&iacute;nh tiền 2 đ&ecirc;m, c&aacute;c điểm tham quan di sản giảm đến 50% gi&aacute; v&eacute; từ nay đến hết th&aacute;ng 7. &Ocirc;ng Thắng cho rằng, để k&iacute;ch cầu hiệu quả cần sự li&ecirc;n kết chặt chẽ trong từng khu vực v&agrave; với c&aacute;c ng&agrave;nh kh&aacute;c, đặc biệt l&agrave; h&agrave;ng kh&ocirc;ng. Dịp nghỉ lễ 30/4 vừa qua, UBND tỉnh Thừa Thi&ecirc;n - Huế miễn ph&iacute; tham quan tại Quần thể di t&iacute;ch Cố đ&ocirc; Huế, thu h&uacute;t hơn 22 ngh&igrave;n lượt kh&aacute;ch.</p>\r\n\r\n<p>Kh&aacute;nh H&ograve;a-một trong số địa phương đ&oacute;n nhiều kh&aacute;ch quốc tế- thời gian tới chuyển hướng phục vụ kh&aacute;ch nội nhiều hơn. &Ocirc;ng Ho&agrave;ng Văn Vinh, Chủ tịch HHDL Kh&aacute;nh H&ograve;a th&ocirc;ng tin: hiệp hội phối hợp Sở Du lịch Kh&aacute;nh H&ograve;a cho ra mắt sản phẩm li&ecirc;n kết v&ugrave;ng TPHCM-Nha Trang-Ph&uacute; Y&ecirc;n-Đ&agrave; Lạt. &Ocirc;ng đề nghị HHDL Việt Nam l&agrave;m cầu nối, tạo điều kiện để c&aacute;c địa phương kết nối khai th&aacute;c tốt c&aacute;c chương tr&igrave;nh h&uacute;t kh&aacute;ch. Sau kỳ nghỉ 1/5, HHDL Kh&aacute;nh H&ograve;a vừa chủ tr&igrave; đ&oacute;n đo&agrave;n caravan doanh nh&acirc;n TPHCM đến Nha Trang-Kh&aacute;nh H&ograve;a để h&uacute;t kh&aacute;ch sau thời gian gi&atilde;n c&aacute;ch. Hơn 40 doanh nghiệp đăng k&yacute; chương tr&igrave;nh k&iacute;ch cầu tại Kh&aacute;nh H&ograve;a, dự kiến giảm gi&aacute; lưu tr&uacute;, ăn uống, vui chơi giải tr&iacute; từ 15-45%.</p>\r\n\r\n<p>H&acirc;m n&oacute;ng nhưng ng&agrave;nh du lịch cam kết đảm bảo y&ecirc;u cầu ph&ograve;ng chống dịch. Đại diện hiệp hội TPHCM đề nghị khi đ&oacute;n kh&aacute;ch, HHDL, doanh nghiệp du lịch c&aacute;c địa phương, c&aacute;c khu điểm du lịch phải k&yacute; cam kết về du lịch an to&agrave;n, thực hiện nghi&ecirc;m c&aacute;c quy định ph&ograve;ng chống dịch. Hội đồng tư vấn du lịch cũng c&oacute; &yacute; tưởng cần d&aacute;n nh&atilde;n c&aacute;c đơn vị lữ h&agrave;nh, du lịch, h&agrave;ng kh&ocirc;ng, vận chuyển hoặc khu du lịch đạt chuẩn phục vụ kh&aacute;ch lẫn ti&ecirc;u chuẩn ph&ograve;ng chống dịch.</p>\r\n\r\n<p><a href=\"https://cafebiz.cafebizcdn.vn/162123310254002176/2020/5/9/photo-1-1588985940882934284535.jpg\"><img alt=\"Tái khởi động kích cầu du lịch: Dân hưởng lợi - Ảnh 1.\" src=\"https://cafebiz.cafebizcdn.vn/thumb_w/640/162123310254002176/2020/5/9/photo-1-1588985940882934284535.jpg\" /></a></p>\r\n\r\n<p>Ng&agrave;nh du lịch phối hợp với h&agrave;ng kh&ocirc;ng để k&iacute;ch cầu du lịch Ảnh: HO&Agrave;NG MẠNH THẮNG</p>\r\n\r\n<blockquote>\r\n<p>&nbsp;</p>\r\n\r\n<p>&Ocirc;ng Vũ Thế B&igrave;nh, Ph&oacute; Chủ tịch HHDL Việt Nam cho biết, chương tr&igrave;nh k&iacute;ch cầu c&oacute; hai phương &aacute;n song song: Giảm gi&aacute; tua 20-30% nhưng giữ nguy&ecirc;n chất lượng dịch vụ, phương &aacute;n c&ograve;n lại l&agrave; giữ nguy&ecirc;n gi&aacute; nhưng tăng dịch vụ cao cấp. &Ocirc;ng B&igrave;nh khẳng định h&agrave;ng kh&ocirc;ng Việt Nam sẵn s&agrave;ng tham gia k&iacute;ch cầu du lịch nội địa; hiệp hội đ&agrave;m ph&aacute;n để c&oacute; gi&aacute; v&eacute; ưu đ&atilde;i, đảm bảo k&iacute;ch cầu đạt hiệu quả.</p>\r\n</blockquote>', 'upload/news\\1588912025.jpeg', 1, 0, 8, 2, 'tai-khoi-dong-kich-cau-du-lich-dan-huong-loi', NULL, '2020-09-07 21:27:05', '2020-09-07 21:27:05'),
(27, 'Son nhận thưởng ở khóa huấn luyện quân sự', 1, '<p>Tiền đạo Son Heung Min đạt th&agrave;nh t&iacute;ch cao nhất trong 157 thực tập sinh thuộc kh&oacute;a huấn luyện qu&acirc;n sự bắt buộc.</p>\r\n\r\n<p>&quot;Son vừa được trao giải thưởng Pilsung, một trong năm giải thưởng d&agrave;nh cho những người thể hiện tốt nhất&quot;, một quan chức&nbsp;Thủy qu&acirc;n lục chiến H&agrave;n Quốc n&oacute;i&nbsp;với tờ&nbsp;<em>Korea Herald</em>.</p>', '<h1>Son nhận thưởng ở kh&oacute;a huấn luyện qu&acirc;n sự</h1>\r\n\r\n<p>Tiền đạo Son Heung Min đạt th&agrave;nh t&iacute;ch cao nhất trong 157 thực tập sinh thuộc kh&oacute;a huấn luyện qu&acirc;n sự bắt buộc.</p>\r\n\r\n<p>&quot;Son vừa được trao giải thưởng Pilsung, một trong năm giải thưởng d&agrave;nh cho những người thể hiện tốt nhất&quot;, một quan chức&nbsp;Thủy qu&acirc;n lục chiến H&agrave;n Quốc n&oacute;i&nbsp;với tờ&nbsp;<em>Korea Herald</em>.</p>\r\n\r\n<p>Pilsung, tiếng H&agrave;n Quốc c&oacute; nghĩa &quot;nhất định chiến thắng&quot;, l&agrave; khẩu hiệu chiến đấu của Thủy qu&acirc;n lục chiến, Hải qu&acirc;n v&agrave; Kh&ocirc;ng qu&acirc;n nước n&agrave;y.</p>\r\n\r\n<p><img alt=\"Son Heung Min hoàn thành khóa huấn luyện bắt buộc.\" src=\"https://i1-thethao.vnecdn.net/2020/05/09/son-6903-1588990298.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=U5n_6fXz6Jd3m-mSHtLtrQ\" /></p>\r\n\r\n<p>Son Heung Min ho&agrave;n th&agrave;nh kh&oacute;a huấn luyện bắt buộc.</p>\r\n\r\n<p>Tranh thủ thời gian b&oacute;ng đ&aacute; bị ho&atilde;n do&nbsp;Covid-19, Son Heung Min trở về H&agrave;n Quốc&nbsp;<a href=\"https://vnexpress.net/son-khong-muon-cdv-chao-don-4083448.html\">tham gia kh&oacute;a huấn luyện</a>&nbsp;qu&acirc;n sự bắt buộc k&eacute;o d&agrave;i ba tuần từ ng&agrave;y 20/4. Tiền đạo 27 tuổi phải học c&aacute;c kỹ năng gồm: bắn s&uacute;ng, d&ugrave;ng lưỡi l&ecirc;, kiến thức về chiến tranh h&oacute;a học, sinh học, ph&oacute;ng xạ, chiến đấu c&aacute; nh&acirc;n,&nbsp;y tế. Theo&nbsp;<em>Korea Herald</em>, trong m&ocirc;n bắn s&uacute;ng, ng&ocirc;i sao Tottenham đạt điểm 10/10.</p>\r\n\r\n<p>&quot;Mọi&nbsp;kh&oacute;a học được đ&aacute;nh gi&aacute; một c&aacute;ch c&ocirc;ng bằng,&nbsp;nghi&ecirc;m ngặt. Son cũng đ&atilde; trải qua kh&oacute;a huấn luyện một c&aacute;ch trung thực&quot;, quan chức Thủy qu&acirc;n lục chiến cho biết th&ecirc;m.</p>\r\n\r\n<p>Kh&oacute;a huấn luyện&nbsp;của Son Heung Min diễn ra trong một căn cứ&nbsp;qu&acirc;n sự biệt lập ở ph&iacute;a nam đảo Jeju. Qu&acirc;n đội kh&ocirc;ng cho cơ quan truyền th&ocirc;ng tiếp cận, do lo ngại nguy cơ l&acirc;y&nbsp;Covid-19. Thay v&agrave;o đ&oacute;, t&agrave;i khoản Facebook của Thủy qu&acirc;n lục chiến đăng ảnh Son trong v&agrave; sau khi được huấn luyện.</p>\r\n\r\n<p>Ở H&agrave;n Quốc, mọi đ&agrave;n &ocirc;ng khỏe mạnh c&oacute; nghĩa vụ tham gia lực lượng vũ trang hai năm. Ch&iacute;nh quyền miễn cho một số trường hợp đặc biệt, như th&agrave;nh t&iacute;ch gi&agrave;nh HC v&agrave;ng Asiad 2018 của Son Heung Min v&agrave; đồng đội. Anh chỉ phải tham gia kh&oacute;a huấn luyện ba th&aacute;ng v&agrave; phục vụ cộng đồng 544 giờ trong 34 th&aacute;ng tới.</p>\r\n\r\n<p>Kết th&uacute;c kh&oacute;a huấn luyện ở H&agrave;n Quốc, Son Heung Min c&oacute; thể trở lại Anh chuẩn bị thi đấu trở lại. Trong thời gian b&oacute;ng đ&aacute; bị ho&atilde;n, tiền đạo của Tottenham&nbsp;kịp b&igrave;nh phục chấn thương tay. Giải Ngoại hạng Anh nhiều khả năng chơi nốt ch&iacute;n v&ograve;ng cuối từ ng&agrave;y 12/6.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://vnexpress.net/son-nhan-thuong-o-khoa-huan-luyen-quan-su-4096717.html\">https://vnexpress.net/son-nhan-thuong-o-khoa-huan-luyen-quan-su-4096717.html</a></p>', 'upload/news\\1588912804.jpeg', 1, 0, 7, 4, 'son-nhan-thuong-o-khoa-huan-luyen-quan-su', NULL, '2020-09-07 21:40:04', '2020-09-07 21:40:04'),
(28, 'Van Gaal: \'Woodward là thiên tài độc ác\'', 4, '<p>HLV Louis van Gaal tr&aacute;ch Ph&oacute; Chủ tịch điều h&agrave;nh&nbsp;Ed Woodward, khi&nbsp;&ocirc;ng bị Man Utd sa thải năm 2016.</p>\r\n\r\n<p>&quot;T&ocirc;i đổ lỗi cho Woodward nhiều hơn l&agrave; Jose Mourinho. Trong mắt t&ocirc;i, Woodward l&agrave; một thi&ecirc;n t&agrave;i &aacute;c độc&quot;, Van Gaal n&oacute;i tr&ecirc;n&nbsp;<em>Een-tweetje met Yves podcast&nbsp;</em>h&ocirc;m 8/5.</p>', '<p>HLV Louis van Gaal tr&aacute;ch Ph&oacute; Chủ tịch điều h&agrave;nh&nbsp;Ed Woodward, khi&nbsp;&ocirc;ng bị Man Utd sa thải năm 2016.</p>\r\n\r\n<p>&quot;T&ocirc;i đổ lỗi cho Woodward nhiều hơn l&agrave; Jose Mourinho. Trong mắt t&ocirc;i, Woodward l&agrave; một thi&ecirc;n t&agrave;i &aacute;c độc&quot;, Van Gaal n&oacute;i tr&ecirc;n&nbsp;<em>Een-tweetje met Yves podcast&nbsp;</em>h&ocirc;m 8/5.</p>\r\n\r\n<p><img alt=\"Woodward (trái) đưa Van Gaal về thay Ryan Giggs năm 2014. Ảnh: MUFC.\" src=\"https://i1-thethao.vnecdn.net/2020/05/09/gaal-1588957319-1588957329-8481-1588957375.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PIUaWiE8TKR-z0Cm_SKRNA\" /></p>\r\n\r\n<p>Woodward (tr&aacute;i) đưa Van Gaal về thay Ryan Giggs năm 2014. Ảnh:&nbsp;<em>MUFC</em>.</p>\r\n\r\n<p>Ng&agrave;y 21/5/2016, Van Gaal đoạt danh hiệu đầu ti&ecirc;n c&ugrave;ng Man Utd: C&uacute;p FA. CLB chủ s&acirc;n Old Trafford cũng chạm kỷ lục của Arsenal với 12 chức v&ocirc; địch c&uacute;p FA. Hai ng&agrave;y sau, Van Gaal v&agrave; c&aacute;c cộng sự H&agrave; Lan bị sa thải. Đến ng&agrave;y 27/5/2016, Man Utd bổ nhiệm Mourinho thế chỗ Van Gaal.</p>\r\n\r\n<p>Trước khi gia nhập Man Utd h&egrave; 2014, Van Gaal được chủ tịch Tottenham - Daniel Levy - ch&egrave;o k&eacute;o. &quot;Levy đ&atilde; đến tận nh&agrave; t&ocirc;i ở&nbsp;Noordwijk (H&agrave; Lan) để thương thảo v&agrave; ra về trong th&ugrave;ng xe v&igrave; l&uacute;c đ&oacute; Jack van Gelder (một ph&oacute;ng vi&ecirc;n địa phương) đang ở b&ecirc;n ngo&agrave;i&quot;, Van Gaal kể lại. &quot;Cuộc đ&agrave;m ph&aacute;n diễn ra k&eacute;o d&agrave;i, v&agrave; v&ocirc; t&igrave;nh &ocirc;ng ấy trao cho Man Utd cơ hội để đưa ra lời đề nghị với t&ocirc;i&quot;.</p>\r\n\r\n<p>Man Utd cũng l&agrave; đội b&oacute;ng cuối c&ugrave;ng Van Gaal dẫn dắt. &Iacute;t ph&uacute;t sau trận thắng Crystal Palace ở chung kết c&uacute;p FA năm 2016, Man Utd để lộ tin đồn rằng Van Gaal sẽ bị sa thải. Một số CĐV thậm ch&iacute; la &oacute; HLV H&agrave; Lan khi &ocirc;ng l&ecirc;n nhận huy chương.</p>\r\n\r\n<p>&quot;B&acirc;y giờ t&ocirc;i l&agrave; người nghỉ hưu&quot;, &ocirc;ng n&oacute;i. &quot;T&ocirc;i kh&ocirc;ng c&oacute; tham vọng trở th&agrave;nh gi&aacute;m đốc kỹ thuật hay chuy&ecirc;n gia b&oacute;ng đ&aacute; tr&ecirc;n truyền h&igrave;nh. C&aacute;ch đ&acirc;y 22 năm, vợ t&ocirc;i đ&atilde; nghỉ việc để theo t&ocirc;i ra nước ngo&agrave;i. T&ocirc;i từng n&oacute;i với b&agrave; ấy rằng t&ocirc;i sẽ nghỉ hưu ở tuổi 55, nhưng thực tế t&ocirc;i đ&atilde; k&eacute;o d&agrave;i đến tận 65 tuổi&quot;.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://vnexpress.net/van-gaal-woodward-la-thien-tai-doc-ac-4096642.html\">https://vnexpress.net/van-gaal-woodward-la-thien-tai-doc-ac-4096642.html</a></p>', 'upload/news\\1588912889.jpeg', 1, 0, 7, 4, 'van-gaal-woodward-la-thien-tai-doc-ac', NULL, '2020-09-07 21:41:29', '2020-09-07 21:41:29'),
(29, 'Đồng đội tiết lộ mâu thuẫn Ibrahimovic và Cavani', 5, '<p>Ibrahimovic lạnh nhạt với Cavani, suốt ba năm ở PSG, thậm ch&iacute; gh&eacute;t l&acirc;y những đồng đội th&acirc;n thiết với tiền đạo người Uruguay.</p>', '<p>ahimovic lạnh nhạt với Cavani, suốt ba năm ở PSG, thậm ch&iacute; gh&eacute;t l&acirc;y những đồng đội th&acirc;n thiết với tiền đạo người Uruguay.</p>\r\n\r\n<p>&quot;Zlatan Ibrahimovic lu&ocirc;n rạch r&ograve;i. Hoặc về phe anh ấy, hoặc chống lại. Tại PSG, anh ấy kết th&acirc;n với mọi người, kể cả HLV Laurent Blanc. Người duy nhất kh&ocirc;ng c&oacute; cảm gi&aacute;c ấy l&agrave; Edinson Cavani, một trong bốn đồng nghiệp Ibrahimovic kh&ocirc;ng thể chịu nổi suốt sự nghiệp&quot;,&nbsp;Michael Ciani n&oacute;i với<em>&nbsp;RMC Sports.</em></p>\r\n\r\n<p><img alt=\"Ibrahimovic và Cavani mâu thuẫn trong suốt ba năm chơi bóng cùng nhau. Ảnh: AFP.\" src=\"https://i1-thethao.vnecdn.net/2020/05/08/11-0-839028555-0050-k05E-U3707-8269-4096-1588928769.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=CzPrg-oHadwnwVVWF08i0Q\" /></p>\r\n\r\n<p>Ibrahimovic v&agrave; Cavani m&acirc;u thuẫn trong suốt ba năm chơi b&oacute;ng c&ugrave;ng nhau. Ảnh:&nbsp;<em>AFP.</em></p>\r\n\r\n<p>Ibrahimovic gia nhập PSG h&egrave; 2012 v&agrave; lập tức gi&uacute;p đội b&oacute;ng thủ đ&ocirc; Paris v&ocirc; địch Ligue 1. Cavani đầu qu&acirc;n cho PSG sau một năm, khi Ibrahimovic đ&atilde; l&agrave; một tượng đ&agrave;i. Tiền đạo người Uruguay kh&ocirc;ng được chơi tiền đạo cắm sở trường v&agrave; buộc phải chơi tiền đạo c&aacute;nh phải suốt ba năm chung chiến tuyến với đ&agrave;n anh Thụy Điển.</p>\r\n\r\n<p>Quan hệ m&acirc;u thuẫn giữa Ibrahimovic v&agrave; Cavani được đồn đo&aacute;n từ l&acirc;u, nhưng giờ mới được Ciani x&aacute;c nhận. &quot;Ibrahimovic thậm ch&iacute; kh&ocirc;ng chịu nổi cả những đồng đội th&acirc;n thiết với Cavani&quot;, cựu trung vệ người Ph&aacute;p nhấn mạnh.</p>\r\n\r\n<p>Chia sẻ của Ciani được thống k&ecirc; ủng hộ. Trong ba m&ugrave;a chơi c&ugrave;ng nhau, từ 2013 đến 2016, th&agrave;nh t&iacute;ch l&agrave;m b&agrave;n của Ibrahimovic lu&ocirc;n gấp rưỡi Cavani. Nhưng ngay khi tiền đạo sinh năm 1981 chuyển sang Man Utd h&egrave; 2016, Cavani lấy lại phong độ, v&agrave; đạt hiệu suất xấp xỉ một b&agrave;n một trận.</p>\r\n\r\n<p>Ciani từng chung đội LA Galaxy với Ibrahimovic năm 2018 v&agrave; nhiều lần hỏi chuyện với đ&agrave;n anh về sự nghiệp lẫy lừng.&nbsp;Anh từng chơi b&oacute;ng nhiều năm ở Ligue 1, trong m&agrave;u &aacute;o Auxerre, Lorient v&agrave; Bordeaux, nhưng chưa một lần chạm tr&aacute;n Ibrahimovic. Khi ch&acirc;n s&uacute;t người Thụy Điển đến PSG, Ciani đ&atilde; đầu qu&acirc;n cho Lazio.</p>\r\n\r\n<p>Ngo&agrave;i Cavani,&nbsp;Ciani c&ograve;n bổ sung Guardiola v&agrave;o danh s&aacute;ch bị Ibrahmovic gh&eacute;t. &quot;Kh&ocirc;ng nghi ngờ g&igrave; về việc ấy. Guardiola kh&ocirc;ng bao giờ bỏ qua khi Ibrahimovic kể nhiều chuyện nội bộ trong cuốn tự truyện&quot;, cựu cầu thủ 36 tuổi nhấn mạnh.</p>\r\n\r\n<p><strong>Thắng Nguyễn</strong>&nbsp;(theo&nbsp;<em>Gazzetta dello Sport</em>)</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://vnexpress.net/dong-doi-tiet-lo-mau-thuan-ibrahimovic-va-cavani-4096476.html\">https://vnexpress.net/dong-doi-tiet-lo-mau-thuan-ibrahimovic-va-cavani-4096476.html</a></p>', 'upload/news\\1588912964.jpeg', 1, 0, 7, 4, 'dong-doi-tiet-lo-mau-thuan-ibrahimovic-va-cavani', NULL, '2020-09-07 21:42:44', '2020-09-11 20:17:49'),
(30, 'Liverpool lập hat-trick giải thưởng', 6, '<p>Thủ qu&acirc;n Jordan Henderson, hậu vệ Trent Alexander-Arnold v&agrave; HLV Jurgen Klopp đều gi&agrave;nh giải thưởng của BBC Sport.</p>', '<p>hủ qu&acirc;n Jordan Henderson, hậu vệ Trent Alexander-Arnold v&agrave; HLV Jurgen Klopp đều gi&agrave;nh giải thưởng của BBC Sport.</p>\r\n\r\n<p>Henderson gi&agrave;nh giải Cầu thủ hay nhất m&ugrave;a 2019-2020. Anh nhận được 23% phiếu bầu từ người h&acirc;m mộ qua h&atilde;ng tin&nbsp;<em>BBC</em>, tr&ecirc;n 17% của đồng đội Sadio Mane v&agrave; 14% của tiền vệ Kevin De Bruyne.</p>\r\n\r\n<p><img alt=\"Henderson và HLV Klopp đều là những ngôi sao mùa này. Ảnh: Reuters.\" src=\"https://i1-thethao.vnecdn.net/2020/05/08/hen-1607-1588935038.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uoEsf4xXvO-7bAOzlo_HjQ\" /></p>\r\n\r\n<p>Henderson v&agrave; HLV Klopp đều l&agrave; những ng&ocirc;i sao m&ugrave;a n&agrave;y. Ảnh:&nbsp;<em>Reuters</em>.</p>\r\n\r\n<p>&quot;Henderson l&agrave; một cầu thủ tuyệt vời. Bạn c&oacute; thể thấy khi cậu ấy kh&ocirc;ng thi đấu, Liverpool nhớ cậu ấy ra sao&quot;, cựu trung vệ Rio Ferdinand n&oacute;i.</p>\r\n\r\n<p>Hậu vệ phải Trent Alexander-Arnold gi&agrave;nh giải Cầu thủ trẻ hay nhất. Ng&ocirc;i sao 21 tuổi nhận được 47% phiếu bầu, vượt xa so với 10% của Marcus Rashford v&agrave; 9% của Dean Henderson, thủ m&ocirc;n của Man Utd đang cho Sheffield United mượn.</p>\r\n\r\n<p>Giải HLV hay nhất thuộc về Jurgen Klopp, một th&agrave;nh vi&ecirc;n kh&aacute;c của Liverpool. &Ocirc;ng nhận tới 55% phiếu bầu, nhiều hơn gấp đ&ocirc;i so với 25% của Chris Wilder, HLV của Sheffield.</p>\r\n\r\n<p>M&ugrave;a 2019-2020 vẫn c&ograve;n ch&iacute;n v&ograve;ng. Liverpool đang dẫn đầu với 25 điểm nhiều hơn đội đứng thứ hai, đương kim v&ocirc; địch Man City. Thầy tr&ograve; Klopp chỉ cần kiếm th&ecirc;m s&aacute;u điểm để so&aacute;n ng&ocirc;i.</p>\r\n\r\n<p>30 năm qua, Liverpool chưa một lần v&ocirc; địch Anh. Cơn kh&aacute;t k&eacute;o d&agrave;i qua nhiều thập kỷ khiến thầy tr&ograve; Klopp quyết t&acirc;m hơn bao giờ hết v&agrave; phản &aacute;nh r&otilde; v&agrave;o phong độ m&ugrave;a n&agrave;y.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://vnexpress.net/liverpool-lap-hat-trick-giai-thuong-4096573.html\">https://vnexpress.net/liverpool-lap-hat-trick-giai-thuong-4096573.html</a></p>', 'upload/news\\1588913035.jpeg', 1, 0, 1, 4, 'liverpool-lap-hat-trick-giai-thuong', NULL, '2020-09-07 21:43:55', '2020-09-07 21:43:55'),
(31, 'SAO triệu đô đang ôm ấp người đẹp bị vợ chĩa súng vào đầu', 8, '<h3>(Tin thể thao, tin hậu trường) Cầu thủ 29 triệu USD bị vợ &quot;t&oacute;m gọn&quot; khi đang &quot;vui vẻ&quot; với người phụ nữ kh&aacute;c.</h3>', '<p>Sự kiện:&nbsp;<a href=\"https://www.24h.com.vn/muon-mau-the-thao-c101e4009.html\">Mu&ocirc;n m&agrave;u thể thao</a></p>\r\n\r\n<p>Theo New York Post (Mỹ), cầu thủ b&oacute;ng bầu dục Earl Thomas&nbsp;hiện đang chơi cho&nbsp;Baltimore Ravens đ&atilde; vướng v&agrave;o vụ scandal đ&atilde; g&acirc;y ầm ĩ tr&ecirc;n b&aacute;o ch&iacute; Mỹ. Cụ thể v&agrave;o h&ocirc;m 13/4, sau khi c&atilde;i v&atilde; với vợ, cầu thủ Thomas ra ngo&agrave;i đi chơi v&agrave; kh&ocirc;ng về nh&agrave;.&nbsp;</p>\r\n\r\n<p><img alt=\"SAO triệu đô đang ôm ấp người đẹp bị vợ chĩa súng vào đầu - 1\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-08/SAO-trieu-do-dang-vui-ve-voi-nguoi-dep-vo-bat-duoc-di-sung-vao-dau-bat2-1588930142-307-width660height371.jpg\" /></p>\r\n\r\n<p>Earl Thomas (tr&aacute;i) ngoại t&igrave;nh bị vợ&nbsp;Nina v&aacute;c s&uacute;ng đi t&igrave;m</p>\r\n\r\n<p>Vợ của Earl, Nina Thomas, sau đ&oacute; đột nhập v&agrave;o Snapchat của chồng&nbsp;để x&aacute;c định vị tr&iacute;. Nina&nbsp;t&igrave;m thấy một đoạn video của Earl, với một người phụ nữ kh&aacute;c, sau đ&oacute; c&ocirc; sử dụng t&agrave;i khoản mạng x&atilde; hội n&agrave;y để t&igrave;m tới nơi chồng đang ở.</p>\r\n\r\n<p>C&ugrave;ng với hai người phụ nữ kh&aacute;c, c&ocirc; vợ của Earl mang theo một khẩu s&uacute;ng, rồi t&igrave;m đến địa điểm m&agrave; chồng v&agrave; c&ocirc; g&aacute;i lạ đang ở.&nbsp;Bước v&agrave;o thấy chồng v&agrave; anh trai chồng, đang ở c&ugrave;ng hai c&ocirc; g&aacute;i kh&aacute;c,&nbsp;Nina lấy khẩu s&uacute;ng ra v&agrave; chĩa v&agrave;o đầu Earl (khoảng c&aacute;ch chưa đầy 1 bước ch&acirc;n), nhưng cầu thủ nhanh ch&oacute;ng giữ tay vợ để cướp s&uacute;ng.&nbsp;</p>\r\n\r\n<p>Vợ của Earl n&oacute;i rằng c&ocirc;&nbsp;chỉ hăm dọa chồng, nhưng mọi chuyện trở n&ecirc;n phức tạp khi trong s&uacute;ng c&oacute; một vi&ecirc;n đạn.&nbsp;Khi cảnh s&aacute;t đến, họ thấy Nina đang cầm một con dao v&agrave; đuổi theo Earl&nbsp;(người đang cầm&nbsp;s&uacute;ng), xung quanh một chiếc xe hơi.</p>\r\n\r\n<p><img alt=\"SAO triệu đô đang ôm ấp người đẹp bị vợ chĩa súng vào đầu - 2\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-08/SAO-trieu-do-dang-vui-ve-voi-nguoi-dep-vo-bat-duoc-di-sung-vao-dau-bat1-1588930142-633-width660height371.jpg\" /></p>\r\n\r\n<p>C&ocirc; vợ Nina sau vụ cầm s&uacute;ng đi t&igrave;m chồng bị cảnh s&aacute;t bắt</p>\r\n\r\n<p>Nina sau đ&oacute; bị cảnh s&aacute;t đưa về đồn về&nbsp;tội tấn c&ocirc;ng người kh&aacute;c bằng vũ kh&iacute; nguy hiểm, c&ograve;n g&atilde; chồng &quot;phản bội&quot;&nbsp;Earl lại được về nh&agrave;. Vụ &aacute;n g&acirc;y rối trật tự, d&ugrave;ng vũ kh&iacute; đe dọa người kh&aacute;c của hai vợ chồng cầu thủ c&oacute; khối t&agrave;i sản 29 triệu đ&ocirc; (theo Forbes) dự kiến được đưa ra x&eacute;t xử trong thời gian tới.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://www.24h.com.vn/the-thao/sao-trieu-do-dang-om-ap-nguoi-dep-bi-vo-chia-sung-vao-dau-c101a1147738.html\">https://www.24h.com.vn/the-thao/sao-trieu-do-dang-om-ap-nguoi-dep-bi-vo-chia-sung-vao-dau-c101a1147738.html</a></p>', 'upload/news\\1588913141.jpeg', 1, 0, 7, 4, 'sao-trieu-do-dang-om-ap-nguoi-dep-bi-vo-chia-sung-vao-dau', NULL, '2020-09-07 21:45:41', '2020-09-11 18:23:40'),
(32, 'Hot girl bóng chuyền Đặng Thu Huyền khoe ảnh gia đình, chờ ngày tái xuất', 7, '<p><strong>Hot girl b&oacute;ng chuyền Đặng Thu Huyền g&acirc;y sốt cộng đồng mạng</strong></p>\r\n\r\n<p>Tr&ecirc;n trang Facebook c&aacute; nh&acirc;n, hot girl b&oacute;ng chuyền Đặng Thu Huyền vừa chia sẻ h&igrave;nh ảnh hạnh ph&uacute;c b&ecirc;n gia đ&igrave;nh, nh&acirc;n dịp sinh nhật của bố m&igrave;nh. C&ocirc; n&agrave;ng sinh năm 2002 n&agrave;y v&agrave; gia đ&igrave;nh nhận được nhiều lời ch&uacute;c mừng của bạn b&egrave; đồng nghiệp v&agrave; người h&acirc;m mộ.</p>', '<p><strong>Hot girl b&oacute;ng chuyền Đặng Thu Huyền g&acirc;y sốt cộng đồng mạng</strong></p>\r\n\r\n<p>Tr&ecirc;n trang Facebook c&aacute; nh&acirc;n, hot girl b&oacute;ng chuyền Đặng Thu Huyền vừa chia sẻ h&igrave;nh ảnh hạnh ph&uacute;c b&ecirc;n gia đ&igrave;nh, nh&acirc;n dịp sinh nhật của bố m&igrave;nh. C&ocirc; n&agrave;ng sinh năm 2002 n&agrave;y v&agrave; gia đ&igrave;nh nhận được nhiều lời ch&uacute;c mừng của bạn b&egrave; đồng nghiệp v&agrave; người h&acirc;m mộ.</p>\r\n\r\n<p><img alt=\"Hot girl bóng chuyền Đặng Thu Huyền khoe ảnh gia đình, chờ ngày tái xuất - 1\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-07/Hot-girl-bong-chuyen-dang-Thu-Huyen-gay-sot-cong-dong-mang------ng-thu-huy---n-1588846561-579-width660height489.jpg\" /></p>\r\n\r\n<p>Đặng Thu Huyền hạnh ph&uacute;c b&ecirc;n gia đ&igrave;nh</p>\r\n\r\n<p>C&ograve;n nhớ hồi th&aacute;ng 4 vừa qua, Đặng Thu Huyền mới bước sang tuổi 18. Sau thời điểm kết th&uacute;c gi&atilde;n c&aacute;ch x&atilde; hội v&agrave; b&oacute;ng chuyền được ph&eacute;p tập luyện trở lại, trang Facebook c&aacute; nh&acirc;n của Thu Huyền nhờ vậy cũng hoạt động t&iacute;ch cực hơn. C&ocirc; đang tập luyện chăm chỉ để chuẩn bị cho những giải đấu quan trọng sau dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Hot girl bóng chuyền Đặng Thu Huyền khoe ảnh gia đình, chờ ngày tái xuất - 2\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-07/Hot-girl-bong-chuyen-dang-Thu-Huyen-gay-sot-cong-dong-mang-unnamed-1588846561-493-width660height496.jpg\" /></p>\r\n\r\n<p>Được tập luyện trở lại gi&uacute;p Thu Huyền (tr&aacute;i) trở n&ecirc;n hứng khởi</p>\r\n\r\n<p><img alt=\"Hot girl bóng chuyền Đặng Thu Huyền khoe ảnh gia đình, chờ ngày tái xuất - 3\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-07/Truyen-nhan-Kim-Hue-gay-2-1588857865-601-width660height495.jpg\" /></p>\r\n\r\n<p><img alt=\"Hot girl bóng chuyền Đặng Thu Huyền khoe ảnh gia đình, chờ ngày tái xuất - 4\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-07/Truyen-nhan-Kim-Hue-gay-3-1588857865-671-width660height495.jpg\" /></p>\r\n\r\n<p>Đặng Thu Huyền v&agrave; c&aacute;c đồng đội</p>\r\n\r\n<p>Đặng Thu Huyền được biết đến l&agrave; một trong những vận động vi&ecirc;n b&oacute;ng chuyền xinh nhất Việt Nam hiện nay. C&ocirc; g&aacute;i v&agrave;ng của đội Th&ocirc;ng tin Li&ecirc;n Việt Post Bank được kỳ vọng sẽ tiếp bước c&aacute;c đ&agrave;n chị Phạm Kim Huệ hay Nguyễn Linh Chi, vừa đẹp người vừa t&agrave;i năng, trở th&agrave;nh trụ cột của đội tuyển b&oacute;ng chuyền Việt Nam trong tương lai gần.</p>\r\n\r\n<p><strong>Trần Đ&igrave;nh Hiếu, hot boy l&agrave;ng b&oacute;ng chuyền tr&ocirc;ng như t&agrave;i tử Hollywood</strong></p>\r\n\r\n<p>Nổi l&ecirc;n từ tuyến trẻ của Tr&agrave;ng An Ninh B&igrave;nh, Trần Đ&igrave;nh Hiếu được người h&acirc;m mộ b&oacute;ng chuyền biết đến nhờ vẻ đẹp trai ấn tượng. Phụ c&ocirc;ng trẻ sinh năm 1996 khiến cộng đồng mạng x&ocirc;n xao v&igrave; những bức ảnh đẹp trai như t&agrave;i tử Hollywood.</p>\r\n\r\n<p>Trong vai tr&ograve; một người mẫu, Hiếu tỏa s&aacute;ng kh&ocirc;ng kh&aacute;c g&igrave; khi anh thi đấu tr&ecirc;n s&acirc;n b&oacute;ng. Những tấm h&igrave;nh được chị em theo d&otilde;i v&agrave; chia sẻ kh&aacute; nhiều.</p>\r\n\r\n<p><img alt=\"Hot girl bóng chuyền Đặng Thu Huyền khoe ảnh gia đình, chờ ngày tái xuất - 5\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-07/Hot-girl-bong-chuyen-dang-Thu-Huyen-gay-sot-cong-dong-mang-45769049_114569209547943_7238697151503532032_n-1588846932-921-width660height670.jpg\" /></p>\r\n\r\n<p>Trần Đ&igrave;nh Hiếu được nhiều người mến mộ tr&ecirc;n trang Facebook c&aacute; nh&acirc;n</p>\r\n\r\n<p>Trước đ&acirc;y, Trần Đ&igrave;nh Hiếu v&agrave; C&ugrave; Văn Ho&agrave;n l&agrave; hai ng&ocirc;i sao được đội b&oacute;ng Tr&agrave;ng An Ninh B&igrave;nh gửi sang Th&aacute;i Lan đ&agrave;o tạo tại CLB Kh&ocirc;ng qu&acirc;n ho&agrave;ng gia Th&aacute;i Lan. Đ&igrave;nh Hiếu hứa hẹn l&agrave; một trong những niềm hy vọng của b&oacute;ng chuyền nam Tr&agrave;ng An Ninh B&igrave;nh khi ch&agrave;ng trai n&agrave;y c&oacute; chiều cao 1,95m v&agrave; c&oacute; lối chơi kỹ thuật.</p>\r\n\r\n<p><strong>L&ecirc;n kế hoạch thu&ecirc; HLV ngoại</strong></p>\r\n\r\n<p>Những năm gần đ&acirc;y, th&agrave;nh t&iacute;ch của c&aacute;c đội tuyển b&oacute;ng chuyền Việt Nam đều chưa đạt được như kỳ vọng, với cả&nbsp;hai đội tuyển nam v&agrave; nữ. Để cải thiện th&agrave;nh t&iacute;ch v&agrave; hướng đến mục ti&ecirc;u xa hơn, việc t&igrave;m bằng được huấn luyện vi&ecirc;n ngoại cho c&aacute;c đội tuyển l&agrave; nhiệm vụ cấp b&aacute;ch đối với Li&ecirc;n đo&agrave;n B&oacute;ng chuyền Việt Nam.</p>\r\n\r\n<p>Ở thời điểm hiện tại, thu&ecirc; HLV ngoại l&agrave; phương &aacute;n được t&iacute;nh đến nhằm cải thiện th&agrave;nh t&iacute;ch của b&oacute;ng chuyền Việt Nam ở c&aacute;c giải đấu khu vực Đ&ocirc;ng Nam &Aacute; v&agrave; ch&acirc;u lục, chuẩn bị cho SEA Games 2021 tr&ecirc;n s&acirc;n nh&agrave;. Tuy vậy, ở thời điểm đại dịch Covid-19 đang ho&agrave;nh h&agrave;nh, Li&ecirc;n đo&agrave;n b&oacute;ng chuyền Việt Nam c&oacute; thể gặp kh&oacute; khăn lớn về mặt kinh ph&iacute; để mời thầy ngoại.</p>\r\n\r\n<p><strong>B&oacute;ng chuyền Việt Nam cần th&ecirc;m những Đinh Ho&agrave;ng Trai</strong></p>\r\n\r\n<p>Năm 2010, vận động vi&ecirc;n Supachai Jitjumroon xin nhập quốc tịch Việt Nam, lấy t&ecirc;n Đinh Ho&agrave;ng Trai. Anh l&agrave; vận động vi&ecirc;n b&oacute;ng chuyền đầu ti&ecirc;n nhập tịch ở Việt Nam. Đinh Ho&agrave;ng Trai (Supachai Jitjumroon) từng l&agrave; đội trưởng đội tuyển b&oacute;ng chuyền Th&aacute;i Lan với những năm th&aacute;ng rực rỡ tr&ecirc;n tuyển tại c&aacute;c kỳ đại hội&nbsp;<a href=\"https://www.24h.com.vn/the-thao-c101.html\">thể thao</a>&nbsp;khu vực.</p>\r\n\r\n<p>Chơi cho Tr&agrave;ng An Ninh B&igrave;nh v&agrave; gi&uacute;p c&ocirc;ng lớn trong việc đưa CLB l&ecirc;n ng&ocirc;i v&ocirc; địch năm 2008. Cầu thủ sinh năm 1979 trưởng th&agrave;nh từ hệ thống b&oacute;ng chuyền của trường Đại học Điện lực Bangkok. Chơi ở vị tr&iacute; phụ c&ocirc;ng, Supachai từng c&ugrave;ng ĐTQG Th&aacute;i Lan v&ocirc; địch SEA Games 7 lần, trong đ&oacute; c&oacute; 3 lần tr&ecirc;n cương vị đội trưởng.</p>\r\n\r\n<p>Nhận định về kế hoạch ph&aacute;t triển b&oacute;ng chuyền nam Việt Nam, c&aacute;c chuy&ecirc;n gia cho rằng kế hoạch trước mắt l&agrave; phải nhập tịch th&ecirc;m một số ng&ocirc;i sao b&oacute;ng chuyền l&agrave;m b&agrave;n đạp th&uacute;c đẩy. Tại SEA Games 30, đội tuyển b&oacute;ng chuyền nam Việt Nam thi đấu &ldquo;bết b&aacute;t&rdquo;, thua cả 3 trận ở v&ograve;ng bảng SEA Games 30 trước Indonesia, Philippines, Campuchia.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://www.24h.com.vn/the-thao/hot-girl-bong-chuyen-dang-thu-huyen-khoe-anh-gia-dinh-cho-ngay-tai-xuat-c101a1146988.html\">https://www.24h.com.vn/the-thao/hot-girl-bong-chuyen-dang-thu-huyen-khoe-anh-gia-dinh-cho-ngay-tai-xuat-c101a1146988.html</a></p>', 'upload/news\\1588913225.jpeg', 1, 0, 5, 3, 'hot-girl-bong-chuyen-dang-thu-huyen-khoe-anh-gia-dinh-cho-ngay-tai-xuat', NULL, '2020-09-07 21:47:05', '2020-09-07 21:47:05'),
(33, 'Federer dẫn đầu cuộc đua tay vợt vĩ đại nhất, Djokovic có vượt qua nổi?', 5, '<p>Ivan Lendl,&nbsp;tay vợt&nbsp;h&agrave;ng đầu thế giới những năm 1980 vừa thổi b&ugrave;ng cuộc tranh c&atilde;i về GOAT (vận động vi&ecirc;n vĩ đại nhất mọi thời đại) trong l&agrave;ng&nbsp;<a href=\"https://www.24h.com.vn/tennis-c119.html\">quần vợt</a>&nbsp;bằng những nhận định đầy t&iacute;nh chuy&ecirc;n m&ocirc;n. Ivan Lendl đ&atilde; c&ugrave;ng với c&acirc;y viết quần vợt nổi tiếng&nbsp;Craig Gabriel tranh luận về người xứng đ&aacute;ng nhất cho danh xưng GOAT ở bộ m&ocirc;n thể thao n&agrave;y.</p>', '<p><img alt=\"Federer dẫn đầu cuộc đua tay vợt vĩ đại nhất, Djokovic có vượt qua nổi? - 1\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-08/tennis-1-660-1588946089-788-width660height371.jpg\" /></p>\r\n\r\n<p>Cuộc đua tam m&atilde; trong kỷ nguy&ecirc;n mở</p>\r\n\r\n<p>Cựu tay vợt&nbsp;Ivan Lendl, gần đ&acirc;y được biết tới trong vai tr&ograve; HLV&nbsp;của&nbsp;Andy Murray v&agrave; Alexander Zverev, đ&atilde; lắng nghe những c&acirc;u hỏi của nh&agrave; b&aacute;o Craig Gabriel v&agrave; trả lời một c&aacute;ch hết sức chi tiết. &Ocirc;ng&nbsp;chia sẻ:&nbsp;&quot;T&ocirc;i kh&ocirc;ng khẳng định c&aacute;i t&ecirc;n n&agrave;o vĩ đại nhất bởi&nbsp;ch&uacute;ng t&ocirc;i ở&nbsp;hai thời đại kh&aacute;c nhau. Trong t&acirc;m tr&iacute; t&ocirc;i lu&ocirc;n l&agrave; h&igrave;nh b&oacute;ng của một tay vợt thế hệ trước, người từng&nbsp;gi&agrave;nh Grand Slam thứ hai trong kỷ nguy&ecirc;n mở, người sở hữu tổng cộng&nbsp;11 Grand Slam từ 1960 đến 1969, đ&oacute; l&agrave; Rod Laver&quot;.</p>\r\n\r\n<p><img alt=\"Federer dẫn đầu cuộc đua tay vợt vĩ đại nhất, Djokovic có vượt qua nổi? - 2\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-08/tennis-2-660-1588946120-816-width660height371.jpg\" /></p>\r\n\r\n<p>Federer đang dẫn đầu cuộc đua gi&agrave;nh&nbsp;GOAT</p>\r\n\r\n<p>&quot;T&ocirc;i nghĩ &ocirc;ng ấy l&agrave; người giỏi nhất trong thời kỳ trước kỷ nguy&ecirc;n mở. Sau đ&oacute;, trong kỷ nguy&ecirc;n mở, mọi chuyện trở n&ecirc;n dễ d&agrave;ng hơn một ch&uacute;t, bạn chỉ cần gi&agrave;nh những danh hiệu lớn v&agrave;&nbsp;chiến thắng trong những cuộc đối đầu trực tiếp. Ch&uacute;ng ta cần t&aacute;ch biệt hai điều đ&oacute;.&nbsp;</p>\r\n\r\n<p>Bạn n&ecirc;n hiểu rằng t&ocirc;i kh&ocirc;ng nh&igrave;n v&agrave;o phong độ, t&ocirc;i chỉ quan t&acirc;m đến kết quả. Usain Bolt c&oacute; phải một VĐV giỏi&nbsp;nhất từ ​​trước đến nay kh&ocirc;ng? C&oacute; chứ. Đối với t&ocirc;i, đ&oacute; l&agrave; kết quả, gi&agrave;nh được bao nhi&ecirc;u danh hiệu, mấy Grand Slam,&nbsp;đ&oacute; l&agrave; điều t&ocirc;i đ&aacute;nh gi&aacute; cao&quot;, Ivan Lendl nhận định.</p>\r\n\r\n<p>&quot;Kh&aacute; kh&oacute; khăn để lựa chọn tay vợt vĩ đại nhất mọi thời đại, bởi những c&aacute;i t&ecirc;n sau n&agrave;y vẫn c&ograve;n đang thi đấu. Đ&oacute; l&agrave; Novak (Djokovic), Rafa (Nadal) v&agrave; Roger (Federer), những người gi&agrave;nh nhiều danh hiệu v&agrave; được coi l&agrave; giỏi nhất trong kỷ nguy&ecirc;n mở.</p>\r\n\r\n<p>Bởi vậy, bạn chỉ c&oacute; thể n&oacute;i rằng nếu so s&aacute;nh&nbsp;Roger ở tuổi 20, Rafa l&uacute;c 19, hay&nbsp;Novak khi 17 tuổi,&nbsp;Roger sẽ l&agrave; người vĩ đại nhất. C&ograve;n l&uacute;c n&agrave;y, tất cả những g&igrave; bạn c&oacute; thể n&oacute;i l&agrave; Roger đang dẫn đầu cuộc đua, cuộc đua vẫn chưa kết th&uacute;c&quot;, cựu huyền thoại&nbsp;quần vợt sinh năm 1960 khẳng định.</p>\r\n\r\n<p><img alt=\"Federer dẫn đầu cuộc đua tay vợt vĩ đại nhất, Djokovic có vượt qua nổi? - 3\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-08/tennis-3-660-1588946162-399-width660height371.jpg\" /></p>\r\n\r\n<p>Liệu Nole c&oacute; tận dụng tốt cơ hội bứt ph&aacute; để vượt&nbsp;<a href=\"https://www.24h.com.vn/roger-federer-tay-vot-vi-dai-c101e3113.html\">Federer</a>&nbsp;trong tương lai?</p>\r\n\r\n<p>Trả lời cho c&acirc;u hỏi chừng n&agrave;o cuộc đua GOAT sẽ kết th&uacute;c,&nbsp;Ivan Lendl cho biết:&nbsp;&quot;Khi họ gi&atilde; từ sự nghiệp, cuộc đua sẽ kết th&uacute;c v&agrave; bạn sẽ c&oacute; thể ph&aacute;n đo&aacute;n ch&iacute;nh x&aacute;c hơn khi đ&oacute;. Hiện tại Roger đang dẫn đầu&nbsp;cuộc đua, nhưng n&oacute;&nbsp;c&ograve;n l&acirc;u mới kết th&uacute;c. Roger năm nay đ&atilde; 39 tuổi, c&ograve;n&nbsp;Novak v&agrave; Rafa th&igrave;&nbsp;trẻ hơn. Họ sẽ thi đấu l&acirc;u hơn một ch&uacute;t, v&igrave; vậy đ&acirc;y&nbsp;sẽ l&agrave; một bước ngoặt của cuộc đua&quot;.</p>\r\n\r\n<p>&quot;Novak c&oacute; lẽ lu&ocirc;n&nbsp;hiểu rằng anh ấy sẽ kh&ocirc;ng bao giờ vượt qua được&nbsp;Roger v&agrave; Rafa trong tr&aacute;i tim người h&acirc;m mộ.&nbsp;T&igrave;nh cảm của họ kh&ocirc;ng c&oacute; ph&eacute;p to&aacute;n n&agrave;o t&iacute;nh được v&agrave; thật kh&oacute; để l&yacute; giải.&nbsp;Ai nổi tiếng nhất l&agrave; một chuyện, nhưng ai l&agrave; tay vợt&nbsp;vĩ đại nhất sẽ kh&ocirc;ng được đ&aacute;nh gi&aacute; qua&nbsp;sự nổi tiếng. Bởi vậy,&nbsp;Novak vẫn c&ograve;n nhiều cơ hội khi anh ấy trẻ tuổi nhất&quot;, cựu số 1 thế giới kết luận.</p>', 'upload/news\\1588913282.jpeg', 1, 0, 5, 3, 'federer-dan-dau-cuoc-dua-tay-vot-vi-dai-nhat-djokovic-co-vuot-qua-noi', NULL, '2020-09-07 21:48:02', '2020-09-07 21:48:02'),
(34, 'Từ Hiểu Đông bị đánh chảy máu miệng - khâu 5 mũi, sắp có 2 trận tỉ thí', 4, '<p>Kể từ khi Từ Hiểu Đ&ocirc;ng nổi l&ecirc;n v&agrave;o năm 2017, v&otilde; sĩ MMA của Trung Quốc vẫn chưa thua trước bất cứ đối thủ n&agrave;o. Người h&acirc;m mộ v&otilde; thuật tại Trung Quốc, nhất l&agrave; những kh&aacute;n giả mến mộ v&otilde; cổ truyền đều mong muốn được nh&igrave;n thấy c&oacute; cao thủ đủ sức đ&aacute;nh bại Từ Hiểu Đ&ocirc;ng, nhưng đến nay vẫn chưa c&oacute; ai l&agrave;m được.&nbsp;</p>', '<p>Kể từ khi Từ Hiểu Đ&ocirc;ng nổi l&ecirc;n v&agrave;o năm 2017, v&otilde; sĩ MMA của Trung Quốc vẫn chưa thua trước bất cứ đối thủ n&agrave;o. Người h&acirc;m mộ v&otilde; thuật tại Trung Quốc, nhất l&agrave; những kh&aacute;n giả mến mộ v&otilde; cổ truyền đều mong muốn được nh&igrave;n thấy c&oacute; cao thủ đủ sức đ&aacute;nh bại Từ Hiểu Đ&ocirc;ng, nhưng đến nay vẫn chưa c&oacute; ai l&agrave;m được.&nbsp;</p>\r\n\r\n<p><img alt=\"Từ Hiểu Đông bị đánh chảy máu miệng - khâu 5 mũi, sắp có 2 trận tỉ thí - 1\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-08/Tu-Hieu-dong-bat-ngo-bi-de-tu-danh-chay-mau-mieng-khau-5-mui-tuhieudong1-1588924254-833-width660height452.jpg\" /></p>\r\n\r\n<p>Từ Hiểu Đ&ocirc;ng bị thương khi tập luyện c&ugrave;ng c&aacute;c học tr&ograve;</p>\r\n\r\n<p>Theo ghi nhận, trong những m&agrave;n thượng đ&agrave;i trước đ&oacute;, Từ Hiểu Đ&ocirc;ng kh&ocirc;ng thua m&agrave; cũng chẳng bị x&acirc;y xước bởi c&aacute;c đối thủ c&oacute; tr&igrave;nh độ qu&aacute; thấp so với tay đấm MMA. Duy nhất c&oacute; một lần, Từ Hiểu Đ&ocirc;ng đổ m&aacute;u l&agrave; khi thi đấu với v&otilde; sĩ Muay Th&aacute;i - Di&ecirc;m S&uacute;y, lần đ&oacute; tay đấm họ Từ bị r&aacute;ch đầu, chảy m&aacute;u mặt phải kh&acirc;u nhiều mũi.&nbsp;</p>\r\n\r\n<p>Mới đ&acirc;y, tr&ecirc;n trang twitter c&aacute; nh&acirc;n ch&iacute;nh Từ Hiểu Đ&ocirc;ng đăng tải h&igrave;nh ảnh bị thương ở v&ugrave;ng miệng. Theo chia sẻ của tay đấm 41 tuổi, do th&aacute;o đồ bảo vệ thi đấu c&ugrave;ng học tr&ograve; trong một buổi tập n&ecirc;n anh đ&atilde; bị đ&aacute;nh r&aacute;ch miệng, phải đi kh&acirc;u 5 mũi với chi ph&iacute;&nbsp;900 tệ (gần 3 triệu đồng) cho vết thương n&agrave;y.&nbsp;</p>\r\n\r\n<p><img alt=\"Từ Hiểu Đông bị đánh chảy máu miệng - khâu 5 mũi, sắp có 2 trận tỉ thí - 2\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-08/Tu-Hieu-dong-bat-ngo-bi-de-tu-danh-chay-mau-mieng-khau-5-mui-tuhieudong2-1588924254-769-width660height446.jpg\" /></p>\r\n\r\n<p>V&otilde; sĩ họ Từ th&ocirc;ng b&aacute;o phải v&agrave;o bệnh viện chăm s&oacute;c y tế h&ocirc;m 6/5</p>\r\n\r\n<p>Chia sẻ của Từ Hiểu Đ&ocirc;ng nhận được nhiều ch&uacute; &yacute; từ kh&aacute;n giả. Người h&acirc;m mộ tay đấm n&agrave;y coi đ&acirc;y l&agrave; chuyện thường khi tập v&otilde;, v&agrave; số gh&eacute;t v&otilde; sĩ họ Từ cảm thấy rất hả h&ecirc;.&nbsp;</p>\r\n\r\n<p>Hiện tại, Từ Hiểu Đ&ocirc;ng đang đứng trước 2 trận đấu lớn với Yi Long v&agrave;&nbsp;&quot;Kỳ nh&acirc;n Th&aacute;i cực quyền&quot; Vương Chiến Qu&acirc;n. Trở lại sau chấn thương, &quot;Đệ nhất Thiếu L&acirc;m&quot; Yi Long sẵn s&agrave;ng quyết chiến với Từ, v&agrave; cao thủ Th&aacute;i Cực Chiến Qu&acirc;n cũng muốn dạy cho tay đấm MMA một b&agrave;i học.</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://www.24h.com.vn/the-thao/tu-hieu-dong-bi-danh-chay-mau-mieng-khau-5-mui-sap-co-2-tran-ti-thi-c101a1147701.html\">https://www.24h.com.vn/the-thao/tu-hieu-dong-bi-danh-chay-mau-mieng-khau-5-mui-sap-co-2-tran-ti-thi-c101a1147701.html</a></p>', 'upload/news\\1588913361.jpeg', 1, 0, 5, 3, 'tu-hieu-dong-bi-danh-chay-mau-mieng-khau-5-mui-sap-co-2-tran-ti-thi', NULL, '2020-09-07 21:49:21', '2020-09-07 21:49:21');
INSERT INTO `news` (`id`, `news_title`, `author_id`, `news_summary`, `news_content`, `news_image`, `news_featured`, `news_view`, `tag_id`, `category_id`, `news_slug`, `deleted_at`, `created_at`, `updated_at`) VALUES
(35, 'MU không Alex Ferguson: Bóng ma nơi nhà hát', 7, '<p><strong>MU v&agrave; 7 năm kh&ocirc;ng Sir Alex</strong></p>\r\n\r\n<p>Thời điểm n&agrave;y c&aacute;ch nay tr&ograve;n 7 năm (ch&iacute;nh x&aacute;c l&agrave; 7 năm 1 ng&agrave;y, 8/5/2013),&nbsp;<a href=\"https://vietnamnet.vn/mu-tag37750.html\" target=\"_blank\">MU</a>&nbsp;lần thứ 20 trong lịch sử gi&agrave;nh chức v&ocirc; địch Anh. 13 chiến thắng trong số đ&oacute; thuộc kỷ nguy&ecirc;n Premier League (bắt đầu từ 1992-93).</p>', '<p><strong>MU v&agrave; 7 năm kh&ocirc;ng Sir Alex</strong></p>\r\n\r\n<p>Thời điểm n&agrave;y c&aacute;ch nay tr&ograve;n 7 năm (ch&iacute;nh x&aacute;c l&agrave; 7 năm 1 ng&agrave;y, 8/5/2013),&nbsp;<a href=\"https://vietnamnet.vn/mu-tag37750.html\" target=\"_blank\">MU</a>&nbsp;lần thứ 20 trong lịch sử gi&agrave;nh chức v&ocirc; địch Anh. 13 chiến thắng trong số đ&oacute; thuộc kỷ nguy&ecirc;n Premier League (bắt đầu từ 1992-93).</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/05/09/08/alex-ferguson-mu.jpg\"><img alt=\"MU không Alex Ferguson: Bóng ma nơi nhà hát\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/05/09/08/alex-ferguson-mu.jpg\" /></a>\r\n\r\n			<ul>\r\n				<li>&nbsp;</li>\r\n				<li>&nbsp;</li>\r\n				<li>&nbsp;</li>\r\n				<li>&nbsp;</li>\r\n			</ul>\r\n\r\n			<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>7 năm trước, Sir Alex Ferguson&nbsp;nghỉ hưu sau khi gi&uacute;p MU gi&agrave;nh vương miện Premier League</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>MU v&ocirc; địch m&ugrave;a 2012-13 &aacute;p đảo, với 11 điểm nhiều hơn đội &aacute; qu&acirc;n Man City. B&ecirc;n cạnh đ&oacute;, Quỷ đỏ sở hữu Vua ph&aacute; lưới Robin van Persie - t&aacute;c giả 26 b&agrave;n thắng.</p>\r\n\r\n<p>C&ugrave;ng với niềm vui chiến thắng, MU n&oacute;i lời chia tay HLV huyền thoại Sir Alex Ferguson.</p>\r\n\r\n<p>Sau nhiều lần tr&igrave; ho&atilde;n, v&igrave; sức khỏe, Sir Alex ch&iacute;nh thức nghỉ hưu, kh&eacute;p lại 27 năm tại vị v&agrave; gi&agrave;nh 38 danh hiệu kh&aacute;c nhau. Nổi bật l&agrave; c&uacute; ăn 3 m&ugrave;a 1998-99, với&nbsp;<a href=\"https://vietnamnet.vn/vn/the-thao/bong-da-quoc-te/bong-da-anh/\" target=\"_blank\">Premier League</a>, Champions League v&agrave; FA Cup.</p>\r\n\r\n<p>C&ugrave;ng với MU, Sir Alex viết những c&acirc;u chuyện kỳ diệu, từ phạm vi nước Anh đến s&acirc;n chơi ch&acirc;u lục.</p>\r\n\r\n<p>Sir Alex Ferguson x&acirc;y dựng cho MU phong c&aacute;ch ri&ecirc;ng, lu&ocirc;n khao kh&aacute;t chiến thắng, v&agrave; sản sinh những thế hệ cầu thủ xuất sắc.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/05/09/08/alex-ferguson-mu-2.jpg\"><img alt=\"MU không Alex Ferguson: Bóng ma nơi nhà hát\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/05/09/08/alex-ferguson-mu-2.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>13 trong 20 chức v&ocirc; địch Anh của MU diễn ra với Sir Alex Ferguson</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>7 năm tr&ocirc;i qua kể từ khi Sir Alex Ferguson r&uacute;t v&agrave;o hậu trường, MU vẫn lu&ocirc;n khắc khoải về h&igrave;nh ảnh của &ocirc;ng, một người m&agrave; khi hiện diện b&ecirc;n ngo&agrave;i đường bi&ecirc;n đều k&iacute;ch th&iacute;ch t&acirc;m l&yacute; chiến đấu mạnh mẽ cho cầu thủ.</p>\r\n\r\n<p>Kh&ocirc;ng Sir Alex, MU đang trải qua thời điểm đen tối, khi th&agrave;nh c&ocirc;ng li&ecirc;n tục xa l&aacute;nh CLB.</p>\r\n\r\n<p><strong>B&oacute;ng ma nơi nh&agrave; h&aacute;t</strong></p>\r\n\r\n<p>Trong kỷ nguy&ecirc;n Alex Ferguson, kể cả khi gặp kh&oacute; khăn, MU vẫn duy tr&igrave; được bản sắc rất ri&ecirc;ng về mặt lối chơi.</p>\r\n\r\n<p>Kể từ khi huyền thoại người Scotland nghỉ hưu, MU đ&aacute;nh mất bản sắc, v&agrave; mỗi năm lu&ocirc;n phải loay hoay để t&igrave;m lại ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/05/09/08/alex-ferguson-mu-3.jpg\"><img alt=\"MU không Alex Ferguson: Bóng ma nơi nhà hát\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/05/09/08/alex-ferguson-mu-3.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Những gi&aacute; trị m&agrave; Sir Alex Ferguson để lại cho MU kh&ocirc;ng thể đong đếm</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Kh&ocirc;ng&nbsp;<a href=\"https://vietnamnet.vn/sir-alex-ferguson-tag50703.html\" target=\"_blank\">Alex Ferguson</a>, MU thất bại về mọi mặt. Đấy l&agrave; những thất bại về chuyển nhượng, cũng như trong c&aacute;c cuộc chiến tr&ecirc;n s&acirc;n cỏ.</p>\r\n\r\n<p>Old Trafford đượcb mệnh danh Nh&agrave; h&aacute;t của những giấc mơ. Trong 7 năm qua, nh&agrave; h&aacute;t bị bao phủ bởi những b&oacute;ng ma v&agrave; sự ảm đạm.</p>\r\n\r\n<p>MU li&ecirc;n tục vung tiền để đầu tư đội h&igrave;nh, nhưng hiệu quả thu về kh&ocirc;ng cao. Trong 10 bản hợp đồng đắt gi&aacute; nhất lịch sử CLB, c&oacute; 9 diễn ra sau khi Sir Alex nghỉ hưu.</p>\r\n\r\n<p>Họ l&agrave; Paul Pogba, Harry Maguire, Romelu Lukaku, Angel Di Maria, Anthony Martial, Fred, Bruno Fernandes, Aaron Wan-Bissaka v&agrave; Juan Mata.</p>\r\n\r\n<p>C&oacute; những kẻ sớm phải rời Old Trafford. Một số kh&aacute;c hoặc ngồi dự bị, hoặc chưa xứng đ&aacute;ng với gi&aacute; trị m&agrave; CLB đầu tư.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/05/09/08/mu-premier-league.jpg\"><img alt=\"MU không Alex Ferguson: Bóng ma nơi nhà hát\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/05/09/08/mu-premier-league.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Trong 7 năm qua, x&eacute;t về kết quả, MU như một đội hạng hai</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Chỉ c&oacute; Maguire v&agrave; Bruno Fernandes mang lại niềm tin về tương lai tươi s&aacute;ng. Nhưng từ hy vọng đến thực tế vẫn l&agrave; khoảng c&aacute;ch kh&ocirc;ng hề ngắn.</p>\r\n\r\n<p>Ole Gunnar Solskjaer - người chịu ảnh hưởng lớn từ Sir Alex - đang cố gắng x&acirc;y dựng một MU như phong c&aacute;ch của người thầy cũ.</p>\r\n\r\n<p>Nhưng bản th&acirc;n Solskjaer c&oacute; nhiều hạn chế so với Sir Alex. Nổi bật l&agrave; c&aacute; t&iacute;nh. &Ocirc;ng chưa đủ mạnh v&agrave; quyết đo&aacute;n để xử l&yacute; những chuyện phức tạp nội bộ - v&iacute; dụ như th&oacute;i đỏng đảnh của Pogba.</p>\r\n\r\n<p>Trong 6 m&ugrave;a đ&atilde; qua thời hậu Alex Ferguson, chỉ 2 lần MU v&agrave;o top 4 Premier League. M&ugrave;a n&agrave;y, trước khi c&aacute;c trận đấu dừng lại v&igrave; Covid-19, Quỷ đỏ chưa thể tiến v&agrave;o nh&oacute;m 4 đội mạnh nhất.</p>\r\n\r\n<p>Xua tan b&oacute;ng ma ở nh&agrave; h&aacute;t v&agrave; đưa MU trở lại l&agrave; nhiệm vụ đầy gian nan với Solskjaer.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Đại Phong</strong></p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://vietnamnet.vn/vn/the-thao/bong-da-quoc-te/mu-7-nam-khong-alex-ferguson-mu-va-bong-ma-that-bai-639714.html\">https://vietnamnet.vn/vn/the-thao/bong-da-quoc-te/mu-7-nam-khong-alex-ferguson-mu-va-bong-ma-that-bai-639714.html</a></p>', 'upload/news\\1588913526.jpeg', 1, 0, 7, 4, 'mu-khong-alex-ferguson-bong-ma-noi-nha-hat', NULL, '2020-09-07 21:52:06', '2020-09-07 21:52:06'),
(36, 'Người Mã \'dọa\' tuyển Việt Nam: Thầy Park bình thản, thế mới hay!', 9, '<p>- Malaysia đang dồn to&agrave;n lực để đấu trận quyết định với tuyển Việt Nam ở v&ograve;ng loại World Cup 2022. Tuy nhi&ecirc;n, HLV Park Hang Seo vẫn kh&aacute; b&igrave;nh thản l&agrave; v&igrave; đ&acirc;u?</p>', '<p>1. Sau một thời gian chờ đợi, Li&ecirc;n đo&agrave;n b&oacute;ng đ&aacute; Malaysia vừa nhận được sự đồng &yacute; từ FIFA khi x&aacute;c nhận cầu thủ gốc Kosovo l&agrave; Liridon Krasnniqi đủ điều kiện kho&aacute;c &aacute;o những ch&uacute; hổ Malaya tham dự v&ograve;ng loại World Cup 2022.</p>\r\n\r\n<p>T&iacute;nh th&ecirc;m Liridon Krasnniqi đến thời điểm hiện tại đội b&oacute;ng của HLV Tan Chang Hoe đ&atilde; c&oacute; 5 ngoại binh nhập tịch gồm hậu vệ tr&aacute;i La&#39;Vere Corbin-Ong (gốc Anh), tiền vệ Brendan Gan v&agrave; bộ đ&ocirc;i tiền đạo Mohamadou Sumareh - Matthew Davies (c&ugrave;ng c&oacute; gốc Australia).</p>\r\n\r\n<p>Ngo&agrave;i những sự bổ sung n&oacute;i tr&ecirc;n, cặp đ&ocirc;i song sinh Ryan v&agrave; Declan Lambert đang chơi b&oacute;ng tại giải hạng dưới H&agrave; Lan cho CLB Den Bosch cũng được x&uacute;c tiến nhập tịch để&nbsp;<a href=\"https://vietnamnet.vn/tuyen-malaysia-tag187766.html\">tuyển Malaysia&nbsp;</a>tr&agrave;n trề hy vọng đ&aacute;nh bại tuyển Việt Nam ở v&ograve;ng loại World Cup 2022 v&agrave; AFF Cup 2020 tới.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2020/05/08/18/nguoi-ma-de-doa-tuyen-viet-nam-vi-dau-thay-park-binh-than.jpg\"><img alt=\"Người Mã \'dọa\' tuyển Việt Nam: Thầy Park bình thản, thế mới hay!\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/05/08/18/nguoi-ma-de-doa-tuyen-viet-nam-vi-dau-thay-park-binh-than.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Sau Sumareh, tuyển Malaysia đang nhập tịch th&ecirc;m kh&aacute; nhiều cầu thủ để đua với tuyển Việt Nam</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>2. Sau 5 lượt trận đầu ti&ecirc;n&nbsp;<a href=\"https://vietnamnet.vn/tuyen-viet-nam-tag69831.html\">tuyển Việt Nam</a>&nbsp;đang dẫn đầu bảng G với 11 điểm, nhiều hơn Malaysia 2 điểm. Dh&iacute;nh bởi thế cuộc đối đầu (lẽ ra diễn ra v&agrave;o th&aacute;ng 3 vừa qua ở s&acirc;n Bukit Jalil) giữa 2 đội gần như chung kết cho chiếc v&eacute;&nbsp;v&ograve;ng loại thứ 3 World Cup 2022.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; trận đấu thực sự quan trọng cho cả 2, bởi nếu tuyển Việt Nam chiến thắng sẽ tiến gần hơn đến c&aacute;nh cửa đi tiếp, trong khi đ&oacute; Malaysia cần 3 điểm để nu&ocirc;i hy vọng đi tiếp đồng thời c&oacute; thể ngăn thầy tr&ograve; HLV Park Hang Seo viết t&ecirc;n m&igrave;nh v&agrave;o lịch sử.</p>\r\n\r\n<p>Kh&ocirc;ng may cho tuyển Việt Nam trận đấu phải ho&atilde;n lại tới th&aacute;ng 10 v&igrave; dịch c&uacute;m Covid-19, v&agrave; để Malaysia tranh thủ thời gian nhập tịch th&agrave;nh c&ocirc;ng th&ecirc;m một ngoại binh khiến mọi thứ th&ecirc;m kh&oacute; khăn với thầy tr&ograve; &ocirc;ng Park.</p>\r\n\r\n<p>3. Những chuyển động của đối thủ đang khiến người h&acirc;m mộ b&oacute;ng đ&aacute; Việt Nam th&ecirc;m &ldquo;n&oacute;ng ruột&rdquo; khi đến l&uacute;c n&agrave;y V-League mới chuẩn bị trở lại, c&ugrave;ng l&uacute;c&nbsp;<a href=\"https://vietnamnet.vn/hlv-park-hang-seo-tag161113.html\">HLV Park Hang Seo</a>&nbsp;kh&ocirc;ng dễ d&agrave;ng c&oacute; được một đội h&igrave;nh mạnh nhất v&igrave; chấn thương, thẻ phạt.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"https://vnn-imgs-f.vgcloud.vn/2019/12/26/18/u23-vn-park.jpg\"><img alt=\"Người Mã \'dọa\' tuyển Việt Nam: Thầy Park bình thản, thế mới hay!\" src=\"https://vnn-imgs-f.vgcloud.vn/2019/12/26/18/u23-vn-park.jpg\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>nhưng với nền tảng sẵn c&oacute;, HLV Park Hang Seo đủ tự tin bất chấp đối thủ thay đổi đến đ&acirc;u</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>C&agrave;ng cảm thấy lo lắng hơn bởi đến l&uacute;c n&agrave;y chiến lược gia người H&agrave;n Quốc tỏ ra tương đối b&igrave;nh thản khi vẫn đi thiện nguyện, đồng thời ra s&acirc;n phủi... chơi b&oacute;ng những ng&agrave;y qua.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, lo lắng của người h&acirc;m mộ d&ugrave; kh&ocirc;ng thừa nhưng với t&igrave;nh h&igrave;nh hiện tại e rằng hơi... th&aacute;i qu&aacute;. Bởi thực tế, &ocirc;ng Park cũng đ&atilde; l&ecirc;n kế hoạch rất s&aacute;t cho tuyển Việt Nam ở chặng đường tới đ&acirc;y khi V-League trở lại.</p>\r\n\r\n<p>N&oacute;i r&otilde; hơn, v&agrave;o l&uacute;c n&agrave;y thuyền trưởng tuyển Việt Nam đang c&oacute; trong tay khoảng 35-40 cầu thủ chất lượng cao để chuẩn bị cho 2 giải đấu rất s&aacute;t nhau l&agrave; v&ograve;ng loại World Cup 2022 lẫn AFF Cup 2020 để sớm được tiết lộ v&agrave;o th&aacute;ng 9 tới, trong đ&oacute; c&oacute; kh&aacute; nhiều t&acirc;n binh.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ b&igrave;nh thản khi đảm bảo được qu&acirc;n số, chất lượng (d&ugrave; dự kiến), sơ đồ v&agrave; lối chơi mới cũng đ&atilde; được chiến lược gia người H&agrave;n Quốc l&ecirc;n sẵn để tới đ&acirc;y triển khai cho tuyển Việt Nam đủ khiến c&aacute;c đối thủ phải bất ngờ.</p>\r\n\r\n<p>Ngo&agrave;i ra, việc Malaysia nhập tịch một c&aacute;ch vội v&atilde; cũng chưa chắc khiến &ocirc;ng Park phải ng&aacute;n, bởi c&ograve;n li&ecirc;n quan đến sự ho&agrave; nhập, văn ho&aacute;, lối chơi chung &ndash; điều kh&ocirc;ng dễ c&oacute; thể th&agrave;nh c&ocirc;ng trong v&agrave;i th&aacute;ng.</p>\r\n\r\n<p>Bởi những điều kể tr&ecirc;n, c&ugrave;ng với bộ khung được coi chắc chắn, nhuần nhuyễn suốt 2 năm qua đủ l&agrave;m &ocirc;ng Park tự tin, kể cả khi Malaysia ra s&acirc;n với 11 cầu thủ nhập tịch...</p>', 'upload/news\\1588913605.jpeg', 1, 0, 1, 1, 'nguoi-ma-doa-tuyen-viet-nam-thay-park-binh-than-the-moi-hay', NULL, '2020-09-07 21:53:25', '2020-09-07 21:53:25'),
(37, 'Lực sĩ \"vào tù ra tội\", xăm trổ đáng sợ nhất thế giới có bạn gái xinh đẹp', 8, '<p>Jens Dalsgaard l&agrave; một c&aacute;i t&ecirc;n nổi tiếng trong l&agrave;ng thể h&igrave;nh Đan Mạch n&oacute;i ri&ecirc;ng v&agrave; thế giới n&oacute;i chung. Lực sĩ 31 tuổi n&agrave;y c&oacute; biệt danh &quot;Qu&aacute;i th&uacute;&quot; với v&ograve;ng ngực v&agrave; bắp tay to đột biến, cộng th&ecirc;m việc anh sở hữu&nbsp;đến 40 h&igrave;nh xăm tr&ecirc;n mặt v&agrave; khắp cơ thể v&ocirc; c&ugrave;ng đ&aacute;ng sợ.</p>', '<p>Jens Dalsgaard l&agrave; một c&aacute;i t&ecirc;n nổi tiếng trong l&agrave;ng thể h&igrave;nh Đan Mạch n&oacute;i ri&ecirc;ng v&agrave; thế giới n&oacute;i chung. Lực sĩ 31 tuổi n&agrave;y c&oacute; biệt danh &quot;Qu&aacute;i th&uacute;&quot; với v&ograve;ng ngực v&agrave; bắp tay to đột biến, cộng th&ecirc;m việc anh sở hữu&nbsp;đến 40 h&igrave;nh xăm tr&ecirc;n mặt v&agrave; khắp cơ thể v&ocirc; c&ugrave;ng đ&aacute;ng sợ.</p>\r\n\r\n<p><img alt=\"Lực sĩ &amp;#34;vào tù ra tội&amp;#34;, xăm trổ đáng sợ nhất thế giới có bạn gái xinh đẹp - 1\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-06/Luc-si-dang-so-nhat-the-gioi-an-9-bua-moi-ngay-ban-gai-xinh-nhu-mong-ripped-367679-1588745501-726-width660height393.jpg\" /></p>\r\n\r\n<p>Jens Dalsgaard xăm k&iacute;n mặt v&agrave; khắp cơ thể khiến nhiều người kinh h&atilde;i</p>\r\n\r\n<p>Dalsgaard c&oacute; v&ograve;ng ngực tới 140cm, chu vi c&aacute;nh tay 58cm&nbsp;v&agrave; ti&ecirc;u thụ trung b&igrave;nh đến 5.500 ca-lo mỗi ng&agrave;y. &quot;Người khổng lồ&quot; xăm trổ n&agrave;y c&oacute; tuổi thơ bất hạnh khi mẹ anh sớm qua đời v&igrave; chứng bệnh đa xơ cứng. Anh từng phải ngồi t&ugrave; 4 năm v&igrave; d&iacute;nh l&iacute;u đến hoạt động phi ph&aacute;p c&ugrave;ng c&aacute;c băng đảng x&atilde; hội đen địa phương.</p>\r\n\r\n<p>Mới đ&acirc;y, chia sẻ tr&ecirc;n tờ Daily Star, Jens Dalsgaard đ&atilde; tiết lộ bất ngờ về việc phải thụ &aacute;n trong trại giam đ&atilde; gi&uacute;p cuộc đời anh rẽ sang một hướng kh&aacute;c đầy t&iacute;ch cực, để trở th&agrave;nh một VĐV v&agrave; HLV thể h&igrave;nh th&agrave;nh c&ocirc;ng như hiện tại:</p>\r\n\r\n<p>&quot;T&ocirc;i đ&atilde; đ&aacute;nh mất ch&iacute;nh m&igrave;nh v&agrave; phải trả gi&aacute;&nbsp;trong t&ugrave;. Nhưng khi t&ocirc;i ở t&ugrave;, t&ocirc;i đ&atilde; được đề nghị thử một số liệu ph&aacute;p điều chỉnh h&agrave;nh vi v&agrave; nhận thức. Những liệu ph&aacute;p n&agrave;y đ&atilde; thay đổi cuộc sống của t&ocirc;i v&agrave; gi&uacute;p t&ocirc;i h&agrave;nh động theo c&aacute;ch đ&uacute;ng đắn m&agrave; m&igrave;nh suy&nbsp;nghĩ. N&oacute; khiến t&ocirc;i muốn gi&uacute;p đỡ người kh&aacute;c trở th&agrave;nh một&nbsp;phi&ecirc;n bản mạnh mẽ hơn, c&acirc;n đối hơn v&agrave; c&acirc;n bằng hơn so với&nbsp;ch&iacute;nh họ hiện tại.&quot;</p>\r\n\r\n<p><img alt=\"Lực sĩ &amp;#34;vào tù ra tội&amp;#34;, xăm trổ đáng sợ nhất thế giới có bạn gái xinh đẹp - 2\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-06/Luc-si-dang-so-nhat-the-gioi-an-9-bua-moi-ngay-ban-gai-xinh-nhu-mong-boy-to-ripped-bodybuilder-482310-1588745501-670-width660height439.jpg\" /></p>\r\n\r\n<p>Ch&agrave;ng trai người Đan Mạch đ&atilde; lột x&aacute;c từ một th&agrave;nh vi&ecirc;n băng đảng tội phạm phải ngồi t&ugrave; 4 năm th&agrave;nh một lực sĩ ki&ecirc;m HLV thể h&igrave;nh với cơ thể khổng lồ</p>\r\n\r\n<p>Dalsgaard tiếp tục thổ lộ: &quot;T&ocirc;i bắt đầu n&acirc;ng tạ khi đ&atilde; 18 tuổi, nhưng điều đ&oacute; chẳng&nbsp;c&oacute; g&igrave; nghi&ecirc;m trọng cả đ&acirc;u. Chỉ đến năm 2013, t&ocirc;i mới ph&aacute;t hiện ra tiềm năng thực sự của thể h&igrave;nh v&agrave; dinh dưỡng.</p>\r\n\r\n<p>T&ocirc;i lu&ocirc;n bị m&ecirc; hoặc bởi những cơ bắp cuồn cuộn&nbsp;v&agrave; t&ocirc;i đ&atilde; học được những phương ph&aacute;p&nbsp;để biến đổi&nbsp;cơ thể trong mơ của m&igrave;nh. T&ocirc;i đ&atilde; ăn đ&uacute;ng phương ph&aacute;p, uống bổ sung những chất cần thiết đ&uacute;ng c&aacute;ch v&agrave; học c&aacute;ch tập luyện hiệu quả nhất để x&acirc;y dựng khối lượng cơ bắp như mong muốn.&quot;</p>\r\n\r\n<p>Jens Dalsgaard tiết lộ rằng để c&oacute; được v&oacute;c d&aacute;ng khổng lồ như hiện tại,&nbsp;anh lu&ocirc;n duy tr&igrave; chế độ ăn đến 9 bữa mỗi ng&agrave;y, bao gồm 5 bữa ch&iacute;nh v&agrave; 4 bữa phụ với những thực phẩm ch&iacute;nh như ức g&agrave;, thịt b&ograve; nạc, c&aacute;, trứng g&agrave;, rau xanh, mỳ, gạo, khoai t&acirc;y, hạnh nh&acirc;n, sữa, chuối,.... C&aacute;c thực đơn cũng được thay đổi h&agrave;ng ng&agrave;y v&agrave; từng tuần để gi&uacute;p anh đảm bảo đủ chất v&agrave; kh&ocirc;ng bị ng&aacute;n.&nbsp;</p>\r\n\r\n<p>Lực sĩ nặng tới 120kg n&agrave;y cũng tu&acirc;n thủ chặt chẽ những b&agrave;i tập gym để n&acirc;ng cao sức bền v&agrave; độ săn chắc của cơ tay, cơ ch&acirc;n v&agrave; v&ugrave;ng ngực của m&igrave;nh.&nbsp;</p>\r\n\r\n<p>Jens Dalsgaard c&oacute; trang c&aacute; nh&acirc;n Instagram thu h&uacute;t đến hơn 45.000 người đăng k&yacute; &quot;Theo d&otilde;i&quot;. Anh cũng đ&atilde; t&igrave;m được &quot;b&oacute;ng hồng&quot; của đời m&igrave;nh. Đ&oacute; l&agrave; mỹ nh&acirc;n c&oacute; t&ecirc;n&nbsp;Frederikke, c&ocirc; bạn g&aacute;i đ&atilde; gắn b&oacute; với &quot;Qu&aacute;i th&uacute;&quot; Dalsgaard suốt 2 năm qua.</p>\r\n\r\n<p><img alt=\"Lực sĩ &amp;#34;vào tù ra tội&amp;#34;, xăm trổ đáng sợ nhất thế giới có bạn gái xinh đẹp - 3\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-05-06/Luc-si-dang-so-nhat-the-gioi-an-9-bua-moi-ngay-ban-gai-xinh-nhu-mong-bodybuilder-and-girlfriend-367693-1588745501-806-width660height665.jpg\" /></p>\r\n\r\n<p>Người đẹp&nbsp;Frederikke b&ecirc;n bạn trai &quot;Qu&aacute;i th&uacute;&quot;&nbsp;Jens Dalsgaard</p>\r\n\r\n<p>Mặc d&ugrave; sở hữu k&iacute;ch thước cơ thể khổng lồ nhưng Dalsgaard t&acirc;m sự anh&nbsp;vẫn muốn cơ thể m&igrave;nh to&nbsp;lớn hơn nữa: &quot;Đ&acirc;y l&agrave; niềm đam m&ecirc; của t&ocirc;i trong cuộc sống n&agrave;y. Sự chuyển đổi cơ thể của t&ocirc;i vẫn chưa kết th&uacute;c, t&ocirc;i vẫn c&ograve;n nhiều việc phải l&agrave;m. C&oacute; một trận chiến kh&ocirc;ng hồi kết giữa t&ocirc;i của hiện tại với phi&ecirc;n bản tốt hơn, mạnh hơn, lớn hơn, v&agrave;&nbsp;t&ocirc;i biết m&igrave;nh c&oacute; thể trở th&agrave;nh người như vậy.&quot;&nbsp;</p>\r\n\r\n<p>nguồn:&nbsp;<a href=\"https://www.24h.com.vn/the-thao/luc-si-vao-tu-ra-toi-dang-so-nhat-the-gioi-co-ban-gai-xinh-nhu-mong-c101a1146563.html\">https://www.24h.com.vn/the-thao/luc-si-vao-tu-ra-toi-dang-so-nhat-the-gioi-co-ban-gai-xinh-nhu-mong-c101a1146563.html</a></p>', 'upload/news\\1588913730.jpeg', 1, 0, 7, 4, 'luc-si-vao-tu-ra-toi-xam-tro-dang-so-nhat-the-gioi-co-ban-gai-xinh-dep', NULL, '2020-09-07 21:55:30', '2020-09-11 18:20:46'),
(39, 'dvcjhsdbhvcd  jbchjsdbchjbsd   cbjhsdbch', 10, '<p>dvcjhsdbhvcd &nbsp;jbchjsdbchjbsd &nbsp; cbjhsdbch</p>', '<p>dvcjhsdbhvcd &nbsp;jbchjsdbchjbsd &nbsp; cbjhsdbch</p>', 'upload/news\\1589103645.jpeg', 1, 0, 9, 12, 'dvcjhsdbhvcd-jbchjsdbchjbsd-cbjhsdbch', '2020-09-11 18:14:28', '2020-09-10 02:40:45', '2020-09-11 18:14:28'),
(41, '1Nga trở thành vùng dịch lớn thứ 5 trên thế giới', 6, '<p>Nga trở th&agrave;nh v&ugrave;ng dịch lớn thứ 5 tr&ecirc;n thế giới</p>', '<p>Nga trở th&agrave;nh v&ugrave;ng dịch lớn thứ 5 tr&ecirc;n thế giới</p>', 'upload/news\\1605562731.jpeg', 1, 0, 5, 2, '1nga-tro-thanh-vung-dich-lon-thu-5-tren-the-gioi', '2020-11-16 14:39:20', '2020-11-16 14:37:41', '2020-11-16 14:39:20');

